ScriptName DLC03WintScript

Begin GameMode


	if ( DLC03BS2.DLC03GhoulFlag == 1 )
		if ( GetDistance DLC03BS2WintTravelMarker < 100 )
			if ( GetDisabled == 0 )
				DLC03WintRef.Disable
			endif
		endif		
	endif

End

