ESP.ORIGINAL:
Scn	NVDLC02ZionArrivalCodeHolderScript

; This script handles the sequence of events upon arriving to Zion. The code takes into account player position in order to adjust the event ordering, and presentation.
; A codebox (scripted Actor-Zone volume) near the entrance into Zion runs the code in GameMode, and will set the appropriate quest stages when the White Legs' ambush plays out.
; Instead of only using Trigger Volumes for kick-starting NPC barks, this script will fire them when appropriate - although I also rely on XMarkerHeading's script-fields to fire barks.

; - Jorge (12/17/10)
; CES 1/21/11 - Added script to reduce health on H/D/E enemies to 50%.

Float	fTimer					; Operational timer variable.

Short	bRickyWalk				; 1 == Ricky starts pathing to the ambush point.
Int		iRickyConvo			; Increasing states in Ricky's convo with Stella.
Int		iJedConvo				; Increasing states in Jed's convo with PC.

Int		iCount					; Operational variable.
Int		bInit						; Variable to control the first set of events that need initialization upon arrival to Zion.
Int		iState						; Values represent states in the intro scene.

Short	bPlayerInterference	; 1 == PC has somehow interfered with critical scene states, and it's time to kick-start a quick version of the ambush event.
Short	bSceneStarts			; 1 == When Stella's first line ends, and players' controls are re-enabled.
Short	bResetVars				; To reset variables in case of player interference with the scripted scene.
Short	bPlayerRushes		; 1 == When players cross bridge without having dealt with any White Legs that complete NVDLC02MQ01 objective 10

Short nTribalHHealth
Short nTribalDHealth
Short nTribalEHealth



BEGIN GameMode

	If (bPlayerInterference == 0)
		If (iState == 0)
			Return;
		Elseif (iState == 1)
			If (bInit == 0)
				ForceWeather NVDLC02ZionValleyWeather 1;
				PlayMusic NVDLC02ZionPeaceful;
				Set bInit to 1;
			Else
				If (iJedConvo == 0)
					Set fTimer to (fTimer + GetSecondsPassed);
					If (fTimer >= 2)
						Set iJedConvo to 1;
						NVDLC02JedREF.EVP;
						Set fTimer to 0;
					Endif
				Elseif (iJedConvo == 2)
					Set fTimer to (fTimer + GetSecondsPassed);
					If (fTimer >= 0.5)
						Set iJedConvo to 3;
						NVDLC02StellaREF.EVP;
						Set fTimer to 0;
					Endif
				Elseif (iJedConvo == 4)
					Set fTimer to (fTimer + GetSecondsPassed);
					If (fTimer >= 0.5)
						Set iJedConvo to 5;
						NVDLC02JedREF.EVP;
						Set fTimer to 0;
					Endif
				Endif
			Endif
		Elseif (iState == 2)
			Set fTimer to (fTimer + GetSecondsPassed);
			If (iRickyConvo == 0)
				If (fTimer >= 2 && bSceneStarts == 1)
					Set iRickyConvo to 1;
					If (NVDLC02DialogueRicky.RickyLeaves == 0)
						NVDLC02RickyRef.EVP;
					Else
						NVDLC02GuardCM2Ref.EVP;
					Endif
					NVDLC02StellaREF.EVP;
					Set fTimer to 0;
					Set iState to 3;
					Return;
				Endif
			Elseif (iRickyConvo == 1)
				If (iCount == 0)
					NVDLC02StellaREF.PlayIdle LooseHandsOnHip;
					Set iCount to 1;
					Return;
				Elseif (iCount == 1 && fTimer >= 3)
					NVDLC02GuardAFRef.PlayIdle LooseLookingAround;
					Set iCount to 2;
					Return;
				Endif
			Elseif (iRickyConvo == 2)
				If (iCount == 0)
					If (NVDLC02DialogueRicky.PsychoProb == 1)
						NVDLC02RickyRef.SetAngle Z 45;
						;NVDLC02RickyRef.Say NVDLC02OpeningConversationA02;
						Set iCount to 1;
						Return;
					Else
						NVDLC02StellaRef.Say NVDLC02OpeningConversationB02;
						Set iCount to 1;
						Return;
					Endif
				Elseif (iCount == 1)

				Endif
			Elseif (iRickyConvo == 3)
				If (iCount == 0)
					If (NVDLC02DialogueRicky.PsychoProb == 1)
						NVDLC02RickyRef.SetAngle Z 228;
						NVDLC02StellaRef.Say NVDLC02OpeningConversationA03;
						Set iCount to 1;
						Return;
					Else
						NVDLC02RickyRef.Say NVDLC02OpeningConversationB03;
						Set iCount to 1;
						Return;
					Endif
				Elseif (iCount == 1)

				Endif
			Elseif (iRickyConvo == 4)
				If (iCount == 0)
					If (NVDLC02DialogueRicky.PsychoProb == 1)
						;NVDLC02RickyRef.Say NVDLC02OpeningConversationA04;
						Set iCount to 1;
						Return;
					Else
						NVDLC02StellaRef.Say NVDLC02OpeningConversationB04;
						Set iCount to 1;
						Return;
					Endif
					NVDLC02GuardAFRef.EVP;
				Elseif (iCount == 1)

				Endif
			Endif
		Elseif (iState == 3)
			If (iCount == 0)
				If NVDLC02JedRef.GetDistance NVDLC02JedIntroWalkMarker03REF <= 256
					NVDLC02JedRef.AddScriptPackage NVDLC02JedStageTwoThreeRunPatrol;
					Set iCount to 1;
				Endif
			Endif
		Elseif (iState == 4)
			Set fTimer to (fTimer + GetSecondsPassed);
			If (fTimer >= 4)
				Set iState to 5;
				Set fTimer to 0;
				Set iCount to 0;
				Return;
			Endif
		Elseif (iState == 5)
			Set fTimer to (fTimer + GetSecondsPassed);
			If (iCount == 0)
				NVDLC02WhiteLegsTribalMQ01REF.Enable;
				NVDLC02WhiteLegsTribalMQ01REF.SetGhost 1;
;				If GetStage NVDLC02MQ01 < 10
;					SetStage NVDLC02MQ01 10
;				Endif
				Set iCount to 1;
				Return
			Elseif (iCount == 1)
				If (NVDLC02GuardCMRef.GetDead == 1)
					NVDLC02WhiteLegsTribalMQ01REF.SetGhost 0;
					If (NVDLC02DialogueRicky.RickyLeaves == 0)
						If (NVDLC02DialogueRicky.PsychoProb == 0)
							NVDLC02RickyRef.AddScriptPackage NVDLC02IntroStageAmbushRickyTravel02Package;
						Else
							NVDLC02RickyRef.AddScriptPackage NVDLC02IntroStageAmbushRickyTravel03Package;
						Endif			
					Else
						NVDLC02GuardCM2Ref.AddScriptPackage NVDLC02IntroStageAmbushRickyTravel01Package;
					Endif
					Set iCount to 2;
					Set fTimer to 0;
				Endif
			Elseif (iCount == 2)
				If (fTimer >= 1)
					NVDLC02GuardAFRef.EVP;
					NVDLC02GuardAFRef.Say NVDLC02AmbushGuardBark01;
					Set iCount to 3;
					Set fTimer to 0;
					Return;
				Endif
			Elseif (iCount == 3)
				If (fTimer >= 2)
					Set iCount to 0;
					Set fTimer to 0;
					Set iState to 6;
					NVDLC02JedRef.EVP;
					NVDLC02StellaRef.EVP;
				Endif
			Endif
		Elseif (iState == 6)
			Set fTimer to (fTimer + GetSecondsPassed);
			If (iCount == 0)
				;NVDLC02GuardAFRef.EVP;
				NVDLC02WhiteLegsTribalMQ01BREF.Enable;
				NVDLC02WhiteLegsTribalMQ01BREF.SetGhost 1;
				If GetStage NVDLC02MQ01 < 10
					SetStage NVDLC02MQ01 10
				Endif
				PlayMusic NVDLC02ZionBattle;
				Set iCount to 1;
				Return
			Elseif (iCount == 1)
				If (NVDLC02GuardAFRef.GetDead == 1)
					NVDLC02WhiteLegsTribalMQ01BREF.SetGhost 0;
					NVDLC02WhiteLegsTribalMQ01GREF.Enable;
					NVDLC02StellaRef.ResetAI;
					If (NVDLC02DialogueRicky.RickyLeaves == 0)
						NVDLC02RickyRef.EVP;
					Else
						NVDLC02GuardCM2Ref.EVP;
					Endif
					Set iCount to 0;
					Set fTimer to 0;
					Set iState to 7;
				Else
					If (fTimer >= 9)
						If (NVDLC02WhiteLegsTribalMQ01REF.GetDead == 1)
							;ShowWarning "9 secs up"
							NVDLC02GuardAFRef.KillActor NVDLC02SaltUponWoundsRef 1;
							NVDLC02GuardAFRef.PlaceAtMe 25mmGrenadeExplosion;
							NVDLC02WhiteLegsTribalMQ01BREF.SetGhost 0;
							NVDLC02WhiteLegsTribalMQ01BREF.EVP;
							Return;
						Else
							If (fTimer >= 12)
								NVDLC02WhiteLegsTribalMQ01REF.Kill
								NVDLC02WhiteLegsTribalMQ01REF.PlaceAtMe 25mmGrenadeExplosion;
								NVDLC02StellaRef.AddScriptPackage NVDLC02IntroStageAmbushStellaTravel01Package;
								Return;
							Endif
						Endif
					Endif
				Endif
			Endif
		Elseif (iState == 7)
			Set fTimer to (fTimer + GetSecondsPassed);
			If (iCount == 0 && (NVDLC02WhiteLegsTribalMQ01BREF.GetDead == 1 || NVDLC02StellaRef.GetDead == 1))
				NVDLC02WhiteLegsTribalMQ01CREF.Enable;
				NVDLC02WhiteLegsTribalMQ01CREF.SetGhost 1;
				Set iCount to 1;
				Return
			Elseif (iCount == 1)
				If (NVDLC02RickyRef.GetDead == 1 || NVDLC02GuardCM2Ref.GetDead == 1)
					NVDLC02WhiteLegsTribalMQ01CREF.SetGhost 0;
					NVDLC02JedRef.EVP;
					NVDLC02StellaRef.ResetAI;
					Set iCount to 0;
					Set fTimer to 0;
					Set iState to 8;
				Endif
			Endif
		Elseif (iState == 8)
			Set fTimer to (fTimer + GetSecondsPassed);
			If (iCount == 0 && (NVDLC02WhiteLegsTribalMQ01CREF.GetDead == 1 || NVDLC02JedRef.GetDead == 1))
				NVDLC02WhiteLegsTribalMQ01FREF.Enable;
				NVDLC02WhiteLegsTribalMQ01FREF.SetGhost 1;
				Set iCount to 1;
				Return
			Elseif (iCount == 1)
				If (NVDLC02StellaRef.GetDead == 1)
					NVDLC02WhiteLegsTribalMQ01FREF.SetGhost 0;
					NVDLC02JedRef.EVP;
					If (NVDLC02WhiteLegsTribalMQ01g2Ref.GetDead == 0)
						NVDLC02WhiteLegsTribalMQ01g2REF.AddToFaction NVDLC02WhiteLegsFaction 1;
						NVDLC02WhiteLegsTribalMQ01g2REF.AddToFaction NVDLC02WhiteLegsAggressiveFaction 1;
					Endif
					Set iCount to 0;
					Set fTimer to 0;
					Set iState to 9;
				Endif
			Endif
		Elseif (iState == 9)
			Set fTimer to (fTimer + GetSecondsPassed);
			If (iCount == 0 && (NVDLC02WhiteLegsTribalMQ01FREF.GetDead == 1 || NVDLC02JedRef.GetDead == 1))
				NVDLC02WhiteLegsTribalMQ01HREF.Enable;
				NVDLC02WhiteLegsTribalMQ01HREF.SetGhost 1;
				Set nTribalHHealth to ((NVDLC02WhiteLegsTribalMQ01HREF.GetAV Health) * 0.5)
				NVDLC02WhiteLegsTribalMQ01HREF.SetAV Health nTribalHHealth

				Set iCount to 1;
				Return
			Elseif (iCount == 1)
				If (NVDLC02JedRef.GetDead == 1)
					NVDLC02WhiteLegsTribalMQ01HREF.SetGhost 0;
					NVDLC02WhiteLegsTribalMQ01HREF.AddToFaction NVDLC02WhiteLegsFaction 1;
					NVDLC02WhiteLegsTribalMQ01HREF.AddToFaction NVDLC02WhiteLegsAggressiveFaction 1;

					NVDLC02WhiteLegsTribalMQ01DREF.Enable;
					Set nTribalDHealth to ((NVDLC02WhiteLegsTribalMQ01DREF.GetAV Health) * 0.5)
					NVDLC02WhiteLegsTribalMQ01DREF.SetAV Health nTribalDHealth

					Set iCount to 2;
					Return;
				Endif
			Elseif (iCount == 2)
				NVDLC02WhiteLegsTribalMQ01EREF.Enable;
				Set nTribalEHealth to ((NVDLC02WhiteLegsTribalMQ01EREF.GetAV Health) * 0.5)
				NVDLC02WhiteLegsTribalMQ01EREF.SetAV Health nTribalEHealth

				Set iCount to 0;
				Set fTimer to 0;
				Set iState to 10;
				Return;
			Endif
		Elseif (iState == 10)
			If (iCount == 0)
				If (GetStageDone NVDLC02MQ01 100 == 1 || GetStageDone NVDLC02MQ06 10 == 1)
					NVDLC02WhiteLegsTribalMQ01REF.Disable;
					NVDLC02WhiteLegsTribalMQ01BREF.Disable;
					NVDLC02WhiteLegsTribalMQ01CREF.Disable;
					NVDLC02WhiteLegsTribalMQ01DREF.Disable;
					NVDLC02WhiteLegsTribalMQ01EREF.Disable;
					NVDLC02WhiteLegsTribalMQ01FREF.Disable;
					NVDLC02WhiteLegsTribalMQ01GREF.Disable;
					NVDLC02WhiteLegsTribalMQ01G2REF.Disable;
					NVDLC02WhiteLegsTribalMQ01HREF.Disable;
					Set iCount to 1;
					Return;
				Endif
			Elseif (iCount == 1)
				Set iCount to 0;
				Set iState to 11;
				Disable;
				Return;
			Endif
		Endif
	Else
		If (bResetVars >= 1)
			If (iCount == 0)
				NVDLC02WhiteLegsTribalMQ01DREF.Enable;
				Set nTribalDHealth to ((NVDLC02WhiteLegsTribalMQ01DREF.GetAV Health) * 0.5)
				NVDLC02WhiteLegsTribalMQ01DREF.SetAV Health nTribalDHealth
				Set iCount to 1;
				Return;
			Elseif (iCount == 1)
				NVDLC02WhiteLegsTribalMQ01EREF.Enable;
				Set nTribalEHealth to ((NVDLC02WhiteLegsTribalMQ01EREF.GetAV Health) * 0.5)
				NVDLC02WhiteLegsTribalMQ01EREF.SetAV Health nTribalEHealth
				Set iCount to 2;
				Return;
			Elseif (iCount == 2)
				If (NVDLC02WhiteLegsTribalMQ01REF.GetDisabled == 1)
					NVDLC02WhiteLegsTribalMQ01REF.Enable;
				Endif
				Set iCount to 3;
				Return;
			Elseif (iCount == 3)
				If (NVDLC02WhiteLegsTribalMQ01BREF.GetDisabled == 1)
					NVDLC02WhiteLegsTribalMQ01BREF.Enable;
				Endif
				Set iCount to 4;
				Return;
			Elseif (iCount == 4)
				If (NVDLC02WhiteLegsTribalMQ01CREF.GetDisabled == 1)
					NVDLC02WhiteLegsTribalMQ01CREF.Enable;
				Endif
				Set iCount to 5;
				Return;
			Elseif (iCount == 5)
				If (NVDLC02WhiteLegsTribalMQ01FREF.GetDisabled == 1)
					NVDLC02WhiteLegsTribalMQ01FREF.Enable;
				Endif
				Set iCount to 6;
				Return;
			Elseif (iCount == 6)
				If (NVDLC02WhiteLegsTribalMQ01HREF.GetDisabled == 1)
					NVDLC02WhiteLegsTribalMQ01HREF.Enable;
					Set nTribalHHealth to ((NVDLC02WhiteLegsTribalMQ01HREF.GetAV Health) * 0.5)
					NVDLC02WhiteLegsTribalMQ01HREF.SetAV Health nTribalHHealth
				Endif
				Set iCount to 7;
				Return;
			Elseif (iCount == 7)
				If (NVDLC02WhiteLegsTribalMQ01GREF.GetDisabled != 1)
					NVDLC02WhiteLegsTribalMQ01GREF.AddToFaction NVDLC02WhiteLegsFaction 1;
					NVDLC02WhiteLegsTribalMQ01GREF.AddToFaction NVDLC02WhiteLegsAggressiveFaction 1;
					NVDLC02WhiteLegsTribalMQ01GREF.EVP;
				Endif
				If (NVDLC02WhiteLegsTribalMQ01G2REF.GetDisabled != 1)
					NVDLC02WhiteLegsTribalMQ01G2REF.AddToFaction NVDLC02WhiteLegsFaction 1;
					NVDLC02WhiteLegsTribalMQ01G2REF.AddToFaction NVDLC02WhiteLegsAggressiveFaction 1;
					NVDLC02WhiteLegsTribalMQ01G2REF.EVP;
				Endif
				If (GetObjectiveDisplayed NVDLC02MQ01 10 != 1 && p20ZionTrailGenericQuest.bTraveledToZionViaTrail == 0)
					SetStage NVDLC02MQ01 10;
				Endif
				Set iCount to 8;
				Return;
			Elseif (iCount == 8)
				If (GetStageDone NVDLC02MQ01 100 == 1 || GetStageDone NVDLC02MQ06 10 == 1)
					NVDLC02WhiteLegsTribalMQ01REF.Disable;
					NVDLC02WhiteLegsTribalMQ01BREF.Disable;
					NVDLC02WhiteLegsTribalMQ01CREF.Disable;
					NVDLC02WhiteLegsTribalMQ01DREF.Disable;
					NVDLC02WhiteLegsTribalMQ01EREF.Disable;
					NVDLC02WhiteLegsTribalMQ01FREF.Disable;
					NVDLC02WhiteLegsTribalMQ01GREF.Disable;
					NVDLC02WhiteLegsTribalMQ01G2REF.Disable;
					NVDLC02WhiteLegsTribalMQ01HREF.Disable;
					Set iCount to 9;
					Return;
				Endif
			Elseif (iCount == 9)
				Set iCount to 10;
				Disable;
				Return;
			Endif
		Else
			NVDLC02WhiteLegsTribalMQ01REF.SetGhost 0;
			NVDLC02WhiteLegsTribalMQ01BREF.SetGhost 0;
			NVDLC02WhiteLegsTribalMQ01CREF.SetGhost 0;
			NVDLC02WhiteLegsTribalMQ01FREF.SetGhost 0;
			NVDLC02WhiteLegsTribalMQ01GREF.SetGhost 0;
			;If (bPlayerRushes == 0)
				SetEnemy NVDLC02HappyTrailsCaravanFaction NVDLC02PlayerFaction 0 0;
			;Endif
			Set iCount to 0;
			Set iState to 5;
			Set fTimer to 0;
			Set bResetVars to 1;
			Return;
		Endif
	Endif

END

Scn	NVDLC02ZionArrivalFailSafeTriggerSCRIPT

; Jorge S. (12/21/10)
; CES 1/21/11 - Added script to reduce health on H/D/E enemies to 50%.

Int		iFailSafe
Int		iCount

Short nTribalHHealth
Short nTribalDHealth
Short nTribalEHealth


Begin OnTriggerEnter Player

	Set iFailSafe to 1;

End

Begin GameMode

	If (iFailSafe == 1)
		If (NVDLC02ZionArrivalBox.iState >= 5)
			If (iCount == 0)
				NVDLC02WhiteLegsTribalMQ01DREF.Enable;
				Set nTribalDHealth to ((NVDLC02WhiteLegsTribalMQ01DREF.GetAV Health) * 0.5)
				NVDLC02WhiteLegsTribalMQ01DREF.SetAV Health nTribalDHealth

				Set iCount to 1;
				Return;
			Elseif (iCount == 1)
				NVDLC02WhiteLegsTribalMQ01EREF.Enable;
				Set nTribalEHealth to ((NVDLC02WhiteLegsTribalMQ01EREF.GetAV Health) * 0.5)
				NVDLC02WhiteLegsTribalMQ01EREF.SetAV Health nTribalEHealth

				Set iCount to 2;
				Return;
			Elseif (iCount == 2)
				Disable;
				Return;
			Endif
		Else
			Set NVDLC02ZionArrivalBox.iCount to 0;
			Set NVDLC02ZionArrivalBox.fTimer to 0;
			Set NVDLC02ZionArrivalBox.iState to 5;
			if NVDLC02JedRef.GetDisabled == 0
				NVDLC02JedRef.EVP;
			endif
			if NVDLC02GuardCMRef.GetDisabled == 0
				NVDLC02GuardCMRef.EVP;
			endif
			if NVDLC02StellaRef.GetDisabled == 0
				NVDLC02StellaRef.EVP;
			endif
			If (NVDLC02DialogueRicky.RickyLeaves == 0)
				if NVDLC02RickyRef.GetDisabled == 0
					NVDLC02RickyRef.EVP;
				endif
			Else
				if NVDLC02JedRef.GetDisabled == 0
					NVDLC02JedRef.EVP;
				endif
			Endif
			;If (NVDLC02ZionArrivalBox.bPlayerRushes == 0)
			;	Set NVDLC02ZionArrivalBox.bPlayerRushes to 1;
			;	Set NVDLC02ZionArrivalBox.bPlayerInterference to 1
			;Endif
			Return;
		Endif			
	Endif

End

scn NVDLC02ZionDoorSCRIPT


; Jorge S. 12/22/10
; Script to handle transition to Zion Valley.
; JSH 01/19/11 - Added auto-firing of New Vegas companions. Sorry, buddies - no Zion for you.
; OMP 02/21/2011- Added code to enable the radios when leaving Zion.


Ref rVCountryRadio;


BEGIN OnActivate

	If (IsActionRef Player == 1)
;		If (Player.GetItemCount NVDLC02ZionMap == 0)
;			ShowMessage NVDLC02NoCaravanMsg
;		Else

			If GetQuestCompleted NVDLC02MQ00 == 0
				
				set p20ZionTrailGenericQuest.bTraveledToZionViaPassage to 1
				set p20ZionTrailGenericQuest.bArrivedWithoutCaravan to 1
				SetStage  ZkbZionTrailQuest01 100

				ZkbZionTrailQuest01ActREF.Disable
				ZkbZionTrailQuest01ActREF.MarkForDelete			

				SetStage NVDLC02MQ00 255
				Set NVDLC02ZionArrivalBox.iCount to 10;
				Set NVDLC02ZionArrivalBox.iState to 11;
				NVDLC02ZionArrivalBox.disable
				
				IncrementScriptedChallenge NVDLC02AchievementRememberedZion
				NVDLC02JedRadioTAREF.Disable
				SetEssential NVDLC02HappyTrailsCaravanGuardAF 0
				SetEssential NVDLC02HappyTrailsCaravanGuardCM 0
				SetEssential NVDLC02HappyTrailsCaravanGuardCM2 0
				SetEssential NVDLC02Jed 0
				SetEssential NVDLC02Ricky 0
				SetEssential NVDLC02Stella 0

				NVDLC02GuardAFREF.SetGhost 0
				NVDLC02GuardCMREF.SetGhost 0
				NVDLC02JedREF.SetGhost 0
				NVDLC02RickyREF.SetGhost 0
				NVDLC02StellaREF.SetGhost 0

				NVDLC02GuardAFREF.Disable
				NVDLC02GuardCMREF.Disable
				NVDLC02JedREF.Disable
				NVDLC02RickyREF.Disable
				NVDLC02StellaREF.Disable

				NVDLC02WhiteLegsTribalMQ01REF.Disable;
				NVDLC02WhiteLegsTribalMQ01BREF.Disable;
				NVDLC02WhiteLegsTribalMQ01CREF.Disable;
				NVDLC02WhiteLegsTribalMQ01DREF.Disable;
				NVDLC02WhiteLegsTribalMQ01EREF.Disable;
				NVDLC02WhiteLegsTribalMQ01FREF.Disable;
				NVDLC02WhiteLegsTribalMQ01GREF.Disable;
				NVDLC02WhiteLegsTribalMQ01G2REF.Disable;
				NVDLC02WhiteLegsTribalMQ01HREF.Disable;
				
				DisablePlayerControls
				StartQuest NVDLC02XPCalculator
				Player.MoveTo NVDLC02StartMarker

				NVDLC02StoreCashRegisterREF.AddItem NVDLC02StoreDeskKey 1

				NVDLC02ZionArrivalForceStartTriggerREF.disable
				NVDLC02ZionArrivalFailSafeTriggerREF.disable
				set NVDLC02ZionArrivalWaitWarnCodeHolderREF.bDoOnce to 1

				SetStage NVDLC02MQ01 20
				set NVDLC02MQ01.bAmbushAvoided to 0

			EndIf

			If ( NVDLC02ZionToMojaveDoorREF.bNVRadiosOff == 0 )
				RNVTARef.Disable;
				RadioBlackMountainTAREF.Disable;
				Set rVCountryRadio To NVDLC02CountryRadioMarkerREF.GetLinkedREF;
				rVCountryRadio.Disable;
				Set NVDLC02ZionToMojaveDoorREF.bNVRadiosOff To 1;
			EndIf

			; --------------------------------	NV Quests Shutdown-------------------------------	\/														; 2/9/11 ETB - Shutting down all NCR/Legion Assassin's quests (Legion "Bad2" one was causing a framerate issue).
