Scn	NVDLC02ZionArrivalForceIntroStartTriggerSCRIPT

; Jorge S. (1/13/11)


Int		iFailSafe
Int		iCount

Begin OnTriggerEnter Player

	Set iFailSafe to 1;

End

Begin GameMode

	If (iFailSafe == 1)
		If (NVDLC02ZionArrivalBox.iState >= 5)
			If (iCount == 0)
				;NVDLC02WhiteLegsTribalMQ01DREF.Enable;
				Set iCount to 1;
				Return;
			Elseif (iCount == 1)
				;NVDLC02WhiteLegsTribalMQ01EREF.Enable;
				Set iCount to 2;
				Return;
			Elseif (iCount == 2)
				Disable;
				Return;
			Endif
		Else
			Set NVDLC02ZionArrivalBox.iCount to 0;
			Set NVDLC02ZionArrivalBox.fTimer to 0;
			Set NVDLC02ZionArrivalBox.iState to 5;
			if NVDLC02JedRef.GetDisabled == 0
				NVDLC02JedRef.EVP;
			endif
			if NVDLC02GuardCMRef.GetDisabled == 0
				NVDLC02GuardCMRef.EVP;
			endif
			if NVDLC02StellaRef.GetDisabled == 0
				NVDLC02StellaRef.EVP;
			endif
			If (NVDLC02DialogueRicky.RickyLeaves == 0)
				if NVDLC02RickyRef.GetDisabled == 0
					NVDLC02RickyRef.EVP;
				endif
			Else
				if NVDLC02JedRef.GetDisabled == 0
					NVDLC02JedRef.EVP;
				endif
			Endif
			;If (NVDLC02ZionArrivalBox.bPlayerRushes == 0)
			;	Set NVDLC02ZionArrivalBox.bPlayerRushes to 1;
			;	Set NVDLC02ZionArrivalBox.bPlayerInterference to 1
			;Endif
			Return;
		Endif			
	Endif

End

scn NVDLC04WeapFlareGunAreaEffectSCRIPT


; This script causes Abominations to flee when a flare lands near them.
; JSH 02/08/11
; JSH 05/09/11 - Added check to make sure Abomination is alive before running AI behavior modifications.
; CES 06/10/11 - Added in flare resistence to prevent people from spamming the flaregun on abominations.


Short nConfidenceRating;		; This stores the actors previous confidence rating


BEGIN ScriptEffectStart

	PlayMagicShaderVisuals Flames01;
	PlayMagicShaderVisuals Smoke01;

	; Grab the existing confidence and store it, then set it to zero and send them fleeing
	If ( GetIsCreatureType 3 == 1 ) && (GetActorValue Confidence > 0 ) && ( GetDead == 0 ) && (GetIsID NVCRMrHouse == 0); Abomination is alive and not currently a coward.

		Set nConfidenceRating to (GetActorValue Confidence);

		SetActorValue Confidence 0;

		ShowMessage NVDLC04AbominationFearMSG;

		ResetAI;

	EndIf

; #########################################
;Copied script for Zion Trail

	; Grab the existing confidence and store it, then set it to zero and send them fleeing
	If ( GetInFaction ZkbVault616Ghoul == 1 ) && (GetActorValue Confidence > 0 ) && ( GetDead == 0 ); V616 Ghoul is alive and not currently a coward.

		Set nConfidenceRating to (GetActorValue Confidence);

		SetActorValue Confidence 0;

		ShowMessage ZkbV616GhoulFearMSG;

		ResetAI;

	EndIf
; #########################################

END


BEGIN ScriptEffectFinish

	StopMagicShaderVisuals Flames01;
	StopMagicShaderVisuals Smoke01;

	; Restore their confidence and then reset their ai
	If ( GetIsCreatureType 3 == 1 ) && ( GetDead == 0 ) && (GetActorValue Confidence == 0 ) && (GetIsID NVCRMrHouse == 0)  ; Abomination

		SetActorValue Confidence nConfidenceRating;

		ResetAI;

	EndIf

; #########################################
;Copied script for Zion Trail

	; Restore their confidence and then reset their ai
	If ( GetInFaction ZkbVault616Ghoul == 1 ) && ( GetDead == 0 ) && (GetActorValue Confidence == 0 )  ; V616 Ghoul

		SetActorValue Confidence nConfidenceRating;

		ResetAI;

	EndIf
; #########################################

END

