					Endif
				Elseif ( nY3PlayerDrink10RadCurrent > nY3PlayerDrink10RadTotal )
					Set bY3SetPlayerDrink10RadTotal to 0;
					Set bSetRadsTotal to 0;
				Endif
			Endif
		Endif
; ------------	16 Rad Drinks ----------	\/
		If ( Player.GetItemCount NVDLC03PerkRelatedDrinks16RadLIST >= 0 );
