scn NVDLC03MQ01SCRIPT


; Quest script for NVDLC03MQ01.
; JSH 01/05/11
; SPW 01/11/11 - Adding a line to turn on crater encounters
; JSH 03/01/11 - Established secondary quest xp values.
; SPW 3/22/11 - Turning off force fields on PS3


Short bDoOnce;	; 1= Run block one.
Short bTimeSet	;	; 1 = Time has been initialized for initial arrival on Big Empty floor.


BEGIN GameMode

; JES 7/21/12 - Reducing all granted XP by 66%.  Levels over 35 ignored due to JSawyer level cap.


	If (bDoOnce == 0)

		Set bDoOnce to 1;
	
		If (Player.GetLevel < 5)
	
			;Total XP Awarded should be approximately 3000 ;JES - Now 1000
			Set NVDLC03Act1XP to (1000 * NVDLC03Act1XPPercentage / 2);
			Set NVDLC03Act2XP to (1000 * NVDLC03Act2XPPercentage / 4);
			Set NVDLC03Act3XP to (1000 * NVDLC03Act3XPPercentage / 3);

			Set NVDLC03SecondaryQuestXP to 75;
	
		ElseIf (Player.GetLevel < 10)
	
			;Total XP Awarded should be approximately 5000 ;JES - Now 1666
			Set NVDLC03Act1XP to (1666 * NVDLC03Act1XPPercentage / 2);
			Set NVDLC03Act2XP to (1666 * NVDLC03Act2XPPercentage / 4);
			Set NVDLC03Act3XP to (1666 * NVDLC03Act3XPPercentage / 3);

			Set NVDLC03SecondaryQuestXP to 125;
	
		ElseIf (Player.GetLevel < 15)
	
			;Total XP Awarded should be approximately 7500 ;JES - Now 2500
			Set NVDLC03Act1XP to (2500 * NVDLC03Act1XPPercentage / 2);
			Set NVDLC03Act2XP to (2500 * NVDLC03Act2XPPercentage / 4);
			Set NVDLC03Act3XP to (2500 * NVDLC03Act3XPPercentage / 3);

			Set NVDLC03SecondaryQuestXP to 175;
	
		ElseIf (Player.GetLevel < 20)
	
			;Total XP Awarded should be approximately 10000 ;JES - Now 3333
			Set NVDLC03Act1XP to (3333 * NVDLC03Act1XPPercentage / 2);
			Set NVDLC03Act2XP to (3333 * NVDLC03Act2XPPercentage / 4);
			Set NVDLC03Act3XP to (3333 * NVDLC03Act3XPPercentage / 3);

			Set NVDLC03SecondaryQuestXP to 250;
	
		ElseIf (Player.GetLevel < 25)
	
			;Total XP Awarded should be approximately 12000 ;JES - Now 4000
			Set NVDLC03Act1XP to (4000 * NVDLC03Act1XPPercentage / 2);
			Set NVDLC03Act2XP to (4000 * NVDLC03Act2XPPercentage / 4);
			Set NVDLC03Act3XP to (4000 * NVDLC03Act3XPPercentage / 3);

			Set NVDLC03SecondaryQuestXP to 300;
	
		ElseIf (Player.GetLevel < 30)
	
			;Total XP Awarded should be approximately 14000 ;JES - Now 4666
			Set NVDLC03Act1XP to (4666 * NVDLC03Act1XPPercentage / 2);
			Set NVDLC03Act2XP to (4666 * NVDLC03Act2XPPercentage / 4);
			Set NVDLC03Act3XP to (4666 * NVDLC03Act3XPPercentage / 3);

			Set NVDLC03SecondaryQuestXP to 350;
	
		ElseIf (Player.GetLevel < 35)
	
			;Total XP Awarded should be approximately 16000 ;JES - Now 5333
			Set NVDLC03Act1XP to (5333 * NVDLC03Act1XPPercentage / 2);
			Set NVDLC03Act2XP to (5333 * NVDLC03Act2XPPercentage / 4);
			Set NVDLC03Act3XP to (5333 * NVDLC03Act3XPPercentage / 3);

			Set NVDLC03SecondaryQuestXP to 400;
	
		ElseIf (Player.GetLevel < 40)
	
			;Total XP Awarded should be approximately 18000
			Set NVDLC03Act1XP to (18000 * NVDLC03Act1XPPercentage / 2);
			Set NVDLC03Act2XP to (18000 * NVDLC03Act2XPPercentage / 4);
			Set NVDLC03Act3XP to (18000 * NVDLC03Act3XPPercentage / 3);

			Set NVDLC03SecondaryQuestXP to 450;
	
		ElseIf (Player.GetLevel < 45)
	
			;Total XP Awarded should be approximately 21000
			Set NVDLC03Act1XP to (21000 * NVDLC03Act1XPPercentage / 2);
			Set NVDLC03Act2XP to (21000 * NVDLC03Act2XPPercentage / 4);
			Set NVDLC03Act3XP to (21000 * NVDLC03Act3XPPercentage / 3);

			Set NVDLC03SecondaryQuestXP to 525;
	
		Else
	
			;Total XP Awarded should be approximately 22000
			Set NVDLC03Act1XP to (22000 * NVDLC03Act1XPPercentage / 2);
			Set NVDLC03Act2XP to (22000 * NVDLC03Act2XPPercentage / 4);
			Set NVDLC03Act3XP to (22000 * NVDLC03Act3XPPercentage / 3);

			Set NVDLC03SecondaryQuestXP to 550;
	
		EndIf
	
		;set iNVDLC03Act1FinalXP to NVDLC03Act1XP * 2
		;set iNVDLC03Act2FinalXP to NVDLC03Act2XP * 2
		;set iNVDLC03Act3FinalXP to NVDLC03Act3XP * 2
		
		; SPW 1/11/11 - Turns on crater encounters
		SetStage NVDLC03Enc 10;
		; SPW 3/22/11 - Turns off force fields on PS3
		If (IsPS3 ==1)
			NVDLC03PS3ForceFieldREF.Disable;
		EndIf

	EndIf

END

SCN NVDLC04XPCalculatorSCRIPT


; Calculate XP for the DLC
; TLS 11/12/10
; TLS 03/14/2011 - Scritping standards update


short bXPDoOnce;						; This Variable runs the code once
short iNVDLC04Act1FinalXP;		; Act 1 final XP value
short iNVDLC04Act2FinalXP;		; Act 2 final XP value 
short iNVDLC04Act3FinalXP;		; Act 3 final XP value


BEGIN GameMode

; XP REWARD CALC
	If (bXPDoOnce == 0)
		Set bXPDOOnce to 1

		If (Player.GetLevel < 5)

			;Total XP Awarded should be approximately 3000 ;JES - Now 1000
			Set NVDLC04Act1XP to (1000 * NVDLC04Act1XPPercentage / 4);
			Set NVDLC04Act2XP to (1000 * NVDLC04Act2XPPercentage / 3);
			Set NVDLC04Act3XP to (1000 * NVDLC04Act3XPPercentage / 3);
			Set NVDLC04SecondaryQuestXP to 75;

		ElseIf (Player.GetLevel < 10)

			;Total XP Awarded should be approximately 5000 ;JES - Now 1666
			Set NVDLC04Act1XP to (1666 * NVDLC04Act1XPPercentage / 4);
			Set NVDLC04Act2XP to (1666 * NVDLC04Act2XPPercentage / 3);
			Set NVDLC04Act3XP to (1666 * NVDLC04Act3XPPercentage / 3);
			Set NVDLC04SecondaryQuestXP to 125;

		ElseIf (Player.GetLevel < 15)

			;Total XP Awarded should be approximately 7500 ;JES - Now 2500
			Set NVDLC04Act1XP to (2500 * NVDLC04Act1XPPercentage / 4);
			Set NVDLC04Act2XP to (2500 * NVDLC04Act2XPPercentage / 3);
			Set NVDLC04Act3XP to (2500 * NVDLC04Act3XPPercentage / 3);
			Set NVDLC04SecondaryQuestXP to 175;

		ElseIf (Player.GetLevel < 20)

			;Total XP Awarded should be approximately 10000 ;JES - Now 3333
			Set NVDLC04Act1XP to (3333 * NVDLC04Act1XPPercentage / 4);
			Set NVDLC04Act2XP to (3333 * NVDLC04Act2XPPercentage / 3);
			Set NVDLC04Act3XP to (3333 * NVDLC04Act3XPPercentage / 3);
			Set NVDLC04SecondaryQuestXP to 250;

		ElseIf (Player.GetLevel < 25)

			;Total XP Awarded should be approximately 12000 ;JES - Now 4000
			Set NVDLC04Act1XP to (4000 * NVDLC04Act1XPPercentage / 4);
			Set NVDLC04Act2XP to (4000 * NVDLC04Act2XPPercentage / 3);
			Set NVDLC04Act3XP to (4000 * NVDLC04Act3XPPercentage / 3);
			Set NVDLC04SecondaryQuestXP to 300;

		ElseIf (Player.GetLevel < 30)

			;Total XP Awarded should be approximately 14000 ;JES - Now 4666
			Set NVDLC04Act1XP to (4666 * NVDLC04Act1XPPercentage / 4);
			Set NVDLC04Act2XP to (4666 * NVDLC04Act2XPPercentage / 3);
			Set NVDLC04Act3XP to (4666 * NVDLC04Act3XPPercentage / 3);
			Set NVDLC04SecondaryQuestXP to 350;

		ElseIf (Player.GetLevel < 35)

			;Total XP Awarded should be approximately 16000 ;JES - Now 5333
			Set NVDLC04Act1XP to (5333 * NVDLC04Act1XPPercentage / 4);
			Set NVDLC04Act2XP to (5333 * NVDLC04Act2XPPercentage / 3);
			Set NVDLC04Act3XP to (5333 * NVDLC04Act3XPPercentage / 3);
			Set NVDLC04SecondaryQuestXP to 400;

		ElseIf (Player.GetLevel < 40)

			;Total XP Awarded should be approximately 18000
			Set NVDLC04Act1XP to (18000 * NVDLC04Act1XPPercentage / 4);
			Set NVDLC04Act2XP to (18000 * NVDLC04Act2XPPercentage / 3);
			Set NVDLC04Act3XP to (18000 * NVDLC04Act3XPPercentage / 3);
			Set NVDLC04SecondaryQuestXP to 450;

		ElseIf (Player.GetLevel < 45)

			;Total XP Awarded should be approximately 21000
			Set NVDLC04Act1XP to (21000 * NVDLC04Act1XPPercentage / 4);
			Set NVDLC04Act2XP to (21000 * NVDLC04Act2XPPercentage / 3);
			Set NVDLC04Act3XP to (21000 * NVDLC04Act3XPPercentage / 3);
			Set NVDLC04SecondaryQuestXP to 525;

		Else

			;Total XP Awarded should be approximately 22000
			Set NVDLC04Act1XP to (22000 * NVDLC04Act1XPPercentage / 4);
			Set NVDLC04Act2XP to (22000 * NVDLC04Act2XPPercentage / 3);
			Set NVDLC04Act3XP to (22000 * NVDLC04Act3XPPercentage / 3);
			Set NVDLC04SecondaryQuestXP to 550;

		endIf

		Set iNVDLC04Act1FinalXP to NVDLC04Act1XP * 2;
		Set iNVDLC04Act2FinalXP to NVDLC04Act2XP * 2;
		Set iNVDLC04Act3FinalXP to NVDLC04Act3XP * 2;

	endIf

END

