scn aaKillRoomTriggerSCRIPT

SHORT 	bPlayerDoOnce;
REF		KilledActor;


BEGIN OnTrigger

	If (GetActionRef != Player) && (GetActionRef != aaCaptorREF) && (GetActionRef != aaCaptor02REF) && (GetActionRef != aaCaptor03REF) && (GetActionRef != aaFordREF) && (GetActionRef != aaCooperREF)
		Set KilledActor to GetActionRef;
		;aaCaptor02REF.Sayto player aaNVB3Main03CompanionKilledBark
		KilledActor.removeallitems aaCompanionGearFootlockerREF; 
		KilledActor.Kill aaBrookshireREF;
		KilledActor.Disable
		KilledActor.Markfordelete
		set aaNVB3Main03.CompanionKillCount to aaNVB3Main03.CompanionKillCount + 1
	EndIf

END

ScriptName aaNVB3SlideShowTriggerSCRIPT
; Used to run this script once for intro and once for ending slide sequences. ; 0 - Neither intro nor ending slide sequences have run ; 1 - Intro slide sequence has started ; 2 - Ending slide sequence has started short bRunScript;
BEGIN OnTriggerEnter Player
; Intro slide check If ( GetQuestRunning aaNVB3IntroSlides ) && ( bRunScript == 0 )
Set bRunScript To 1;
; aaNVB3NarratorREF.SayTo Player aaNVB3IntroSlidesTOPIC01;
EndIf
; Ending slide check If ( GetQuestRunning aaNVB3Ending ) && ( bRunScript == 1 )
Set bRunScript To 2;
aaNVB3NarratorREF.SayTo Player aaNVB3EndingTopic01Intro; setquestdelay aaNVB3Ending 5 disable markfordelete
EndIf
END

