ESP.ORIGINAL:
scn NVDLC03X8TunnelWildWastelandCheckSCRIPT


;script that checks if player has added or removed the wildwasteland perk when entering the x8 tunnels, and repositions the cyberdogs, if needed
;JDJ 04/06/2011


BEGIN OnTriggerEnter Player

		If (Player.HasPerk WildWasteland == 1) && (X8TunnelEntryTrigger.bDogBehavior ==0)				;switches cyberdog package to playing poker if player has wild wasteland
			Set X8TunnelEntryTrigger.bDogBehavior to 1;
			X8TunnelCyberDog01.ResetAI;
			X8TunnelCyberDog02.ResetAI;
			X8TunnelCyberDog03.ResetAI;
			X8TunnelCyberDog04.ResetAI;
			X8TunnelCyberDog05.ResetAI;
			X8TunnelCyberDog01.Moveto X8DogPokerMarker01;
			X8TunnelCyberDog02.Moveto X8DogPokerMarker02;
			X8TunnelCyberDog03.Moveto X8DogPokerMarker03;
			X8TunnelCyberDog04.Moveto X8DogPokerMarker04;
			X8TunnelCyberDog05.Moveto X8DogPokerMarker05;
		ElseIf (Player.HasPerk WildWasteland == 0) && (X8TunnelEntryTrigger.bDogBehavior == 1)		;switches dogs to looking at the chalkboard if player doesn't have wildwasteland	
			Set X8TunnelEntryTrigger.bDogBehavior to 0;
			X8TunnelCyberDog01.ResetAI;
			X8TunnelCyberDog02.ResetAI;
			X8TunnelCyberDog03.ResetAI;
			X8TunnelCyberDog04.ResetAI;
			X8TunnelCyberDog05.ResetAI;
			X8TunnelCyberDog01.Moveto X8DogBlackBoardMarker01;
			X8TunnelCyberDog02.Moveto X8DogBlackBoardMarker02;
			X8TunnelCyberDog03.Moveto X8DogBlackBoardMarker03;
			X8TunnelCyberDog04.Moveto X8DogBlackBoardMarker04;
			X8TunnelCyberDog05.Moveto X8DogBlackBoardMarker05;
		EndIf

END

scn NVDLC03X13CrRobobrainSCRIPT

; script control to wake up turrets onhit
; RL 4/5/11

Short bOnce;		;do once to prevent spamming


BEGIN OnHit Player

	Set X13TurretCodeBoxREF.bAngry to 1;
	X13TurretCodeBoxREF.Enable;

END


BEGIN OnDeath

	If (NVDLC03X13Setup.nTest == 0)
		If (NVDLC03X13Setup.bCheat == 0) && (X13Robot1REF.GetDead) && (X13Robot2REF.GetDead) && (X13Robot3REF.GetDead) && (GetQuestCompleted NVDLC03X13Setup == 0)
			NVDLC03StealthSuitVoiceCrREF.Say StealthsuitX13RobotsBlownUpBark 1 NVDLC03StealthSuitVoiceCr 1;
			Set NVDLC03X13Setup.bCheat to 1;
		ElseIf (NVDLC03X13Replay.bCheat == 0) && (X13Robot1REF.GetDead) && (X13Robot2REF.GetDead) && (X13Robot3REF.GetDead) && (GetQuestCompleted NVDLC03X13Setup == 1)
			NVDLC03StealthSuitVoiceCrREF.Say StealthsuitX13RobotsBlownUpBark 1 NVDLC03StealthSuitVoiceCr 1;
			Set NVDLC03X13Replay.bCheat to 1;
		EndIf
	EndIf

END

ScriptName NVDLC03HQBuddyBarkTimerSCRIPT


; HQ Buddy barks random stuff when player is near them.
; OMP 03/29/2011
; OMP 04/01/2011 - Changed the condition checking for the installation of the Sink Main Computer Holotape.
; OMP 04/01/2011 - Reworked the logic determining when barks happen. Added code for the Player Brain barks.
; OMP 04/06/2011 - Prevent the barks from occurring if the Sink personalities are turned off.


Int		nBarker;							; Used to determine who's going to try to bark
Float	fBarkTimer;						; Used to keep track of how long it's been since the last bark.
Float	fNextBarkDelay;				; Used to determine how long to wait before next bark.

; Vars used to know who's going to bark a line.
Short	bAutoDocBarks;
Short	bBioLabBarks;
Short	bBookChuteBarks;
Short	bCentralIntelBarks;
Short	bJukeboxBarks;
Short	bLightSwitch01Barks;
Short	bLightSwitch02Barks;
Short bToasterBarks;
Short bMuggyBarks;
Short bPBrainBarks;

; Vars used to track how long it's been since last time Buddy barked.
Float	fAutoDocTimer;
Float	fBioLabTimer;
Float	fBookChuteTimer;
Float	fCentralIntelTimer;
Float	fJukeboxTimer;
Float	fLightSwitch01Timer;
Float	fLightSwitch02Timer;
Float	fMuggyTimer;
Float	fToasterTimer;
Float	fPBrainTimer;

; Vars used by triggers to determine which room the player is in.
Short	bRoom1;
Short	bRoom2;
Short	bRoom3;
Short	bRoom4;
Short	bRoom5;


BEGIN GameMode

	If ( Player.GetInCell NVDLC03TheSink == 1 )
		; Decrement everyone's bark timer
		Set fAutoDocTimer To ( fAutoDocTimer - GetSecondsPassed );
		Set fBioLabTimer To ( fBioLabTimer - GetSecondsPassed );
		Set fBookChuteTimer To ( fBookChuteTimer - GetSecondsPassed );
		Set fCentralIntelTimer To ( fCentralIntelTimer - GetSecondsPassed );
		Set fJukeboxTimer To ( fJukeboxTimer - GetSecondsPassed );
		Set fLightSwitch01Timer To ( fLightSwitch01Timer - GetSecondsPassed );
		Set fLightSwitch02Timer To ( fLightSwitch02Timer - GetSecondsPassed );
		Set fMuggyTimer To ( fMuggyTimer - GetSecondsPassed );
		Set fToasterTimer To ( fToasterTimer - GetSecondsPassed );
		Set fPBrainTimer To ( fPBrainTimer - GetSecondsPassed );

		Set fBarkTimer To ( fBarkTimer + GetSecondsPassed );

		If ( NVDLC03DialogueHQbuddies.bBuddiesTalk == 0 )																				; Do nothing if Sink personalities are turned off
			Return;
		ElseIf ( fBarkTimer < fNextBarkDelay )																									; Do nothing if last bark happen too recently
			Return;
		EndIf

		If ( bRoom1 == 1 )
			Set nBarker To ( GetRandomPercent * 4 / 100 );																					; Set who wants to do the barking

			If ( nBarker == 0 )
				Set bAutoDocBarks To 1;
			ElseIf ( nBarker == 1 )
				Set bBookChuteBarks To 1;
			ElseIf ( nBarker == 2 )
				Set bCentralIntelBarks To 1;
			Else
				Set bMuggyBarks To 1;
			EndIf
		ElseIf ( bRoom2 == 1 )
			Set nBarker To ( GetRandomPercent * 2 / 100 );																					; Set who wants to do the barking

			If ( nBarker == 0 )
				Set bBioLabBarks To 1;
			Else
				Set bMuggyBarks To 1;
			EndIf
		ElseIf ( bRoom3 == 1 )
			Set nBarker To ( GetRandomPercent * 4 / 100 );																					; Set who wants to do the barking

			If ( nBarker == 0 )
				Set bJukeboxBarks To 1;
			ElseIf ( nBarker == 1 )
				Set bLightSwitch01Barks To 1;
			ElseIf ( nBarker == 2 )
				Set bToasterBarks To 1;
			Else
				Set bMuggyBarks To 1;
			EndIf
		ElseIf ( bRoom4 == 1 )
			Set nBarker To ( GetRandomPercent * 2 / 100 );																					; Set who wants to do the barking

			If ( nBarker == 0 )
				Set bLightSwitch02Barks To 1;
			Else
				Set bMuggyBarks To 1;
			EndIf
		ElseIf (bRoom5 == 1 )
			Set nBarker To ( GetRandomPercent * 2 / 100 );																					; Set who wants to do the barking

			If ( nBarker == 0 )
				If (Player.HasPerk NVDLC03BrainlessPerk == 1)
					Set bPBrainBarks To 1;
				Else
					Set bMuggyBarks To 1;
				EndIf
			Else
				Set bMuggyBarks To 1;
			EndIf
		Else
			Set bMuggyBarks To 1;
		EndIf

		If ( bAutoDocBarks == 1 ) && ( NVDLC03DialogueHQBuddies.bAutoDocUnlocked == 1 )										; AutoDoc wants to bark
			Set bAutoDocBarks To 0;

			If ( fAutoDocTimer <= 0.0 )																												; Is it time for another bark?
				NVDLC03SinkAutoDocREF.SayTo Player NVDLC03SinkAutoDocBarks;
				Set fBarkTimer To 0.0;
				Set fAutoDocTimer To ( 30 + GetRandomPercent * ( 180 - 30 + 1 ) / 100 );												; Set this buddy's next bark to occur 30-180 seconds from now
			EndIf
		ElseIf ( bBioLabBarks == 1 ) && ( NVDLC03DialogueHQBuddies.bBiologyUnlocked == 1 )									; BioLab wants to bark
			Set bBioLabBarks To 0;

			If ( fBioLabTimer <= 0.0 )																													; Is it time for another bark?
				NVDLC03SinkBioResearchStationREF.Say NVDLC03SinkBioLabBarks;
				Set fBarkTimer To 0.0;
				Set fBioLabTimer To ( 30 + GetRandomPercent * ( 180 - 30 + 1 ) / 100 );													; Set this buddy's next bark to occur 30-180 seconds from now
			EndIf
		ElseIf ( bBookChuteBarks == 1 ) && ( NVDLC03DialogueHQBuddies.bBookChuteUnlocked == 1 )							; BookChute wants to bark
			Set bBookChuteBarks To 0;

			If ( fBookChuteTimer <= 0.0 )																											; Is it time for another bark?
				NVDLC03SinkBookChuteREF.Say NVDLC03SinkBookChuteBarks;
				Set fBarkTimer To 0.0;
				Set fBookChuteTimer To ( 30 + GetRandomPercent * ( 180 - 30 + 1 ) / 100 );												; Set this buddy's next bark to occur 30-180 seconds from now
			EndIf
		ElseIf ( bCentralIntelBarks == 1 ) && ( NVDLC03SinkMainComputerREF.bMainComputerUnlocked == 1 )						; Central Intelligence wants to bark
			Set bCentralIntelBarks To 0;

			If ( fCentralIntelTimer <= 0.0 )																											; Is it time for another bark?
				NVDLC03SinkMainComputerREF.Say NVDLC03SinkCentralIntelligenceBarks;
				Set fBarkTimer To 0.0;
				Set fCentralIntelTimer To ( 30 + GetRandomPercent * ( 180 - 30 + 1 ) / 100 );											; Set this buddy's next bark to occur 30-180 seconds from now
			EndIf
		ElseIf ( bJukeboxBarks == 1 ) && ( NVDLC03DialogueHQBuddies.bJukeboxUnlocked == 1 )								; Jukebox wants to bark
			Set bJukeboxBarks To 0;

			If ( fJukeboxTimer <= 0.0 )																												; Is it time for another bark?
				NVDLC03SinkJukeBoxTAREF.Say NVDLC03SinkJukeboxBarks;
				Set fBarkTimer To 0.0;
				Set fJukeboxTimer To ( 30 + GetRandomPercent * ( 180 - 30 + 1 ) / 100 );												; Set this buddy's next bark to occur 30-180 seconds from now
			EndIf
		ElseIf ( bLightSwitch01Barks == 1 ) && ( NVDLC03DialogueHQBuddies.bLightSwitch01Unlocked == 1 )						; LightSwitch01 wants to bark
			Set bLightSwitch01Barks To 0;

			If ( fLightSwitch01Timer <= 0.0 )																										; Is it time for another bark?
				NVDLC03SinkLightSwitch01REF.Say NVDLC03SinkLightSwitch01Barks;
				Set fBarkTimer To 0.0;
				Set fLightSwitch01Timer To ( 30 + GetRandomPercent * ( 180 - 30 + 1 ) / 100 );											; Set this buddy's next bark to occur 30-180 seconds from now
			EndIf
		ElseIf ( bLightSwitch02Barks == 1 ) && ( NVDLC03DialogueHQBuddies.bLightSwitch02Unlocked == 1 )						; LightSwitch02 wants to bark
			Set bLightSwitch02Barks To 0;

			If ( fLightSwitch02Timer <= 0.0 )																										; Is it time for another bark?
				NVDLC03SinkLightSwitch02REF.Say NVDLC03SinkLightSwitch02Barks;
				Set fBarkTimer To 0.0;
				Set fLightSwitch02Timer To ( 30 + GetRandomPercent * ( 180 - 30 + 1 ) / 100 );											; Set this buddy's next bark to occur 30-180 seconds from now
			EndIf
		ElseIf ( bMuggyBarks == 1 ) && ( NVDLC03MuggyREF.GetDistance Player <= 500 ) && ( NVDLC03DialogueHQBuddies.bMuggyUnlocked == 1 )	; Muggy wants to bark
			Set bMuggyBarks To 0;

			If ( fMuggyTimer <= 0.0 )																													; Is it time for another bark?
				NVDLC03MuggyREF.Say NVDLC03SinkMuggyBarks;
				Set fBarkTimer To 0.0;
				Set fMuggyTimer To ( 30 + GetRandomPercent * ( 180 - 30 + 1 ) / 100 );													; Set this buddy's next bark to occur 30-180 seconds from now
			EndIf
		ElseIf ( bToasterBarks == 1 ) && ( NVDLC03DialogueHQBuddies.bToasterUnlocked== 1 )									; Toaster wants to bark
			Set bToasterBarks To 0;

			If ( fToasterTimer <= 0.0 )																												; Is it time for another bark?
				NVDLC03SinkToasterofDoomREF.Say NVDLC03SinkToasterBarks;
				Set fBarkTimer To 0.0;
				Set fToasterTimer To ( 30 + GetRandomPercent * ( 180 - 30 + 1 ) / 100 );													; Set this buddy's next bark to occur 30-180 seconds from now
			EndIf
		ElseIf ( bPBrainBarks == 1 ) && ( NVDLC03BrainJarREF.bBrainInTank == 1 )
			Set bPBrainBarks To 0;

			If ( fPBrainTimer <= 0.0 )
				NVDLC03BrainTankSinkREF.Say NVDLC03PlayerBrainBarks;
				Set fBarkTimer To 0.0;
				Set fPBrainTimer To ( 30 + GetRandomPercent * ( 180 - 30 + 1 ) / 100 );
			EndIf
		EndIf

		Set fNextBarkDelay To ( 30 + GetRandomPercent * ( 60 - 30 + 1 ) / 100 );														; Check for bark in 30-60 seconds from now
	EndIf

END

scn NVDLC03HologramBeamTriggerSCRIPT


;When the player enters the beam in the hologram facility, it knocks the player out and sets the gamehour ahead
;JDJ 03/23/2011


Float fTimer;		;Timer for the knockout sequence
Int nCount;			;Counter fo knockout sequence


BEGIN OnTriggerEnter Player

	If (nCount == 0)

		DisablePlayerControls 1 1 1 1 1 1 1;
		Set nCount to 1;

	EndIf

END


BEGIN GameMode

	If (nCount >= 1)

		Set fTimer to fTimer + GetSecondsPassed;

		If (nCount == 1)

			Set nCount to 2;

		EndIf

		If (fTimer >=1) && (fTimer <= 4)
			Player.AddScriptPackage NVDLC03PlayerKnockOutPackage;
		EndIf

		If (fTimer >= 5) && (nCount == 2)

			ApplyImageSpaceModifier FadeToBlackISFX;
			Set GameHour To GameHour + 12;
			Set nCount to 3;

		ElseIf (fTimer >= 6) && (nCount == 3)

			ApplyImageSpaceModifier FadeInFromBlack2sISFX;
			Player.RemoveScriptPackage NVDLC03PlayerKnockOutPackage;
			EnablePlayerControls;
			Set fTimer to 0;
			Set nCount to 0;

		EndIf
	EndIf

END

scn NVDLC03ChallengeCazaDeathDealer02SCRIPT

; Reward script for challenge "Caza-Death Dealer" (1 through 3). Controls XP and PERK grants/increments.
; [NOTE: The actual "IncrementScriptedChallenge" call is made in "NVDLC03CazadorSCRIPT" (ondeath block) attached to all Cazadores in the Big MT.]
; 1/11/11 ETB
; TLS 03/21/2011 - Splitting into multiple scripts for each challenge


BEGIN ScriptEffectStart

		Player.AddPerk NVDLC03DNAvengerPerk;
		ShowMessage NVDLC03DNAvengerRankMsg;
		UnlockChallenge NVDLC03ChallengeCazaDeathDealer03;
		RewardXP 125;

END

scn NVDLC03BrainTankOpenTimerSCRIPT


; Timer quest to handle initial opening of player Brain Tank.
; JSH 03/14/11


Short bInitializeTimer					; 1 = Start time has been set.
Float fTimer								; Tracks how many seconds have passed.


BEGIN GameMode

	If ( bInitializeTimer == 0 )
		Set fTimer to 5;
		Set bInitializeTimer to 1;
	EndIf

	If ( fTimer < 0 )
		Set NVDLC03DialoguePlayerBrain.bAllowDialogue to 1;
		NVDLC03PlayerBrainREF.SetDestroyed 0;
		StopQuest NVDLC03BrainTankOpenTimer;
	Else
		Set fTimer to ( fTimer - GetSecondsPassed );
	EndIf

END

scn NVDLC03EnableControlsTriggerSCRIPT


; Safety trigger for the PC - enables player controls.
; JSH 03/09/11
; AJD 03/22/11 - Added in script to start the radio.

Short bRadioOnce;	;Controls starting the radio only once.


BEGIN OnTrigger Player

	If ( GetPlayerControlsDisabled == 1)
		EnablePlayerControls;

	EndIf

	If (bRadioOnce == 0)
		Set bRadioOnce to 1;
		PipboyRadio 1;
		PipboyRadio tune NVDLC03RadioRef;
		PlaySound UIRadioSignalFound;
	EndIf

END

scn NVDLC03X13TurretScript


;Script that makes the turrets in x-13 become hostile if the robobrains in the test are killed
;JDJ 04/04/2011

Short bAngry;		;Lock-out variable once all turrets and robots set to angry
Short nSetup;		;Incremented setup variable


BEGIN GameMode

	If (bAngry == 1)
		If (nSetup == 0)
			X13Turret01REF.SetUnconscious 0;
			SetPCEnemyOfFaction NVDLC03X13RobotFaction 1;
			X13Turret01REF.SetAV Aggression 2;
			X13Turret01REF.StartCombat Player;
			Set nSetup to (nSetup + 1);
			Return;
		ElseIf (nSetup == 1)
			X13Turret02REF.SetUnconscious 0;
			SetPCEnemyOfFaction NVDLC03X13RobotFaction 1;
			X13Turret02REF.SetAV Aggression 2;
			X13Turret02REF.StartCombat Player;
			Set nSetup to (nSetup + 1);
			Return;
		ElseIf (nSetup == 2)
			X13Turret03REF.SetUnconscious 0;
			SetPCEnemyOfFaction NVDLC03X13RobotFaction 1;
			X13Turret03REF.SetAV Aggression 2;
			X13Turret03REF.StartCombat Player;
			Set nSetup to (nSetup + 1);
			Return;
		ElseIf (nSetup == 3)
			X13Turret04REF.SetUnconscious 0;
			SetPCEnemyOfFaction NVDLC03X13RobotFaction 1;
			X13Turret04REF.SetAV Aggression 2;
			X13Turret04REF.StartCombat Player;
			Set nSetup to (nSetup + 1);
			Return;
		ElseIf (nSetup == 4)
			X13Robot1REF.StartCombat Player;
			X13Robot2REF.StartCombat Player;
			X13Robot3REF.StartCombat Player;
			SetPCEnemyOfFaction NVDLC03X13RobotFaction 1;
			X13Robot1REF.SetAV Aggression 2;
			X13Robot2REF.SetAV Aggression 2;
			X13Robot3REF.SetAV Aggression 2;
			Set nSetup to 0;
			Set bAngry to 0;
			Disable;
		EndIf
	Else
		Disable
	EndIf

END

scn NVDLC03FakeSterilizerGloveSCRIPT

;adds Sterilizer Glove to the player's inventory & spawns 3 NPCs
;SPW 03/03/11
;SPW 03/08/11 AddItemHealthPercent 


Short bDoOnce;	runs script once


BEGIN OnActivate Player

	If ((GetActionRef == Player) && (bDoOnce ==0))

	;adds glove
		NVDLC03FakeSterilizerGloveREF.Disable;
		Player.AddItemHealthPercent NVDLC03WeapGlovesSterilizer 1 .60;
		Set bDoOnce To 1;
		NVDLC03FakeSterilizerGloveREF.MarkForDelete;

	;spawns npcs
		NVDLC03SterilizerGloveSpawnREF.Enable;
		NVDLC03SterilizerGloveSpawnREF.StartCombat Player;
	EndIf

END

