scn NVDLC03BrainTankOpenTriggerSCRIPT


; Causes the player Brain Tank to open as the player approaches.
; JSH 03/03/11


BEGIN OnTrigger Player

	If ( GetStageDone NVDLC03MQ05 40 == 1 )

		NVDLC03PlayerBrainREF.AddScriptPackage NVDLC03BrainTankOpenPackage;
		StartQuest NVDLC03BrainTankOpenTimer;
		NVDLC03BrainTankSnoreREF.Disable;
		Disable;

	EndIf

END

scn NVDLC03HQBuddySeedBox03SCRIPT

; adds seeds to player inventory
; SPW 03/03/11

Ref rSelf;	this box

BEGIN OnActivate Player

	If (GetActionRef == Player)
		Player.AddItem NVDLC03DriedBrocFlowerMISC 1;
		Player.AddItem NVDLC03DriedJalapenoMISC 1;
		Player.AddItem NVDLC03DriedPintoBeanPodMISC 1;
		Player.AddItem NVDLC03DriedXanderRootMISC 1;
		Set NVDLC03HQBuddy.bBox03 To 1;
		Set rSelf To getSelf;
		rSelf.Disable;
		rSelf.MarkForDelete;
	EndIf

END

