scn NVDLC03X8TunnelExitDoorSCRIPT


;Keeps lobotomites from exiting tunnels
;JDJ 02/18/2011


BEGIN OnActivate

	If (IsActionRef Player == 1)
		If (X8TunnelIntercom.nBark01 < 3)
			Set X8TunnelIntercom.nBark01 to 3;
			X8TunnelBarkTrigger02REF.Disable;
		EndIf
		If (X8TunnelIntercom.nBark02 < 4)
			Set X8TunnelIntercom.nBark02 to 4;
			X8TunnelBarkTrigger03REF.Disable;
		EndIf
		If (X8TunnelIntercom.nBark03 < 4)
			Set X8TunnelIntercom.nBark03 to 4;
			X8TunnelBarkTrigger04REF.Disable;
		EndIf
		If (X8TunnelIntercom.nBark04 < 3)
			Set X8TunnelIntercom.nBark04 to 3;
			X8TunnelBarkTrigger05REF.Disable;
		EndIf
		Activate;
	ElseIf (IsActionRef X8CyberDogExperiment01 == 1) 
		Activate;
	EndIf

END

scn NVDLC03CyberdogGunSCRIPT

; Controls scripted actions for "K9000 Cyberdog Gun" (NVDLC03WeapCyberdogGun).
; [NOTE: All dialogue for K9000 is located in NVDLC03AuralStealthSuitQUEST/Topic.]
; EBeaumont (ETB) 2/16/11


Short bBarkOnce
Short bSayDelay

Float fSayDelayTimer
Float fSayTimerSetting


BEGIN OnEquip Player

	If ( bSayDelay == 0 )
		NVDLC03StealthSuitVoiceCrREF.Say K9000EQUIP 1 NVDLC03StealthSuitVoiceCr 1;
		Set bSayDelay to 1;
		Set fSayTimerSetting to 2;
	Endif

END; onequip player



BEGIN OnUnequip Player

	If ( bSayDelay == 0 )
		NVDLC03StealthSuitVoiceCrREF.Say K9000UNEQUIP 1 NVDLC03StealthSuitVoiceCr 1;
		Set bSayDelay to 1;
		Set fSayTimerSetting to 2;
	Endif

END; onunequip Player


BEGIN GameMode

	If ( Player.GetEquipped NVDLC03WeapCyberdogGun == 1 && Player.IsInCombat == 1 )
		If ( bBarkOnce == 0 )
			NVDLC03StealthSuitVoiceCrREF.Say K9000CombatStart 1 NVDLC03StealthSuitVoiceCr 1;
			Set bBarkOnce to 1;
		Endif
	Elseif ( Player.GetEquipped NVDLC03WeapCyberdogGun == 1 && Player.IsInCombat == 0 )
		Set bBarkOnce to 0;
	Endif

	If ( bSayDelay == 1 )
		If ( fSayDelayTimer < fSayTimerSetting )
			Set fSayDelayTimer to fSayDelayTimer + GetSecondsPassed;
		Else
			Set bSayDelay to 0;
			Set fSayDelayTimer to 0;
		Endif
	Endif

END; gamemode

scn NVDLC03X2AntennaActivatorSCRIPT


; The static version of the X-2 Antenna. Adds the real thing to the player when activated.
; JSH 02/16/11


BEGIN OnActivate

	If ( IsActionRef Player == 1 )
		Player.AddItemHealthPercent NVDLC03WeapAntenna 1 0.80;
		SetStage NVDLC03MQ02 100;
		NVDLC03SatelliteIntercomREF.Say NVDLC03MobiusSatelliteBark 1;
		NVDLC03SatelliteScorpionEXTREF.Enable;											;Enables exterior scorpion spawns
		NVDLC03SatelliteScorpionINTREF.Enable;											;Enables scorpions spawns in the Satellite Station
		Disable;
	EndIf

END

scn NVDLC03BigMTSpeakerSCRIPT


; Handles Mobius' retort to Klein after Klein mentions player's plot progression.
; JSH 02/16/11


BEGIN SayToDone NVDLC03DialogueKleinPlayerReclaimedTech

	If ( NVDLC03PlotReactionBark.nX2Done == 1 )

		NVDLC03BigMTSpeakerREF.Say NVDLC03DialogueMobiusReclaimedX2 1;
		Set NVDLC03PlotReactionBark.nX2Done to 2;

	;ElseIf ( NVDLC03PlotReactionBark.nX8Done == 1 )

	;	NVDLC03BigMTSpeakerREF.Say NVDLC03DialogueMobiusReclaimedX8 1;
	;	Set NVDLC03PlotReactionBark.nX8Done to 2;

	ElseIf ( NVDLC03PlotReactionBark.nX13Done == 1 )

		NVDLC03BigMTSpeakerREF.Say NVDLC03DialogueMobiusReclaimedX13 1;
		Set NVDLC03PlotReactionBark.nX13Done to 2;

	EndIf

END

scn NVDLC03PlotReactionBarkSCRIPT


; Handles when Klein and Mobius say stuff about the player's progression in the main quest.
; JSH 02/16/11

Short bThinkTankDone		; Mobius said his initial threat to the player.
Short nX2Done;				; 1 = Klein said something, 2 = Mobius responded. 
Short nX8Done;				; 1 = Klein said something, 2 = Mobius responded.
Short nX13Done;				; 1 =Klein said something, 2 = Mobius responded.
Short nMobiusCount			; A delay timer so Mobius doesn't spout his initial threat too immediately.
Short nKleinDelay				; Another delay timer to give Mobius time to speak his reaction to X-2 before Klein chimes in.
Short nTechCount				; Tracks number of tech the player has retrieved.

Short bDelay					; Delay processing to allow VO time to complete.


BEGIN GameMode

	If ( Player.GetInWorldspace NVDLC03BigMT == 1 )

		If ( bThinkTankDone == 0 )

			If ( nMobiusCount < 3 )
				Set nMobiusCount to ( nMobiusCount + 1 );
				Return;
			Else
				NVDLC03BigMTSpeakerREF.MoveTo Player;
				NVDLC03BigMTSpeakerREF.SayTo Player NVDLC03DialogueMobiusReclaimedStart 1;
				Set bThinkTankDone to 1;
				StartQuest NVDLC03BigMTRandomBarkTimer;
			EndIf

		EndIf

		If ( nX2Done == 1 )

			If ( bDelay == 0 )
				Set bDelay to 1;
				Return;
			Else
				NVDLC03BigMTSpeakerREF.MoveTo Player;
				NVDLC03BigMTSpeakerREF.Say NVDLC03DialogueMobiusReclaimedX2 1;
				Set NVDLC03PlotReactionBark.nX2Done to 2;
				Set bDelay to 0;
				StartQuest NVDLC03BigMTRandomBarkTimer;
				NVDLC03X2BarkTrigger01REF.Disable;
				NVDLC03X2BarkTrigger01REF.MarkForDelete;
				NVDLC03X2BarkTrigger02REF.Disable;
				NVDLC03X2BarkTrigger02REF.MarkForDelete;
			EndIf

		EndIf

		If ( nX8Done == 1 )

			If ( bDelay == 0 )
				Set bDelay to 1;
				Return;
			Else
				NVDLC03BigMTSpeakerREF.MoveTo Player;
				NVDLC03BigMTSpeakerREF.Say NVDLC03DialogueMobiusReclaimedX8 1;
				Set NVDLC03PlotReactionBark.nX8Done to 2;
				Set bDelay to 0;
				StartQuest NVDLC03BigMTRandomBarkTimer;
				NVDLC03X8BarkTriggerREF.Disable;
				NVDLC03X8BarkTriggerREF.MarkForDelete;
			EndIf

		EndIf

		If ( nX13Done == 1 )

			If ( bDelay == 0 )
				Set bDelay to 1;
				Return;
			Else
				NVDLC03BigMTSpeakerREF.MoveTo Player;
				NVDLC03BigMTSpeakerREF.Say NVDLC03DialogueMobiusReclaimedX13 1;
				Set NVDLC03PlotReactionBark.nX13Done to 2;
				Set bDelay to 0;
				StartQuest NVDLC03BigMTRandomBarkTimer;
				NVDLC03X13BarkTriggerREF.Disable;
				NVDLC03X13BarkTriggerREF.MarkForDelete;
			EndIf

		EndIf

	EndIf

END

scn NVDLC03FakeCorrosiveGloveSCRIPT

;adds the corrosive glove to the player's inventory & spawns 3 corpse suits
;SPW 02/16/11
;SPW 03/08/11 AddItemHealthPercent 


Short bDoOnce;	runs script once


BEGIN OnActivate Player

	If ((GetActionRef == Player) && (bDoOnce ==0))

	;adds glove
		NVDLC03FakeGloveREF.Disable;
		Player.AddItemHealthPercent NVDLC03WeapGlovesCorrosive 1 .60;
		Set bDoOnce To 1;
		NVDLC03FakeGloveREF.MarkForDelete;

	;spawns npcs
		NVDLC03GloveSpawnREF.Enable;
		NVDLC03GloveSpawnREF.StartCombat Player;
	EndIf

END

scn NVDLC03BigMTRandomBarkTimerSCRIPT


; Timer handling Mobius/Think Tank barks in Big MT exterior. 40% chance of Mobius line, 15% each for the other four Brains.
; JSH 02/15/11


Short nRandomDoc;			; Randomly chooses a Brain to spout a line.
Short bSkipOnce;				; Skips the first bark so Mobius has a chance to say his initial threat.
Short bPending;				; 1 = Player wasn't in NVDLC03BigMT, so say a line the next time he is.


BEGIN GameMode

	If ( Player.GetInWorldSpace NVDLC03BigMT == 1 )

		If ( bSkipOnce == 0 )
			Set bSkipOnce to 1;
			Return;
		EndIf

		; Skip a cycle to avoid weird VO overlap.
		If ( NVDLC03PlotReactionBark.nX2Done == 2 )
			Set NVDLC03PlotReactionBark.nX2Done to 3;
			Return;
		ElseIf ( NVDLC03PlotReactionBark.nX8Done == 2 )
			Set NVDLC03PlotReactionBark.nX8Done to 3;
			Return;
		ElseIf ( NVDLC03PlotReactionBark.nX13Done == 2 )
			Set NVDLC03PlotReactionBark.nX13Done to 3;
			Return;
		EndIf

		Set nRandomDoc to GetRandomPercent;

		NVDLC03BigMTSpeakerREF.MoveTo Player;

		If ( nRandomDoc <= 40 )
			NVDLC03BigMTSpeakerREF.Say NVDLC03DialogueMobiusBigMTRandom 1;
		ElseIf ( nRandomDoc > 40 ) && ( nRandomDoc <= 55 )
			NVDLC03BigMTSpeakerREF.Say NVDLC03DialogueDalaBigMTRandom 1;
		ElseIf ( nRandomDoc > 55 ) && ( nRandomDoc <= 70 )
			NVDLC03BigMTSpeakerREF.Say NVDLC03DialogueBorousBigMTRandom 1;
		ElseIf ( nRandomDoc > 70 ) && ( nRandomDoc <= 85 )
			NVDLC03BigMTSpeakerREF.Say NVDLC03Dialogue0BigMTRandom 1;
		Else
			NVDLC03BigMTSpeakerREF.Say NVDLC03DialogueKleinBigMTRandom 1;
		EndIf

		If ( bPending == 1 )
			SetQuestDelay NVDLC03BigMTRandomBarkTimer 240;													; Restore normal bark delay.
			Set bPending to 0;
		EndIf

		Return;

	Else

		If ( bPending == 0 )
			SetQuestDelay NVDLC03BigMTRandomBarkTimer 15;														; Speed up quest update so a bark happens sooner.
			Set bPending to 1;
		EndIf

	EndIf

END

scn NVDLC03X8RobobrainExperimentSCRIPT


;controls lines for robobrain from x8 splicing experiment
;JDJ 02/15/2011
;JDJ 02/16/2011 continued with initial script writing
;JDJ 03/11/2011 standards update
; JSH 03/28/11 - Added GameMode block to handle unconscious state from Robotics Expert.
;JDJ 04/05/2011 added GameMode block to handle timer to better time the death line and the death


Int bSayLine01;		;Variable that makes sure robobrain does not say the line twice		
Int bSayLine02;		;Variable that makes sure robobrain does not say the line twice;Int bDeath;				;1 starts timer to kill robobrain in GameMode Script
Int bDeath;				;1 starts timer to kill robobrain in GameMode Script
Float fDeathTimer;		;Timer for the RoboBrain death
Int bKill;					;Set to 1 to kill and say death line


BEGIN GameMode

	If ( GetUnconscious == 1 )

		SetUnconscious 0;
		X8RoboBrainExperiment01REF.PlaceAtMe EyebotExplosion;
		X8RoboBrainExperiment01REF.KillActor X8RoboBrainExperiment02REF 1 0;
		Set bKill to 1;

	EndIf

END


BEGIN SayToDone NVDLC03X8RobobrainBorn01;

	If (bSayLine01 == 0)

		Say NVDLC03X8RobobrainStrangeWorld 1;
		Set bSayLine01 to 1;

	EndIf

END


BEGIN SayToDone NVDLC03X8RobobrainBorn02;

	If (bSayLine01 == 0)

		Say NVDLC03X8RobobrainStrangeWorld 1;
		Set bSayLine01 to 1;

	EndIf

END


BEGIN SayToDone NVDLC03X8RobobrainStrangeWorld;

	If (bSayLine02 == 0)

		X8RoboBrainExperiment01REF.PlaceAtMe NVDLC03DoghouseExplosion;
		Say NVDLC03X8RobobrainBornDeath 1;
		Set bDeath to 1;
		Set bSayLine02 to 1;

	EndIf

END


BEGIN OnHit

	If (bSayLine02 == 0)

		X8RoboBrainExperiment01REF.PlaceAtMe NVDLC03DoghouseExplosion;
		Say NVDLC03X8RobobrainBornDeath 1;
		Set bDeath to 1;
		Set bSayLine02 to 1;

	EndIf


END


BEGIN GameMode

	If (bDeath == 1)
		Set fDeathTimer to fDeathTimer + GetSecondsPassed;
		If (fDeathTimer >= 1.5)
			If (bKill == 0)

				X8RoboBrainExperiment01REF.KillActor X8RoboBrainExperiment02REF 7 0;
				Set bKill to 1;
				Set fDeathTimer to 0;
				Set bDeath to 0;

			EndIf

		EndIf

	EndIf

END

scn NVDLC03X8SchoolDownstairsBarkSCRIPT


;sets off some of borous's barks
;JDJ 02/15/2011
;JDJ 03/11/2011 standards update
;JDJ 04/05/2011 added a possibility for borous to say library line on first playthrough


Int bDoOnce;				;line only barks once during at test


BEGIN OnActivate

	If (IsActionRef Player == 1)
		If (X8MainCodeBox.nTestRunning == 2)	&& (bDoOnce == 0)
			If (X8TunnelEntryTrigger.bTestTwo == 0)
				X8SchoolIntercomREF.SayTo Player NVDLC03BorousX8School07 1;
				Set bDoOnce to 1;
				Activate;
			Else
				X8SchoolIntercomREF.SayTo Player NVDLC03BorousX8School03 1;
				Set bDoOnce to 1;
				Activate;			
			EndIf
		ElseIf (X8MainCodeBox.nTestRunning == 1) && (bDoOnce == 0)
			If (X8TunnelEntryTrigger.bTestOne == 1)
				X8SchoolIntercomREF.SayTo Player NVDLC03BorousX8School05 1;
				Set bDoOnce to 1;
				Activate;
			ElseIf (X8TunnelEntryTrigger.bTestOne == 0) && (NVDLC03SchoolEntryTrigger.nTalk == 1)
				X8SchoolIntercomREF.SayTo Player NVDLC03BorousX8School03 1;
				Set bDoOnce to 1;
				Activate;
			Else
				Activate;
			EndIf
		Else
			Activate;
		EndIf
	EndIf

END

scn NVDLC03X8SchoolUpstairsBarkSCRIPT


;sets off some of borous's barks
;JDJ 02/15/2011
;JDJ 03/11/2011 standards update
;JDJ 03/14/2011  adding scripting to make only the player able it open the door


Int bDoOnce;				;line only barks once during at test
Ref rSelf;					;used to get the door's ref


BEGIN OnActivate

	Set rSelf to GetSelf;

	If (IsActionRef Player == 1)
		If (rSelf == X8SchoolDoor07)
			If (X8MainCodeBox.nTestRunning == 2)	&& (bDoOnce == 0)
				If (X8TunnelEntryTrigger.bTestTwo == 0)
					X8SchoolIntercomREF.SayTo Player NVDLC03BorousX8School08 1;
					Set bDoOnce to 1;
					Activate;
				Else
					X8SchoolIntercomREF.SayTo Player NVDLC03BorousX8School06 1;
					Set bDoOnce to 1;
					Activate;			
				EndIf
			Else
				Activate;
			EndIf
		Else
			Activate;
		EndIf
	Else
		Activate;
	EndIf

END

scn NVDLC03SinkMuggySCRIPT


; Pre-post unlock script for Muggy to handle his activation prior to the Player finding his unlock holodisk.
; 02/11/11 ETB

; SPW 02/24/11 	- Adding vars for HQ Buddy quest
; ETB 03/24/11 		- Added gamemode start conversation routine (to get conversation to start directly after a message box pop-up).


Short bMuggyUnlocked								; 1 = Player has turned in the unlock holodisk: "NVDLC03HQBuddyMuggyNOTE".
Short bDoOnce 
Short bTalkAfterMessage
Short nCoffeeItem01Value 
Short nCoffeeItem02Value 
Short nCoffeePotItem01Value 
Short nDinnerPlateItem01Value 
Short nWhitePlateItem01Value 
Short nRedPlateItem01Value 
Short nGreenPlateItem01Value 
Short nGreenPlateItem02Value 
Short nTinPlateItem01Value 
Short nTinPlateItem02Value 
Short nCeramicPlateItem01Value 
Short nCeramicPlateItem02Value 

Short bAwaitingInput
Short bLaunchMenu 
Short nButton



BEGIN GameMode

	If ( bDoOnce == 0 )
		Set bDoOnce to 1;
		NVDLC03MuggyREF.SetUnconscious 1;
	Endif

	If ( bTalkAfterMessage == 1 )
		Set bTalkAfterMessage to 0;
		Activate;
	Endif

	If ( NVDLC03DialogueHQBuddies.bLaunchMenu == 1 )
		Set NVDLC03DialogueHQBuddies.bLaunchMenu to 0;
		Set bAwaitingInput to 1;
		ShowMessage NVDLC03HQMuggyMSG;
	Endif

END; gamemode



BEGIN OnActivate

	If ( IsActionRef Player == 1 )
		If ( bMuggyUnlocked == 1 )
			If ( Player.GetHasNote NVDLC03HQUpgradeMuggyPartsProductionNOTE == 1 )
				Player.RemoveNote NVDLC03HQUpgradeMuggyPartsProductionNOTE;
				Set NVDLC03DialogueHQBuddies.bMuggyUpgraded to 1;
				Set NVDLC03HQBuddy.iMuggy to 2;													; SPW
				ShowMessage NVDLC03HQMuggyUpgradeMSG;
			Endif
			If ( NVDLC03DialogueHQBuddies.bBuddiesTalk == 0 )
				ShowMessage NVDLC03HQBuddiesNoTalkMSG;									; Displays in corner to let Player know voice/dialogue is deactivated.
				If ( ( NVDLC03DialogueHQBuddies.bMuggyUpgraded == 1 && NVDLC03DialogueHQBuddies.bMuggyPartsReady  == 1 ) || Player.GetItemCount NVDLC03HQBuddiesMuggyAllDinnerwareLIST >= 1 )
					Set bAwaitingInput to 1;
					ShowMessage NVDLC03HQMuggyMSG;
				Else
					If ( NVDLC03DialogueHQBuddies.bMuggyUpgraded == 0 )
						ShowMessage NVDLC03HQMuggyNoItemsMSG;
					Elseif ( NVDLC03DialogueHQBuddies.bMuggyUpgraded == 1 )
						ShowMessage NVDLC03HQMuggyNoItemsServicesMSG;
					Endif
				Endif
			Elseif ( NVDLC03DialogueHQBuddies.bBuddiesTalk == 1 )
				Set bTalkAfterMessage to 1;
			Endif
		Else
			If ( Player.GetHasNote NVDLC03HQBuddyMuggyNOTE == 0 )
				ShowMessage NVDLC03HQBuddyLockedMSG;
			Elseif ( Player.GetHasNote NVDLC03HQBuddyMuggyNOTE == 1 )
				Player.RemoveNote NVDLC03HQBuddyMuggyNOTE;
				ShowMessage 	NVDLC03HQBuddyUnlockMSG;
				IncrementScriptedChallenge NVDLC03AchievementMakingFriends;	
				Set bMuggyUnlocked to 1;
				Set NVDLC03HQBuddy.iMuggy to 1;													; SPW
				Set NVDLC03DialogueHQBuddies.bMuggyUnlocked to 1;
				NVDLC03MuggyREF.SetUnconscious 0;
				Set bTalkAfterMessage to 1;
			Endif
		Endif
	Endif

END; onactivate



BEGIN MenuMode 1001

	If ( bAwaitingInput == 1 )
		Set nButton to GetButtonPressed;

		If ( nButton > -1 )
			Set bAwaitingInput to 0;

; -------------------------------------------------------------------	BUTTON 0 ----------------------------------------------	\/	; "Do Nothing"
			If ( nButton == 0 )
				Set nButton to -1;
				Return;
; -------------------------------------------------------------------	BUTTON 1 ----------------------------------------------	\/ 	; "Process Coffee Mugs and Dishes"
			Elseif ( nButton == 1 )
				Set nButton to -1;	
; ---------------------------	Coffee Mug --------------------	\/
				Set NVDLC03DialogueHQBuddies.nCoffeeCount to Player.GetItemCount CoffeeMug01;
; ---------------------------	Coffee Pot ----------------------	\/
				Set NVDLC03DialogueHQBuddies.nCoffeePotCount to Player.GetItemCount CoffeeKettle;			
; ---------------------------	Dinner Plate ------------------	\/
				Set NVDLC03DialogueHQBuddies.nDinnerPlateCount to Player.GetItemCount DinnerPlate01;		
; ---------------------------	White Plate --------------------	\/
				Set NVDLC03DialogueHQBuddies.nWhitePlateCount to Player.GetItemCount Plate01;				
; ---------------------------	Red Plate ----------------------	\/
				Set NVDLC03DialogueHQBuddies.nRedPlateCount to Player.GetItemCount Plate02;				
; ---------------------------	Green Plate -------------------	\/
				Set NVDLC03DialogueHQBuddies.nGreenPlateCount to Player.GetItemCount Plate03;				
; ---------------------------	Tin Plate ------------------------	\/
				Set NVDLC03DialogueHQBuddies.nTinPlateCount to Player.GetItemCount Plate04;						
; ---------------------------	Ceramic Dinner Plate ----	\/
				Set NVDLC03DialogueHQBuddies.nCeramicPlateCount to Player.GetItemCount PlateDinnerCeramic;		
; ---------------------------	Total Dinnerware -----------	\/
				Set NVDLC03DialogueHQBuddies.nDinnerwareTotal to ( NVDLC03DialogueHQBuddies.nCoffeeCount + NVDLC03DialogueHQBuddies.nCoffeePotCount + NVDLC03DialogueHQBuddies.nDinnerPlateCount + NVDLC03DialogueHQBuddies.nWhitePlateCount + NVDLC03DialogueHQBuddies.nRedPlateCount + NVDLC03DialogueHQBuddies.nGreenPlateCount + NVDLC03DialogueHQBuddies.nTinPlateCount + NVDLC03DialogueHQBuddies.nCeramicPlateCount );

; ---------------------------	Remove Items ---------------	\/
				Player.RemoveItem CoffeeMug01 NVDLC03DialogueHQBuddies.nCoffeeCount;
				Player.RemoveItem CoffeeKettle NVDLC03DialogueHQBuddies.nCoffeePotCount;
				Player.RemoveItem DinnerPlate01 NVDLC03DialogueHQBuddies.nDinnerPlateCount;
				Player.RemoveItem Plate01 NVDLC03DialogueHQBuddies.nWhitePlateCount;
				Player.RemoveItem Plate02 NVDLC03DialogueHQBuddies.nRedPlateCount;
				Player.RemoveItem Plate03 NVDLC03DialogueHQBuddies.nGreenPlateCount;
				Player.RemoveItem Plate04 NVDLC03DialogueHQBuddies.nTinPlateCount;
				Player.RemoveItem PlateDinnerCeramic NVDLC03DialogueHQBuddies.nCeramicPlateCount;

; -----------------------------------------------	Add Item Multiplers ----------------------	\/

; ---------------------------	Coffee Mug --------------------	\/
				Set nCoffeeItem01Value to ( NVDLC03DialogueHQBuddies.nCoffeeCount * 2 );
				Set nCoffeeItem02Value to ( NVDLC03DialogueHQBuddies.nCoffeeCount * 3 );
; ---------------------------	Coffee Pot ----------------------	\/
				Set nCoffeePotItem01Value to ( NVDLC03DialogueHQBuddies.nCoffeePotCount * 2 );
; ---------------------------	Dinner Plate ------------------	\/
				Set nDinnerPlateItem01Value to ( NVDLC03DialogueHQBuddies.nDinnerPlateCount * 3 );
; ---------------------------	White Plate --------------------	\/
				Set nWhitePlateItem01Value to ( NVDLC03DialogueHQBuddies.nWhitePlateCount * 20 );		
; ---------------------------	Red Plate ----------------------	\/
				Set nRedPlateItem01Value to ( NVDLC03DialogueHQBuddies.nRedPlateCount * 50 );		
; ---------------------------	Green Plate -------------------	\/
				Set nGreenPlateItem01Value to ( NVDLC03DialogueHQBuddies.nGreenPlateCount * 10 );	
				Set nGreenPlateItem02Value to ( NVDLC03DialogueHQBuddies.nGreenPlateCount * 8 );	
; ---------------------------	Tin Plate ------------------------	\/
				Set nTinPlateItem01Value to ( NVDLC03DialogueHQBuddies.nTinPlateCount * 20 );	
				Set nTinPlateItem02Value to ( NVDLC03DialogueHQBuddies.nTinPlateCount * 15 );
; ---------------------------	Ceramic Dinner Plate ----	\/
				Set nCeramicPlateItem01Value to ( NVDLC03DialogueHQBuddies.nCeramicPlateCount * 20 );	
				Set nCeramicPlateItem02Value to ( NVDLC03DialogueHQBuddies.nCeramicPlateCount * 50 );

; ---------------------------	Add Items ----------------------	\/
				Player.AddItem WonderGlue nCoffeeItem01Value;
				Player.AddItem SurgicalSyringe01 nCoffeeItem02Value;
				Player.AddItem SpareParts nCoffeePotItem01Value;
				Player.AddItem SpareParts nDinnerPlateItem01Value;
				Player.AddItem Lead nWhitePlateItem01Value;
				Player.AddItem PowderPistol nRedPlateItem01Value;
				Player.AddItem Case357Magnum nGreenPlateItem01Value;
				Player.AddItem Case44Magnum nGreenPlateItem02Value;
				Player.AddItem Case357Magnum nTinPlateItem01Value;
				Player.AddItem Case44Magnum nTinPlateItem02Value;
				Player.AddItem Lead nCeramicPlateItem01Value;
				Player.AddItem PowderPistol nCeramicPlateItem02Value;

; -------------------------------------------------------------------	BUTTON 2 ----------------------------------------------	\/	; "Collect Electronic Components"
			Elseif ( nButton == 2 )
				Set nButton to -1;
				Set NVDLC03DialogueHQBuddies.bMuggyPartsReady to 0;
				Set NVDLC03DialogueHQBuddies.bMuggyTimerSet to 0;
				Player.AddItem AmmoSmallEnergyCell 50;
				Player.AddItem AmmoMicroFusionCell 50;
				Player.AddItem ScrapElectronics 5;

			Endif
		Endif
	Else
		Return;
	Endif

END; menumode 1001

scn NVDLC03SinkTriggerSCRIPT


; Handles post-DLC dialogue after the end slide show.
; JSH 02/11/11


BEGIN OnTrigger Player

	If ( GetStageDone NVDLC03MQ05 100 == 1 )
		RemoveImageSpaceModifier FadeToBlackPermanent;
		EnablePlayerControls;
		ShowMessage NVDLC03TeleporterAcquiredMsg;
		SetInChargen 0;
		EnablePlayerControls;
		Player.RemoveScriptPackage;
		IncrementScriptedChallenge NVDLC03AchievementOutSmarted;

		; Cue cheers.
		If ( NVDLC03SinkMainComputerREF.bMainComputerUnlocked == 1 )
			NVDLC03SinkMainComputerREF.Say NVDLC03HQBuddiesCheer 1;
		EndIf

		If ( NVDLC03SinkAutoDocREF.bAutoDocUnlocked == 1 )
			NVDLC03SinkAutoDocREF.Say NVDLC03HQBuddiesCheer 1;
		EndIf

		If ( NVDLC03SinkBioResearchStationREF.bBiologyUnlocked == 1 )
			NVDLC03SinkBioResearchStationREF.Say NVDLC03HQBuddiesCheer 1;
		EndIf

		If ( NVDLC03DialogueHQBuddies.bSinkBuddyUnlocked == 1 )
			NVDLC03SinkSinkREF.Say NVDLC03HQBuddiesCheer 1;
		EndIf

		If ( NVDLC03SinkToasterofDoomREF.bToasterUnlocked == 1 )
			NVDLC03SinkToasterofDoomREF.Say NVDLC03HQBuddiesCheer 1;
		EndIf

		If ( NVDLC03DialogueHQBuddies.bBuddiesTalk == 1 )
			NVDLC03SinkJukeBoxTAREF.Say NVDLC03HQBuddiesCheer 1;
		EndIf

		If ( NVDLC03SinkLightSwitch01REF.bLightSwitch01Unlocked == 1 )
			NVDLC03SinkLightSwitch01REF.Say NVDLC03HQBuddiesCheer 1;
		EndIf

		If ( NVDLC03SinkLightSwitch02REF.bLightSwitch02Unlocked == 1 )
			NVDLC03SinkLightSwitch02REF.Say NVDLC03HQBuddiesCheer 1;
		EndIf

		If ( NVDLC03MuggyREF.bMuggyUnlocked == 1 )
			NVDLC03MuggyREF.Say NVDLC03HQBuddiesCheer 1;
		EndIf

		If ( NVDLC03SinkBookChuteREF.bBookChuteUnlocked == 1 )
			NVDLC03SinkBookChuteTalkerREF.Say NVDLC03HQBuddiesCheer 1;
		EndIf

		Disable;

	EndIf

END

ScriptName NVDLC03StealthSuitISFXSCRIPT


; Script control for stealth suit vision upgrade
; RL 02/11/2011
; OMP 03/08/2011 - Added a condition to include NVDLC03SlideShow as another cell to prevent the vision upgrade.
; OMP 04/28/2011 - Changed the condition regarding slide shows.


Short bVision;								; toggle for when suit vision is in effect.  1 = yes.
Short bShutdown;							; holder for shutting down the quest


BEGIN GameMode

	; Don't do effects if the end slide shows are in progress
	If ( GetInCharGen == 1 )
		Return;
	EndIf

	If ( Player.IsSneaking == 1 ) && ( Player.GetEquipped NVDLC03AuralStealthSuit == 1 )
		If ( bVision == 0 )
			ApplyImageSpaceModifier NVDLC03StealthSuitONISFX;
			Set bVision To 1;
		ElseIf ( bVision == 1 ) && ( IsImageSpaceActive NVDLC03StealthSuitISFX == 0 )
			ApplyImageSpaceModifier NVDLC03StealthSuitISFX;
			RemoveImageSpaceModifier NVDLC03StealthSuitONISFX;
		EndIf
	ElseIf ( Player.IsSneaking == 0 ) && ( bVision == 1 )
		ApplyImageSpaceModifier NVDLC03StealthSuitOFFISFX;
		RemoveImageSpaceModifier NVDLC03StealthSuitISFX;
		Set bVision To 0;
	EndIf

END

