scn NVDLC03BattleBrewStillSCRIPT

; allows player to make Battle Brew using vodka and other ingredients
; RL 2/7/11

Short bCrafting;			;player has activated the still
Short nButton;				;button press


BEGIN OnActivate

	Set bCrafting to 1;
	ShowMessage NVDLC03BattleBrewCraftMsg;

END


BEGIN GameMode

	If (bCrafting == 1)
		Set nButton to GetButtonPressed;

		If (nButton > -1)
			If (nButton == 0)
				; Do Nothing
			ElseIf (nButton == 1)
				Player.RemoveItem Vodka 1;
				Player.RemoveItem NVDLC03CaveFungusINGESTIBLE 1;
				Player.RemoveItem NVDLC03SalientGreen 1;
				Player.AddItem NVDLC03BattleBrew 1;
			ElseIf (nButton == 2)
				Player.RemoveItem Vodka 6;
				Player.RemoveItem NVDLC03CaveFungusINGESTIBLE 6;
				Player.RemoveItem NVDLC03SalientGreen 6;
				Player.AddItem NVDLC03BattleBrew 6;
			ElseIf (nButton == 3)
				Player.RemoveItem Vodka 12;
				Player.RemoveItem NVDLC03CaveFungusINGESTIBLE 12;
				Player.RemoveItem NVDLC03SalientGreen 12;
				Player.AddItem NVDLC03BattleBrew 12;
			EndIf

			Set bCrafting to 0;
		EndIf
	EndIf

END

scn NVDLC03BattleBrewEquipSCRIPT

; plays sound and ISFX when player drinks battle brew
; RL 2/7/11


BEGIN ScriptEffectStart

	IMOD NVDLC03RedMistISFX;
	CastImmediateOnSelf NVDLC03RedMist;

END


BEGIN ScriptEffectFinish

	PlaySound NVDLC03RedMistWearOff;

END

scn NVDLC03HazmatSuitSCRIPT

;adds the hazmat suit to the player's inventory
;SPW 02/02/11
;SPW 02/23/11 - Adding item conditions
;SPW 03/07/11 - Changing the name of the force field ref


Short bDoOnce;		runs script once

BEGIN OnActivate Player

	If ((GetActionRef == Player) && (bDoOnce== 0) && (NVDLC03HazmatFieldREF.GetDisabled))
		NVDLC03HazmatSuitREF.disable;
		Player.AddItemHealthPercent NVDLC03GhostHoodlessHead 1 .85;
		Player.AddItemHealthPercent NVDLC03GhostHoodlessBody 1 .85;
		Set bDoOnce To 1;
		NVDLC03HazmatSuitREF.MarkForDelete;
	EndIf

END

scn NVDLC03GiantRoboScorpionCombatSCRIPT
; Handles combat behavior for the Mega Robo-Scorpion. Since it's guarding a specific spot, it's too easy for the player to snipe from range. This fixes that. ; Player is given 10 seconds to take in the situation and find cover after Mega Robo-Scorpion powers on. ; After that, if Mega Robo-Scorpion isn't currently in combat and it can "see" the player, it'll fire a shot at him. ; JSH 02/02/11
Short bStartTimer ; 1 = Grace period before combat initiated. Player has time to take in things before he starts getting blasted. Short bGraceOver ; 1 = Grace period over. Giant Robo-Scorpion will now come after the player, ready or not.
BEGIN GameMode
If ( bGraceOver == 0 )
Set bGraceOver to 1;
;NVDLC03ForbiddenTalkingActivatorREF.Say NVDLC03DialogueMobiusScorpionTest;
SetEnemy NVDLC03RoboScorpionGiantFaction PlayerFaction;
NVDLC03RoboScorpionGiantLVLREF.SetGhost 0;
If ( NVDLC03RoboScorpionGiantLVLREF.bCombatStyle == 0 )
NVDLC03RoboscorpionGiantLVLREF.AddScriptPackage NVDLC03RoboscorpionGiantUseWeapon;
Else
NVDLC03RoboscorpionGiantLVLREF.StartCombat Player;
EndIf
Else
If ( NVDLC03RoboScorpionGiantLVLREF.GetLineOfSight Player ) && ( NVDLC03RoboScorpionGiantLVLREF.bCombatStyle == 0 )
NVDLC03RoboscorpionGiantLVLREF.AddScriptPackage NVDLC03RoboscorpionGiantUseWeapon;
EndIf
EndIf
END

ScriptName NVDLC03AuralStealthSuitBarksSCRIPT


; bark controller quest for Aural Stealth Suit
; RL 02/02/2011
; RL 02/02/2011 - Moved suit GameMode block and variables to this quest
; RL 02/07/2011 - Added sneak variable for ISFX control of adv optics
; JDJ 02/21/2011 - Deleted adv optics section because vr test is being deleted
; JDJ 03/11/2011 - Standards update
; OMP 04/28/2011	- Added an escape if CharGen mode is active, preventing barks during intro/ending slide sequences.


Short bDoOnce;
Short bSneaking;
Short bWarnOnce;
Short bEquipBark;
Short bUnequipBark;
Short bInCombat;
Short bStartupSeq;
Short bSayDelay;
Short bSayDelayMorphineUsed;									; [NOTE: "Morphine" (Ingestible's ID) = "Med-X" (Ingestible's Name)]
Short bSayDelayMorphineOut;										; <<<
Short bSayDelayStimpakUsed; 
Short bSayDelayStimpakOut;
Short bSayDelayRandom;
Short bRandomNumberSet;
Short nRandomNumber;

Float fSayTimer;
Float fSayTimerNumber;
Float fSayDelayTimer;
Float fStartupSeqTimer;
Float fSayRandomTimer;
Float fSayRandomTimerNumber;


BEGIN GameMode

	; Prevent any barks when in CharGen mode
	If ( GetInCharGen == 1 )
		Return;
	EndIf

	If ( bEquipBark == 1 )
		NVDLC03StealthSuitVoiceCrREF.Say StealthSuitEQUIP 1 NVDLC03StealthSuitVoiceCr 1;					; <Suit introduces itself>
		Set bSayDelay to 1;
		Set fSayTimerNumber to 30;																								; "Say Delay" set to 30 seconds
		Set bEquipBark to 0;
		Set bStartupSeq to 1;
	EndIf

	If ( bUnequipBark == 1 )
		NVDLC03StealthSuitVoiceCrREF.Say StealthSuitUNEQUIP 1 NVDLC03StealthSuitVoiceCr 1;				; <Suit comments on removal>
		Set bSayDelay to 1;
		Set fSayTimerNumber to 10;
		Set bUnequipBark to 0;
		Set bStartupSeq to 0;
	Endif

	If ( bSayDelay == 1 )
		If ( fSayDelayTimer < fSayTimerNumber )
			Set fSayDelayTimer to fSayDelayTimer + GetSecondsPassed;
		Else
			Set bSayDelay to 0;
			Set fSayDelayTimer to 0;
		Endif
	Elseif ( bSayDelayMorphineUsed == 1 )
		If ( Player.GetAv EnduranceCondition >= 50 && Player.GetAv PerceptionCondition >= 50 && Player.GetAv LeftMobilityCondition >= 50 && Player.GetAv RightMobilityCondition >= 50 && Player.GetAv LeftAttackCondition >= 50 && Player.GetAv RightAttackCondition >= 50 )
			Set bSayDelayMorphineUsed to 0;
		Endif
	Elseif ( bSayDelayMorphineOut == 1 )
		If ( Player.GetItemCount Morphine >= 1 )
			Set bSayDelayMorphineOut to 0;
		Endif
	Elseif ( bSayDelayStimpakUsed == 1 )
		If ( Player.GetHealthPercentage >= 0.5 )
			Set bSayDelayStimpakUsed to 0;
		Endif
	Elseif ( bSayDelayStimpakOut == 1 )
		If ( Player.GetItemCount Stimpak >= 1 )
			Set bSayDelayStimpakOut to 0;
		Endif
	Endif

	If ( bSayDelayRandom == 1 )
		If ( fSayRandomTimer < fSayRandomTimerNumber )
			Set fSayRandomTimer to fSayRandomTimer + GetSecondsPassed;
		Else
			Set bSayDelayRandom to 0;
			Set fSayRandomTimer to 0;
		Endif
	Endif

	; -----------------------------------------------------------------------------	Out-of-Combat Barks -----------------------------------------------------------------	\/

	; ----------------------------------------------------	Startup Sequence Barks --------------------------------------------	\/
	If ( Player.GetEquipped NVDLC03AuralStealthsuit == 1 && Player.IsInCombat == 0 && bSayDelay == 0 )

		; --------------------------------	Status-of-Chemical Barks ----------------------------	 \/
		If ( bStartupSeq == 1 )
			If ( Player.GetItemCount Stimpak == 0 )
				Set bSayDelay to 1;
				Set fSayTimerNumber to 30;
				; <Suit comments on missing Stimpak supply>																; <<<ADD TOPIC ENTRY HERE>>>
			Elseif ( Player.GetItemCount Stimpak >= 1 && Player.GetItemCount Stimpak <= 3 )
				Set bSayDelay to 1;
				Set fSayTimerNumber to 30;
				; <Suit comments on low Stimpak supply>																		; <<<ADD TOPIC ENTRY HERE>>>
			Elseif ( Player.GetItemCount Stimpak >= 4 )
				Set bSayDelay to 1;
				Set fSayTimerNumber to 30;
				; <Suit comments on sufficient Stimpak supply>																; <<<ADD TOPIC ENTRY HERE>>>
			Elseif ( Player.GetItemCount Morphine == 0 )	
				Set bSayDelay to 1;
				Set fSayTimerNumber to 30;
				; <Suit comments on missing Morphine supply>																; <<<ADD TOPIC ENTRY HERE>>>
			Elseif ( Player.GetItemCount Morphine >= 1 && Player.GetItemCount Morphine <= 3 )
				Set bSayDelay to 1;
				Set fSayTimerNumber to 30;
				; <Suit comments on low Morphine supply>																		; <<<ADD TOPIC ENTRY HERE>>>
			Elseif ( Player.GetItemCount Morphine >= 4 )
				Set bSayDelay to 1;
				Set fSayTimerNumber to 30;
				; <Suit comments on sufficient Morphine supply>																; <<<ADD TOPIC ENTRY HERE>>>
			Endif
			Set bStartupSeq to 0;
		Endif
	Endif

	; ----------------------------------------------------	Random Barks ------------------------------------------------------	\/
	If ( bRandomNumberSet == 0 )
		Set nRandomNumber to GetRandomPercent
		Set bRandomNumberSet to 1;
	Endif

	If ( bRandomNumberSet == 1 )
		If ( bSayDelayRandom == 0 && bSayDelay == 0 )
			If ( nRandomNumber <= 33 )
				Set bSayDelayRandom to 1;
				Set bRandomNumberSet to 0;
				Set fSayRandomTimerNumber to 300;
				Return;
			Else ( nRandomNumber > 33 )
				Set bSayDelayRandom to 1;
				Set bRandomNumberSet to 0;
				Set fSayRandomTimerNumber to 1800;
				NVDLC03StealthSuitVoiceCrREF.Say StealthSuitFunnyLines 1 NVDLC03StealthSuitVoiceCr 1		; <Suit says a random funny line>
			Endif
		Endif
	Endif

	If ( Player.GetEquipped NVDLC03AuralStealthsuit == 1 && Player.IsInCombat == 0 && bSayDelay == 0 )

	; -----------------------------------------------------------------------------	In-Combat Barks -----------------------------------------------------------------------	\/
	Elseif ( Player.GetEquipped NVDLC03AuralStealthsuit == 1 && Player.IsInCombat == 1 && bSayDelay == 0 )
		If ( Player.HasMagicEffect MorphineEffect == 0 )																		; player doesn't have morphine in his system.
			If ( Player.GetAv EnduranceCondition <= 25 )
				If ( Player.GetItemCount Morphine > 0 && bSayDelayMorphineUsed == 0 )								; player has morphine available in his inventory.
					Player.CastImmediateOnSelf MS14Morphine
					Player.RemoveItem Morphine 1
					Set bSayDelay to 1;
					Set fSayTimerNumber to 1;
					Set bSayDelayMorphineUsed to 1;
					NVDLC03StealthSuitVoiceCrREF.Say StealthSuitMedX 1 NVDLC03StealthSuitVoiceCr 1			; <Suit comments on Med-X injection>
				ElseIf ( Player.GetItemCount Morphine == 0 && bSayDelayMorphineOut == 0 )	
					Set bSayDelay to 1;
					Set fSayTimerNumber to 1;
					Set bSayDelayMorphineOut to 1;														
					NVDLC03StealthSuitVoiceCrREF.Say StealthSuitMedX 1 NVDLC03StealthSuitVoiceCr 1			; <Suit comments on missing Med-X supply>
				EndIf
			ElseIf ( Player.GetAv PerceptionCondition <= 25 )
				If ( Player.GetItemCount Morphine > 0 && bSayDelayMorphineUsed == 0 )
					Player.CastImmediateOnSelf MS14Morphine
					Player.RemoveItem Morphine 1
					Set bSayDelay to 1;
					Set fSayTimerNumber to 1;
					Set bSayDelayMorphineUsed to 1;
					NVDLC03StealthSuitVoiceCrREF.Say StealthSuitMedX 1 NVDLC03StealthSuitVoiceCr 1			; <Suit comments on Med-X injection>
				ElseIf ( Player.GetItemCount Morphine == 0 && bSayDelayMorphineOut == 0 )		
					Set bSayDelay to 1;
					Set fSayTimerNumber to 1;	
					Set bSayDelayMorphineOut to 1;															
					NVDLC03StealthSuitVoiceCrREF.Say StealthSuitMedX 1 NVDLC03StealthSuitVoiceCr 1			; <Suit comments on missing Med-X supply>	
				EndIf
			ElseIf ( Player.GetAv LeftMobilityCondition <= 25 )
				If ( Player.GetItemCount Morphine > 0 && bSayDelayMorphineUsed == 0 )
					Player.CastImmediateOnSelf MS14Morphine
					Player.RemoveItem Morphine 1
					Set bSayDelay to 1;
					Set fSayTimerNumber to 1;
					Set bSayDelayMorphineUsed to 1;
					NVDLC03StealthSuitVoiceCrREF.Say StealthSuitMedX 1 NVDLC03StealthSuitVoiceCr 1			; <Suit comments on Med-X injection>
				ElseIf ( Player.GetItemCount Morphine == 0 && bSayDelayMorphineOut == 0 )	
					Set bSayDelay to 1;
					Set fSayTimerNumber to 1;
					Set bSayDelayMorphineOut to 1;																		
					NVDLC03StealthSuitVoiceCrREF.Say StealthSuitMedX 1 NVDLC03StealthSuitVoiceCr 1			; <Suit comments on missing Med-X supply>	
				EndIf
			ElseIf ( Player.GetAv RightMobilityCondition <= 25 )
				If ( Player.GetItemCount Morphine > 0 && bSayDelayMorphineUsed == 0 )
					Player.CastImmediateOnSelf MS14Morphine
					Player.RemoveItem Morphine 1
					Set bSayDelay to 1;
					Set fSayTimerNumber to 1;
					Set bSayDelayMorphineUsed to 1;
					NVDLC03StealthSuitVoiceCrREF.Say StealthSuitMedX 1 NVDLC03StealthSuitVoiceCr 1			; <Suit comments on Med-X injection>
				ElseIf ( Player.GetItemCount Morphine == 0 && bSayDelayMorphineOut == 0 )	
					Set bSayDelay to 1;
					Set fSayTimerNumber to 1;
					Set bSayDelayMorphineOut to 1;																	
					NVDLC03StealthSuitVoiceCrREF.Say StealthSuitMedX 1 NVDLC03StealthSuitVoiceCr 1			; <Suit comments on missing Med-X supply>	
				EndIf
			ElseIf ( Player.GetAv LeftAttackCondition <= 25 )
				If ( Player.GetItemCount Morphine > 0 && bSayDelayMorphineUsed == 0 )
					Player.CastImmediateOnSelf MS14Morphine
					Player.RemoveItem Morphine 1
					Set bSayDelay to 1;
					Set fSayTimerNumber to 1;
					Set bSayDelayMorphineUsed to 1;
					NVDLC03StealthSuitVoiceCrREF.Say StealthSuitMedX 1 NVDLC03StealthSuitVoiceCr 1			; <Suit comments on Med-X injection>
				ElseIf ( Player.GetItemCount Morphine == 0 && bSayDelayMorphineOut == 0 )	
					Set bSayDelay to 1;
					Set fSayTimerNumber to 1;
					Set bSayDelayMorphineOut to 1;																	
					NVDLC03StealthSuitVoiceCrREF.Say StealthSuitMedX 1 NVDLC03StealthSuitVoiceCr 1			; <Suit comments on missing Med-X supply>	
				EndIf
			ElseIf ( Player.GetAv RightAttackCondition <= 25 )
				If ( Player.GetItemCount Morphine > 0 && bSayDelayMorphineUsed == 0 )
					Player.CastImmediateOnSelf MS14Morphine
					Player.RemoveItem Morphine 1
					Set bSayDelay to 1;
					Set fSayTimerNumber to 1;
					Set bSayDelayMorphineUsed to 1;
					NVDLC03StealthSuitVoiceCrREF.Say StealthSuitMedX 1 NVDLC03StealthSuitVoiceCr 1			; <Suit comments on Med-X injection>
				ElseIf ( Player.GetItemCount Morphine == 0	 && bSayDelayMorphineOut == 0 )
					Set bSayDelay to 1;
					Set fSayTimerNumber to 1;	
					Set bSayDelayMorphineOut to 1;																	
					NVDLC03StealthSuitVoiceCrREF.Say StealthSuitMedX 1 NVDLC03StealthSuitVoiceCr 1			; <Suit comments on missing Med-X supply>	
				EndIf
			EndIf
		EndIf
		If ( Player.HasMagicEffect RestoreHealth == 0	)																		; Player doesn't have Stimpak in his system (Hardcore only).
			If ( Player.GetHealthPercentage <= 0.33 && bSayDelayStimpakUsed == 0 )
				If ( Player.GetItemCount Stimpak > 0 )
					Player.CastImmediateOnSelf NVDLC03StealthSuitStimpak;
					Player.RemoveItem Stimpak 1;
					Set bSayDelay to 1;
					Set fSayTimerNumber to 1;
					Set bSayDelayStimpakUsed to 1;
					NVDLC03StealthSuitVoiceCrREF.Say StealthSuitStimpak 1 NVDLC03StealthSuitVoiceCr 1;		; <Suit comments on Stimpak injection>
				ElseIf ( Player.GetItemCount Stimpak == 0 && bSayDelayStimpakOut == 0 )
					Set bSayDelay to 1;
					Set fSayTimerNumber to 1;	
					Set bSayDelayStimpakOut to 1;
					NVDLC03StealthSuitVoiceCrREF.Say StealthSuitStimpak 1 NVDLC03StealthSuitVoiceCr 1;		; <Suit comments on missing Stimpak supply>	
				EndIf
			Endif
		Endif
	EndIf

	; -----------------------------------------------------------------------------	Detection-of-Combat Barks -------------------------------------------------------	\/
	If ( Player.GetEquipped NVDLC03AuralStealthsuit == 1 && bInCombat == 0 && bSayDelay == 0 )
		If ( Player.IsInCombat == 1 )
			NVDLC03StealthSuitVoiceCrREF.Say StealthSuitCombatStart 1 NVDLC03StealthSuitVoiceCr 1;			;<Suit comments on the start of combat>
			Set bSayDelay to 1;
			Set fSayTimerNumber to 30;
			Set bInCombat to 1;
		EndIf
	Elseif ( Player.GetEquipped NVDLC03AuralStealthSuit == 1 && bInCombat == 1 && bSayDelay == 0 )
		If ( Player.IsInCombat == 0 )
			NVDLC03StealthSuitVoiceCrREF.Say StealthSuitCombatEnd 1 NVDLC03StealthSuitVoiceCr 1;			;<Suit comments on the end of combat>
			Set bSayDelay to 1;
			Set fSayTimerNumber to 30;
			Set bInCombat to 0;
		EndIf
	EndIf

END; gamemode

scn NVDLC03MobiusThinkTankMonitorSCRIPT


; Initiates Mobius dialogue with the player from the Think Tank Monitor.
