scn NVDLC03AcidWeapSfxSCRIPT

; Shows message "Enemy struck by acid" and plays "sizzling" sounds periodically during effect.


Float fTimerEffect



BEGIN ScriptEffectStart

	Float RandomTime;
	Set RandomTime to 5.0 + 20.0/99.0 * GetRandomPercent
	Set fTimerEffect to RandomTime;
	ShowMessage NVDLC03AcidWeapMSG;

END; scripteffectstart


BEGIN ScriptEffectUpdate


	If ( fTimerEffect <= 0 )
		PlaySound FXPoisonStinger;
		Set fTimerEffect to 5.0 + 20/99.0 *GetRandomPercent;
	Else
		Set fTimerEffect to ( fTimerEffect - GetSecondsPassed );
	Endif 

END; scripteffectupdate

scn NVDLC03EnableTriggerSCRIPT

;Enables linked ref then removes the trigger
;SPW 01/31/11


Ref rMyLink;	linked ref
Ref rSelf;		this trigger

BEGIN OnTriggerEnter Player

	If (GetActionRef == Player)
		Set rMylink To GetLinkedRef;
		rMyLink.enable;
	EndIf
	Set rSelf To GetSelf;
	rSelf.Disable;
	rSelf.MarkForDelete;

END

scn NVDLC03SinkJukeboxLockedSCRIPT

; This script controls interactions with the "disabled" version of the Sink Jukebox (before the Player brings the unlock holodisk/NOTE "NVDLC03HQBuddyJukeboxNOTE").
; ETB 01/30/11
; SPW 02/24/11 Adding vars for HQ Buddy quest
; SPW 02/28/11 Adding line to show objective for finding the audio samples
; SPW 03/01/11 Removing quest update


BEGIN OnActivate

	If ( IsActionRef Player )
		If ( Player.GetHasNote NVDLC03HQBuddyJukeboxNOTE == 0 )
			ShowMessage NVDLC03HQBuddyLockedMSG;
		Elseif ( Player.GetHasNote NVDLC03HQBuddyJukeboxNOTE == 1 )
			Set NVDLC03DialogueHQBuddies.bJukeboxUnlocked to 1;
			Set NVDLC03HQBuddy.iJuke To 1;								SPW
			;SetObjectiveDisplayed NVDLC03HQBuddySamples 20 1;	SPW - shows objective to find audio samples. 03/01/11 - Quest now updates after all upgrades are in place.
			Player.RemoveNote NVDLC03HQBuddyJukeboxNOTE;
			ShowMessage NVDLC03HQBuddyUnlockMSG;
			SetDestroyed 1;
			IncrementScriptedChallenge NVDLC03AchievementMakingFriends;	
			NVDLC03SinkJukeBoxTAREF.Enable;
			Set NVDLC03SinkJukeBoxTAREF.bJukeboxFirstEnabled to 1;
		Endif
	Else
		Return;
	Endif

END; onactivate

scn NVDLC03X8ReplayQuestSCRIPT


;Script to track variables related to X8 School Tests after the player has completed the x8 quest
;JDJ 01/13/2011
;JDJ 02/18/2011 scripting standards
;JDJ 03/11/2011 standards update


Int bRecords;			;tracks if the player found the records


BEGIN GameMode

	;script runs it player is in x8 school, tracks when the player has the notes and sets the quest objective depending on which test the player is doing
	If (Player.GetInCell NVDLC03X8School == 1)

		If (X8MainCodeBox.nTestRunning == 1)

			If (bRecords == 0)

				;When the player has all three notes, completes the find note objective, starts the exit objective, and unlocks the door
				If (Player.GetHasNote NVDLC03X8SchoolGrades == 1) && (Player.GetHasNote NVDLC03X8SchoolSchedule == 1) && (Player.GetHasNote NVDLC03X8SchoolRecord == 1)

					If (X8MainCodeBox.bSpawn == 0)
						X8SchoolExitParentMarker.Enable;
					ElseIf (X8MainCodeBox.bSpawn == 1)
						X8SchoolExitNSParentMarker.Enable;
					EndIf

					NVDLC03X8SchooltoTunnel.Unlock;
					SetObjectiveCompleted NVDLC03X8Replay 10 1;
					RewardXP 100;
					SetObjectiveDisplayed NVDLC03X8Replay 20 1;
					Set bRecords to 1;

				EndIf

			EndIf

		ElseIf (X8MainCodeBox.nTestRunning == 2)

			If (bRecords == 0)

				;When the player has all three notes, completes the find note objective, starts the exit objective, and unlocks the door
				If (Player.GetHasNote NVDLC03X8SchoolGrades == 1) && (Player.GetHasNote NVDLC03X8SchoolSchedule == 1) && (Player.GetHasNote NVDLC03X8SchoolRecord == 1)

					If (X8MainCodeBox.bSpawn == 0)
						X8SchoolExitParentMarker.Enable;
					ElseIf (X8MainCodeBox.bSpawn == 1)
						X8SchoolExitNSParentMarker.Enable;
					EndIf

					NVDLC03X8SchooltoTunnel.Unlock;
					SetObjectiveCompleted NVDLC03X8Replay 10 1;
					RewardXP 100;
					SetObjectiveDisplayed NVDLC03X8Replay 20 1;
					Set bRecords to 1;

				EndIf

			EndIf

		EndIf

	EndIf
END

scn NVDLC03TeleportEffectTimerSCRIPT


; Timer to control player's teleport fx into the Big Empty.
; JSH 10/04/10
; OMP 02/16/2011 - Moved the call to start the NVDLC03Intro quest to NVDLC03CrashedSatelliteActivatorSCRIPT.
; OMP 02/24/2011 - Trigger the MoveTo call if we're not skipping the intro.
; JSH 04/27/11 - Moved companion firing here.


Short	nStage;																	; Script blocker.
Float	fTimer;																	; Timer used for animation control.
Short bTeleportDone;															; Teleport has completed.


BEGIN GameMode

	If ( bTeleportDone == 1 )
		Return;
	EndIf

	If ( nStage == 0 )

		; Fire humanoid follower.
		If (VNPCFollowers.bBooneHired == 1)

			Set VNPCFollowers.bHumanoidInParty to 0;

			CraigBooneREF.SetPlayerTeammate 0;
			CraigBooneREF.RemoveFromFaction FollowerFaction;
			CraigBooneREF.SetActorValue Assistance 0;
			CraigBooneREF.RemovePerk CompanionSuite;

			Set CraigBooneREF.Waiting to 0;
			Set VNPCFollowers.bBooneHired to 0;
			Set VNPCFollowers.bBooneFired to 1;
			Set VNPCFollowers.bBooneL38 to 0;

			CraigBooneREF.MoveTo BooneHomeMarkerREF;

			; Remove Player from Boone's faction so Legion isn't automatically hostile.
			Player.RemoveFromFaction VBooneFaction;

			Player.RemovePerk Spotting;
			ShowMessage FollowerMessagePerkBooneRemove;
			ShowMessage FollowerMessageLeaveBoone;

		ElseIf (VNPCFollowers.ArcadeHired == 1)

			Set VNPCFollowers.bHumanoidInParty to 0;

			ArcadeREF.SetPlayerTeammate 0;
			ArcadeREF.RemoveFromFaction FollowerFaction;
			ArcadeREF.SetActorValue Assistance 0;
			ArcadeREF.RemovePerk CompanionSuite;

			Set ArcadeREF.Waiting to 0;
			Set VNPCFollowers.ArcadeHired to 0;
			Set VNPCFollowers.ArcadeFired to 1;
			Set VNPCFollowers.bArcadeL38 to 0;

			Player.RemovePerk BetterHealing;
			ShowMessage FollowerMessagePerkArcadeRemove;
			ShowMessage FollowerMessageLeaveArcade;

			ArcadeREF.MoveTo ArcadeHomeMarker;

		ElseIf (VNPCFollowers.bCassHired == 1)

			Set VNPCFollowers.bHumanoidInParty to 0;

			RoseofSharonCassidyREF.SetPlayerTeammate 0;
			RoseofSharonCassidyREF.RemoveFromFaction FollowerFaction;
			RoseofSharonCassidyREF.SetActorValue Assistance 0;
			RoseofSharonCassidyREF.RemovePerk CompanionSuite;

			Set RoseofSharonCassidyREF.Waiting to 0;
			Set VNPCFollowers.bCassHired to 0;
			Set VNPCFollowers.bCassFired to 1;
			Set VNPCFollowers.bCassL38 to 0;

			Player.RemovePerk WhiskeyRose;
			ShowMessage FollowerMessagePerkCassRemove;
			ShowMessage FollowerMessageLeaveCass;

			RoseofSharonCassidyREF.MoveTo CassHomeMarkerREF;

			StopQuest vCassTimer;

		ElseIf (VNPCFollowers.bLilyHired == 1)

			Set VNPCFollowers.bHumanoidInParty to 0;

			LilyREF.SetPlayerTeammate 0;
			LilyREF.RemoveFromFaction FollowerFaction;
			LilyREF.SetActorValue Assistance 0;
			LilyREF.RemovePerk CompanionSuite;

			Set LilyREF.Waiting to 0;
			Set VNPCFollowers.bLilyHired to 0;
			Set VNPCFollowers.bLilyFired to 1;
			Set VNPCFollowers.bLilyL38 to 0;

			LilyREF.MoveTo LilyJacobstownMarker;	

			Player.RemovePerk StealthGirl;
			ShowMessage FollowerMessagePerkLilyRemove;
			ShowMessage FollowerMessageLeaveLily;

			StopQuest LilysMedicineTimer;

		ElseIf (VNPCFollowers.bVeronicaHired == 1)

			Set VNPCFollowers.bHumanoidInParty to 0;

			VeronicaREF.SetPlayerTeammate 0;
			VeronicaREF.RemoveFromFaction FollowerFaction;
			VeronicaREF.SetActorValue Assistance 0;
			VeronicaREF.RemovePerk CompanionSuite;

			Set VeronicaREF.Waiting to 0;
			Set VNPCFollowers.bVeronicaHired to 0;
			Set VNPCFollowers.bVeronicaFired to 1;
			Set VNPCFollowers.bVeronicaL38 to 0;

			Player.RemovePerk ScribeAssistant;
			ShowMessage FollowerMessagePerkVeronicaRemove;
			ShowMessage FollowerMessageLeaveVeronica;

			VeronicaREF.MoveTo VeronicaHomeMarkerREF;

		ElseIf (VNPCFollowers.RaulHired == 1)

			Set VNPCFollowers.bHumanoidInParty to 0;

			RaulREF.SetPlayerTeammate 0;
			RaulREF.RemoveFromFaction FollowerFaction;
			RaulREF.SetActorValue Assistance 0;
			RaulREF.RemovePerk CompanionSuite;

			Set RaulREF.Waiting to 0;
			Set VNPCFollowers.RaulHired to 0;
			Set VNPCFollowers.RaulFired to 1;
			Set VNPCFollowers.bRaulL38 to 0;

			Player.RemovePerk RegularMaintenance;
			ShowMessage FollowerMessagePerkRaulRemove;
			ShowMessage FollowerMessageLeaveRaul;

			ShowMap RaulsShackMarkerREF 1;
			RaulREF.MoveTo RaulHomeMarker;

		EndIf

		; Fire Rex or ED-E
		If (VNPCFollowers.RexHired == 1)

			Set VNPCFollowers.bCritterInParty to 0;

			RexREF.SetPlayerTeammate 0;
			RexREF.RemoveFromFaction FollowerFaction;
			RexREF.SetActorValue Assistance 0;
			RexREF.RemovePerk CompanionSuite;

			Set RexREF.Waiting to 0;
			Set VNPCFollowers.RexHired to 0;
			Set VNPCFollowers.RexFired to 1;
			Set VNPCFollowers.bRexL38 to 0;

			Player.RemovePerk SearchAndMark;
			ShowMessage FollowerMessagePerkRexRemove;
			ShowMessage FollowerMessageLeaveRex;

			RexREF.MoveTo RexHomeMarker;

		ElseIf (VNPCFollowers.bEDEHired == 1)

			Set VNPCFollowers.bCritterInParty to 0;

			EDE1REF.SetPlayerTeammate 0;
			EDE1REF.RemoveFromFaction FollowerFaction;
			EDE1REF.SetActorValue Assistance 0;
			EDE1REF.RemovePerk CompanionSuite;

			EDE2REF.SetPlayerTeammate 0;
			EDE2REF.RemoveFromFaction FollowerFaction;
			EDE2REF.SetActorValue Assistance 0;
			EDE2REF.RemovePerk CompanionSuite;

			EDE3REF.SetPlayerTeammate 0;
			EDE3REF.RemoveFromFaction FollowerFaction;
			EDE3REF.SetActorValue Assistance 0;
			EDE3REF.RemovePerk CompanionSuite;

			Set EDE1REF.Waiting to 0;
			Set EDE2REF.Waiting to 0;
			Set EDE3REF.Waiting to 0;
			Set VNPCFollowers.bEDEHired to 0;
			Set VNPCFollowers.bEDEFired to 1;
			Set VNPCFollowers.bEDEL38 to 0;

			; Reset ED-E's aggresion in case he got frenzied.
			EDE1REF.SetActorValue Aggression 0;
			EDE2REF.SetActorValue Aggression 0;
			EDE3REF.SetActorValue Aggression 0;

			Player.RemovePerk EnhancedSensors;
			ShowMessage FollowerMessagePerkEDERemove;
			ShowMessage FollowerMessageLeaveEDE;

			EDE1REF.MoveTo EDEHomeMarker;
			EDE2Ref.MoveTo EDEHomeMarker;
			EDE3Ref.MoveTo EDEHomeMarker;

		EndIf

		Set VNPCFollowers.bPlayerHasFollower to 0;
		Set VNPCFollowers.nCurrentFollowers to 0;

		NVDLC03FXTeleportREF.MoveTo Player 0 0 0;
		ApplyImageSpaceModifier NVDLC03TeleportOutISFX;
		Set fTimer To 7.5;
		Set nStage To 1;
		PlaySound OBJSateProjActivate
	EndIf

	Set fTimer To ( fTimer - GetSecondsPassed );

	If ( fTimer <= 5.5 ) && ( nStage == 1 )
		NVDLC03FXTeleportREF.Enable;									; Enable the swirling teleport effect.
		;NVDLC03FXTeleportREF.PlayGroup Forward 1;
		Set nStage To 2;
	EndIf

	If ( fTimer <= 0 ) 
		Set bTeleportDone To 1;
		If ( NVDLC03Intro.bSkipIntro == 0 )
			Player.MoveTo NVDLC03SlideShowMarkerREF;			; Teleport the player to the NVDLC03 slide show.
		EndIf
		StopQuest NVDLC03TeleportEffectTimer;
	EndIf

END

scn NVDLC03CaveFungusMutantSCRIPT

; Mutant Cave Fungus (pickable) script
; ETB 1/27/11
; SPW 02/03/11 changed addItem to NVDLC03CaveFungusINGESTIBLE


Int iState


BEGIN OnActivate

	If (iState == 0 && GetActionRef == Player)
		Player.AddItem NVDLC03CaveFungusINGESTIBLE 1;
		Set iState to 1;
		PlayGroup Forward 1;
		SetDestroyed 1;
	EndIf

END


BEGIN OnLoad

	If (iState == 0)
		PlayGroup Backward 1;
	ElseIf (iState == 1)
		PlayGroup Forward 1;	
	EndIf

END


BEGIN OnReset

	If (iState == 1)
		PlayGroup Backward 1;
		Set iState to 0;
		SetDestroyed 0;
	Endif

END

Scn	NVDLC03HoloFlickerEffectSCRIPT


; Sets Actor Alpha and the Flicker rate for the Holograms using the NVDLC03HologramFlickerEffect effect.
; - Jorge 2010
; CKwock 1/27/2011 - Replaced "NVDLC01" references with NVDLC03 reference in the script and Header. 
; CKwock 2/02/2011 - Edited script to meet scripting standards.
; CKwock 3/03/2011 - Revised script based on script review notes.  Added line between Header and description. Added missing Parentheses around nCount >10. Capitalized EndIf/ElseIf statements.


Short	bEnd;																					;If 1, then we calculate a random period of time under which there will be no flickering (Actor Alpha == 1). If 0, then we begin the variable flickering timings, and effect calls.
Short	bFlickers;																			;1/0 control the calculations that define flicker in, and flicker on, effects.
Int		nCount;																				;Counter under which we perform bFlicker checks, up to a max of 10, until we return to a bEnd == 1 state.		
Float	fTimer;																				;Used for all timer calculations, varies depending on the operation block (defined by the variables above), and on the results of GetRandomPercent calls.
Float	fRandom	;																			;Used to store the randomizer GetRandomPercent calls.													
Ref	rSelf;																					;Ref that must be turned on/off with SetActorAlpha calls.


BEGIN ScriptEffectStart

	Set fTimer to ((GetRandomPercent + 50) / 10);									;Sets the fTimer used for all timer calculations
	Set rSelf to GetSelf;																	;Sets rSelf reference to the hologram using this script.

END


BEGIN ScriptEffectUpdate

	If (bEnd == 1);																			;Start to calculate a random period of time which there will be no flickering.
		If (fTimer <= 0);
			Set fRandom to GetRandomPercent;										;Stores randomizer from GetRandomPercent call as fRandom
			Set fTimer to (fRandom / 100);
			Set bEnd to 0;
			Return;			
		Else
			Set fTimer to (fTimer - ScriptEffectElapsedSeconds);
		EndIf
	Else
		If (nCount < 10);																	;Checks counter under which we perform bFlicker checks. Holograms alpha will alternate between 1 and 0.
			Set fTimer to (fTimer - ScriptEffectElapsedSeconds);
			If (bFlickers == 0 && fTimer <= 0)
				rSelf.SetActorAlpha 1;
				Set fRandom to GetRandomPercent;
				Set fTimer to (fRandom / 200);
				Set bFlickers to 1;
				Return;
			ElseIf (bFlickers == 1 && fTimer <= 0)
				rSelf.SetActorAlpha 0;
				Set fRandom to GetRandomPercent;
				Set fTimer to (fRandom / 600);
				Set nCount to (nCount + 1);
				Set bFlickers to 0;
				Return;
			EndIf
		Else
			rSelf.SetActorAlpha 1;															;Sets Holograms Alpha, fTimer, nCount, and bEnd to their initial state.
			Set fTimer to ((GetRandomPercent + 50) / 10);							
			Set nCount to 0;
			Set bEnd to 1;
			Return;
		EndIf
	EndIf

END

Scn	NVDLC03HoloFlickerOutEffectSCRIPT


; Sets Actor Alpha and the Flicker rate for the NVDLC03HologramFadeOutEffect effect that makes the Hologram Flicker out of view.
; - Jorge 2010
; CK 1/27/2011 - Replaced "NVDLC01" references with NVDLC03 reference in the script and Header.
; CK 2/02/2011 - Edited script to meet scripting standards.
; CK 3/03/2011 - Revised script based on script review notes.  Added line between Header and description. Capitalized EndIf/ElseIf statements.



Short	bEnd;																					;If 1, then we calculate a random period of time under which there will be no flickering (Actor Alpha == 1). If 0, then we begin the variable flickering timings, and effect calls.
Short	bFlickers;																			;1/0 control the calculations that define flicker in, and flicker on, effects.
Int		nCount;																				;Counter under which we perform bFlicker checks, up to a max of 10, until we return to a bEnd == 1 state.		
Float	fTimer;																				;Used for all timer calculations, varies depending on the operation block (defined by the variables above), and on the results of GetRandomPercent calls.
Float	fRandom	;																			;Used to store the randomizer GetRandomPercent calls.													
Ref	rSelf;																					;Ref that must be turned on/off with SetActorAlpha calls.


BEGIN ScriptEffectStart

	Set fTimer to (GetRandomPercent / 50);											;Sets the fTimer used for all timer calculations
	Set rSelf to GetSelf;																	;Sets rSelf reference to the NPC using this script.
	rSelf.SetActorAlpha 1;																	;Sets SetActorAlpha calls on rSelf to 1.


END


BEGIN ScriptEffectUpdate

	If (nCount < 5);																			;Checks counter under which we perform bFlicker checks. Holograms alpha will alternate between 1 and 0 four times.
		Set fTimer to (fTimer - ScriptEffectElapsedSeconds);						;Updates fTimer initially set at the start of the script.  Affects time between each flicker on the hologram.
		If (bFlickers == 1 && fTimer <= 0)
			rSelf.SetActorAlpha 1;
			Set fRandom to GetRandomPercent;
			Set fTimer to (fRandom / 200);												;Sets the time between the next flicker.
			Set bFlickers to 0;
			Return;
		ElseIf (bFlickers == 0 && fTimer <= 0)
			rSelf.SetActorAlpha 0;
			Set fRandom to GetRandomPercent;
			Set fTimer to (fRandom / 600);
			Set nCount to (nCount + 1);
			Set bFlickers to 1;
			Return;
		EndIf
	ElseIf (nCount >= 5 && nCount < 10);												;Checks counter under which we perform bFlicker checks. Holograms alpha will alternate between 1 and 0 five times.
		Set fTimer to (fTimer - ScriptEffectElapsedSeconds);
		If (bFlickers == 1 && fTimer <= 0)
			rSelf.SetActorAlpha 1;
			Set fRandom to GetRandomPercent;
			Set fTimer to (fRandom / 600);												;Sets the time between the next flicker.
			Set bFlickers to 0;
			Return;
		ElseIf (bFlickers == 0 && fTimer <= 0)
			rSelf.SetActorAlpha 0;
			Set fRandom to GetRandomPercent;
			Set fTimer to (fRandom / 600);
			Set nCount to (nCount + 1);
			Set bFlickers to 1;
			Return;
		EndIf
	Else
		rSelf.SetActorAlpha 0;																;Sets Holograms Alpha to 0 after the Hologram is finished flickering ou.
		Set nCount to 11;																	;nCount set to 11 so the script will not perform bFlicker checks.															
		Set bFlickers to 0;																	;Resets Timers and Counters used to calculate the Holograms flicker.
		Set fTimer to 0;
		Set fRandom to 0;
		Return;
	EndIf

END


BEGIN ScriptEffectFinish

	rSelf.SetActorAlpha 0;																	;Sets Hologram's Alpha to 1 and resets all timer and counters used to calculate flicker.  
	Set nCount to 0;
	Set bFlickers to 0;
	Set fTimer to 0;
	Set fRandom to 0;

END

Scn	NVDLC03HoloFlickerInEffectSCRIPT


; Sets Actor Alpha and the Flicker rate for the NVDLC03HologramFadeInEffect effect that makes the Hologram Flicker into view.
; - Jorge 2010
; CKwock 1/27/2011 - Replaced "NVDLC01" references with NVDLC03 reference in the script and Header.
; CKwock 2/02/2011 - Edited script to meet scripting standards.
; CKwock 3/10/2011 - Edited script to meet scripting standards.  Added "2010" creation date to Header.  Added blank line between header and description.  Capitalized ElseIf/ EndIf in script.


Short	bEnd;																					;If 1, then we calculate a random period of time under which there will be no flickering (Actor Alpha == 1). If 0, then we begin the variable flickering timings, and effect calls.
Short	bFlickers;																			;1/0 control the calculations that define flicker in, and flicker on, effects.
Int		nCount;																				;Counter under which we perform bFlicker checks, up to a max of 10, until we return to a bEnd == 1 state.		
Float	fTimer;																				;Used for all timer calculations, varies depending on the operation block (defined by the variables above), and on the results of GetRandomPercent calls.
Float	fRandom	;																			;Used to store the randomizer GetRandomPercent calls.													
Ref	rSelf;																					;Ref that must be turned on/off with SetActorAlpha calls.


BEGIN ScriptEffectStart

	Set fTimer to (GetRandomPercent / 50);											;Sets the fTimer used for all timer calculations
	Set rSelf to GetSelf;																	;Sets rSelf reference to the NPC using this script.
	rSelf.SetActorAlpha 0;																	;Sets SetActorAlpha calls on rSelf to 0.

END


BEGIN ScriptEffectUpdate

	If (nCount < 5);																			;Checks counter under which we perform bFlicker checks. Holograms alpha will alternate between 0.7 and 0 four times.
		Set fTimer to (fTimer - ScriptEffectElapsedSeconds);						;Updates fTimer initially set at the start of the script.  Affects time between each flicker on the hologram.
		If (bFlickers == 1) && (fTimer <= 0);
			rSelf.SetActorAlpha 0.7;														;Sets Holograms Alpha to 0.7
			Set fRandom to GetRandomPercent;
			Set fTimer to (fRandom / 600);												;Sets the time the Hologram's alpha remains at 0.7 and time between the next flicker.
			Set bFlickers to 0;
			Return;
		ElseIf (bFlickers == 0) && (fTimer <= 0)
			rSelf.SetActorAlpha 0;
			Set fRandom to GetRandomPercent;
			Set fTimer to (fRandom / 200);
			Set nCount to (nCount + 1);
			Set bFlickers to 1;
			Return;
		EndIf
	ElseIf (nCount >= 5) && (nCount < 10);											;Checks counter under which we perform bFlicker checks. Holograms alpha will alternate between 1 and 0.4 five times.
		Set fTimer to (fTimer - ScriptEffectElapsedSeconds);
		If (bFlickers == 1 && fTimer <= 0)
			rSelf.SetActorAlpha 1;
			Set fRandom to GetRandomPercent;
			Set fTimer to (fRandom / 600);
			Set bFlickers to 0;
			Return;
		ElseIf (bFlickers == 0) && (fTimer <= 0)
			rSelf.SetActorAlpha 0.4;
			Set fRandom to GetRandomPercent;
			Set fTimer to (fRandom / 600);
			Set nCount to (nCount + 1);
			Set bFlickers to 1;
			Return;
		EndIf
	Else
		rSelf.SetActorAlpha 1;																;Sets Holograms Alpha to 1 after the Hologram is finished flickering in.
		Set nCount to 0;																	;Resets Timers and Counters used to calculate the Holograms Flicker to 0.
		Set bFlickers to 0;
		Set fTimer to 0;
		Set fRandom to 0;
		Return;
	EndIf

END


BEGIN ScriptEffectFinish

	rSelf.SetActorAlpha 1;																	;Sets Hologram's Alpha to 1 and resets all timer and counters used to calculate flicker.  
	Set nCount to 0;
	Set bFlickers to 0;
	Set fTimer to 0;
	Set fRandom to 0;

END

scn NVDLC03X13SoundCueTriggerSCRIPT

Ref rLinked;				;Linked Reference
Short nRandom;		;Random number

BEGIN OnTriggerEnter Player

	If (NVDLC03X13Setup.bStalkers == 0)
		Set rLinked to GetLinkedRef;
		Set nRandom to GetRandomPercent;

		If (nRandom <= 20)
			rLinked.PlaySound3D NVDLC03X13EMTNightStalkerBig;
		ElseIf (nRandom > 20) && (nRandom <= 40)
			rLinked.PlaySound3D NVDLC03X13NPCNightStalkerConsciousLPLg;
		Else (nRandom > 40) && (nRandom <= 60)
			rLinked.PlaySound3D NVDLC03X13NPCNightStalkerConsciousLP;
		Else (nRandom > 60) && (nRandom <= 80)
			rLinked.PlaySound3D NVDLC03X13NPCNightStalkerConsciousLPSm;
		Else
			rLinked.PlaySound3D NVDLC03X13NPCNightStalkerGrowl;
		EndIf
	EndIf

	Disable;

END

scn NVDLC03X13NightStalkerTriggerScript
; Link objects to this with this object as the "enable parent" and the "opposite" box checked. When the player enters this trigger the trigger will ; Be disabled so it only fires once, and the objects in question will enable ; RLee 1/25/11
BEGIN OnTriggerEnter Player
Set NVDLC03X13Setup.bStalkers to 1;
Disable;
END

scn NVDLC03X13SetupReplayCodeBoxSCRIPT

; X13 replay code box used for changing level settings after the X13 quest is completed
; RLee 1/24/11

Short bTest4Once;			; do once for test 3 to track replay objective completion
Short bCheat;				; bool VO controller for destroying the robots
Float fBarkTimer;			; timer to delay suit barks after popup message
Short bBark;				; holder variable to run suit bark


BEGIN GameMode

	If (Player.HasPerk RoboticsExpert == 1) && (Player.HasPerk NVDLC03X13RobotCompliance == 1)
		Player.RemovePerk NVDLC03X13RobotCompliance;
	EndIf

	If (NVDLC03X13Setup.nTest > 0) && (GetObjectiveDisplayed NVDLC03X13Setup 5 == 0) && (NVDLC03X13Setup.bDetected == 0) && (Player.GetInCell NVDLC03X13RepconHQ03 == 1)

		If (X13Robot1REF.GetDetected Player) && (X13Robot1REF.GetDead == 0)

			ShowMessage NVDLC03X13SpottedMsg;
			Set bBark to 1;
			Set NVDLC03X13Setup.bDetected to 1;

		ElseIf (X13Robot2REF.GetDetected Player) && (X13Robot2REF.GetDead == 0)

			ShowMessage NVDLC03X13SpottedMsg;
			Set bBark to 1;
			Set NVDLC03X13Setup.bDetected to 1;

		ElseIf (X13Robot3REF.GetDetected Player) && (X13Robot3REF.GetDead == 0)
			ShowMessage NVDLC03X13SpottedMsg;
			Set bBark to 1;
			Set NVDLC03X13Setup.bDetected to 1;

		ElseIf (NVDLC03X13Setup.nTest == 4)

			If (X13Robot1REF.GetDead) || (X13Robot2REF.GetDead) || (X13Robot3REF.GetDead)
				ShowMessage NVDLC03X13DestroyedMsg;																		;Tell the player a robot was destroyed
				Set NVDLC03X13Setup.bDetected to 1;

			ElseIf (X13Robot1REF.GetUnconscious) && (X13Robot2REF.GetUnconscious) && (X13Robot3REF.GetUnconscious)
				NVDLC03X13COMPREF.Activate NVDLC03X13COMPREF 1;

			EndIf

		EndIf
	EndIf

	If (NVDLC03X13Setup.bDetected == 1)
		X13FAILREF.Activate X13FAILREF 1;
	EndIf

	If (bBark == 1)
		Set fBarkTimer to (fBarkTimer + GetSecondsPassed);

		If (fBarkTimer > 1)
			NVDLC03StealthSuitVoiceCrREF.Say StealthSuitX13RobotDetectionBark 1 NVDLC03StealthSuitVoiceCr 1;		;Tell the player they were spotted by robots
			Set fBarkTimer to 0;
			Set bBark to 0;
		EndIf
	EndIf

	If (Player.GetInCell NVDLC03X13RepconHQ03 == 0)
		ResetQuest NVDLC03X13Replay;
		StopQuest NVDLC03X13Replay;
	EndIf

END

scn NVDLC03CrashedSatelliteActivatorSCRIPT


; Activator script for the crashed satellite that transports player into NVDLC03.
; JSH 01/24/11
; OMP 02/16/2011 - Added a call to start the NVDLC03Intro quest after starting the teleport timer; set stage of NVDLC03MQ00 to 100.
; AH 02/24/2011 - Set the message for the DLC start to pull the NVDLC03MinLevel global variable
; JSH 04/27/11 - Moved companion firing to teleport effect timer.


Short nButton;		; Button pressed for message box menu.


BEGIN OnActivate

	If ( IsActionRef Player == 1 )

		If ( IsPlayerActionActive 9 == 1 ) 														; Iron Sights mode.
			ShowMessage MQ05CantUseNowMessage;
			Return;
		EndIf

		If ( Player.IsSneaking == 1 )																; Crouched.
			ShowMessage NVDLC03SneakCantUseMSG;
			Return;
		EndIf

		If ( Player.IsInCombat == 1 )
			ShowMessage NVDLC03CrashedSatelliteNoCombatMsg;
		ElseIf ( NVDLC03MQ00.bMoviePlaying == 0 )
			ShowMessage NVDLC03CrashedSatelliteNotMidnightMsg;
		Else
			ShowMessage NVDLC03CrashedSatelliteMsg NVDLC03MinLevel;		; Show the message while passing in the global NVDLC03MinLevel in the message text
		EndIf

	EndIf

END


BEGIN MenuMode 1001

	Set nButton to GetButtonPressed;

	If ( nButton == 1 )

		SetStage NVDLC03MQ00 100;							; Complete the starting quest for DLC03.

		PurgeCellBuffers;											; Free up memory.
		SetDestroyed 1;											; Make the satellite no longer interactive.
		StartQuest NVDLC03TeleportEffectTimer;			; Play the teleport out image space modifiers.
		StartQuest NVDLC03Intro;								; Initialize the Intro Slide sequence
		DisablePlayerControls 1 1 1 1 0 1 1;					; Freeze the player in place.

	EndIf

END


BEGIN GameMode

	If ( GameHour < 3.00 ) || ( GameHour >= 23.00 )				; Time is between 11 PM and 3 AM.

		If ( NVDLC03MQ00.bMoviePlaying == 0 )		

			Set NVDLC03MQ00.bMoviePlaying to 1;

			NVDLC03SatProjectionLightREF.Enable;
			NVDLC03SatProjectionREF.Enable;
			NVDLC03DriveInMovieREF.Enable;
			NVDLC03CrashedSatelliteProjectorREF.PlayGroup Backward 1;

		EndIf

	ElseIf  ( GameHour >= 3.00 ) &&  ( GameHour < 23.00 )		; Time is after 3 AM and before 11 PM.

		If ( NVDLC03MQ00.bMoviePlaying == 1 )

			Set NVDLC03MQ00.bMoviePlaying to 0;

			NVDLC03SatProjectionLightREF.Disable;
			NVDLC03SatProjectionREF.Disable;
			NVDLC03DriveInMovieREF.Disable;
			NVDLC03CrashedSatelliteProjectorREF.PlayGroup Left 1;

		EndIf

	EndIf

END

scn NVDLC03X13ProximityMineSCRIPT

; controls the variables for the mines
; RLee 1/21/11

Short bOnce;		;Initial setup do once
Short bOn;			;Mine is on
Short bReset;		;Simple reset variable called by test startup to set all variables on the mine to 0
Short nBeeps;		;Increments each time a beep occurs to prevent spamming audio
Ref rLinked;			;Linked Reference
Ref rSelf;			;The Mine
Float fTimer;		;Timer for detection sequence


BEGIN OnLoad

	If (bOnce == 0)
		Set rSelf to GetSelf;
		Set bReset to 1;	;Initialize the mine
		Set bOnce to 1;		;Lock-out for OnLoad block, only needs to run once.
	EndIf

END


BEGIN OnActivate

	If (IsActionRef Player) && (bOn == 1)
		PlaySound3D WPNMineDisarm;
		PlayGroup Forward 1;							;Play the 'unlit/off' animation
		Set nBeeps to 20;
		Set fTimer to 0;
		SetDestroyed 1;
		Set bOn to 0;
	EndIf

END


BEGIN OnHitWith NVDLC03SonicWeaponList

	If (NVDLC03MQ03.bSonicEmitterUpgraded == 1) && (bOn == 1)
		PlaySound3D WPNMineDisarm;
		PlayGroup Forward 1;							;Play the 'unlit/off' animation
		Set fTimer to 0;										;Reset variables to stop mine playing
		Set nBeeps to 0;
		Set bOn to 0;
	EndIf

END


BEGIN GameMode

	If (bOn == 1) && (NVDLC03X13Setup.nTest > 2) && (bReset == 0) && (Player.HasPerk LightStep == 0)
		If (GetDistance Player <= 512)
			Set fTimer to (fTimer + GetSecondsPassed);

			If (fTimer >= 1) && (nBeeps == 0)
				PlaySound3D NVDLC03X13EMTMedicalBeep02;
				Set nBeeps to 1;
			ElseIf (fTimer >= 2) && (nBeeps == 1)
				PlaySound3D NVDLC03X13EMTMedicalBeep02;
				Set nBeeps to 2;
			ElseIf (fTimer >= 3) && (nBeeps == 2)
				PlaySound3D NVDLC03X13EMTMedicalBeep02;
				Set nBeeps to 3;
			ElseIf (fTimer >= 3.5) && (nBeeps == 3)
				PlaySound3D NVDLC03X13EMTMedicalBeep02;
				Set nBeeps to 4;
			ElseIf (fTimer >= 4) && (nBeeps == 4)
				PlaySound3D NVDLC03X13EMTMedicalBeep02;
				Set nBeeps to 5;
			ElseIf (fTimer >= 4.5) && (nBeeps == 5)
				PlaySound3D NVDLC03X13EMTMedicalBeep02;
				Set nBeeps to 6;
			ElseIf (fTimer >= 5) && (nBeeps == 6)
				PlaySound3D NVDLC03X13EMTMedicalBeep02;
				Set nBeeps to 7;
			ElseIf (fTimer >= 5.5) && (nBeeps == 7)
				PlaySound3D NVDLC03X13EMTMedicalBeep02;
				Set nBeeps to 8;
			ElseIf (fTimer >= 6) && (nBeeps == 8)
				PlaySound3D NVDLC03X13EMTMedicalBeep02;
				Set nBeeps to 9;
			ElseIf (fTimer >= 6.25) && (nBeeps == 9)
				PlaySound3D NVDLC03X13EMTMedicalBeep02;
				Set nBeeps to 10;
			ElseIf (fTimer >= 6.5) && (nBeeps == 10)
				PlaySound3D NVDLC03X13EMTMedicalBeep02;
				Set nBeeps to 11;
			ElseIf (fTimer >= 6.75) && (nBeeps == 11)
				PlaySound3D NVDLC03X13EMTMedicalBeep02;
				Set nBeeps to 12;
			ElseIf (fTimer >= 7) && (nBeeps == 12)
				PlaySound3D NVDLC03X13EMTMedicalBeep02;
				Set nBeeps to 13;
			ElseIf (fTimer >= 7.25) && (nBeeps == 13)
				PlaySound3D NVDLC03X13EMTMedicalBeep02;
				Set nBeeps to 14;
			ElseIf (fTimer >= 7.5) && (nBeeps == 14)
				PlaySound3D NVDLC03X13EMTMedicalBeep02;
				Set nBeeps to 15;
			ElseIf (fTimer >= 7.75) && (nBeeps == 15)
				PlaySound3D NVDLC03X13EMTMedicalBeep02;
				Set nBeeps to 16;
			ElseIf (fTimer >= 8) && (nBeeps == 16)						;Time is up
				PlaySound3D NVDLC03X13Lucky38AlarmLP;			;Play detection sound
				Set NVDLC03X13Setup.bDetected to 1;					;Start up the failure script
				ShowMessage NVDLC03X13MineMsg;					;"You have been detected."

				If (NVDLC03X13Setup.bCheat == 0) && (X13Robot1REF.GetDead) && (X13Robot2REF.GetDead) && (X13Robot3REF.GetDead) && (GetQuestCompleted NVDLC03X13Setup == 0)
					NVDLC03StealthSuitVoiceCrREF.Say StealthsuitX13RobotsBlownUpBark 1 NVDLC03StealthSuitVoiceCr 1;
					Set NVDLC03X13Setup.bCheat to 1;
				ElseIf (NVDLC03X13Replay.bCheat == 0) && (X13Robot1REF.GetDead) && (X13Robot2REF.GetDead) && (X13Robot3REF.GetDead) && (GetQuestCompleted NVDLC03X13Setup == 1)
					NVDLC03StealthSuitVoiceCrREF.Say StealthsuitX13RobotsBlownUpBark 1 NVDLC03StealthSuitVoiceCr 1;
					Set NVDLC03X13Replay.bCheat to 1;
				Else
					NVDLC03StealthSuitVoiceCrREF.Say StealthSuitX13MineDetectionBark 1 NVDLC03StealthSuitVoiceCr 1;	;Tell player they were detected by a mine
				EndIf

				Set fTimer to 0;													;Reset variables to stop mine playing
				Set nBeeps to 0;
				Set bOn to 0;
			EndIf
		ElseIf (GetDistance Player > 512) && (fTimer > 0)				;If player walks away from the mine, 
			Set fTimer to 0;														;reset the timer
			Set nBeeps to 0;
		EndIf
	ElseIf (bReset == 1)														;Reset the mine to on
		Set bOn to 1;
		Set rSelf to GetSelf;
		PlayGroup Backward 1;												;Play the 'lit/on' animation
		Set nBeeps to 0;
		Set fTimer to 0;
		SetDestroyed 0;
		Set bReset to 0;
	EndIf

END

scn NVDLC03X13LaserTripwireBSCRIPT

; Tripwire script for the friendly laser tripwires in X13
; RLee, 1/5/11
;JDJ 02/24/2011 added lines to make turrets conscious if they are unconscious

Ref rActor;			; Actor entering the tripwire


BEGIN OnTriggerEnter
	Set rActor to getActionRef;

	If (rActor == Player)

	ElseIf (rActor.GetInFaction NVDLC03X13RobotFaction == 0)

		If (X13Turret01REF.GetUnconscious == 1)
  			X13Turret01REF.SetUnconscious 0;
		EndIf
		If (X13Turret02REF.GetUnconscious == 1)
			X13Turret02REF.SetUnconscious 0;
		EndIf
		If (X13Turret03REF.GetUnconscious == 1)
			X13Turret03REF.SetUnconscious 0;
		EndIf
		If (X13Turret04REF.GetUnconscious == 1)
			X13Turret04REF.SetUnconscious 0;
		EndIf

		rActor.AddToFaction NVDLC03X13HatedFaction 1;
		X13Turret01REF.StartCombat rActor;
		X13Turret02REF.StartCombat rActor;
		X13Turret03REF.StartCombat rActor;
		X13Turret04REF.StartCombat rActor;

	EndIf

END

scn NVDLC03ForbiddenScorpionTriggerSCRIPT


; Updates MQ05 and activates Mega Roboscorpion.
; JSH 01/20/11

float fLightTimer
short mode	


BEGIN OnTriggerEnter Player

	SetEnemy NVDLC03RoboScorpionGiantFaction NVDLC03X42TurretFaction 1 1;
	SetEnemy NVDLC03RoboScorpionGiantFaction NVDLC03X42DroneFaction 1 1;

	SetAlly NVDLC03RoboScorpionGiantFaction PlayerFaction;
	NVDLC03RoboScorpionGiantLVLREF.SetGhost 1;

	ApplyImageSpaceModifier NVDLC03X42Lighten2;
	NVDLC03X42ShutdownEffectParentREF.Enable;
	NVDLC03ForbiddenTalkingActivatorREF.Say NVDLC03DialogueMobiusScorpionTest 1;
	NVDLC03RoboScorpionGiantLVLREF.AddScriptPackage NVDLC03RoboscorpionGiantWakeUp;
	SetStage NVDLC03MQ05 25;
	Disable;

END

BEGIN GameMode

	Set fLightTimer to fLightTimer + GetSecondsPassed
		if mode == 0
			if fLightTimer > 2
				PlaySound NVDLC03QSTEndPowerUpTurbine
				NVDLC03ScorpionLight01REF.Enable;
				NVDLC03ScorpionLight02REF.Enable;
				set mode to 1
			endif
		endif
	if mode == 1
			if fLightTimer > 4
				PlaySound NVDLC03QSTEndPowerUpTurbine
				NVDLC03ScorpionLight03REF.Enable;
				NVDLC03ScorpionLight04REF.Enable;
				set mode to 2
			endif
		endif
	if mode == 2
			if fLightTimer > 6
				PlaySound NVDLC03QSTEndPowerUpTurbine
				NVDLC03ScorpionLight05REF.Enable;
				NVDLC03ScorpionLight06REF.Enable;
				NVDLC03ScorpionLight07REF.Enable;
				set mode to 3
			endif
		endif

END

SCN NVDLC03SinkToasterOfDoomSCRIPT


; Talking Activator script for Toaster
; tls 01/19/2011
; SPW 02/24/11 - Adding vars for HQ Buddy quest
; SPW 03/01/11 - Removing note from player's inventory after turn in.
; TLS 05/02/2011 - Setting toaster to only upgrade one Saturnite Power Fist at a time.


Short bToasterUnlocked								; 1 = Player has turned in the unlock holodisk: "NVDLC03HQBuddyToasterNOTE".

Short bTalkAfterMessage 							; Variable set in onactivate block to start dialogue in the gamemode block (so the Player doesn't have to "re-activate" after exiting a message box pop-up.
Short bAwaitingInput									; Message box variable.
Short nButton											; Which message box option the player has pressed.
Short nToasterPostwarCount 
Short nToasterPrewarCount 
Short nToasterCount 
Short nCameraCount 
Short nIronCount 
Short nHotPlateCount 
Short nApplianceTotal
Short nMicrofusionCellReturned
Short nSmallEnergyCellReturned  
Short bLaunchMenu 


BEGIN OnLoad

	SetTalkingActivatorActor NVDLC03SinkToasterTalkerREF;

END


BEGIN OnActivate

	If (IsActionRef Player == 1)
		If ( bToasterUnlocked == 1 )
			If ( NVDLC03DialogueHQBuddies.bBuddiesTalk == 0 )
				ShowMessage NVDLC03HQBuddiesNoTalkMSG;
				;If ( Player.GetItemCount ToasterPostwar >= 1 || Player.GetItemCount ToasterPrewar >= 1 || Player.GetItemCount NVDLC03WeapSaturniteFist >= 1 )	; 4/1/11 ETB - Changed to formlist 
				If ( Player.GetItemCount NVDLC03HQBuddiesToasterAllApplianceLIST >= 1 )
					Set bAwaitingInput to 1;
				  	ShowMessage NVDLC03HQToasterMSG;
				Else
					ShowMessage NVDLC03HQToasterNoItemsMSG;
				Endif
			Elseif ( NVDLC03DialogueHQBuddies.bBuddiesTalk == 1 )
				Set bTalkAfterMessage to 1;
			Endif
		Else
			If ( Player.GetHasNote NVDLC03HQBuddyToasterNOTE== 0 )
				ShowMessage NVDLC03HQBuddyLockedMSG;
			Elseif ( Player.GetHasNote NVDLC03HQBuddyToasterNOTE== 1 )
				ShowMessage 	NVDLC03HQBuddyUnlockMSG;
				Set bToasterUnlocked to 1;
				Player.RemoveNote NVDLC03HQBuddyToasterNOTE;	SPW
				Set NVDLC03HQBuddy.iToaster to 1;							SPW
				Set NVDLC03DialogueHQBuddies.bToasterUnlocked to 1;
				IncrementScriptedChallenge NVDLC03AchievementMakingFriends;	
				Set bTalkAfterMessage to 1;
			Endif
		Endif
	Endif

END; onactivate


BEGIN GameMode

	If ( bTalkAfterMessage == 1 )
		Set bTalkAfterMessage to 0;
		;SetTalkingActivatorActor NVDLC03SinkToasterTalkerREF;
		Activate;
	Endif

	If ( bLaunchMenu == 1 )
		Set bLaunchMenu to 0;
		Set bAwaitingInput to 1;
		ShowMessage NVDLC03HQToasterMSG;
	Endif 

END; gamemode


BEGIN MenuMode 1001

	If ( bAwaitingInput == 1 )
		Set nButton to GetButtonPressed;

		If ( nButton > -1 )
			Set bAwaitingInput to 0;

; -------------------------------------------------------------------	BUTTON 0 ----------------------------------------------	\/	; "Do Nothing"
			If ( nButton == 0 )
				Set nButton to -1;
				Return;
; -------------------------------------------------------------------	BUTTON 1 ----------------------------------------------	\/ 	; "Process Toasters and Applicances"
			Elseif ( nButton == 1 )
				Set nButton to -1;
; ---------------------------	Toasters ------------------------	\/
				Set nToasterPostwarCount to Player.GetItemCount ToasterPostwar;
				Set nToasterPrewarCount to Player.GetItemCount ToasterPrewar;
				Set nToasterCount to ( nToasterPostwarCount + nToasterPrewarCount );
; ---------------------------	Camera --------------------------	\/
				Set nCameraCount to Player.GetItemCount Camera;
; ---------------------------	Iron --------------------------------	\/
				Set nIronCount to Player.GetItemCount Iron;
; ---------------------------	Hot Plate ------------------------	\/
				Set nHotPlateCount to Player.GetItemCount HotPlate;
; ---------------------------	Total Appliances ------------	\/
				Set nApplianceTotal to ( nToasterCount + nCameraCount + nIronCount + nHotPlateCount );
; ---------------------------	Remove Items ---------------	\/
				Player.RemoveItem ToasterPostwar nToasterPostwarCount;
				Player.RemoveItem ToasterPrewar nToasterPrewarCount;
				Player.RemoveItem Camera nCameraCount;
				Player.RemoveItem Iron nIronCount;
				Player.RemoveItem HotPlate nHotPlateCount;
; ---------------------------	Add Items ----------------------	\/
				Set nMicrofusionCellReturned to ( nApplianceTotal * 3 );
				Set nSmallEnergyCellReturned to ( nApplianceTotal * 5 );
				Player.AddItem AmmoMicroFusionCell nMicrofusionCellReturned;
				Player.AddItem AmmoSmallEnergyCell nSmallEnergyCellReturned;
				Player.AddItem ScrapElectronics nToasterCount;
; -------------------------------------------------------------------	BUTTON 2 ----------------------------------------------	\/	; "Super heat Saturnite Fist"
			Elseif ( nButton == 2 )
				Set nButton to -1;
				Player.RemoveItem NVDLC03WeapSaturniteFist 1;
				Player.AddItemHealthPercent NVDLC03WeapSaturniteFistHeat 1 0.85;
			Endif
		Endif
	Else
		Return;
	Endif

END; menumode 1001

SCN NVDLC03SinkBookChuteSCRIPT


; Talking Activator script for Book Chute
; tls 01/24/2011
; SPW 02/25/11	- adding HQBuddy quest vars
; ETB 03/23/11 		- Added gamemode start conversation routine (to get conversation to start directly after a message box pop-up).
; OMP 04/06/2011	- Removed a duplicate count of BurnedBook07.


Short bBookChuteUnlocked					; 1 = Player has turned in the unlock holodisk: "NVDLC03HQBuddyBookChuteNOTE".

Short bAwaitingInput							; Message box variable (menumode).
Short bCallBookChuteUI 						; Message box variable (gamemode).
Short nButton									; Which message box option the player has pressed.
Short bTalkAfterMessage 					; Used to start dialogue directly after a message box pop-up (gamemode).

Short nBookCount								; The number of non-skill books in Player inventory.
Short nPreWarBookCount
Short nBurnedBookTotal
Short nBurnedBook08Count
Short nBurnedBook07Count
Short nBurnedBook06Count
Short nBurnedBook05Count
Short nBurnedBook04Count
Short nBurnedBook03Count
Short nBurnedBook02Count
Short nBurnedBook01Count
Short nBookGenericTotal
Short nBookGeneric08bCount
Short nBookGeneric07bCount
Short nBookGeneric06bCount
Short nBookGeneric05bCount
Short nBookGeneric04bCount
Short nBookGeneric03bCount
Short nBookGeneric02bCount
Short nBookGeneric01Count
Short nPencilCount
Short nLeadReturned
Short nClipboard01Count
Short nClipboard02Count
Short nClipboard03Count
Short nClipboard04Count
Short nClipboardCount


BEGIN OnLoad

	SetTalkingActivatorActor NVDLC03SinkBookChuteTalkerREF;

END


BEGIN OnActivate

	If (IsActionRef Player == 1)
		If ( bBookChuteUnlocked == 1 )
			If ( Player.GetHasNote NVDLC03HQUpgradeBookChuteMulchingNOTE == 1 )
				Player.RemoveNote NVDLC03HQUpgradeBookChuteMulchingNOTE;
				Set NVDLC03DialogueHQBuddies.bBookChuteUpgraded to 1;
				Set NVDLC03HQBuddy.iBook to 2;													;SPW
				ShowMessage NVDLC03HQBookChuteUpgradeMSG;
			Endif
			If ( NVDLC03DialogueHQBuddies.bBuddiesTalk == 0 )
				ShowMessage NVDLC03HQBuddiesNoTalkMSG;
				If ( Player.GetItemCount NVDLC03BooksAllNonSkill >= 1 || Player.GetItemCount NVDLC03Clipboards >= 1 || Player.GetItemCount Pencil01 >= 1 )
					Set bAwaitingInput to 1;
					ShowMessage NVDLC03HQBookChuteMSG;
				Else
					ShowMessage NVDLC03HQBookChuteNoItemsMSG;
				Endif
			Elseif ( NVDLC03DialogueHQBuddies.bBuddiesTalk == 1 )
				Set bTalkAfterMessage to 1;
			Endif
		Else
			If ( Player.GetHasNote NVDLC03HQBuddyBookChuteNOTE == 0 )
				ShowMessage NVDLC03HQBuddyLockedMSG;
			Elseif ( Player.GetHasNote NVDLC03HQBuddyBookChuteNOTE == 1 )
				Player.RemoveNote NVDLC03HQBuddyBookChuteNOTE;
				ShowMessage 	NVDLC03HQBuddyUnlockMSG;
				Set bBookChuteUnlocked to 1;
				Set NVDLC03HQBuddy.iBook to 1;													;SPW
				Set NVDLC03DialogueHQBuddies.bBookChuteUnlocked to 1;
				NVDLC03BookChuteLeftEyeOffREF.Disable;
				NVDLC03BookChuteRightEyeOffREF.Disable;
				NVDLC03BookChuteLeftEyeOnREF.Enable 0;
				NVDLC03BookChuteRightEyeOnREF.Enable 0;
				IncrementScriptedChallenge NVDLC03AchievementMakingFriends;
				Set bTalkAfterMessage to 1;
			Endif
		Endif
	Endif

END; onactivate


BEGIN GameMode

	If ( bCallBookChuteUI == 1 )
		Set bAwaitingInput to 1;
		ShowMessage NVDLC03HQBookChuteMSG;
		Set bCallBookChuteUI to 0;
	Endif

	If ( bTalkAfterMessage == 1 )
		Set bTalkAfterMessage to 0;
		;SetTalkingActivatorActor NVDLC03SinkBookChuteTalkerREF;
		Activate;
	Endif

END; gamemode


BEGIN MenuMode 1001

	If ( bAwaitingInput == 1 )
		Set nButton to GetButtonPressed;

		If ( nButton > -1 )
			Set bAwaitingInput to 0;

; -------------------------------------------------------------------	BUTTON 0 ----------------------------------------------	\/	; "Do Nothing"
			If ( nButton == 0 )
				Set nButton to -1;
				Return;
; -------------------------------------------------------------------	BUTTON 1 ----------------------------------------------	\/ 	; "Process Books"
			Elseif ( nButton == 1 )
				Set nButton to -1;	
				Set nPreWarBookCount to Player.GetItemCount PreWarBook;
				Set nBurnedBook08Count to Player.GetItemCount BurnedBook08;
				Set nBurnedBook07Count to Player.GetItemCount BurnedBook07;
				Set nBurnedBook06Count to Player.GetItemCount BurnedBook06;
				Set nBurnedBook05Count to Player.GetItemCount BurnedBook05;
				Set nBurnedBook04Count to Player.GetItemCount BurnedBook04;
				Set nBurnedBook03Count to Player.GetItemCount BurnedBook03;
				Set nBurnedBook02Count to Player.GetItemCount BurnedBook02;
				Set nBurnedBook01Count to Player.GetItemCount BurnedBook01;
				Set nBookGeneric08bCount to Player.GetItemCount BookGeneric08b;
				Set nBookGeneric07bCount to Player.GetItemCount BookGeneric07b;
				Set nBookGeneric06bCount to Player.GetItemCount BookGeneric06b;
				Set nBookGeneric05bCount to Player.GetItemCount BookGeneric05b;
				Set nBookGeneric04bCount to Player.GetItemCount BookGeneric04b;
				Set nBookGeneric03bCount to Player.GetItemCount BookGeneric03b;
				Set nBookGeneric02bCount to Player.GetItemCount BookGeneric02b;
				Set nBookGeneric01Count to Player.GetItemCount BookGeneric01;
				Set nBurnedBookTotal to ( nBurnedBook08Count + nBurnedBook07Count + nBurnedBook06Count + nBurnedBook05Count + nBurnedBook04Count + nBurnedBook03Count + nBurnedBook02Count + nBurnedBook01Count );
				Set nBookGenericTotal to ( nBookGeneric08bCount + nBookGeneric07bCount + nBookGeneric06bCount + nBookGeneric05bCount + nBookGeneric04bCount + nBookGeneric03bCount + nBookGeneric02bCount  + nBookGeneric01Count );
				Set nBookCount to ( nPreWarBookCount + nBurnedBookTotal + nBookGenericTotal );
				Player.RemoveItem PreWarBook nPreWarBookCount;
				Player.RemoveItem BurnedBook08 nBurnedBook08Count;
				Player.RemoveItem BurnedBook07 nBurnedBook07Count;
				Player.RemoveItem BurnedBook06 nBurnedBook06Count;
				Player.RemoveItem BurnedBook05 nBurnedBook05Count;
				Player.RemoveItem BurnedBook04 nBurnedBook04Count;
				Player.RemoveItem BurnedBook03 nBurnedBook03Count;
				Player.RemoveItem BurnedBook02 nBurnedBook02Count;
				Player.RemoveItem BurnedBook01 nBurnedBook01Count;
				Player.RemoveItem BookGeneric08b nBookGeneric08bCount ;
				Player.RemoveItem BookGeneric07b nBookGeneric07bCount ;
				Player.RemoveItem BookGeneric06b nBookGeneric06bCount ;
				Player.RemoveItem BookGeneric05b nBookGeneric05bCount ;
				Player.RemoveItem BookGeneric04b nBookGeneric04bCount ;
				Player.RemoveItem BookGeneric03b nBookGeneric03bCount ;
				Player.RemoveItem BookGeneric02b nBookGeneric02bCount ;
				Player.RemoveItem BookGeneric01 nBookGeneric01Count ;
				Player.AddItem NVDLC03BlankBookMISC nBookCount;
; -------------------------------------------------------------------	BUTTON 2 ----------------------------------------------	\/	; "Process Pencils"
			Elseif ( nButton == 2 )
				Set nButton to -1;
				Set nPencilCount to Player.GetItemCount Pencil01;
				Set nLeadReturned to ( nPencilCount * 5 );
				Player.RemoveItem Pencil01 nPencilCount;
				Player.AddItem Lead nLeadReturned;
				Player.AddItem SpareParts nPencilCount;
; -------------------------------------------------------------------	BUTTON 3 ----------------------------------------------	\/	; "Process Clipboards"
			Elseif ( nButton == 3 )
				Set nButton to -1;
				Set nClipboard01Count to Player.GetItemCount Clipboard01;
				Set nClipboard02Count to Player.GetItemCount Clipboard02;
				Set nClipboard03Count to Player.GetItemCount Clipboard03;
				Set nClipboard04Count to Player.GetItemCount Clipboard04;
				Set nClipboardCount to ( nClipboard01Count + nClipboard02Count + nClipboard03Count + nClipboard04Count );
				Player.RemoveItem Clipboard01 nClipboard01Count ;
				Player.RemoveItem Clipboard02 nClipboard02Count ;
				Player.RemoveItem Clipboard03 nClipboard03Count ;
				Player.RemoveItem Clipboard04 nClipboard04Count ;
				Player.AddItem DuctTape nClipboardCount;
				Player.AddItem SpareParts nClipboardCount;
			Endif

		Endif
	Else
		Return;
	Endif

END; menumode 1001

ScriptName NVDLC03SinkAutoDocSCRIPT


; Talking Activator script for Auto-Doc
; ETB 01/25/11 		- Elaborated on menumode section.
; SPW 02/24/11	- Adding vars for HQ Buddy quest.
; JSH 03/04/11		- Added completion of optional MQ05 objectives for heart and spine.
; ETB 03/25/11 		- Added gamemode start conversation routine (to get conversation to start directly after a message box pop-up).
; TLS 03/28/2011	- Added scripting for Psych Eval popup
; OMP 04/07/2011	- Restructured the menu code to display after message queue is done processing.


Short	bAutoDocUnlocked;					; 1 = Player has turned in the unlock holodisk: "NVDLC03HQBuddyAutoDocNOTE".
Short	bTalkAfterMessage;					; 1 = "Activate" (start dialogue) in gamemode (set in onactivate block).
Short	bMenuAfterMessage;					; 1 = "

Short	bAwaitingInput;							; Message box variable (menumode).
Short	bCallAutoDocUI; 						; Message box variable (gamemode - set in dialogue result scripts to call up the message box UI).
Short	bCallNoBarberMSG; 					; Message box pop-up variable (being called from another message box).
Short	bCallNoSurgeryMSG; 				; Message box pop-up variable (being called from another message box).
Short	nButton;									; Which message box option the player has pressed.
Short	bPsychEvalUI;							; Message box variable for Psych Eval
Short	bPsychEvalDoOnce;					; Only show the warning pop-up the first time the player selects the psych eval
Short	bPsychEvalAwaitingInput;			; Message box variable for Psych Eval warning

Short	bBarberActive;							; 0 = no barber option, 1 = barber option unlocked.
Short	bCosmeticSurgeryActive;			; 0 = no cosmetic surgery option, 1 = cosmetic surgery option unlocked.
Short	bRestoreBodyParts;					; 0 = use "NVDLC03HQAutoDocMsg" menumode conditions (below), 1 = use "NVDCL03HQAutoDocPlayerPartsMSG" menumode conditions (below). 
Short	bImplants;								; 0 = use "NVDLC03HQAutoDocMsg" menumode conditions (below), 1 = use "NVDLC03HQAutodocImplantMSG" menumode conditions (below).
Short	bImplantC13Active;					; 0 = no implant option, 1 = implant unlocked.
Short	bImplantM5Active;						; <<<
Short	bImplantY3Active;						; <<<
Short	bImplantY7Active;						; <<<
Short	nImplantC13Installed;				; Used to displayed a message after the implant is installed (used in "NVDLC03AutoDocTimerSCRIPT" as part of this routine).
Short	nImplantM5Installed;					; <<<
Short	nImplantY3Installed;					; <<<
Short	nImplantY7Installed;					; <<<

Short	bMakeUpMindOnce;					; Increments Brain-related achievement only once.
Short	bFeelForHeartOnce;					; Increments Heart-related achievement only once.
Short	bFindSpineOnce;						; Increments Spine-related achievement only once.

Short	bBrainGainOnce;						; Shows the full description message for new perk only once (for organic organ perks).
Short	bHeartGainOnce;						; <<<
Short	bSpineGainOnce;						; <<<

Short	nBrainlessSwapMsg;					; 0 = do nothing, 1 = show "swap in" message, 2 = show "swap out" message.
Short	nHeartlessSwapMsg;					; 0 = do nothing, 1 = show "swap in" message, 2 = show "swap out" message.
Short	nSpinelessSwapMsg;				; 0 = do nothing, 1 = show "swap in" message, 2 = show "swap out" message.
Short	nBigBrainedSwapMsg;				; 0 = do nothing, 1 = show "swap in" message, 2 = show "swap out" message, 3 = show "gained" message.
Short	nCardiacArrestSwapMsg;			; 0 = do nothing, 1 = show "swap in" message, 2 = show "swap out" message, 3 = show "gained" message.
Short	nReinforcedSpineSwapMsg;		; 0 = do nothing, 1 = show "swap in" message, 2 = show "swap out" message, 3 = show "gained" message.


BEGIN OnLoad

	SetTalkingActivatorActor NVDLC03SinkAutoDocTalkerREF;

END


BEGIN OnActivate

	If (IsActionRef Player == 1)
		If ( bAutoDocUnlocked == 1 )

			; ----------------------------------------------------	BARBER UPGRADE -----------------------------------------------	\/
			If ( Player.GetHasNote NVDLC03HQUpgradeAutoDocBarberNOTE == 1 )
				Set bBarberActive To 1;
				Player.RemoveNote NVDLC03HQUpgradeAutoDocBarberNOTE;
				Set NVDLC03HQBuddy.iAutoDoc To NVDLC03HQBuddy.iAutoDoc + 1;				;SPW
				Set NVDLC03HQBuddy.bDocBarber To 1;													;SPW
				If ( NVDLC03DialogueHQBuddies.bBuddiesTalk == 0 )
					ShowMessage NVDLC03HQBuddiesNoTalkMSG;
				EndIf
				ShowMessage NVDLC03HQAutoDocBarberUnlockMSG;
			EndIf

			; ----------------------------------------------------	COSMETIC UPGRADE -------------------------------------------	\/
			If ( Player.GetHasNote NVDLC03HQUpgradeAutoDocCosmeticNOTE == 1 )
				Set bCosmeticSurgeryActive To 1;
				Set NVDLC03HQBuddy.iAutoDoc To NVDLC03HQBuddy.iAutoDoc + 1;				;SPW
				Set NVDLC03HQBuddy.bDocCosmetic To 1;												;SPW
				Player.RemoveNote NVDLC03HQUpgradeAutoDocCosmeticNOTE;
				If ( NVDLC03DialogueHQBuddies.bBuddiesTalk == 0 )
					ShowMessage NVDLC03HQBuddiesNoTalkMSG;
				EndIf
				ShowMessage NVDLC03HQAutoDocSurgeryUnlockMSG;
			EndIf

			; ----------------------------------------------------	IMPLANT C-13 UPGRADE -------------------------------------	\/
			If ( Player.GetHasNote NVDLC03HQUpgradeAutoDocC13NOTE == 1 )
				Set bImplantC13Active To 1;
				Player.RemoveNote NVDLC03HQUpgradeAutoDocC13NOTE;
				Set NVDLC03HQBuddy.iAutoDoc To NVDLC03HQBuddy.iAutoDoc + 1;				;SPW
				Set NVDLC03HQBuddy.bDocC13 To 1;														;SPW
				If ( NVDLC03DialogueHQBuddies.bBuddiesTalk == 0 )
					ShowMessage NVDLC03HQBuddiesNoTalkMSG;
				EndIf
				ShowMessage NVDLC03HQAutoDocImplantC13UnlockMSG;
			EndIf

			; ----------------------------------------------------	IMPLANT M-5 UPGRADE --------------------------------------	\/
			If ( Player.GetHasNote NVDLC03HQUpgradeAutoDocM5NOTE == 1 )
				Set bImplantM5Active To 1;
				Player.RemoveNote NVDLC03HQUpgradeAutoDocM5NOTE;
				Set NVDLC03HQBuddy.iAutoDoc To NVDLC03HQBuddy.iAutoDoc + 1;				;SPW
				Set NVDLC03HQBuddy.bDocM5 To 1;														;SPW
				If ( NVDLC03DialogueHQBuddies.bBuddiesTalk == 0 )
					ShowMessage NVDLC03HQBuddiesNoTalkMSG;
				EndIf
				ShowMessage NVDLC03HQAutoDocImplantM5UnlockMSG;
			EndIf

			; ----------------------------------------------------	IMPLANT Y-3 UPGRADE ----------------------------------------	\/
			If ( Player.GetHasNote NVDLC03HQUpgradeAutoDocY3NOTE == 1 )
				Set bImplantY3Active To 1;
				Player.RemoveNote NVDLC03HQUpgradeAutoDocY3NOTE;
				Set NVDLC03HQBuddy.iAutoDoc To NVDLC03HQBuddy.iAutoDoc + 1;				;SPW
				Set NVDLC03HQBuddy.bDocY3 To 1;															;SPW
				If ( NVDLC03DialogueHQBuddies.bBuddiesTalk == 0 )
					ShowMessage NVDLC03HQBuddiesNoTalkMSG;
				EndIf
				ShowMessage NVDLC03HQAutoDocImplantY3UnlockMSG;
			EndIf

			; ----------------------------------------------------	IMPLANT Y-7 UPGRADE ----------------------------------------	\/
			If ( Player.GetHasNote NVDLC03HQUpgradeAutoDocY7NOTE == 1 )
				Set bImplantY7Active To 1;
				Player.RemoveNote NVDLC03HQUpgradeAutoDocY7NOTE;
				Set NVDLC03HQBuddy.iAutoDoc To NVDLC03HQBuddy.iAutoDoc + 1;				;SPW
				Set NVDLC03HQBuddy.bDocY7 To 1;															;SPW
				If ( NVDLC03DialogueHQBuddies.bBuddiesTalk == 0 )
					ShowMessage NVDLC03HQBuddiesNoTalkMSG;
				EndIf
				ShowMessage NVDLC03HQAutoDocImplantY7UnlockMSG;
			EndIf
		EndIf
	EndIf


	If (IsActionRef Player == 1)
		; ----------------------------------------------------	AUTO-DOC UNLOCKED -----------------------------------------	\/
		If ( bAutoDocUnlocked == 1 )
			If ( NVDLC03DialogueHQBuddies.bBuddiesTalk == 0 )
				Set bMenuAfterMessage To 1;
			Else
				Set bTalkAfterMessage To 1;
			EndIf

		; ----------------------------------------------------	AUTO-DOC LOCKED ----------------------------------------------	\/
		Else
			If ( Player.GetHasNote NVDLC03HQBuddyAutoDocNOTE == 0 )
				If ( NVDLC03DialogueHQBuddies.bBuddiesTalk == 0 )
					ShowMessage NVDLC03HQBuddiesNoTalkMSG;
				EndIf
				ShowMessage NVDLC03HQBuddyLockedMSG;

			; ----------------------------------------------------	AUTO-DOC UNLOCK SEQUENCE---------------------------	\/
			Else
				Player.RemoveNote NVDLC03HQBuddyAutoDocNOTE;
				If ( NVDLC03DialogueHQBuddies.bBuddiesTalk == 0 )
					ShowMessage NVDLC03HQBuddiesNoTalkMSG;
				EndIf
				ShowMessage 	NVDLC03HQBuddyUnlockMSG;
				Set bAutoDocUnlocked To 1;
				Set NVDLC03HQBuddy.iAutoDoc To 1;														;SPW
				;SetObjectiveDisplayed NVDLC03HQBuddyUpgrade 20 1;								;SPW - shows upgrade quest for the Auto-Doc
				Set NVDLC03DialogueHQBuddies.bAutoDocUnlocked To 1;
				IncrementScriptedChallenge NVDLC03AchievementMakingFriends;	
				Set bTalkAfterMessage To 1;
			EndIf
		EndIf
	EndIf

END; OnActivate


BEGIN GameMode

	If ( bCallAutoDocUI == 1 )
		Set bAwaitingInput To 1;
		ShowMessage NVDLC03HQAutoDocMsg;
		Set bCallAutoDocUI To 0;

	ElseIf ( bPsychEvalUI == 1 )
		Set bPsychEvalAwaitingInput To 1;
		ShowMessage NVDLC03TraitRespecMSG;
		Set bPsychEvalUI To 0;

	ElseIf ( bCallNoBarberMSG == 1 )
		ShowMessage NVDLC03HQAutoDocNoBarberMsg;
		Set bCallNoBarberMSG To 0;
		Set bCallAutoDocUI To 1;
		Return;

	ElseIf ( bCallNoSurgeryMSG == 1 )
		ShowMessage NVDLC03HQAutoDocNoSurgeryMsg;
		Set bCallNoSurgeryMSG To 0;
		Set bCallAutoDocUI To 1;
		Return;

	ElseIf ( bRestoreBodyParts == 1 )
		If ( GetStageDone NVDLC03MQ05 50 == 0 )															; JSH 02/18/11
			ShowMessage NVDLC03HQAutoDocPlayerPartsFailMSG;
			Set bRestoreBodyParts To 0;
			Set bCallAutoDocUI To 1;
			Return;
		Else
			Set bAwaitingInput To 1;
			ShowMessage NVDLC03HQAutoDocPlayerPartsMSG;
		EndIf

	ElseIf ( bImplants == 1 )
		Set bAwaitingInput To 1;
		ShowMessage NVDLC03HQAutodocImplantMSG;
	EndIf

	; ----------------------------------------------------	IMPLANT C-13 INSTALLED MSG ---------------------------	\/
	If ( nImplantC13Installed == 2 )
		Set nImplantC13Installed To 0;
		ShowMessage NVDLC03HQAutoDocInstalledC13MSG;	
	EndIf

	; ----------------------------------------------------	IMPLANT M-5 INSTALLED MSG ----------------------------	\/
	If ( nImplantM5Installed == 2 )
		Set nImplantM5Installed To 0;
		ShowMessage NVDLC03HQAutoDocInstalledM5MSG;	
	EndIf

	; ----------------------------------------------------	IMPLANT Y-3 INSTALLED MSG -----------------------------	\/
	If ( nImplantY3Installed == 2 )
		Set nImplantY3Installed To 0;
		ShowMessage NVDLC03HQAutoDocInstalledY3MSG;	
	EndIf

	; ----------------------------------------------------	IMPLANT Y-7 INSTALLED MSG -----------------------------	\/
	If ( nImplantY7Installed == 2 )
		Set nImplantY7Installed To 0;
		ShowMessage NVDLC03HQAutoDocInstalledY7MSG;
	EndIf

	; ----------------------------------------------------	BRAINLESS PERK MSG ----------------------------------------	\/
	If ( nBrainlessSwapMsg > 0 )

		; ----------------------	Swap In Message -------------------------	\/
		If ( nBrainlessSwapMsg == 1 )
			Set nBrainlessSwapMsg To 0;
			ShowMessage NVDLC03BrainlessPerkSwapInMSG;

		; ----------------------	Swap Out Message ----------------------	\/
		ElseIf ( nBrainlessSwapMsg == 2 )
			Set nBrainlessSwapMsg To 0;
			ShowMessage NVDLC03BrainlessPerkSwapOutMSG;
		EndIf

	; ----------------------------------------------------	HEARTLESS PERK MSG ---------------------------------------	\/
	ElseIf ( nHeartlessSwapMsg > 0 )

		; ----------------------	Swap In Message -------------------------	\/
		If ( nHeartlessSwapMsg == 1 )
			Set nHeartlessSwapMsg To 0;
			ShowMessage NVDLC03HeartlessPerkSwapInMSG;

		; ----------------------	Swap Out Message ----------------------	\/
		ElseIf ( nHeartlessSwapMsg == 2 )
			Set nHeartlessSwapMsg To 0;
			ShowMessage NVDLC03HeartlessPerkSwapOutMSG;
		EndIf

	; ----------------------------------------------------	SPINELESS PERK MSG ----------------------------------------	\/
	ElseIf ( nSpinelessSwapMsg > 0 )

		; ----------------------	Swap In Message -------------------------	\/
		If ( nSpinelessSwapMsg == 1 )
			Set nSpinelessSwapMsg To 0;
			ShowMessage NVDLC03SpinelessPerkSwapInMSG;

		; ----------------------	Swap Out Message ----------------------	\/
		ElseIf ( nSpinelessSwapMsg == 2 )
			Set nSpinelessSwapMsg To 0;
			ShowMessage NVDLC03SpinelessPerkSwapOutMSG;
		EndIf

	; ----------------------------------------------------	BIG BRAINED PERK MSG -------------------------------------	\/
	ElseIf ( nBigBrainedSwapMsg > 0 )

		; ----------------------	Swap In Message -------------------------	\/
		If ( nBigBrainedSwapMsg == 1 )
			Set nBigBrainedSwapMsg To 0;
			ShowMessage NVDLC03BigBrainedPerkSwapInMSG;

		; ----------------------	Swap Out Message ----------------------	\/
		ElseIf ( nBigBrainedSwapMsg == 2 )
			Set nBigBrainedSwapMsg To 0;
			ShowMessage NVDLC03BigBrainedPerkSwapOutMSG;

		; ----------------------	Gained Message --------------------------	\/
		ElseIf ( nBigBrainedSwapMsg == 3 )
			Set nBigBrainedSwapMsg To 0;
			ShowMessage NVDLC03BigBrainedPerkGainMSG;
		EndIf

	; ----------------------------------------------------	CARDIAC ARREST PERK MSG ------------------------------	\/
	ElseIf ( nCardiacArrestSwapMsg > 0 )

		; ----------------------	Swap In Message -------------------------	\/
		If ( nCardiacArrestSwapMsg == 1 )
			Set nCardiacArrestSwapMsg To 0;
			ShowMessage NVDLC03CardiacArrestPerkSwapInMSG;

		; ----------------------	Swap Out Message ----------------------	\/
		ElseIf ( nCardiacArrestSwapMsg == 2 )
			Set nCardiacArrestSwapMsg To 0;
			ShowMessage NVDLC03CardiacArrestPerkSwapOutMSG;

		; ----------------------	Gained Message --------------------------	\/
		ElseIf ( nCardiacArrestSwapMsg == 3 )
			Set nCardiacArrestSwapMsg To 0;
			ShowMessage NVDLC03CardiacArrestPerkGainMSG;
		EndIf

	; ----------------------------------------------------	REINFORCED SPINE PERK MSG --------------------------	\/
	ElseIf ( nReinforcedSpineSwapMsg > 0 )

		; ----------------------	Swap In Message -------------------------	\/
		If ( nReinforcedSpineSwapMsg == 1 )
			Set nReinforcedSpineSwapMsg To 0;
			ShowMessage NVDLC03ReinforcedSpinePerkSwapInMSG;

		; ----------------------	Swap Out Message ----------------------	\/
		ElseIf ( nReinforcedSpineSwapMsg == 2 )
			Set nReinforcedSpineSwapMsg To 0;
			ShowMessage NVDLC03ReinforcedSpinePerkSwapOutMSG;	

		; ----------------------	Gained Message --------------------------	\/
		ElseIf ( nReinforcedSpineSwapMsg == 3 )
			Set nReinforcedSpineSwapMsg To 0;
			ShowMessage NVDLC03ReinforcedSpinePerkGainMSG;
		EndIf
	EndIf

	If ( bTalkAfterMessage == 1 )
		Set bTalkAfterMessage To 0;
		;SetTalkingActivatorActor NVDLC03SinkAutoDocTalkerREF;
		Activate;
	EndIf

	If ( bMenuAfterMessage == 1 )
		Set bMenuAfterMessage To 0;
		Set bAwaitingInput To 1;
		ShowMessage NVDLC03HQBuddiesNoTalkMSG;
		ShowMessage NVDLC03HQAutoDocMsg;
	EndIf

END; gamemode


BEGIN MenuMode 1001

	If (bPsychEvalAwaitingInput == 1)
		Set nButton To GetButtonPressed;
		If (nButton > -1)
			Set bPsychEvalAwaitingInput To 0;
			If (nButton == 0)
				Set bPsychEvalUI To 0;
			ElseIf (nButton == 1)
				Set NVDLC03DialogueHQBuddies.bPsychEval To 1;
				Player.RemovePerk WildWasteland;
				Player.RemovePerk TriggerDiscipline;
				Player.RemovePerk SmallFrame;
				Player.RemovePerk NVDLC03TraitSkilled;
				Player.RemovePerk NVDLC03TraitLogansLoophole;
				Player.RemovePerk NVDLC03TraitHotBlooded;
				Player.RemovePerk NVDLC03TraitHoarder;
				Player.RemovePerk NVDLC03TraitEarlyBird;
				Player.RemovePerk NVDLC03TraitClaustrophobia;
				Player.RemovePerk LooseCannon;
				Player.RemovePerk Kamikaze;
				Player.RemovePerk HeavyHanded;
				Player.RemovePerk GoodNatured;
				Player.RemovePerk FourEyes;
				Player.RemovePerk FastShot;
				Player.RemovePerk BuiltToDestroy;
				ShowTraitMenu;
			EndIf
		EndIf
	EndIf

	If ( bAwaitingInput == 1 )
		Set nButton To GetButtonPressed;

		If ( nButton > -1 )
			Set bAwaitingInput To 0;

			; -------------------------------------------------------------------	BUTTON 0 ----------------------------------------------	\/	; "Do nothing."
			If ( nButton == 0 )
				Set nButton To -1;
				If ( bImplants == 1 )
					Set bImplants To 0;
					Set bCallAutoDocUI To 1;
					Return;
				ElseIf ( bRestoreBodyParts == 1)														; JSH 02/18/11
					Set bRestoreBodyParts To 0;
					Return;
				Else
					Return;
				EndIf

			; -------------------------------------------------------------------	BUTTON 1 ----------------------------------------------	\/ 	; "Basic Physical Exam" / "Insert your Brain back into your skull." / "Install Implant C-13"
			ElseIf ( nButton == 1 )
				Set nButton To -1;	
				If ( bRestoreBodyParts == 0 )
					If ( bImplants == 1 )
						If (Player.GetItemCount Caps001 >= 8000)
							Player.RemoveItem Caps001 8000;
							Set bImplants To 0;
							Player.AddPerk NVDLC03ImplantC13Perk;	
							Set bImplantC13Active To 0;													; Setting this to 0 removes this option from the message box UI (so you can't install it over and over).
							Set nImplantC13Installed To 1;													; Setting this to 1 causes "NVDLC03AutoDocTimerSCRIPT" to set it to 2 (when it completes) which inturn calls a "installed" message (gamemode above).
							Set NVDLC03AutoDocTimer.bNoHealthyMessage To 1;					; These three lines are needed to do the screen fade and sfx (and not get the "healthy" message).
							DisablePlayerControls;															; <<<
							StartQuest NVDLC03AutoDocTimer;											; <<<
							Return;		
						Else
							ShowMessage NVDLC03HQAutodocImplantNoCapsMSG;
						EndIf
					Else
						DisablePlayerControls;															; This is the one location where "bNoHealthyMessage to 1" is not needed.
						StartQuest NVDLC03AutoDocTimer;											; <<<

						; Heal hit points.
						Player.ResetHealth;

						; Heal crippled limbs.
						Player.RestoreActorValue PerceptionCondition 100;
						Player.RestoreActorValue EnduranceCondition 100;
						Player.RestoreActorValue LeftAttackCondition 100;
						Player.RestoreActorValue RightAttackCondition 100;
						Player.RestoreActorValue LeftMobilityCondition 100;
						Player.RestoreActorValue RightMobilityCondition 100;

						; Remove addictions.
						Player.RemoveSpell WithdrawalAlcohol;
						Player.RemoveSpell WithdrawalAntNectar;
						Player.RemoveSpell WithdrawalBuffout;
						Player.RemoveSpell WithdrawalHydra;
						Player.RemoveSpell WithdrawalJet;
						Player.RemoveSpell WithdrawalMentats;
						Player.RemoveSpell WithdrawalMorphine;
						Player.RemoveSpell WithdrawalPsycho;
						Player.RemoveSpell WithdrawalQuantumNukacola;
						Player.RemoveSpell WithdrawalRebound;
						Player.RemoveSpell WithdrawalSteady;
						Player.RemoveSpell WithdrawalTobacco;
						Player.RemoveSpell WithdrawalTurbo;
						Set Generic.Addicted To 0;

						; Remove radiation sickness.
						Player.RestoreActorValue RadiationRads 1000;
					EndIf
				Else
					Set NVDLC03DialoguePlayerBrain.bAllowDialogue To 0;					; JSH 03/11/11 - Disallow interaction with Brain Tank if player brain restored.
					NVDLC03BrainTankSinkREF.SetDestroyed 1;
					Set bRestoreBodyParts To 0;
					Set NVDLC03AutoDocTimer.bNoHealthyMessage To 1;						; These three lines are needed to do the screen fade and sfx (and not get the "healthy" message).
					DisablePlayerControls;																; <<<
					StartQuest NVDLC03AutoDocTimer;												; <<<
					Player.RemovePerk NVDLC03BrainlessPerk;
					Player.AddPerk NVDLC03BigBrainedPerk;
					If ( bBrainGainOnce == 0 )
						Set bBrainGainOnce To 1;
						Set nBigBrainedSwapMsg To 3;
					Else
						Set nBigBrainedSwapMsg To 1;
					EndIf
					Set nBrainlessSwapMsg To 2;
					NVDLC03BrainJarREF.PlayGroup Forward 1;									; JSH 02/18/11
					Set NVDLC03MQ05.bRestoredBrain To 1;
				EndIf

			; -------------------------------------------------------------------	BUTTON 2 ----------------------------------------------	\/	; "Cosmetic Surgery" / "Place your Heart back in your chest" / "Install Implant M-5"
			ElseIf ( nButton == 2 )
				Set nButton To -1;

				If ( bRestoreBodyParts == 0 )
					If ( bImplants == 1 )
						If (Player.GetItemCount Caps001 >= 10000)
							Player.RemoveItem Caps001 10000;
							Set bImplants To 0;
							Player.AddPerk NVDLC03ImplantM5Perk;	
							Set bImplantM5Active To 0;														; Setting this to 0 removes this option from the message box UI (so you can't install it over and over).			
							Set nImplantM5Installed To 1;													; Setting this to 1 causes "NVDLC03AutoDocTimerSCRIPT" to set it to 2 (when it completes) which inturn calls a "installed" message (gamemode above).
							Set NVDLC03AutoDocTimer.bNoHealthyMessage To 1;					; These three lines are needed to do the screen fade and sfx (and not get the "healthy" message).
							DisablePlayerControls;															; <<<
							StartQuest NVDLC03AutoDocTimer;											; <<<
							Return;
						Else
							ShowMessage NVDLC03HQAutodocImplantNoCapsMSG;
						EndIf
					Else
						If ( bCosmeticSurgeryActive == 1 )
							ShowPlasticSurgeonMenu;
						Else
							Set bCallNoSurgeryMSG To 1;
						EndIf
					EndIf
				Else
					Set bRestoreBodyParts To 0;
					Player.RemovePerk NVDLC03HeartlessPerk;
					Player.AddPerk NVDLC03CardiacArrestPerk;
					If ( bHeartGainOnce == 0 )
						Set bHeartGainOnce To 1;
						Set nCardiacArrestSwapMsg To 3;
					Else
						Set nCardiacArrestSwapMsg To 1;
					EndIf
					Set nHeartlessSwapMsg To 2;
					NVDLC03HeartJarREF.PlayGroup Forward 1;									; JSH 02/18/11
					Set NVDLC03MQ05.bRestoredHeart To 1;
					If ( GetObjectiveDisplayed NVDLC03MQ05 52 == 1 ) && ( GetObjectiveCompleted NVDLC03MQ05 52 == 0 )
						SetObjectiveCompleted NVDLC03MQ05 52 1;
					EndIf
					Set NVDLC03AutoDocTimer.bNoHealthyMessage To 1;						; These three lines are needed to do the screen fade and sfx (and not get the "healthy" message).
					DisablePlayerControls;																; <<<
					StartQuest NVDLC03AutoDocTimer;												; <<<
				EndIf

			; -------------------------------------------------------------------	BUTTON 3 ----------------------------------------------	\/	; "Barber" / "Re-attach your Spine." / "Install Implant Y-3"
			ElseIf ( nButton == 3 )
				Set nButton To -1;

				If ( bRestoreBodyParts == 0 )
					If ( bImplants == 1 )
						If (Player.GetItemCount Caps001 >= 12000)
							Player.RemoveItem Caps001 12000;
							Set bImplants To 0;
							Player.AddPerk NVDLC03ImplantY3Perk;		
							Set bImplantY3Active To 0;														; Setting this to 0 removes this option from the message box UI (so you can't install it over and over).
							Set nImplantY3Installed To 1;													; Setting this to 1 causes "NVDLC03AutoDocTimerSCRIPT" to set it to 2 (when it completes) which inturn calls a "installed" message (gamemode above).
							Set NVDLC03AutoDocTimer.bNoHealthyMessage To 1;					; These three lines are needed to do the screen fade and sfx (and not get the "healthy" message).
							DisablePlayerControls;															; <<<
							StartQuest NVDLC03AutoDocTimer;											; <<<
							Return;
						Else
							ShowMessage NVDLC03HQAutodocImplantNoCapsMSG;
						EndIf
					Else
						If ( bBarberActive == 1 )
							ShowBarberMenu;
						Else
							Set bCallNoBarberMSG To 1;
						EndIf
					EndIf
				Else
					Set bRestoreBodyParts To 0;
					Player.RemovePerk NVDLC03SpinelessPerk;
					Player.AddPerk NVDLC03ReinforcedSpinePerk;
					If ( bSpineGainOnce == 0 )
						Set bSpineGainOnce To 1;
						Set nReinforcedSpineSwapMsg To 3;
					Else
						Set nReinforcedSpineSwapMsg To 1;
					EndIf
					Set nSpinelessSwapMsg To 2;
					NVDLC03SpineJarREF.PlayGroup Forward 1;									; JSH 02/18/11
					Set NVDLC03MQ05.bRestoredSpine To 1;
					If ( GetObjectiveDisplayed NVDLC03MQ05 53 == 1 ) && ( GetObjectiveCompleted NVDLC03MQ05 53 == 0 )
						SetObjectiveCompleted NVDLC03MQ05 53 1;
					EndIf
					Set NVDLC03AutoDocTimer.bNoHealthyMessage To 1;						; These three lines are needed to do the screen fade and sfx (and not get the "healthy" message).
					DisablePlayerControls;																; <<<
					StartQuest NVDLC03AutoDocTimer;												; <<<
				EndIf

			; -------------------------------------------------------------------	BUTTON 4 ----------------------------------------------	\/	; "Swap Body Parts" / "Replace your Brain with the synthetic version./"Install Implant Y-7"
			ElseIf ( nButton == 4 )
				Set nButton To -1;
					If ( bRestoreBodyParts == 0 )
						If ( bImplants == 1 )
							If (Player.GetItemCount Caps001 >=20000)
								Player.RemoveItem Caps001 20000;
								Set bImplants To 0;
								Player.AddPerk NVDLC03ImplantY7Perk;	
								Set bImplantY7Active To 0;													; Setting this to 0 removes this option from the message box UI (so you can't install it over and over).			
								Set nImplantY7Installed To 1;												; Setting this to 1 causes "NVDLC03AutoDocTimerSCRIPT" to set it to 2 (when it completes) which inturn calls a "installed" message (gamemode above).
								Set NVDLC03AutoDocTimer.bNoHealthyMessage To 1;				; These three lines are needed to do the screen fade and sfx (and not get the "healthy" message).
								DisablePlayerControls;														; <<<
								StartQuest NVDLC03AutoDocTimer;										; <<<
								Return;
							Else
								ShowMessage NVDLC03HQAutodocImplantNoCapsMSG;
							EndIf
						Else
							Set bRestoreBodyParts To 1;
							Return;	
						EndIf
					Else
						Set NVDLC03DialoguePlayerBrain.bAllowDialogue To 1;				; JSH 03/11/11 - Allow interaction with Brain Tank if player brain removed.
						NVDLC03BrainTankSinkREF.SetDestroyed 0;
						Set bRestoreBodyParts To 0;
						Player.RemovePerk NVDLC03BigBrainedPerk;
						Player.AddPerk NVDLC03BrainlessPerk;
						Set nBrainlessSwapMsg To 1;
						Set nBigBrainedSwapMsg To 2;
						NVDLC03BrainJarREF.PlayGroup Backward 1;
						Set NVDLC03MQ05.bRestoredBrain To 0;
						Set NVDLC03AutoDocTimer.bNoHealthyMessage To 1;					; These three lines are needed to do the screen fade and sfx (and not get the "healthy" message).
						DisablePlayerControls;															; <<<
						StartQuest NVDLC03AutoDocTimer;											; <<<
					EndIf

			; -------------------------------------------------------------------	BUTTON 5 ----------------------------------------------	\/	; "Install Implants" / "Swap your Heart for the synthetic version."
			ElseIf ( nButton == 5 )
				If ( bRestoreBodyParts == 0 )
					If ( bImplants == 0 )
						Set nButton To -1;
						Set bImplants To 1;
						Return;
					EndIf
				Else
					Set bRestoreBodyParts To 0;
					Player.RemovePerk NVDLC03CardiacArrestPerk;
					Player.AddPerk NVDLC03HeartlessPerk;
					Set nHeartlessSwapMsg To 1;
					Set nCardiacArrestSwapMsg To 2;
					NVDLC03HeartJarREF.PlayGroup Backward 1;										
					Set NVDLC03MQ05.bRestoredHeart To 0;
					Set NVDLC03AutoDocTimer.bNoHealthyMessage To 1;						; These three lines are needed to do the screen fade and sfx (and not get the "healthy" message).
					DisablePlayerControls;																; <<<
					StartQuest NVDLC03AutoDocTimer;												; <<<
				EndIf

			; -------------------------------------------------------------------	BUTTON 6 ----------------------------------------------	\/	; "Switch your Spine for the synthetic version."
			ElseIf ( nButton == 6 )
				Set nButton To -1;
					If ( bRestoreBodyParts == 1 )
						Set bRestoreBodyParts To 0;
						Player.RemovePerk NVDLC03ReinforcedSpinePerk;
						Player.AddPerk NVDLC03SpinelessPerk;
						Set nSpinelessSwapMsg To 1;
						Set nReinforcedSpineSwapMsg To 2;
						NVDLC03SpineJarREF.PlayGroup Backward 1;									
						Set NVDLC03MQ05.bRestoredSpine To 0;
						Set NVDLC03AutoDocTimer.bNoHealthyMessage To 1;					; These three lines are needed to do the screen fade and sfx (and not get the "healthy" message).
						DisablePlayerControls;															; <<<
						StartQuest NVDLC03AutoDocTimer;											; <<<
					EndIf
			EndIf
		EndIf
	Else
		Return;
	EndIf

END; menumode 1001

ScriptName NVDLC03SinkSinkSCRIPT


; Talking Activator script for Sink
; TLS 01/19/2011
; SPW 02/24/11 	- Adding vars for HQ Buddy quest
; OMP 03/11/2011 	- Prevent the player from using the Sink until it's unlocked.
; ETB 03/24/11 		- Added gamemode start conversation routine (to get conversation to start directly after a message box pop-up).


Short bAwaitingInput;							; Message box variable (menumode).
Short bCallSinkBuddyUI;						; Message box variable (gamemode).
Short nButton;									; Which message box option the player has pressed.
Short bTalkAfterMessage

; Count variables for drink types
Short nMilkCount;
Short nNukaColaCount;
Short nWhiskeyCount;
Short nSunsetCount;
Short nSunsetNoFloatCount;
Short nSodaCount;
Short nScotchCount;
Short nEmptyBottleCount;


BEGIN OnLoad

	SetTalkingActivatorActor NVDLC03SinkSinkTalkerREF;

END


BEGIN OnActivate

	If ( IsActionRef Player == 1 )
		If ( NVDLC03DialogueHQBuddies.bSinkBuddyUnlocked == 1 )
			If ( Player.GetHasNote NVDLC03HQUpgradeSinkWaterNOTE == 1 )
				Player.RemoveNote NVDLC03HQUpgradeSinkWaterNOTE;
				Set NVDLC03HQBuddy.iSink To 2;														; SPW
				Set NVDLC03DialogueHQBuddies.bSinkBuddyUpgraded To 1;
				ShowMessage NVDLC03HQSinkBuddyUpgradeMSG;
			EndIf
			If ( NVDLC03DialogueHQBuddies.bBuddiesTalk == 0 )
				ShowMessage NVDLC03HQBuddiesNoTalkMSG;
				Set bAwaitingInput To 1;
				ShowMessage NVDLC03HQSinkBuddyMSG;
			Else
				Set bTalkAfterMessage to 1;
			EndIf
		Else
			If ( Player.GetHasNote NVDLC03HQBuddySinkNOTE== 0 )
				If ( NVDLC03DialogueHQBuddies.bBuddiesTalk == 0 )
					ShowMessage NVDLC03HQBuddiesNoTalkMSG;
				EndIf
				ShowMessage NVDLC03HQBuddyLockedMSG;
			ElseIf ( Player.GetHasNote NVDLC03HQBuddySinkNOTE== 1 )
				Player.RemoveNote NVDLC03HQBuddySinkNOTE;
				Set NVDLC03HQBuddy.iSink To 1;														; SPW
				If ( NVDLC03DialogueHQBuddies.bBuddiesTalk == 0 )
					ShowMessage NVDLC03HQBuddiesNoTalkMSG;
				EndIf
				ShowMessage 	NVDLC03HQBuddyUnlockMSG;
				Set NVDLC03DialogueHQBuddies.bSinkBuddyUnlocked To 1;
				IncrementScriptedChallenge NVDLC03AchievementMakingFriends;	
				Set bTalkAfterMessage to 1;
			EndIf
		EndIf
	EndIf

END; onactivate


BEGIN GameMode

	If ( bCallSinkBuddyUI == 1 )
		Set bAwaitingInput To 1;
		ShowMessage NVDLC03HQSinkBuddyMSG;
		Set bCallSinkBuddyUI To 0;
	EndIf

	If ( bTalkAfterMessage == 1 )
		Set bTalkAfterMessage to 0;
		;SetTalkingActivatorActor NVDLC03SinkSinkTalkerREF;
		Activate;
	Endif

END; gamemode


BEGIN MenuMode 1001

	If ( bAwaitingInput == 1 )
		Set nButton To GetButtonPressed;

		If ( nButton > -1 )
			Set bAwaitingInput To 0;

			; -------------------------------------------------------------------	BUTTON 0 ----------------------------------------------	\/	; "Do Nothing"
			If ( nButton == 0 )
				Set nButton To -1;
				Return;
			; -------------------------------------------------------------------	BUTTON 1 ----------------------------------------------	\/ 	; "Fill empty bottles"
			ElseIf ( nButton == 1 )
				Set nButton To -1;	
				Set nMilkCount To Player.GetItemCount Milkbottle01;
				Set nNukaColaCount To Player.GetItemCount NukaColaBottle;
				Set nWhiskeyCount To Player.GetItemCount WhiskeyBottle01Empty01;
				Set nSunsetCount To Player.GetItemCount SSBottleEmpty;
				Set nSunsetNoFloatCount To Player.GetItemCount SSBottleEmptyNoFloat;
				Set nSodaCount To Player.GetItemCount SodaBottleEmpty01;
				Set nScotchCount To Player.GetItemCount ScotchBottle01Empty01;
				Set nEmptyBottleCount To ( nMilkCount + nNukaColaCount + nWhiskeyCount + nSunsetCount + nSunsetNoFloatCount + nSodaCount + nScotchCount  );
				Player.RemoveItem Milkbottle01 nMilkCount;
				Player.RemoveItem NukaColaBottle nNukaColaCount;
				Player.RemoveItem WhiskeyBottle01Empty01 nWhiskeyCount;
				Player.RemoveItem SSBottleEmpty nSunsetCount;
				Player.RemoveItem SSBottleEmptyNoFloat nSunsetNoFloatCount;
				Player.RemoveItem SodaBottleEmpty01 nSodaCount;
				Player.RemoveItem ScotchBottle01Empty01 nScotchCount;
				Player.AddItem WaterPurified nEmptyBottleCount;
			; -------------------------------------------------------------------	BUTTON 2 ----------------------------------------------	\/	; "Drink"
			ElseIf ( nButton == 2 )
				Set nButton To -1;
				NVDLC03DrinkSinkREF.Activate Player 1;
			EndIf
		EndIf
	Else
		Return;
	EndIf

END; menumode 1001

SCN NVDLC03SinkBioResearchStationSCRIPT

; Talking Activator script for Bio-Research Station
; tls 01/19/2011
; SPW 02/24/11 	- Adding vars for HQ Buddy quest
; SPW 03/01/11 	- Removing line that kicked off the samples quest
; ETB 03/24/11 		- Added gamemode start conversation routine (to get conversation to start directly after a message box pop-up).


Short bBiologyUnlocked								; 1 = Player has turned in the unlock holodisk: "NVDLC03HQBuddyBiologyNOTE".

Short bAwaitingInput									; Message box variable (menumode).
Short bCallBiologyUI 									; Message box variable (gamemode).
Short nButton											; Which message box option the player has pressed.
Short bTalkAfterMessage

Short nBananaYuccaCount 
Short nBarrelCactusCount 
Short nBrocFlowerCount 
Short nHoneyMesquiteCount 
Short nJalapenoCount 
Short nMaizeCount 
Short nNevadaAgaveCount 
Short nPintoBeanCount 
Short nPricklyPearCount 
Short nWhiteHorsenettleCount 
Short nXanderRootCount 
Short nPlantCount 

Short nSetAnyTimer 
; ------------------------------------------	
Short bSetBananaYuccaTimer 
Float fBananaYuccaCurrentDay 
Float fBananaYuccaLastDay 
; ------------------------------------------	
Short bSetBarrelCactusTimer 
Float fBarrelCactusCurrentDay 
Float fBarrelCactusLastDay 
; ------------------------------------------	
Short bSetBrocFlowerTimer 
Float fBrocFlowerCurrentDay 
Float fBrocFlowerLastDay 
; ------------------------------------------	
Short bSetHoneyMesquiteTimer 
Float fHoneyMesquiteCurrentDay 
Float fHoneyMesquiteLastDay 
; ------------------------------------------	
Short bSetJalapenoTimer 
Float fJalapenoCurrentDay 
Float fJalapenoLastDay 
; ------------------------------------------	
Short bSetMaizeTimer 
Float fMaizeCurrentDay 
Float fMaizeLastDay 
; ------------------------------------------	
Short bSetNevadaAgaveTimer 
Float fNevadaAgaveCurrentDay 
Float fNevadaAgaveLastDay 
; ------------------------------------------	
Short bSetPintoBeanPodTimer 
Float fPintoBeanPodCurrentDay 
Float fPintoBeanPodLastDay 
; ------------------------------------------	
Short bSetPricklyPearTimer 
Float fPricklyPearCurrentDay 
Float fPricklyPearLastDay 
; ------------------------------------------	
Short bSetXanderRootTimer 
Float fXanderRootCurrentDay 
Float fXanderRootLastDay 
; ------------------------------------------	
Short bSetWhiteHorseNettleTimer 
Float fWhiteHorseNettleCurrentDay 
Float fWhiteHorseNettleLastDay 


BEGIN OnLoad

	SetTalkingActivatorActor NVDLC03SinkBioResearchLabTalkerREF;

END


BEGIN OnActivate

	If (IsActionRef Player == 1)
		If ( bBiologyUnlocked == 1 )
			If ( NVDLC03DialogueHQBuddies.bBuddiesTalk == 0 )
				ShowMessage NVDLC03HQBuddiesNoTalkMSG;
				If ( Player.GetItemCount NVDLC03AllDriedPlants >= 1 || Player.GetItemCount NVDLC03AllIngestiblePlants >= 1 ) 
					Set bAwaitingInput to 1;
				  	ShowMessage NVDLC03HQBioResearchStationMSG;
				Else
					ShowMessage NVDLC03HQBioResearchStationNoItemsMSG;					
				Endif
			Elseif ( NVDLC03DialogueHQBuddies.bBuddiesTalk == 1 )
				Set bTalkAfterMessage to 1;
			Endif
		Else
			If ( Player.GetHasNote NVDLC03HQBuddyBiologyNOTE == 0 )
				ShowMessage NVDLC03HQBuddyLockedMSG;
			Elseif ( Player.GetHasNote NVDLC03HQBuddyBiologyNOTE == 1 )
				Player.RemoveNote NVDLC03HQBuddyBiologyNOTE;
				Set bBiologyUnlocked to 1;
				Set NVDLC03HQBuddy.iBio to 1;														;SPW
				;SetObjectiveDisplayed NVDLC03HQBuddySamples 20 1;						;SPW - Gives the objective to find the seeds. 3/11 This quest now starts after everything has been upgraded.
				Set NVDLC03DialogueHQBuddies.bBiologyUnlocked to 1;		
				ShowMessage 	NVDLC03HQBuddyUnlockMSG;	
				IncrementScriptedChallenge NVDLC03AchievementMakingFriends;	
				Set bTalkAfterMessage to 1;
			Endif
		Endif
	Endif

END; onactivate


BEGIN GameMode

	If ( bCallBiologyUI == 1 )
		Set bAwaitingInput to 1;
		ShowMessage NVDLC03HQBioResearchStationMSG;
		Set bCallBiologyUI to 0;
	Endif

	If ( bTalkAfterMessage == 1 )
		Set bTalkAfterMessage to 0;
		;SetTalkingActivatorActor NVDLC03SinkBioResearchLabTalkerREF;
		Activate;
	Endif

; ------------	Biological Research Center Day-Timer -----------	\/

	If ( bBiologyUnlocked == 1 && nSetAnyTimer > 0 && nSetAnyTimer <= 11 )					
; ---------------- Banana Yucca Timer ---------------	\/
		If ( bSetBananaYuccaTimer == 1 )																
			Set fBananaYuccaCurrentDay to GameDaysPassed;									
			If ( ( fBananaYuccaCurrentDay - fBananaYuccaLastDay ) >= 1 )
				NVDLC03SinkBananaYucca01REF.Enable;
				NVDLC03SinkBananaYucca02REF.Enable;
				Set bSetBananaYuccaTimer to 0;
			Endif
		Endif
; ---------------- Barrel Cactus Timer -----------------	\/
		If ( bSetBarrelCactusTimer == 1 )																
			Set fBarrelCactusCurrentDay to GameDaysPassed;									
			If ( ( fBarrelCactusCurrentDay - fBarrelCactusLastDay ) >= 1 )
				NVDLC03SinkBarrelCactus01REF.Enable;
				Set bSetBarrelCactusTimer to 0;
			Endif
		Endif
; ---------------- Broc Flower Timer --------------------	\/
		If ( bSetBrocFlowerTimer == 1 )																
			Set fBrocFlowerCurrentDay to GameDaysPassed;									
			If ( ( fBrocFlowerCurrentDay - fBrocFlowerLastDay ) >= 1 )
				NVDLC03SinkBrocFlower01REF.Enable;
				NVDLC03SinkBrocFlower02REF.Enable;
				Set bSetBrocFlowerTimer to 0;
			Endif
		Endif
; ---------------- Honey Mesquite Timer -------------	\/
		If ( bSetHoneyMesquiteTimer == 1 )																
			Set fHoneyMesquiteCurrentDay to GameDaysPassed;									
			If ( ( fHoneyMesquiteCurrentDay - fHoneyMesquiteLastDay ) >= 1 )
				NVDLC03SinkHoneyMesquite01REF.Enable;
				Set bSetHoneyMesquiteTimer to 0;
			Endif
		Endif
; --------------------- Jalapeno Timer -------------------	\/
		If ( bSetJalapenoTimer == 1 )																
			Set fJalapenoCurrentDay to GameDaysPassed;									
			If ( ( fJalapenoCurrentDay - fJalapenoLastDay ) >= 1 )
				NVDLC03SinkJalapeno01REF.Enable;
				NVDLC03SinkJalapeno02REF.Enable;
				NVDLC03SinkJalapeno03REF.Enable;
				Set bSetJalapenoTimer to 0;
			Endif
		Endif
; --------------------- Maize Timer ------------------------	\/
		If ( bSetMaizeTimer == 1 )																
			Set fMaizeCurrentDay to GameDaysPassed;									
			If ( ( fMaizeCurrentDay - fMaizeLastDay ) >= 1 )
				NVDLC03SinkMaize01REF.Enable;
				NVDLC03SinkMaize02REF.Enable;
				NVDLC03SinkMaize03REF.Enable;
				Set bSetMaizeTimer to 0;
			Endif
		Endif
; --------------------- Nevada Agave Timer -----------	\/
		If ( bSetNevadaAgaveTimer == 1 )																
			Set fNevadaAgaveCurrentDay to GameDaysPassed;									
			If ( ( fNevadaAgaveCurrentDay - fNevadaAgaveLastDay ) >= 1 )
				NVDLC03SinkNevadaAgave01REF.Enable;
				NVDLC03SinkNevadaAgave02REF.Enable;
				NVDLC03SinkNevadaAgave03REF.Enable;
				Set bSetNevadaAgaveTimer to 0;
			Endif
		Endif
; --------------------- Pinto Bean Pod Timer ---------	\/
		If ( bSetPintoBeanPodTimer == 1 )																
			Set fPintoBeanPodCurrentDay to GameDaysPassed;									
			If ( ( fPintoBeanPodCurrentDay - fPintoBeanPodLastDay ) >= 1 )
				NVDLC03SinkPintoBean01REF.Enable;
				NVDLC03SinkPintoBean02REF.Enable;
				NVDLC03SinkPintoBean03REF.Enable;
				Set bSetPintoBeanPodTimer to 0;
			Endif
		Endif
; --------------------- Prickly Pear Timer ---------------	\/
		If ( bSetPricklyPearTimer == 1 )																
			Set fPricklyPearCurrentDay to GameDaysPassed;									
			If ( ( fPricklyPearCurrentDay - fPricklyPearLastDay ) >= 1 )
				NVDLC03SinkPricklyPear01REF.Enable;
				NVDLC03SinkPricklyPear02REF.Enable;
				NVDLC03SinkPricklyPear03REF.Enable;
				Set bSetPricklyPearTimer to 0;
			Endif
		Endif
; --------------------- Xander Root Timer---------------	\/
		If ( bSetXanderRootTimer == 1 )																
			Set fXanderRootCurrentDay to GameDaysPassed;									
			If ( ( fXanderRootCurrentDay - fXanderRootLastDay ) >= 1 )
				NVDLC03SinkXanderRoot01REF.Enable;
				NVDLC03SinkXanderRoot02REF.Enable;
				Set bSetXanderRootTimer to 0;
			Endif
		Endif
; ---------------- White Horse Nettle Timer --------	\/
		If ( bSetWhiteHorseNettleTimer == 1 )																
			Set fWhiteHorseNettleCurrentDay to GameDaysPassed;									
			If ( ( fWhiteHorseNettleCurrentDay - fWhiteHorseNettleLastDay ) >= 1 )
				NVDLC03SinkWhiteHorseNettle01REF.Enable;
				NVDLC03SinkWhiteHorseNettle02REF.Enable;
				Set bSetWhiteHorseNettleTimer to 0;
			Endif
		Endif

	Endif

END; gamemode


BEGIN MenuMode 1001

	If ( bAwaitingInput == 1 )
		Set nButton to GetButtonPressed;

		If ( nButton > -1 )
			Set bAwaitingInput to 0;

; -------------------------------------------------------------------	BUTTON 0 ----------------------------------------------	\/	; "Do Nothing"
			If ( nButton == 0 )
				Set nButton to -1;
				Return;
; -------------------------------------------------------------------	BUTTON 1 ----------------------------------------------	\/ 	; "Process Seed into planter"
			Elseif ( nButton == 1 )
				Set nButton to -1;	
				If ( Player.GetItemCount NVDLC03DriedBananaYuccaMISC == 1 )
					Player.RemoveItem NVDLC03DriedBananaYuccaMISC 1;
					Set NVDLC03HQBuddy.bYucca to 1;												; SPW
					Set fBananaYuccaLastDay to GameDaysPassed;								
					Set bSetBananaYuccaTimer to 1;													
					If ( nSetAnyTimer < 11 )
						Set nSetAnyTimer to ( nSetAnyTimer + 1 );
					Endif
				Endif
				If ( Player.GetItemCount NVDLC03DriedBarrelCactusMISC == 1 )
					Player.RemoveItem NVDLC03DriedBarrelCactusMISC 1;
					Set NVDLC03HQBuddy.bCactus to 1;												; SPW
					Set fBarrelCactusLastDay to GameDaysPassed;								
					Set bSetBarrelCactusTimer to 1;													
					If ( nSetAnyTimer < 11 )
						Set nSetAnyTimer to ( nSetAnyTimer + 1 );
					Endif
				Endif
				If ( Player.GetItemCount NVDLC03DriedBrocFlowerMISC == 1 )
					Player.RemoveItem NVDLC03DriedBrocFlowerMISC 1;
					Set NVDLC03HQBuddy.bBroc to 1;												; SPW
					Set fBrocFlowerLastDay to GameDaysPassed;		
					Set bSetBrocFlowerTimer to 1;													
					If ( nSetAnyTimer < 11 )
						Set nSetAnyTimer to ( nSetAnyTimer + 1 );
					Endif
				Endif
				If ( Player.GetItemCount NVDLC03DriedHoneyMesquiteMISC == 1 )
					Player.RemoveItem NVDLC03DriedHoneyMesquiteMISC 1;
					Set NVDLC03HQBuddy.bMesquite to 1;											; SPW
					Set fHoneyMesquiteLastDay to GameDaysPassed;								
					Set bSetHoneyMesquiteTimer to 1;													
					If ( nSetAnyTimer < 11 )
						Set nSetAnyTimer to ( nSetAnyTimer + 1 );
					Endif
				Endif
				If ( Player.GetItemCount NVDLC03DriedJalapenoMISC == 1 )
					Player.RemoveItem NVDLC03DriedJalapenoMISC 1;
					Set NVDLC03HQBuddy.bJalapeno to 1;											; SPW
					Set fJalapenoLastDay to GameDaysPassed;								
					Set bSetJalapenoTimer to 1;													
					If ( nSetAnyTimer < 11 )
						Set nSetAnyTimer to ( nSetAnyTimer + 1 );
					Endif
				Endif
				If ( Player.GetItemCount NVDLC03DriedMaizeMISC == 1 )
					Player.RemoveItem NVDLC03DriedMaizeMISC 1;
					Set NVDLC03HQBuddy.bMaize to 1;												; SPW
					Set fMaizeLastDay to GameDaysPassed;								
					Set bSetMaizeTimer to 1;													
					If ( nSetAnyTimer < 11 )
						Set nSetAnyTimer to ( nSetAnyTimer + 1 );
					Endif
				Endif
				If ( Player.GetItemCount NVDLC03DriedNevadaAgaveMISC == 1 )
					Player.RemoveItem NVDLC03DriedNevadaAgaveMISC 1;
					Set NVDLC03HQBuddy.bAgave to 1;												; SPW
					Set fNevadaAgaveLastDay to GameDaysPassed;								
					Set bSetNevadaAgaveTimer to 1;													
					If ( nSetAnyTimer < 11 )
						Set nSetAnyTimer to ( nSetAnyTimer + 1 );
					Endif
				Endif
				If ( Player.GetItemCount NVDLC03DriedPintoBeanPodMISC == 1 )
					Player.RemoveItem NVDLC03DriedPintoBeanPodMISC 1;
					Set NVDLC03HQBuddy.bPinto to 1;												; SPW
					Set fPintoBeanPodLastDay to GameDaysPassed;								
					Set bSetPintoBeanPodTimer to 1;													
					If ( nSetAnyTimer < 11 )
						Set nSetAnyTimer to ( nSetAnyTimer + 1 );
					Endif
				Endif
				If ( Player.GetItemCount NVDLC03DriedPricklyPearMISC == 1 )
					Player.RemoveItem NVDLC03DriedPricklyPearMISC 1;
					Set NVDLC03HQBuddy.bPear to 1;												; SPW
					Set fPricklyPearLastDay to GameDaysPassed;								
					Set bSetPricklyPearTimer to 1;													
					If ( nSetAnyTimer < 11 )
						Set nSetAnyTimer to ( nSetAnyTimer + 1 );
					Endif
				Endif
				If ( Player.GetItemCount NVDLC03DriedXanderRootMISC == 1 )
					Player.RemoveItem NVDLC03DriedXanderRootMISC 1;
					Set NVDLC03HQBuddy.bXander to 1;												; SPW
					Set fXanderRootLastDay to GameDaysPassed;																
					Set bSetXanderRootTimer to 1;																										
					If ( nSetAnyTimer < 11 )																
						Set nSetAnyTimer to ( nSetAnyTimer + 1 );									
					Endif
				Endif
				If ( Player.GetItemCount NVDLC03DriedWhiteHorseNettleMISC == 1 )
					Player.RemoveItem NVDLC03DriedWhiteHorseNettleMISC 1;
					Set NVDLC03HQBuddy.bNettle to 1;												; SPW
					Set fWhiteHorseNettleLastDay to GameDaysPassed;																
					Set bSetWhiteHorseNettleTimer to 1;																										
					If ( nSetAnyTimer < 11 )																
						Set nSetAnyTimer to ( nSetAnyTimer + 1 );									
					Endif
				Endif
; -------------------------------------------------------------------	BUTTON 2 ----------------------------------------------	\/	; "Convert plant matter into Salient Green"
			Elseif ( nButton == 2 )
				Set nButton to -1;
				Set nBananaYuccaCount to Player.GetItemCount NVFreshBananaYuccaFruit;		
				Set nBarrelCactusCount to Player.GetItemCount NVFreshBarrelCactusFruit;		
				Set nBrocFlowerCount to Player.GetItemCount BrocFlower;								
				Set nHoneyMesquiteCount to Player.GetItemCount NVFreshHoneyMesquiteFruit;		
				Set nJalapenoCount to Player.GetItemCount JalapenoPepper;							
				Set nMaizeCount to Player.GetItemCount NVFreshMaize;							
				Set nNevadaAgaveCount to Player.GetItemCount NevadaAgaveFruit;			
				Set nPintoBeanCount to Player.GetItemCount PintoBeanPod;						
				Set nPricklyPearCount to Player.GetItemCount PricklyPearCactusFruit;					
				Set nWhiteHorsenettleCount to Player.GetItemCount WhiteHorsenettleBerry;			
				Set nXanderRootCount to Player.GetItemCount XanderRoot;								
				Set nPlantCount to ( nBananaYuccaCount + nBarrelCactusCount + nBrocFlowerCount + nHoneyMesquiteCount + nJalapenoCount + nMaizeCount + nNevadaAgaveCount + nPintoBeanCount + nPricklyPearCount + nWhiteHorsenettleCount + nXanderRootCount );
				Player.RemoveItem NVFreshBananaYuccaFruit nBananaYuccaCount;
				Player.RemoveItem NVFreshBarrelCactusFruit nBarrelCactusCount;
				Player.RemoveItem BrocFlower nBrocFlowerCount;
				Player.RemoveItem NVFreshHoneyMesquiteFruit nHoneyMesquiteCount;
				Player.RemoveItem JalapenoPepper nJalapenoCount;
				Player.RemoveItem NVFreshMaize nMaizeCount;
				Player.RemoveItem NevadaAgaveFruit nNevadaAgaveCount;
				Player.RemoveItem PintoBeanPod nPintoBeanCount;
				Player.RemoveItem PricklyPearCactusFruit nPricklyPearCount;
				Player.RemoveItem WhiteHorsenettleBerry nWhiteHorsenettleCount;
				Player.RemoveItem XanderRoot nXanderRootCount;
				Player.AddItem NVDLC03SalientGreen nPlantCount;
			Endif
		Endif
	Else
		Return;
	Endif

END; menumode 1001

scn NVDLC03RoboScorpionGiantSCRIPT


; This script controls the combat style swapping on scorpions so they switch between melee and range based off of distance to the player
; JSH 01/19/11
; JSH 02/03/11 - Will now switch combat style for all enemies, not just player.


Short bAddPerkOnce		; Adds perk(s) to creature once only.
Short bCombatStyle;		; This variable tracks the combat style that is currently active
								; 0 - Ranged
								; 1 - Melee

Float fTimer					; Timer variable tracking how long the scorpion is powered down.

;Ref rCurrentTarget			; Scorpion's current combat target.


BEGIN GameMode

	If ( bAddPerkOnce == 0 )
		Set bAddPerkOnce to 1;
		AddPerk Stonewall;														; ETB 02/24/11 Adding Stonewall perk to RoboScorpion to keep him from getting knocked down by the Player.
	Endif

	;If ( GetDead == 0 ) && ( GetDisabled == 0 )

		;Set rCurrentTarget to GetCombatTarget;

		;If the player is close, set to melee, else set them to range
		If ( GetDistance Player <= 800 ) 	
			If ( bCombatStyle == 0 )
				Set bCombatStyle to 1;
				SetCombatStyle CSGiantRoboScorpionMelee;
				RemoveScriptPackage;											; Remove the Fire Weapon package, if assigned.
				EvaluatePackage;
			EndIf
		Else	
			If ( bCombatStyle == 1 )
				Set bCombatStyle to 0;
				SetCombatStyle CSGiantRoboScorpionRanged;
				EvaluatePackage;
			EndIf
		EndIf

	;EndIf

END ; GameMode


BEGIN OnDeath

	PlaceAtMe NVDLC03GiantRoboScorpionExplosion;

	Kill NVDLC03DoctorMobiusREF 0;
	Kill NVDLC03DoctorMobiusREF 1;
	Kill NVDLC03DoctorMobiusREF 3;
	Kill NVDLC03DoctorMobiusREF 5;
	Kill NVDLC03DoctorMobiusREF 7;
	Kill NVDLC03DoctorMobiusREF 10;

	If ( GetStageDone NVDLC03MQ05 30 == 0 )
		SetStage NVDLC03MQ05 30;
	Endif

	Set NVDLC03MQ05.bGiantRoboscorpionDead to 1;

	StopQuest NVDLC03GiantRoboScorpionCombat;

END ; OnDeath

scn NVDLC03X13TestItemASCRIPT

; updates quest objective for the first X13 test.
; RLee 1/19/11
; JSH 02/22/11 - Modified script so that Basic test completes MQ04 and triggers Robo-Scorpion attack.


BEGIN OnAdd Player

	If (NVDLC03X13Setup.nTest > 0)

		If (GetQuestCompleted NVDLC03X13Setup == 0)									;If the initial X-13 test quest is running
			If (GetObjectiveCompleted NVDLC03X13Setup 20 == 0) && (GetObjectiveDisplayed NVDLC03X13Setup 20 == 1)
				SetObjectiveCompleted NVDLC03X13Setup 20 1;							;Complete Basic Stealth Objective
				X13StealthSuitDiagramPanel5REF.PlayGroup Left 0;						;Update panels with boots lit
				X13StealthSuitDiagramPanel4REF.PlayGroup Left 0;						;Update panels with boots lit
				X13StealthSuitDiagramPanel3REF.PlayGroup Left 0;						;Update panels with boots lit
				RewardXP 100;
				X13RewardSafeREF.AddItem AmmoSmallEnergyCell 10;				;Award item placed in starting safe
				X13RewardSafeREF.AddItem MagazineNVSneak 1;
				ShowMessage NVDLC03X13Perk11Msg;									;Show perk message in top left
				ShowMessage NVDLC03X13AwardMsg;										;Tell the player there is a reward in the safe
				IncrementScriptedChallenge NVDLC03ChallengeX13Testing01;		;JDJ 02/16/2011- completes challenge
				SetStage NVDLC03MQ04 100;													; JSH 02/22/11 - Now completes MQ04.
				StartQuest NVDLC03X13RobotScorpions;									;Enable robot scorpions!
				NVDLC03StealthSuitVoiceCrREF.Say StealthSuitUpgradedBark 1 NVDLC03StealthSuitVoiceCr 1
				Set NVDLC03AuralStealthSuitQUEST.bOverride to 1;						;Prevent stealth suit random barks
				Set NVDLC03X13Setup.nBasicReplay to 0;

			ElseIf (GetObjectiveDisplayed NVDLC03X13Setup 25 == 1) && (GetObjectiveCompleted NVDLC03X13Setup 25 == 0) && (GetObjectiveDisplayed NVDLC03X13Setup 25 == 1)
				SetObjectiveCompleted NVDLC03X13Setup 25 1;							;Complete replay objective
				RewardXP 100;
				X13RewardSafeREF.AddItem AmmoSmallEnergyCell 10;				;Award item placed in starting safe
				X13RewardSafeREF.AddItem MagazineNVSneak 1;
				ShowMessage NVDLC03X13AwardMsg;										;Tell the player there is a reward in the safe
				Set NVDLC03X13Setup.nBasicReplay to 0;

			ElseIf (GetObjectiveCompleted NVDLC03X13Setup 30 == 0) && (GetObjectiveDisplayed NVDLC03X13Setup 30 == 1)
				SetObjectiveCompleted NVDLC03X13Setup 30 1;							;Complete replay objective
				X13StealthSuitDiagramPanel5REF.PlayGroup Forward 0;				;Update panels with boots lit
				X13StealthSuitDiagramPanel4REF.PlayGroup Forward 0;				;Update panels with boots lit
				X13StealthSuitDiagramPanel3REF.PlayGroup Forward 0;				;Update panels with boots lit
				RewardXP 200;
				;SetStage NVDLC03MQ04 100;													;Complete MQ04, suit programmed
				SetObjectiveDisplayed NVDLC03X13Setup 100 1;							;Show optional complete all tests objective
				X13RewardSafeREF.AddItem AmmoMicroFusionCell 10;					;Award item placed in starting safe
				X13RewardSafeREF.AddItem MagazineNVRepair 1;
				ShowMessage NVDLC03X13Perk12Msg;									;Show perk message in top left
				ShowMessage NVDLC03X13AwardMsg;										;Tell the player there is a reward in the safe
				IncrementScriptedChallenge NVDLC03ChallengeX13Testing02;		;JDJ 02/16/2011- completes challenge
				NVDLC03StealthSuitVoiceCrREF.Say StealthSuitUpgradedBark 1 NVDLC03StealthSuitVoiceCr 1
				Set NVDLC03X13Setup.nAdvancedReplay to 0;

			ElseIf (GetObjectiveDisplayed NVDLC03X13Setup 35 == 1) && (GetObjectiveCompleted NVDLC03X13Setup 35 == 0) && (GetObjectiveDisplayed NVDLC03X13Setup 35 == 1)
				SetObjectiveCompleted NVDLC03X13Setup 35 1;							;Complete replay objective
				RewardXP 100;
				X13RewardSafeREF.AddItem AmmoMicroFusionCell 10;					;Award item placed in starting safe
				X13RewardSafeREF.AddItem MagazineNVRepair 1;
				ShowMessage NVDLC03X13AwardMsg;										;Tell the player there is a reward in the safe
				Set NVDLC03X13Setup.nAdvancedReplay to 0;

			ElseIf (GetObjectiveCompleted NVDLC03X13Setup 40 == 0) && (GetObjectiveDisplayed NVDLC03X13Setup 40 == 1)
				SetObjectiveCompleted NVDLC03X13Setup 40 1;							;Complete replay objective
				X13StealthSuitDiagramPanel5REF.PlayGroup Right 0;					;Update panels with boots lit
				X13StealthSuitDiagramPanel4REF.PlayGroup Right 0;					;Update panels with boots lit
				X13StealthSuitDiagramPanel3REF.PlayGroup Right 0;					;Update panels with boots lit
				RewardXP 300;
				X13RewardSafeREF.AddItem MagazineNVScience 1;						;Award item placed in starting safe
				X13RewardSafeREF.AddItem MagazineNVLockpick 1;
				ShowMessage NVDLC03X13Perk13Msg;									;Show perk message in top left
				ShowMessage NVDLC03X13AwardMsg;										;Tell the player there is a reward in the safe
				NVDLC03StealthSuitVoiceCrREF.Say StealthSuitUpgradedBark 1 NVDLC03StealthSuitVoiceCr 1
				Set NVDLC03X13Setup.nExpertReplay to 0;

			ElseIf (GetObjectiveDisplayed NVDLC03X13Setup 45 == 1) && (GetObjectiveCompleted NVDLC03X13Setup 45 == 0) && (GetObjectiveDisplayed NVDLC03X13Setup 45 == 1)
				SetObjectiveCompleted NVDLC03X13Setup 45 1;							;Complete replay objective
				RewardXP 100;
				X13RewardSafeREF.AddItem MagazineNVScience 1;						;Award item placed in starting safe
				X13RewardSafeREF.AddItem MagazineNVLockpick 1;
				ShowMessage NVDLC03X13AwardMsg;										;Tell the player there is a reward in the safe
				Set NVDLC03X13Setup.nExpertReplay to 0;
			EndIf

		Else
			If (NVDLC03X13Setup.nTest == 1)
				SetObjectiveCompleted NVDLC03X13Replay 25 1;						;Complete Basic Stealth Replay Objective
				RewardXP 100;
				X13RewardSafeREF.AddItem AmmoSmallEnergyCell 10;				;Award item placed in starting safe
				ShowMessage NVDLC03X13AwardMsg;										;Tell the player there is a reward in the safe
				Set NVDLC03X13Setup.nBasicReplay to 0;

			ElseIf (NVDLC03X13Setup.nTest == 2)
				SetObjectiveCompleted NVDLC03X13Replay 35 1;						;Complete Advanced Stealth Replay Objective
				RewardXP 100;
				X13RewardSafeREF.AddItem AmmoMicroFusionCell 10;					;Award item placed in starting safe
				ShowMessage NVDLC03X13AwardMsg;										;Tell the player there is a reward in the safe
				Set NVDLC03X13Setup.nAdvancedReplay to 0;

			ElseIf (NVDLC03X13Setup.nTest == 3)
				SetObjectiveCompleted NVDLC03X13Replay 45 1;						;Complete Expert Stealth Replay Objective
				RewardXP 100;
				X13RewardSafeREF.AddItem MagazineNVSneak 1;						;Award item placed in starting safe
				ShowMessage NVDLC03X13AwardMsg;										;Tell the player there is a reward in the safe
				Set NVDLC03X13Setup.nExpertReplay to 0;
			EndIf
		EndIf

		PlaySound OBJBuzzerBell;																;Play the award sound

		X13Robot1REF.SetAV Aggression 0;													;Reset robots in case they're frenzied
		X13Robot2REF.SetAV Aggression 0;
		X13Robot3REF.SetAV Aggression 0;

		If (X13Emitter1REF.bOn)																	;Turn off laser tripwires if they're currently on.
			X13Emitter1REF.Activate X13Emitter1REF 1;
		EndIf
		If (X13Emitter2REF.bOn)
			X13Emitter2REF.Activate X13Emitter2REF 1;
		EndIf
		If (X13Emitter3REF.bOn)
			X13Emitter3REF.Activate X13Emitter3REF 1;
		EndIf
		If (X13Emitter4REF.bOn)
			X13Emitter4REF.Activate X13Emitter4REF 1;
		EndIf
		If (X13Emitter5REF.bOn)
			X13Emitter5REF.Activate X13Emitter5REF 1;
		EndIf
		If (X13Emitter6REF.bOn)
			X13Emitter6REF.Activate X13Emitter6REF 1;
		EndIf
		If (X13Emitter7REF.bOn)
			X13Emitter7REF.Activate X13Emitter7REF 1;
		EndIf
		If (X13Emitter8REF.bOn)
			X13Emitter8REF.Activate X13Emitter8REF 1;
		EndIf
		If (X13Emitter9REF.bOn)
			X13Emitter9REF.Activate X13Emitter9REF 1;
		EndIf
		If (X13Emitter10REF.bOn)
			X13Emitter10REF.Activate X13Emitter10REF 1;
		EndIf
		If (X13Emitter11REF.bOn)
			X13Emitter11REF.Activate X13Emitter11REF 1;
		EndIf
		If (X13Emitter12REF.bOn)
			X13Emitter12REF.Activate X13Emitter12REF 1;
		EndIf
		If (X13Emitter13REF.bOn)
			X13Emitter13REF.Activate X13Emitter13REF 1;
		EndIf
		If (X13Emitter14REF.bOn)
			X13Emitter14REF.Activate X13Emitter14REF 1;
		EndIf
		If (X13Emitter15REF.bOn)
			X13Emitter15REF.Activate X13Emitter15REF 1;
		EndIf
		If (X13Emitter16REF.bOn)
			X13Emitter16REF.Activate X13Emitter16REF 1;
		EndIf

		NVDLC03X13RepconBackDoorREF.Unlock;
		NVDLC03X13RepconFrontDoorREF.Unlock;
		NVDLC03X13RepconFrontDoor1REF.Unlock;
		NVDLC03X13RepconDoor1REF.Unlock;
		NVDLC03X13RepconDoor2REF.Unlock;

		NVDLC03X13RepconBackDoorREF.Unlock;
		If (NVDLC03X13RepconBackDoorREF.GetOpenState == 3)										;if door is closed, 1 = open
			NVDLC03X13RepconBackDoorREF.activate NVDLC03X13RepconBackDoorREF;		;open the door
		EndIf

		NVDLC03X13RepconFrontDoorREF.Unlock;
		If (NVDLC03X13RepconFrontDoorREF.GetOpenState == 3)										;if door is closed, 1 = open
			NVDLC03X13RepconFrontDoorREF.activate NVDLC03X13RepconFrontDoorREF;		;open the door
		EndIf

		NVDLC03X13RepconFrontDoor1REF.Unlock;
		If (NVDLC03X13RepconFrontDoor1REF.GetOpenState == 3)										;if door is closed, 1 = open
			NVDLC03X13RepconFrontDoor1REF.activate NVDLC03X13RepconFrontDoorREF;	;open the door
		EndIf

		NVDLC03X13RepconDoor1REF.Unlock;
		NVDLC03X13RepconDoor2REF.Unlock;

		Set NVDLC03X13Setup.nTest to 0;													;Turn off detection system in X13Setup

	EndIf

END

