	Kill NVDLC03DoctorMobiusREF 10;

END

scn NVDLC03SinkNukaColaMachineSCRIPT

; Allows the player to buy versions of Nuka-Cola from the machine. Restocks on cell rest.
; JSH 01/12/11


Short nButton	; Which button was pressed.


BEGIN OnActivate

	If ( IsActionRef Player )

		ShowMessage NVDLC03SinkNukaColaMachineMsg;

	EndIf

END


BEGIN MenuMode 1001

	Set nButton to GetButtonPressed;

	If ( nButton == 1 )

		If ( Player.GetItemCount caps001 >= 20 )

			Player.AddItem NukaCola 1;
			Player.RemoveItem caps001 20;

		Else

			ShowMessage NVDLC03NotEnoughCapsMsg;

		EndIf

	ElseIf ( nButton == 2 )

		If ( Player.GetItemCount caps001 >= 30 )

			Player.AddItem MS05NukaColaQtm 1;
			Player.RemoveItem caps001 30;

		Else

			ShowMessage NVDLC03NotEnoughCapsMsg;

		EndIf

	ElseIf ( nButton == 3 )

		If ( Player.GetItemCount caps001 >= 40 )

			Player.AddItem NVNukaColaQuartz 1;
			Player.RemoveItem caps001 40;

		Else

			ShowMessage NVDLC03NotEnoughCapsMsg;

		EndIf

	ElseIf ( nButton == 4 )

		If ( Player.GetItemCount caps001 >= 75 )

			Player.AddItem NVNukaColaVictory 1;
			Player.RemoveItem caps001 75;

		Else

			ShowMessage NVDLC03NotEnoughCapsMsg;

		EndIf

	EndIf

END

ScriptName NVDLC03SinkJukeBoxSCRIPT


; Player can recalibrate the Soundwave Gun at this jukebox. Or he can just play music.
; JSH 01/11/11
; ETB	01/29/11 	- Added Message/MenuMode functionality.
; OMP 02/24/2011 	- Added the nSoundsInstalled variable to track how many sounds the player installs.
; SPW 02/25/2011 - Added vars for HQBuddy quest.
; ETB 03/23/11 		- Added gamemode start conversation routine (to get conversation to start directly after a message box pop-up).
; OMP 04/06/2011 - Moved the "found new frequencies" scripting into NVDLC03DialogueHQBuddies, under the "I found a soundwave..." option.


Short	bDoOnce;						; Used to run some initialization code only once.
Short	nButton;							; Which message box option the player has pressed.
Short	bAwaitingInput;					; Used to await player input.
Short	bTalkAfterMessage 			; Used to activate Jukebox (for dialogue) in gamemode.
Short	bInputMode;					; Selects Input Mode options for MenuMode 1001 message box UI options (Player is selecting which Sonic Emitter type they wish to be modified).					
Short	bOutputMode;					; Selects Output Mode options for MenuMode 1001 message box UI options (Player is selecting which Sonic Emitter type they wish to receive).
Short	bJukeboxUnlocked;			; Used to run some initialization code only once.
Short	bJukeboxFirstEnabled;		; Set to 1 in "NVDLC03SinkJukeboxLockedSCRIPT" so the Jukebox can force-greet when first enabled.
Int		nSoundsInstalled;				; Used to track the number of sounds the player installs in the Jukebox.


BEGIN OnLoad

	SetTalkingActivatorActor NVDLC03SinkJukeboxTalkerREF;

END


BEGIN OnActivate

	If ( bJukeboxUnlocked == 1 )																						; This ensures that the Jukebox talks when first enabled (regardless of "NoTalk" setting).
		If ( IsActionRef Player == 1 )
			If ( NVDLC03DialogueHQBuddies.bBuddiesTalk == 0 )
				ShowMessage NVDLC03HQBuddiesNoTalkMSG;													; Displays in corner to let Player know voice/dialogue is deactivated.
				If ( Player.GetItemCount NVDLC03WeapSonicGunGabrielsBark >= 1 || Player.GetItemCount NVDLC03WeapSonicGunOperaSinger >= 1 || Player.GetItemCount NVDLC03WeapSonicGunRevelation >= 1 || Player.GetItemCount NVDLC03WeapSonicGunRoboScorpion >= 1 || Player.GetItemCount NVDLC03WeapSonicGunTarantula >= 1 )
					Set bInputMode To 1;
				Else								
					ShowMessage NVDLC03HQJukeboxNoEmitterMSG;
				EndIf
			ElseIf ( NVDLC03DialogueHQBuddies.bBuddiesTalk == 1 )
				;SetTalkingActivatorActor NVDLC03SinkJukeboxTalkerREF;
				Activate;
			EndIf
		EndIf
	Else
		Set bTalkAfterMessage To 1;
	EndIf

END; onactivate


BEGIN GameMode

	If ( bInputMode == 1 )
		Set bAwaitingInput To 1; 
		ShowMessage NVDLC03HQJukeboxInputMSG;
	EndIf

	If ( bOutputMode == 1 )
		Set bAwaitingInput To 1;
		ShowMessage	NVDLC03HQJukeboxOutputMSG;
	EndIf

	If ( bDoOnce == 0 )
		If ( bJukeboxFirstEnabled == 1 )
			Set bDoOnce To 1;
			;SetTalkingActivatorActor NVDLC03SinkJukeboxTalkerREF;
			NVDLC03SinkJukeBoxTAREF.Activate Player 1;
		EndIf
	EndIf

	If ( bTalkAfterMessage == 1 )
		Set bTalkAfterMessage To 0;
		;SetTalkingActivatorActor NVDLC03SinkJukeboxTalkerREF;
		Activate;
		If ( bJukeboxUnlocked == 0 )
			Set bJukeboxUnlocked To 1;
		EndIf
	EndIf

END; gamemode


BEGIN MenuMode 1001

	If ( bAwaitingInput == 1 )
		Set nButton To GetButtonPressed;

		If ( nButton > -1 )
			Set bAwaitingInput To 0;

			; -------------------------------------------------------------------	BUTTON 0 ----------------------------------------------	\/	; "Do nothing."
			If ( nButton == 0 )
				Set nButton To -1;
				Set bInputMode To 0;
				Return;

			; -------------------------------------------------------------------	BUTTON 1 ----------------------------------------------	\/	; "'The Opera Singer'"
			ElseIf ( nButton == 1 )
				Set nButton To -1;
				If ( bInputMode == 1 )
					Set bInputMode To 0;
					Player.RemoveItem NVDLC03WeapSonicGunOperaSinger 1;
					Set bOutputMode To 1;
					Return;
				ElseIf ( bOutputMode == 1 )
					Set bOutputMode To 0;
					Player.AddItemHealthPercent NVDLC03WeapSonicGunOperaSinger 1 0.95;
				EndIf

			; -------------------------------------------------------------------	BUTTON 2 ----------------------------------------------	\/	; "'Gabriel's Bark'"
			ElseIf ( nButton == 2 )
				Set nButton To -1;
				If ( bInputMode == 1 )
					Set bInputMode To 0;
					Player.RemoveItem NVDLC03WeapSonicGunGabrielsBark 1;
					Set bOutputMode To 1;
					Return;
				ElseIf ( bOutputMode == 1 )
					Set bOutputMode To 0;
					Player.AddItemHealthPercent NVDLC03WeapSonicGunGabrielsBark 1 0.95;
				EndIf
			; -------------------------------------------------------------------	BUTTON 3 ----------------------------------------------	\/	; "'Revelation'"
			ElseIf ( nButton == 3 )
				Set nButton To -1;
				If ( bInputMode == 1 )
					Set bInputMode To 0;
					Player.RemoveItem NVDLC03WeapSonicGunRevelation 1;
					Set bOutputMode To 1;
					Return;
				ElseIf ( bOutputMode == 1 )
					Set bOutputMode To 0;
					Player.AddItemHealthPercent NVDLC03WeapSonicGunRevelation 1 0.95;
				EndIf

			; -------------------------------------------------------------------	BUTTON 4 ----------------------------------------------	\/	; "'Robo-Scorpion'"
			ElseIf ( nButton == 4 )
				Set nButton To -1;
				If ( bInputMode == 1 )
					Set bInputMode To 0;
					Player.RemoveItem NVDLC03WeapSonicGunRoboScorpion 1;
					Set bOutputMode To 1;
					Return;
				ElseIf ( bOutputMode == 1 )
					Set bOutputMode To 0;
					Player.AddItemHealthPercent NVDLC03WeapSonicGunRoboScorpion 1 0.95;
				EndIf

			; -------------------------------------------------------------------	BUTTON 5 ----------------------------------------------	\/	; "'Tarantula'"
			ElseIf ( nButton == 5 )
				Set nButton To -1;
				If ( bInputMode == 1 )
					Set bInputMode To 0;
					Player.RemoveItem NVDLC03WeapSonicGunTarantula 1;
					Set bOutputMode To 1;
					Return;
				ElseIf ( bOutputMode == 1 )
					Set bOutputMode To 0;
					Player.AddItemHealthPercent NVDLC03WeapSonicGunTarantula 1 0.95;
				EndIf
			EndIf

		EndIf
	Else
		Return;
	EndIf

END; menumode 1001

scn NVDLC03SnowGlobeSCRIPT

; This script controls snowglobe functionality for NVDLC03.
; Unlike FNV snowglobes, when the player picks this up it instantly is added to the collection.
; We still pop up the regular snowglobe message though.
; JSH 01/11/11


ref rNVDLC03Snowglobe			;Static snowglobe reference stored for using PlaceAtMe


BEGIN OnAdd Player

	;Check to show snowglobe messages
	If (VDialogueLucky38Penthouse.bFirstSnowGlobeMessage == 0)
		ShowMessage vSnowglobeFirstMessage;
		Set VDialogueLucky38Penthouse.bFirstSnowGlobeMessage to 1;
	ElseIf (VDialogueLucky38Penthouse.bFirstSnowGlobeMessage == 1)
		ShowMessage VSnowglobeMessage;
	EndIf

	; Inform the player that it's been added to the Lucky 38 collection.
	ShowMessage NVDLC03SnowGlobeMessage;

	;Enable the Static Version in the Lucky38
	Set rNVDLC03Snowglobe to STGlobeREF.PlaceAtMe NVDLC03SnowGlobeSTATIC 1;

	;Move it to the correct position
	rNVDLC03Snowglobe.SetPos X 1676.8359;
	rNVDLC03Snowglobe.SetPos Y -9830.5244;
	rNVDLC03Snowglobe.SetPos Z 11092.6885;

	;Give caps reward.
	Player.AddItem caps001 2000;

	;Remove the picked up item from player's inventory.
	RemoveMe;

END

scn NVDLC03MeetKleinTriggerSCRIPT


; Locks the player controls and forces him into position to meet Doctor Klein for the first time.
; JSH 01/10/11


BEGIN OnTrigger Player

	If ( NVDLC03DoctorKleinREF.GetTalkedToPC == 0 )
		DisablePlayerControls;
		NVDLC03ThinkTankTalkingActivatorREF.PlayGroup Forward 1;
		Player.MoveTo NVDLC03ThinkTankPlayerMarker;
		NVDLC03DoctorKleinREF.AddScriptPackage NVDLC03DoctorKleinGreetPlayerDialogue;
		Disable;
	Else
		Disable;
	EndIf

END

ScriptName NVDLC03AuralStealthSuitQuestSCRIPT


; Quest script used for object "NVDLC03AuralStealthSuit" functionality.
; ETB 01/10/2011
; RL 02/07/2011	- Added sneak variable for ISFX control of adv optics.
; JDJ 02/21/2011 	- Deleted adv optics section because vr test is being deleted.
; JDJ 03/11/2011 	- Standards update.
; ETB 03/22/2011	- Reduced SayDelay timer amount, fixed pip-boy light comment conditions, added "first-time-outside" bark scripting.
; RL 03/28/2011	- Added override variable so we can prevent automatic barks from happening during scripted VO sequences.
; TLS 04/25/2011	- Re-scripted combat behavior so it will work consistently and logically.
; OMP 04/28/2011	- Added an escape if CharGen mode is active, preventing barks during intro/ending slide sequences.


Short	bDoOnce;
Short	bSneaking;
Short	bWarnOnce;
Short	bEquipBark;
Short	bUnequipBark;
Short	bInCombat;
Short	bStartupSeq;
Short	bSayDelay;
Short	bSayDelayMorphineUsed;									; [NOTE: "Morphine" (Ingestible's ID) = "Med-X" (Ingestible's Name)]
Short	bSayDelayMorphineOut;										; <<<
Short	bSayDelayStimpakUsed; 
Short	bSayDelayStimpakOut;
Short	bSayDelayRandom;
Short	bRandomNumberSet;
Short	nRandomNumber;
Short	bStimpaksChecked;
Short	bMedXChecked;
Short	bPipBoyLightComment;
Short	bFirstTimeOutside;
Short	bOverride;

Float	fSayTimer;
Float	fSayTimerNumber;
Float	fSayDelayTimer;
Float	fStimpakSayDelayTimer;
Float	fMorphineSayDelayTimer;
Float	fStartupSeqTimer;
Float	fSayRandomTimer;
Float	fSayRandomTimerNumber;


BEGIN GameMode

	; Prevent any barks when in CharGen mode
	If ( GetInCharGen == 1 )
		Return;
	EndIf

	If ( bOverride == 0 )

		; -----------------------------------------------------------------------------	Dialogue Delay Timers -------------------------------------------------------------	\/
		If ( bSayDelay == 1 )
			If ( fSayDelayTimer < fSayTimerNumber )
				Set fSayDelayTimer To fSayDelayTimer + GetSecondsPassed;
			Else
				Set bSayDelay To 0;
				Set fSayDelayTimer To 0;
			EndIf
		EndIf
		If ( bSayDelayMorphineUsed == 1 )
			If ( fMorphineSayDelayTimer < 5 )
				Set fMorphineSayDelayTimer To ( fMorphineSayDelayTimer + GetSecondsPassed );
			Else
				Set fMorphineSayDelayTimer To 0;
				Set bSayDelayMorphineUsed To 0;
			EndIf
			If ( Player.GetActorValue EnduranceCondition >= 50 && Player.GetActorValue PerceptionCondition >= 50 && Player.GetActorValue LeftMobilityCondition >= 50 && Player.GetActorValue RightMobilityCondition >= 50 && Player.GetActorValue LeftAttackCondition >= 50 && Player.GetActorValue RightAttackCondition >= 50 )
				Set bSayDelayMorphineUsed To 0;
			EndIf
		EndIf
		If ( bSayDelayMorphineOut == 1 )
			If ( fMorphineSayDelayTimer < 10 )
				Set fMorphineSayDelayTimer To ( fMorphineSayDelayTimer + GetSecondsPassed );
			Else
				Set fMorphineSayDelayTimer To 0;
				Set bSayDelayMorphineUsed To 0;
			EndIf

			If ( Player.GetItemCount Morphine >= 1 )
				Set bSayDelayMorphineOut To 0;
			EndIf
		EndIf
		If ( bSayDelayStimpakUsed == 1 )
			If ( fStimpakSayDelayTimer < 5 )
				Set fStimpakSayDelayTimer To ( fStimpakSayDelayTimer + GetSecondsPassed );
			Else
				Set fStimpakSayDelayTimer To 0;
				Set bSayDelayStimpakUsed To 0;
			EndIf
			If ( Player.GetHealthPercentage >= 0.75 )
				Set bSayDelayStimpakUsed To 0;
			EndIf
		EndIf
		If ( bSayDelayStimpakOut == 1 )
			If ( fStimpakSayDelayTimer < 10 )
				Set fStimpakSayDelayTimer To ( fStimpakSayDelayTimer + GetSecondsPassed );
			Else
				Set fStimpakSayDelayTimer To 0;
				Set bSayDelayStimpakOut To 0;
			EndIf
			If ( Player.GetItemCount Stimpak >= 1 )
				Set bSayDelayStimpakOut To 0;
			ElseIf ( Player.GetHealthPercentage >= 0.5 )
				Set bSayDelayStimpakOut To 0;
			EndIf
		EndIf

		If ( bSayDelayRandom == 1 )
			If ( fSayRandomTimer < fSayRandomTimerNumber )
				Set fSayRandomTimer To ( fSayRandomTimer + GetSecondsPassed );
			Else
				Set bSayDelayRandom To 0;
				Set fSayRandomTimer To 0;
			EndIf
		EndIf

		; -----------------------------------------------------------------------------	Equip/Unequip Barks ----------------------------------------------------------------	\/
		If ( bEquipBark == 1 && bSayDelay == 0 )
			NVDLC03StealthSuitVoiceCrREF.Say StealthSuitEQUIP 1 NVDLC03StealthSuitVoiceCr 1;						; <Suit introduces itself>
			Set bSayDelay To 1;
			Set fSayTimerNumber To 5;																									; "Say Delay" set to 10 seconds
			Set bEquipBark To 0;
			Set bStartupSeq To 1;
		Else
			Set bEquipBark To 0;
		EndIf

		If ( bUnequipBark == 1 && bSayDelay == 0 )
			NVDLC03StealthSuitVoiceCrREF.Say StealthSuitUNEQUIP 1 NVDLC03StealthSuitVoiceCr 1;					; <Suit comments on removal>
			Set bSayDelay To 1;
			Set fSayTimerNumber To 3;
			Set bUnequipBark To 0;
			Set bStartupSeq To 0;
		Else
			Set bUnequipBark To 0;
		EndIf

		; -----------------------------------------------------------------------------	Out-of-Combat Barks -----------------------------------------------------------------	\/
		If ( Player.GetEquipped NVDLC03AuralStealthsuit == 1 ) && ( Player.IsInCombat == 0) && (bSayDelay == 0 )

			; --------------------------------	Status-of-Chemical Barks ----------------------------	 \/
			If ( bStartupSeq == 1 )
				If ( bStimpaksChecked == 0 )
					If ( Player.GetItemCount Stimpak == 0 )
						Set bSayDelay To 1;
						Set fSayTimerNumber To 5;
						NVDLC03StealthSuitVoiceCrREF.Say StealthSuitStartupSeq 1 NVDLC03StealthSuitVoiceCr 1;			; <Suit comments on missing Stimpak supply>
					ElseIf ( Player.GetItemCount Stimpak >= 1 && Player.GetItemCount Stimpak <= 3 )
						Set bSayDelay To 1;
						Set fSayTimerNumber To 5;
						NVDLC03StealthSuitVoiceCrREF.Say StealthSuitStartupSeq 1 NVDLC03StealthSuitVoiceCr 1;			; <Suit comments on low Stimpak supply>	
					ElseIf ( Player.GetItemCount Stimpak >= 4 )
						Set bSayDelay To 1;
						Set fSayTimerNumber To 5;
						NVDLC03StealthSuitVoiceCrREF.Say StealthSuitStartupSeq 1 NVDLC03StealthSuitVoiceCr 1;			; <Suit comments on sufficient Stimpak supply>
					EndIf
					Set bStimpaksChecked To 1;
					Return;
				EndIf
				If ( bStimpakschecked == 1 && bMedXChecked == 0 )
					If ( Player.GetItemCount Morphine == 0 )	
						Set bSayDelay To 1;
						Set fSayTimerNumber To 5;
						NVDLC03StealthSuitVoiceCrREF.Say StealthSuitStartupSeq 1 NVDLC03StealthSuitVoiceCr 1;			; <Suit comments on missing Morphine supply>
					ElseIf ( Player.GetItemCount Morphine >= 1 && Player.GetItemCount Morphine <= 3 )
						Set bSayDelay To 1;
						Set fSayTimerNumber To 5;
						NVDLC03StealthSuitVoiceCrREF.Say StealthSuitStartupSeq 1 NVDLC03StealthSuitVoiceCr 1;			; <Suit comments on low Morphine supply>
					ElseIf ( Player.GetItemCount Morphine >= 4 )
						Set bSayDelay To 1;
						Set fSayTimerNumber To 5;
						NVDLC03StealthSuitVoiceCrREF.Say StealthSuitStartupSeq 1 NVDLC03StealthSuitVoiceCr 1;			; <Suit comments on sufficient Morphine supply>
					EndIf
					Set bMedXChecked To 1;
					Return;
				EndIf
				If ( bStimpaksChecked == 1 && bMedXChecked == 1 )
					Set bStartupSeq To 0;
					Set bStimpaksChecked To 0;
					Set bMedXChecked To 0;
				EndIf
			EndIf

			; ----------------------------------------------------	Detection-of-Pipboy Light -------------------------------------------	\/
			If ( Player.IsSpellTarget PipBoyLight == 1 && Player.IsSneaking == 1 && bPipBoyLightComment == 0 )
				Set bPipBoyLightComment To 1;
				NVDLC03StealthSuitVoiceCrREF.Say StealthSuitPipBoyLight 1 NVDLC03StealthSuitVoiceCr 1;				;<Suit comments that the PipBoy light is on>
				Set bSayDelay To 1;
				Set fSayTimerNumber To 5;
			ElseIf ( Player.IsSneaking == 0 || Player.IsSpellTarget PipBoyLight == 0 )
				Set bPipBoyLightComment To 0;
			EndIf

			; ----------------------------------------------------	First-Time-Outside ------------------------------------------------------	\/
			If ( NVDLC03PlotReactionBark.nX13Done == 2 && bFirstTimeOutside == 0 )										; Makes sure Mobius/Klein aren't talking.
				Set bFirstTimeOutside To 1;
				If ( Player.GetInWorldSpace NVDLC03BigMT )
					NVDLC03StealthSuitVoiceCrREF.Say StealthSuitX13LeaveBark 1 NVDLC03StealthSuitVoiceCr 1;			;<Suit comments on being outside for the first time>
				EndIf
			EndIf

			; ----------------------------------------------------	Random Barks -----------------------------------------------------------	\/
			If ( bRandomNumberSet == 0 )
				Set nRandomNumber To GetRandomPercent;
				Set bRandomNumberSet To 1;
			EndIf

			If ( bRandomNumberSet == 1 )
				If ( bSayDelayRandom == 0 && bSayDelay == 0 )
					If ( nRandomNumber <= 33 )
						Set bSayDelayRandom To 1;
						Set bRandomNumberSet To 0;
						Set fSayRandomTimerNumber To 300;
						Return;
					Else ( nRandomNumber > 33 )
						Set bSayDelayRandom To 1;
						Set bRandomNumberSet To 0;
						Set fSayRandomTimerNumber To 300;
						NVDLC03StealthSuitVoiceCrREF.Say StealthSuitFunnyLines 1 NVDLC03StealthSuitVoiceCr 1;		; <Suit says a random funny line>
						Set bSayDelay To 1;
						Set fSayTimerNumber To 5;
					EndIf
				EndIf
			EndIf

		; -----------------------------------------------------------------------------	In-Combat Behavior ------------------------------------------------------------------	\/
		ElseIf ( Player.GetEquipped NVDLC03AuralStealthsuit == 1 ) && ( Player.IsInCombat == 1 )
			If ( Player.GetHealthPercentage < 0.5 )
				If ( Player.GetItemCount Stimpak > 0 )																						; <Does the player have a Stimpak?>
					If ( Player.HasMagicEffect RestoreHealth == 0 ) && ( Player.IsHardcore == 1 )											; If the player is in hardcore mode, don't apply more than one Stimpak at a time
						PlaySound NPCHumanUsingStimpak;
						Player.CastImmediateOnSelf NVDLC03StealthSuitStimpak;													; <Suit gives player a Stimpak use>
						Player.RemoveItem Stimpak 1;
						If ( bSayDelayStimpakUsed == 0 ) && ( bSayDelay == 0 )														; <If suit hasn't said a Stimpak line yet...>
							NVDLC03StealthSuitVoiceCrREF.Say StealthSuitStimpak 1 NVDLC03StealthSuitVoiceCr 1;			; <Suit comments on Stimpak injection>
							Set bSayDelayStimpakUsed To 1;
							Set bSayDelay To 1;
							Set fSayTimerNumber To 5;
						EndIf
					ElseIf ( Player.IsHardcore == 0 )																							; If the player is not Hardcore, apply Stimpaks until the player's Health is above 50%
						PlaySound NPCHumanUsingStimpak;
						Player.CastImmediateOnSelf NVDLC03StealthSuitStimpak;													; <Suit gives player a Stimpak use>
						Player.RemoveItem Stimpak 1;
						If ( bSayDelayStimpakUsed == 0 ) && ( bSayDelay == 0 )														; <If suit hasn't said a Stimpak line yet...>
							NVDLC03StealthSuitVoiceCrREF.Say StealthSuitStimpak 1 NVDLC03StealthSuitVoiceCr 1;			; <Suit comments on Stimpak injection>
							Set bSayDelayStimpakUsed To 1;
							Set bSayDelay To 1;
							Set fSayTimerNumber To 5;
						EndIf
					EndIf
				ElseIf ( Player.GetItemCount Stimpak == 0 )																				; If the player is out of Stimpaks...>
					If ( bSayDelayStimpakOut == 0 ) && ( bSayDelay == 0 )																						; <If suit hasn't said a Stimpak line yet...>
						NVDLC03StealthSuitVoiceCrREF.Say StealthSuitStimpak 1 NVDLC03StealthSuitVoiceCr 1;			; <Suit comments on missing Stimpak supply>	
						Set bSayDelayStimpakOut To 1;
						Set bSayDelay To 1;
						Set fSayTimerNumber To 10;
					EndIf
				EndIf
			EndIf

			If ( Player.HasMagicEffect ChemIncDmgResistPsycho == 0 )																		; player doesn't have morphine in his system.
				If ( Player.GetActorValue EnduranceCondition <= 25 ) || ( Player.GetActorValue PerceptionCondition <= 25 ) || ( Player.GetActorValue LeftMobilityCondition <= 25 ) || ( Player.GetActorValue RightMobilityCondition <= 25 ) || ( Player.GetActorValue LeftAttackCondition <= 25 ) || ( Player.GetActorValue RightAttackCondition <= 25 )
					If ( Player.GetItemCount Morphine > 0 )																			; player has morphine available in his inventory and isn't currently using Med-X
						Player.EquipItem Morphine 1;
						If ( bSayDelayMorphineUsed == 0 ) && ( bSayDelay == 0 )
							NVDLC03StealthSuitVoiceCrREF.Say StealthSuitMedX 1 NVDLC03StealthSuitVoiceCr 1;			; <Suit comments on Med-X injection>
							Set bSayDelayMorphineUsed To 1;
							Set bSayDelay To 1;
							Set fSayTimerNumber To 5;
						EndIf
					ElseIf ( Player.GetItemCount Morphine == 0 && bSayDelayMorphineOut == 0 )	 && (bSayDelay == 0)
						Set bSayDelayMorphineOut To 1;														
						NVDLC03StealthSuitVoiceCrREF.Say StealthSuitMedX 1 NVDLC03StealthSuitVoiceCr 1;			; <Suit comments on missing Med-X supply>
						Set bSayDelay To 1;
						Set fSayTimerNumber To 10;
					EndIf
				EndIf
			EndIf
		EndIf; in/out-of-combat barks conditions

		; -----------------------------------------------------------------------------	Detection-of-Combat Barks -------------------------------------------------------	\/
		If ( Player.GetEquipped NVDLC03AuralStealthsuit == 1 && bInCombat == 0 && bSayDelay == 0 )
			If ( Player.IsInCombat == 1 )
				NVDLC03StealthSuitVoiceCrREF.Say StealthSuitCombatStart 1 NVDLC03StealthSuitVoiceCr 1;			;<Suit comments on the start of combat>
				Set bSayDelay To 1;
				Set fSayTimerNumber To 5;
				Set bInCombat To 1;
			EndIf
		ElseIf ( Player.GetEquipped NVDLC03AuralStealthSuit == 1 && bInCombat == 1 && bSayDelay == 0 )
			If ( Player.IsInCombat == 0 )
				NVDLC03StealthSuitVoiceCrREF.Say StealthSuitCombatEnd 1 NVDLC03StealthSuitVoiceCr 1;			;<Suit comments on the end of combat>
				Set bSayDelay To 1;
				Set fSayTimerNumber To 5;
				Set bInCombat To 0;
			EndIf
		EndIf
	EndIf

END; gamemode

scn NVDLC03MQ04SCRIPT


; Quest script for NVDLC03MQ04 - Brain Food.
; JSH 01/10/11
; JSH 03/27/11 - Added GameMode block for safety quest updating.
; RL 03/28/11 - Added a 2s delay timer to ensure the message popup doesn't prevent the quest update from displaying.


; Tracks the player acquisition of the X-13 Stealth Suit pieces.
Short bGotBoots
Short bGotGloves
Short bGotChest
Short bOnce
Float fTimer


BEGIN GameMode

	If ( GetStageDone NVDLC03MQ04 50 == 0 )

		If ( Player.GetItemCount NVDLC03X13SuitBoots == 1 ) && ( Player.GetItemCount NVDLC03X13SuitGloves == 1 ) && ( Player.GetItemCount NVDLC03X13SuitChest == 1 )

			Set fTimer to (fTimer + GetSecondsPassed);

			If (bOnce == 0) && (fTimer > 1)
				ShowMessage NVDLC03X13SuitCompleteMsg;
				Set bOnce to 1;
			EndIf			

			If (fTimer > 2)
				Player.RemoveItem NVDLC03X13SuitBoots 1 1;
				Player.RemoveItem NVDLC03X13SuitChest 1 1;
				Player.RemoveItem NVDLC03X13SuitGloves 1 1;
				Player.AddItem NVDLC03AuralStealthSuit 1;
				SetObjectiveCompleted NVDLC03MQ04 20 1;
				SetObjectiveCompleted NVDLC03MQ04 30 1;
				SetObjectiveCompleted NVDLC03MQ04 40 1;
				SetObjectiveDisplayed NVDLC03MQ04 50 1;
				SetStage NVDLC03MQ04 50;		; Update NVDLC03MQ04 if all pieces have been acquired.
			EndIf
		EndIf

	EndIf

END

scn NVDLC03DoctorMobiusSCRIPT


; NPC script for Doctor Mobius.
; JSH 01/07/11


Short bMobiusCloaked	; 0 = Uncloaked, 1 = Cloaked.
Short bCloakFailed		; 0 = Capable of cloaking, 1 = Cloak has failed.
Short bSpawnMinions	; 1 = Minions have spawned to assist Mobius.


BEGIN OnActivate

	If ( IsActionRef Player == 1 )

		If ( GetDead == 1 )

			Activate;
			Return;

		EndIf

		If ( GetFactionRelation Player == 1 )			; Enemy of Player.

			If ( IsInCombat == 0 )
				SayTo Player AlertIdle;
			Else
				SayTo Player StartCombatResponse;
			EndIf

			StartCombat Player;

		Else

			StartConversation Player;

		EndIf

	EndIf

END


; When Mobius first enters combat, he cloaks himself with a stealth field.
BEGIN OnStartCombat

	SetEnemy NVDLC03MobiusFaction PlayerFaction;

	If ( GetStageDone NVDLC03MQ05 40 == 0 )
    		SetStage NVDLC03MQ05 35;
	EndIf

	If (bMobiusCloaked == 0) && (bCloakFailed == 0)
		AddSpell StealthBoyInvisibilitySpell;
		Set bMobiusCloaked to 1;
	EndIf

	If ( bSpawnMinions == 0 )
		NVDLC03ForbiddenRoboScorpionParentREF.Enable;
		Set bSpawnMinions to 1;
	EndIf

END


BEGIN OnDeath

	SetEnemy NVDLC03MobiusFaction PlayerFaction;
	Set NVDLC03MQ05.bDoctorMobiusDead to 1;

END


; When combat ends, Mobius decloaks.
BEGIN OnCombatEnd

	If (bMobiusCloaked == 1)
		RemoveSpell StealthBoyInvisibilitySpell;
		Set bMobiusCloaked to 0;
	EndIf

END


BEGIN GameMode

	; Mobius' cloak fails when his health reaches 50% or less and he's in combat.
	If ( IsInCombat ) && ( GetHealthPercentage <= 0.50 )
		Set bMobiusCloaked to 0;
		Set bCloakFailed to 1;
		RemoveSpell StealthBoyInvisibilitySpell;
	EndIf

END

scn NVDLC03X8CaveCodeBoxSCRIPT


;opening and closing giant dog fight sequence
;JDJ 01/05/2011
;JDJ 01/14/2011 form corrections
;JDJ 01/18/2011 Added Dialogue Countdown
;JDJ 01/21/2011 Removed disable of Gabe, changed explosion to one with damage
;JDJ 01/25/2011 Changed scripting in nDoOnce == 1 using sonic bark weapon
;JDJ 01/28/2011 Added explosion damage to Gabe after death
;JDJ 02/02/2011 adding placing of sonic gun in a random dig hole
; JSH 02/11/11 - Updated MQ03 with "search gabe's digging holes objective"
;JDJ 02/22/2011 moved countdown to talking activator
;JDJ 02/25/2011 made completely gamemode script because trigger didn't seem to be firing consistently
;JDJ 03/04/2011 adding scripting to get the name of the mound the bark is placed in
;JDJ 03/21/2011 adding a variable for when the countdown completes
;JDJ 03/23/2011 adding timer to space beginning of countdown from gabe's death a little to try to keep lines from overlapping


Int bStart;												;Starts Sequence when trigger is triggered
Int nDoOnce;											;makes sure each stage and the whole sequence is only done once
Int nCount;												;counts stages in sequence
Int bDogBehavior;										;switches Gabe's AI Packages
Float fTimer;											;timer for sequence
Short nSpot;												;assigned a random number to determine the spot the sonic gun is buried
Int bCountdownStart;									;says first line of countdown
Ref rCorrectDigSpot;									;gets the name of which mound gabriels bark is actually in
Int bCountdownComplete;							;set at the end of Mobius' countdown line, 0 if not complete, 1 if complete
Float fGabeTimer;										;slight timer after gabe's death to delay countdown start



BEGIN GameMode

	If (bStart == 0)																					;Starts Sequence when triggered

		Set nSpot to (0 + GetRandomPercent * 6 / 99);

		If (nSpot == 0)
			NVDLC03X3CaveMound01REF.AddItem NVDLC03SonicGunGabrielsBarkNOTE 1;
			NVDLC03X3CaveMound01REF.AddItem AmmoSmallEnergyCell 50;
			Set rCorrectDigSpot to NVDLC03X3CaveMound01REF;
		ElseIf (nSpot == 1)
			NVDLC03X3CaveMound02REF.AddItem NVDLC03SonicGunGabrielsBarkNOTE 1;
			NVDLC03X3CaveMound02REF.AddItem AmmoSmallEnergyCell 50;
			Set rCorrectDigSpot to NVDLC03X3CaveMound02REF;
		ElseIf (nSpot == 2)
			NVDLC03X3CaveMound03REF.AddItem NVDLC03SonicGunGabrielsBarkNOTE 1;
			NVDLC03X3CaveMound03REF.AddItem AmmoSmallEnergyCell 50;
			Set rCorrectDigSpot to NVDLC03X3CaveMound03REF;
		ElseIf (nSpot == 3)
			NVDLC03X3CaveMound04REF.AddItem NVDLC03SonicGunGabrielsBarkNOTE 1;
			NVDLC03X3CaveMound04REF.AddItem AmmoSmallEnergyCell 50;
			Set rCorrectDigSpot to NVDLC03X3CaveMound04REF;
		ElseIf (nSpot == 4)
			NVDLC03X3CaveMound05REF.AddItem NVDLC03SonicGunGabrielsBarkNOTE 1;
			NVDLC03X3CaveMound05REF.AddItem AmmoSmallEnergyCell 50;
			Set rCorrectDigSpot to NVDLC03X3CaveMound05REF;
		ElseIf (nSpot == 5)
			NVDLC03X3CaveMound06REF.AddItem NVDLC03SonicGunGabrielsBarkNOTE 1;
			NVDLC03X3CaveMound06REF.AddItem AmmoSmallEnergyCell 50;
			Set rCorrectDigSpot to NVDLC03X3CaveMound06REF;
		ElseIf (nSpot == 6)
			NVDLC03X3CaveMound07REF.AddItem NVDLC03SonicGunGabrielsBarkNOTE 1;
			NVDLC03X3CaveMound07REF.AddItem AmmoSmallEnergyCell 50;
			Set rCorrectDigSpot to NVDLC03X3CaveMound07REF;
		EndIf

		X8CaveIntercom3.SayTo Player NVDLC03BorousX8Cave01 1;
		X8CaveDog01REF.SetGhost 1;															;Set Dog to Ghost so it Behaves when explosion bark happens
		StartQuest NVDLC03X8CountdownQuest;											;Starts Quest for Countdown

		SetStage NVDLC03MQ03 60;															; JSH 02/11/11 - Player told to search Gabe's digging holes.

		Set bStart to 1;
		Set nDoOnce to 1;

	EndIf

	If (nDoOnce == 1)

		Set fTimer to fTimer + GetSecondsPassed;															;Begin Timer

		If (nCount == 0) && (fTimer >= 1)

			X8CaveDog01REF.AddScriptPackage NVDLC03X8GabeUseWeaponPackage;			;Set Dog to AI Package without fallout behaviors so it behaves when explosion bark happens
			X8CaveDog01REF.EvaluatePackage;

			Set nCount to 1;

		ElseIf (nCount == 1) && (fTimer >= 4)

			X8CaveDog01REF.FireWeapon NVDLC03SonicBark;			
			Set nCount to 2;

		ElseIf (nCount == 2)																							;Set to 2 in NVDLC03X8GabeUseWeaponPackage

			If (fTimer >= 2)

				X8CaveDog01REF.RemoveScriptPackage NVDLC03X8GabeUseWeaponPackage;	
				X8DoghousePlankMarker01.Disable;																;Doghouse Disable
				X8TunnelDoghouseParent.Disable;																;Disable doghouse in tunnel
				X8CaveExplosionMarker.PlaceAtMe NVDLC03DoghouseExplosion;						;Explosion at doghouse as if from bark
				Set nCount to 3;
				Return;

			EndIf

		ElseIf (nCount == 3)

			If (fTimer >= 3)

				X8CaveDog01REF.AddToFaction NVDLC03X8Faction 1;
				X8CaveDog01REF.SetGhost 0;													;Remove Ghost
				Set bDogBehavior to 1;															;Set Dog to AI Package with fallout behaviors enabled
				X8CaveDog01REF.ResetAI;
				SetQuestDelay NVDLC03MQ03 .3;											;Increasing the quest so it will update faster when the player gets gabe's bark
				Set fTimer to 22.5;																	;Set the timer for the final countdown when gabe dies
				Set nCount to 4;
				Set nDoOnce to 2;
				Return;

			EndIf

		EndIf

	EndIf

	If (nDoOnce == 3)																			;Variable set to 3 ondeath on Gabe

		Set fGabeTimer to fGabeTimer + GetSecondsPassed;

		If (bCountdownStart == 0) && (fGabeTimer >= 4)
			X8CaveIntercom.SayTo Player NVDLC03X8CountdownOpening 1;				;Dialogue Countdown
			Set bCountdownStart to 1;
		EndIf

	EndIf

END

scn NVDLC03X13TriggerSCRIPT


; Updates NVDLC03MQ04 when the player enters the X-13 Research Facility.
; JSH 01/05/10


BEGIN OnTrigger Player

	If ( GetStageDone NVDLC03MQ04 20 == 0 )

		SetStage NVDLC03MQ04 20;
		StartQuest NVDLC03X13Setup;
		Disable;

	EndIf

END

scn NVDLC03MQ01SCRIPT


; Quest script for NVDLC03MQ01.
; JSH 01/05/11
; SPW 01/11/11 - Adding a line to turn on crater encounters
; JSH 03/01/11 - Established secondary quest xp values.
; SPW 3/22/11 - Turning off force fields on PS3


Short bDoOnce;	; 1= Run block one.
Short bTimeSet	;	; 1 = Time has been initialized for initial arrival on Big Empty floor.


BEGIN GameMode

	If (bDoOnce == 0)

		Set bDoOnce to 1;

		If (Player.GetLevel < 5)

			;Total XP Awarded should be approximately 3000
			Set NVDLC03Act1XP to (3000 * NVDLC03Act1XPPercentage / 2);
			Set NVDLC03Act2XP to (3000 * NVDLC03Act2XPPercentage / 4);
			Set NVDLC03Act3XP to (3000 * NVDLC03Act3XPPercentage / 3);

			Set NVDLC03SecondaryQuestXP to 75;

		ElseIf (Player.GetLevel < 10)

			;Total XP Awarded should be approximately 5000
			Set NVDLC03Act1XP to (5000 * NVDLC03Act1XPPercentage / 2);
			Set NVDLC03Act2XP to (5000 * NVDLC03Act2XPPercentage / 4);
			Set NVDLC03Act3XP to (5000 * NVDLC03Act3XPPercentage / 3);

			Set NVDLC03SecondaryQuestXP to 125;

		ElseIf (Player.GetLevel < 15)

			;Total XP Awarded should be approximately 7500
			Set NVDLC03Act1XP to (7500 * NVDLC03Act1XPPercentage / 2);
			Set NVDLC03Act2XP to (7500 * NVDLC03Act2XPPercentage / 4);
			Set NVDLC03Act3XP to (7500 * NVDLC03Act3XPPercentage / 3);

			Set NVDLC03SecondaryQuestXP to 175;

		ElseIf (Player.GetLevel < 20)

			;Total XP Awarded should be approximately 10000
			Set NVDLC03Act1XP to (10000 * NVDLC03Act1XPPercentage / 2);
			Set NVDLC03Act2XP to (10000 * NVDLC03Act2XPPercentage / 4);
			Set NVDLC03Act3XP to (10000 * NVDLC03Act3XPPercentage / 3);

			Set NVDLC03SecondaryQuestXP to 250;

		ElseIf (Player.GetLevel < 25)

			;Total XP Awarded should be approximately 12000
			Set NVDLC03Act1XP to (12000 * NVDLC03Act1XPPercentage / 2);
			Set NVDLC03Act2XP to (12000 * NVDLC03Act2XPPercentage / 4);
			Set NVDLC03Act3XP to (12000 * NVDLC03Act3XPPercentage / 3);

			Set NVDLC03SecondaryQuestXP to 300;

		ElseIf (Player.GetLevel < 30)

			;Total XP Awarded should be approximately 14000
			Set NVDLC03Act1XP to (14000 * NVDLC03Act1XPPercentage / 2);
			Set NVDLC03Act2XP to (14000 * NVDLC03Act2XPPercentage / 4);
			Set NVDLC03Act3XP to (14000 * NVDLC03Act3XPPercentage / 3);

			Set NVDLC03SecondaryQuestXP to 350;

		ElseIf (Player.GetLevel < 35)

			;Total XP Awarded should be approximately 16000
			Set NVDLC03Act1XP to (16000 * NVDLC03Act1XPPercentage / 2);
			Set NVDLC03Act2XP to (16000 * NVDLC03Act2XPPercentage / 4);
			Set NVDLC03Act3XP to (16000 * NVDLC03Act3XPPercentage / 3);

			Set NVDLC03SecondaryQuestXP to 400;

		ElseIf (Player.GetLevel < 40)

			;Total XP Awarded should be approximately 18000
			Set NVDLC03Act1XP to (18000 * NVDLC03Act1XPPercentage / 2);
			Set NVDLC03Act2XP to (18000 * NVDLC03Act2XPPercentage / 4);
			Set NVDLC03Act3XP to (18000 * NVDLC03Act3XPPercentage / 3);

			Set NVDLC03SecondaryQuestXP to 450;

		ElseIf (Player.GetLevel < 45)

			;Total XP Awarded should be approximately 21000
			Set NVDLC03Act1XP to (21000 * NVDLC03Act1XPPercentage / 2);
			Set NVDLC03Act2XP to (21000 * NVDLC03Act2XPPercentage / 4);
			Set NVDLC03Act3XP to (21000 * NVDLC03Act3XPPercentage / 3);

			Set NVDLC03SecondaryQuestXP to 525;

		Else

			;Total XP Awarded should be approximately 22000
			Set NVDLC03Act1XP to (22000 * NVDLC03Act1XPPercentage / 2);
			Set NVDLC03Act2XP to (22000 * NVDLC03Act2XPPercentage / 4);
			Set NVDLC03Act3XP to (22000 * NVDLC03Act3XPPercentage / 3);

			Set NVDLC03SecondaryQuestXP to 550;

		EndIf

		;set iNVDLC03Act1FinalXP to NVDLC03Act1XP * 2
		;set iNVDLC03Act2FinalXP to NVDLC03Act2XP * 2
		;set iNVDLC03Act3FinalXP to NVDLC03Act3XP * 2

		; SPW 1/11/11 - Turns on crater encounters
		SetStage NVDLC03Enc 10;
		; SPW 3/22/11 - Turns off force fields on PS3
		If (IsPS3 ==1)
			NVDLC03PS3ForceFieldREF.Disable;
		EndIf

	EndIf

END

Scn NVDLC03EncListSCRIPT

; This script lists the possible encounters found in FNVDLC02. A TriggerEnter event runs the association of selected spawns with declared Ref Variables.
; JS 2010
; SPW 01/05/11 Testing for DLC03
; JS 01/24/11 - Added new functionality to the Encounter List code (from NVDLC02) so that encounters are not randomized - rather, they are pulled from two distinct possibilities for each
; spawner codebox. Old World Blues' exterior worldspace is meant to have a more discrete partition of creature types per zone than NVDLC02's Zion Valley.
; JS 01/24/11 - Added comments explaining what this script does, in detail.
; SPW 03/09/11 - Removing a spawn.
; SPW 03/21/11 - Removing 3 spawns from little yangtze & turning off scorpion spawns after Mobius has benn dealt with.
; SPW 04/05/11 - Removing 2 spawns from the botanical garden


Ref	rActor01
Ref	rActor02
Ref	rActor03
Ref	rActor04

Ref	rActor05
Ref	rActor06
Ref	rActor07
Ref	rActor08
Ref	rActor09

Ref	rActor10
Ref	rActor11
Ref	rActor12
Ref	rActor13
Ref	rActor14
Ref	rActor15
Ref	rActor16
Ref	rActor17
Ref	rActor18


Begin OnTriggerEnter Player																; An EnterTrigger event gets called to this script owner's ref (NVDLC03EncBoxREF) by the Encounter System's master script,
																									; only when this script has been queried by a spawner-box to select an encounter to spawn.
	If (NVDLC03Enc.bEncounterChosen == 0)											; If we haven't yet chosen an encounter (when this var is 1, set below, the master script tells the last active spawner-box to spawn the chosen encounter.
		If (NVDLC03Enc.iEncounterNumber == 1)										; If we've got to choose encounter for the first active spawner-box, then...
			Set NVDLC03Enc.rBoxOne to NVDLC03Enc.rBoxSpawn;					; ... we define BoxOne to this first active spawner-box's ref (this has been stored in the master script, as defined by the spawner-box's code itself).
		Elseif (NVDLC03Enc.iEncounterNumber == 2)									; However, if this is the second encounter active to spawn, then
			Set NVDLC03Enc.rBoxTwo to NVDLC03Enc.rBoxOne;					; ... we switch BoxOne to be BoxTwo (because it's #2 the one that gets "cleaned" when we hit encounter count limit - we want to clean the old box, not the latest active one.
			Set NVDLC03Enc.rBoxOne to NVDLC03Enc.rBoxSpawn;					; Now, we define BoxOne to the latest active spawner-box's ref.
			;Set NVDLC03Enc.iEncounterTwo to NVDLC03Enc.iEncounterOne;	; JS 01/24/11 - I commented out these lines, as they are not needed with your NVDLC03 encounter setup.
			;Set NVDLC03Enc.iEncounterOne to NVDLC03Enc.iEncRand;			; Instead, we use iEncRand in each codebox-defined encounters to set their possible arrangements:
		Endif																						; that is, we match an rActor ref with a given creature ref. The spawner codebox script ties these to its linked markers.
		If (NVDLC03Enc.iBoxCall == 1)														; If the last active spawner-box (the one for which we need to spawn stuff) is box #1 (in NVDLC03's main worldspace).
			If (NVDLC03Enc.iEncRand == 1)												; If iEncRand is 1 (defined in the master script), we assign the actors as intended for their first arrangement.
				;Mr. Gutsy - 9
				;Set rActor01 to NVDLC03EncGutsy01REF;								; Since rActors will be tied to rMarks with the same numbers, we can change which critters appear where by changing
				;Set rActor02 to NVDLC03EncGutsy02REF;								; what creature refs become our defined script ref vars here.
				Set rActor03 to NVDLC03EncGutsy03REF;								; For now, the encounter list system only observes one possible arrangement.
				Set rActor04 to NVDLC03EncGutsy04REF;
				Set rActor05 to NVDLC03EncGutsy05REF;
				Set rActor06 to NVDLC03EncGutsy06REF;
				Set rActor07 to NVDLC03EncGutsy07REF;
				Set rActor08 to NVDLC03EncGutsy08REF;
				Set rActor09 to NVDLC03EncGutsy09REF;
				rActor01.Resurrect;															; Once actors are assigned, we resurrect them to reset their AI, Stats, Inventory, etc.
				rActor02.Resurrect;
				rActor03.Resurrect;
				rActor04.Resurrect;
				rActor05.Resurrect;
				rActor06.Resurrect;
				rActor07.Resurrect;
				rActor08.Resurrect;
				rActor09.Resurrect;
				Set NVDLC03Enc.iEncounterSpawns to 7;								; This variable tells the spawner-box code to only move, and enable, a given number of actors. Failing to match this number
				Set NVDLC03Enc.bEncounterChosen to 1;								; to the number of linked markers, or the defined actors, will cause the EnterTrigger event to get stuck on a performance loop that
				Return;																			; will destroy performance - watch out. Note, once we've chosen, and assigned, encounter refs, we set bEncounterChosen to 1 - the master script can now proceed to the next step.
			Elseif (NVDLC03Enc.iEncRand == 2)											; This space below can be used to defined different arrangement for a given spawner-box's spawns.

			Endif
		Elseif (NVDLC03Enc.iBoxCall == 2)												; We repeat the same steps above, but for box #2, and so forth. Each box-related chunk of code will run once, and then Return at the end of the operations...
			If (NVDLC03Enc.iEncRand == 1)												; ... to avoid having the script run checks unnecessarily.
				; Robo Scorpion - 8
				If (GetStage NVDLC03MQ05 < 40)											; If Mobius has been dealt with then turn off these spawns
					Set rActor01 to NVDLC03EncRoboScorpion01REF;
					Set rActor02 to NVDLC03EncRoboScorpion02REF;
					Set rActor03 to NVDLC03EncRoboScorpion03REF;
					Set rActor04 to NVDLC03EncRoboScorpion04REF;
					Set rActor05 to NVDLC03EncRoboScorpion05REF;
					Set rActor06 to NVDLC03EncRoboScorpion06REF;
					Set rActor07 to NVDLC03EncRoboScorpion07REF;
					Set rActor08 to NVDLC03EncRoboScorpion08REF;
					Set rActor09 to NVDLC03EncRoboScorpion09REF;
					rActor01.Resurrect;
					rActor02.Resurrect;
					rActor03.Resurrect;
					rActor04.Resurrect;
					rActor05.Resurrect;
					rActor06.Resurrect;
					rActor07.Resurrect;
					rActor08.Resurrect;
					rActor09.Resurrect;
					Set NVDLC03Enc.iEncounterSpawns to 9;
					Set NVDLC03Enc.bEncounterChosen to 1;
					Return;		
				ElseIf (GetStage NVDLC03MQ05 >= 40)		
					Set NVDLC03Enc.iEncounterSpawns to 0;
					Set NVDLC03Enc.bEncounterChosen to 1;
					Return;	
				EndIf		
					Set NVDLC03Enc.iEncounterSpawns to 0;
					Set NVDLC03Enc.bEncounterChosen to 1;
					Return;				
			Elseif (NVDLC03Enc.iEncRand == 2)

			Endif
		Elseif (NVDLC03Enc.iBoxCall == 3)
			If (NVDLC03Enc.iEncRand == 1)
				; Securitron - 8, Cazador - 1
				Set rActor01 to NVDLC03EncSecuritron01REF;
				Set rActor02 to NVDLC03EncSecuritron02REF;
				Set rActor03 to NVDLC03EncSecuritron03REF;
				Set rActor04 to NVDLC03EncSecuritron04REF;
				Set rActor05 to NVDLC03EncSecuritron05REF;
				Set rActor06 to NVDLC03EncSecuritron06REF;
				Set rActor07 to NVDLC03EncSecuritron07REF;
				Set rActor08 to NVDLC03EncSecuritron08REF;
				Set rActor09 to NVDLC03EncCazador09REF;
				rActor01.Resurrect;
				rActor02.Resurrect;
				rActor03.Resurrect;
				rActor04.Resurrect;
				rActor05.Resurrect;
				rActor06.Resurrect;
				rActor07.Resurrect;
				rActor08.Resurrect;
				rActor09.Resurrect;
				Set NVDLC03Enc.iEncounterSpawns to 9;
				Set NVDLC03Enc.bEncounterChosen to 1;
				Return;			
			Elseif (NVDLC03Enc.iEncRand == 2)

			Endif
		Elseif (NVDLC03Enc.iBoxCall == 4)
			If (NVDLC03Enc.iEncRand == 1)
				; Cazadores - 3, Nightstalker - 3, Cyber Dog - 1
				Set rActor01 to NVDLC03EncCazador01REF;
				Set rActor02 to NVDLC03EncCazador02REF;
				Set rActor03 to NVDLC03EncCazador03REF;
				Set rActor04 to NVDLC03EncNightstalker10REF;
				Set rActor05 to NVDLC03EncNightstalker11REF;
				Set rActor06 to NVDLC03EncNightstalker12REF;
				Set rActor07 to NVDLC03EncCyberDog07REF;
;				Set rActor08 to NVDLC03EncSecuritron09REF;
;				Set rActor09 to NVDLC03EncCazador08REF;
				rActor01.Resurrect;
				rActor02.Resurrect;
				rActor03.Resurrect;
				rActor04.Resurrect;
				rActor05.Resurrect;
				rActor06.Resurrect;
				rActor07.Resurrect;
;				rActor08.Resurrect;
;				rActor09.Resurrect;
				Set NVDLC03Enc.iEncounterSpawns to 7;
				Set NVDLC03Enc.bEncounterChosen to 1;
				Return;			
			Elseif (NVDLC03Enc.iEncRand == 2)

			Endif
		Elseif (NVDLC03Enc.iBoxCall == 5)
