				rActor02.Resurrect;
				rActor03.Resurrect;
				rActor04.Resurrect;
				rActor05.Resurrect;
				rActor06.Resurrect;
;				rActor07.Resurrect;
;				rActor08.Resurrect;
;				rActor09.Resurrect;
				Set NVDLC03Enc.iEncounterSpawns to 6;
				Set NVDLC03Enc.bEncounterChosen to 1;
				Return;			
			Elseif (NVDLC03Enc.iEncRand == 2)

			Endif
		Elseif (NVDLC03Enc.iBoxCall == 12)
			If (NVDLC03Enc.iEncRand == 1)
				; Lobotomite - 2, Nightstalker - 7
				Set rActor01 to NVDLC03EncLobotomite16REF;
				Set rActor02 to NVDLC03EncLobotomite17REF;
				Set rActor03 to NVDLC03EncNightstalker14REF;
				Set rActor04 to NVDLC03EncNightstalker15REF;
				Set rActor05 to NVDLC03EncNightstalker16REF;
				Set rActor06 to NVDLC03EncNightstalker17REF;
				Set rActor07 to NVDLC03EncNightstalker18REF;
				Set rActor08 to NVDLC03EncNightstalker19REF;
				Set rActor09 to NVDLC03EncNightstalker20REF;
				rActor01.Resurrect;
				rActor02.Resurrect;
				rActor03.Resurrect;
				rActor04.Resurrect;
				rActor05.Resurrect;
				rActor06.Resurrect;
				rActor07.Resurrect;
				rActor08.Resurrect;
				rActor09.Resurrect;
				Set NVDLC03Enc.iEncounterSpawns to 9;
				Set NVDLC03Enc.bEncounterChosen to 1;
				Return;			
			Elseif (NVDLC03Enc.iEncRand == 2)

			Endif
		Endif
	Endif

End

Scn	NVDLC03EncCodeHolderSCRIPT

; Script attached to Actor Zones (code boxes), from where to call variables, and events, in the Encounter Master Script (quest) according to player position, distance to code box, story-stage, etc.
; JS 2010
; SPW 01/05/11 Testing for DLC03
; SPW 01/11/11 Adding encounters based on volume
; SPW 01/18/11 Adding missing boxes
; JS 01/24/11 - Tweaking script to handle GetSelf calls without fail. Added code to ensure that changes to the logic of spawn-ordering from NVDLC02 preserves variable, and script reading, integrity
; in this NVDLC03 version of the random encounter, and event, systems I devised for NVDLC02.
; JS 01/24/11 - Added comments explaining what this script does, in detail.

;Encounter Positional Variables:

Ref	rSelf					; This will be the reference of the spawner-box running this code.
Ref	rMark01				; These Marks' refs are the XMarkerHeadings chain-linked to the spawner-box.
Ref	rMark02
Ref	rMark03
Ref	rMark04
Ref	rMark05
Ref	rMark06
Ref	rMark07
Ref	rMark08
Ref	rMark09

; Encounter Actors Variables:

Ref	rActor01				; These refs are actors defined in the NVDLC03EncListSCRIPT - different actors for each spawner-box.
Ref	rActor02
Ref	rActor03
Ref	rActor04
Ref	rActor05
Ref	rActor06
Ref	rActor07
Ref	rActor08
Ref	rActor09

Int		iEncounter			; The number of "Encounters" active. This will never be more than 2 - if it is, this code tells the NVDLC03EncSCRIPT (master script) to begin culling the old encounters.

; Operational Variables:

Short	bSpawn				; To control the teleportation, and enabling/resurrection, of encounter references (Actors).
Short	bCancel				; To control the event of cancelling/removing encounter's references (Actors).

Int		iState					; Defines the states of the code box. That is, it governs what to run according to player position, encounter chosen, order of operations, etc.
Int		iCount				; To assist in segmenting tasks within the particular iState logic.

Float	fDistance			; Retrieves distance from Player actor to code box.
Float	fEventDay			; Retrieves time when encounter spawns.
Float	fTimer				; To space operations in time, to prevent excessive toll on a given frame.
Float	fDelay				; Delay Timer


Begin OnLoad

	Set fDelay to 0.1;																		; When this codebox gets loaded for the first time, we associate its linked refs to the Mark refs declared above.
	Set rSelf to GetSelf;
	If (rSelf.GetLinkedRef != 0)
		Set rMark01 to rSelf.GetLinkedRef;
		If (rMark01.GetLinkedRef !=0)
			Set rMark02 to rMark01.GetLinkedRef;
			If (rMark02.GetLinkedRef != 0)
				Set rMark03 to rMark02.GetLinkedRef;
				If (rMark03.GetLinkedRef != 0)
					Set rMark04 to rMark03.GetLinkedRef;
					If (rMark04.GetLinkedRef != 0)
						Set rMark05 to rMark04.GetLinkedRef;
						If (rMark05.GetLinkedRef != 0)
							Set rMark06 to rMark05.GetLinkedRef;
							If (rMark06.GetLinkedRef != 0)
								Set rMark07 to rMark06.GetLinkedRef;
								If (rMark07.GetLinkedRef != 0)
									Set rMark08 to rMark07.GetLinkedRef;
									If (rMark08.GetLinkedRef != 0)
										Set rMark09 to rMark08.GetLinkedRef;
									Endif
								Endif
							Endif
						Endif
					Endif
				Endif
			Endif
		Endif
	Endif

End


Begin OnTriggerEnter Player

	If (NVDLC03Enc.bEncounterCleanUp == 0)											; When a TriggerEnter event is called on this box, through the NVDLC03EncSCRIPT (master), we check for
		Set fDelay to 0.1;																		; two different possible operations. 
		Set rSelf to GetSelf;																	; 1- for bEncounterCleanUp == 0, we perform regular spawning functions before enabling the actors.
		If (NVDLC03Enc.iEncounterSpawns > 0)											; We assign actor refs declared above, and defined in NVDLC03EncListSCRIPT,
			Set rActor01 to NVDLC03EncBox.rActor01;									; and we move these refs to this spawner-box's linked markers.
			rActor01.MoveTo rMark01;								
			If (NVDLC03Enc.iEncounterSpawns > 1)
				Set rActor02 to NVDLC03EncBox.rActor02;
				rActor02.MoveTo rMark02;
				If (NVDLC03Enc.iEncounterSpawns > 2)
					Set rActor03 to NVDLC03EncBox.rActor03;
					rActor03.MoveTo rMark03;
					If (NVDLC03Enc.iEncounterSpawns > 3)
						Set rActor04 to NVDLC03EncBox.rActor04;
						rActor04.MoveTo rMark04;
						If (NVDLC03Enc.iEncounterSpawns > 4)
							Set rActor05 to NVDLC03EncBox.rActor05;
							rActor05.MoveTo rMark05;
							If (NVDLC03Enc.iEncounterSpawns > 5)
								Set rActor06 to NVDLC03EncBox.rActor06;
								rActor06.MoveTo rMark06;
								If (NVDLC03Enc.iEncounterSpawns > 6)
									Set rActor07 to NVDLC03EncBox.rActor07;
									rActor07.MoveTo rMark07;
									If (NVDLC03Enc.iEncounterSpawns > 7)
										Set rActor08 to NVDLC03EncBox.rActor08;
										rActor08.MoveTo rMark08;
										If (NVDLC03Enc.iEncounterSpawns > 8)
											Set rActor09 to NVDLC03EncBox.rActor09;
											rActor09.MoveTo rMark09;
										Endif
									Endif
								Endif
							Endif
						Endif
					Endif
				Endif
			Endif
		Endif

		Set bSpawn to 1;																		; When bSpawn == 1 then the GameMode portion of this spawner-box's script will begin enabling the
		;ShowWarning "Actors Moved."													; actor refs we've declared, and defined, thus far.
		SetQuestDelay NVDLC03Enc 9;													; We slow down the master encounter script, since we don't need it to run its code until new spawner-boxes
		Return;																					; ask permission to spawn new things.

	Elseif (NVDLC03Enc.bEncounterCleanUp == 1)										; 2 - for bEncounterCleanUp == 1
		rActor01.Disable;																		; We disable declared actors (they belong only to this spawner-box), reset variables, and decrease the
		rActor02.Disable;																		; iEncounterNumber variable by 1, so that the last active spawner-box knows that it's safe to spawn new creatures.
		rActor03.Disable;
		rActor04.Disable;
		rActor05.Disable;
		rActor06.Disable;
		rActor07.Disable;
		rActor08.Disable;
		rActor09.Disable;
		SetQuestDelay NVDLC03Enc 9;													; Again, we can safely slow down the master script's update-delay. It will kick into gear when a spawner box tells it
		Set NVDLC03Enc.rBoxTwo to 0;													; that it's time to choose an encounter, or cull an old one.
		Set NVDLC03Enc.iEncounterTwo to 0;											; We reset operational variables, here.
		Set NVDLC03Enc.iEncounterEnd to 0;
		Set NVDLC03Enc.bEncounterCleanUp to 0

		If (NVDLC03Enc.iEncounterNumber >= 1)										; We decrease the integer that keeps track of the active encounters' number.
			Set NVDLC03Enc.iEncounterNumber to (NVDLC03Enc.iEncounterNumber - 1);
		Endif

		Set iState to 0;
	Endif

End


Begin GameMode

	If (NVDLC03Enc.bNoEnc == 0)															; If bNoEnc == 1, then we skip any GameMode code.
		If (iState == 0)																			; If this spawner-box is inactive (ready to spawn new creatures) 
			Set fDistance to (GetDistance Player);										; We calculate its distance to the player (defined in the master script: 7300 units).
			If (fDistance <= NVDLC03Enc.fRange)
				If (NVDLC03Enc.iEncounterNumber >= 2)								; If the number of active encounters is 2, or higher (which should never be, but just in case) then it's time to cull encounters
					If (Player.IsInCombat == 0)												; We make sure to not despawn anything if the player is in combat, to avoid attacking creatures from vanishing.
						If (NVDLC03Enc.iEncounterState == 0)							; If the master script is not busy doing something else, we continue.
							If (NVDLC03Enc.iEncounterEnd == 0)							; We check this var to only set the update delay once (we want the master script to run fast, for a couple of secs).
								SetQuestDelay NVDLC03Enc 0.1 ;fDelay;				; This delay may need to be smaller, since we need the quest script to act quickly once we ask to clean, or spawn, creatures. JS (01/24/11)
								Set NVDLC03Enc.iEncounterEnd to 1;					; This var gets set to 0 once the cleaning up of old encounters has taken place (in the TriggerEnter event, above).
							Endif
						Endif
					Endif
				Else																				; Else, if iEncounterNumber is less than 2, we are ready to spawn creatures from this spawner-box.
					If (NVDLC03Enc.iEncounterState == 0)								; We wait to make sure that the master script is not doing something else.
						If (rSelf == NVDLC03Enc.rBox01)									; We set the iBoxCall integer to the number of this box's ref ID.
							Set NVDLC03Enc.iBoxCall to 1;								; We'll use this iBoxCall value to determine what to spawn from the NVDLC03EncListSCRIPT.
						Elseif (rSelf == NVDLC03Enc.rBox02)
							Set NVDLC03Enc.iBoxCall to 2;
						Elseif (rSelf == NVDLC03Enc.rBox03)
							Set NVDLC03Enc.iBoxCall to 3;
						Elseif (rSelf == NVDLC03Enc.rBox04)
							Set NVDLC03Enc.iBoxCall to 4;
						Elseif (rSelf == NVDLC03Enc.rBox05)
							Set NVDLC03Enc.iBoxCall to 5;
						Elseif (rSelf == NVDLC03Enc.rBox06)
							Set NVDLC03Enc.iBoxCall to 6;
						Elseif (rSelf == NVDLC03Enc.rBox07)
							Set NVDLC03Enc.iBoxCall to 7;
						Elseif (rSelf == NVDLC03Enc.rBox08)
							Set NVDLC03Enc.iBoxCall to 8;
						Elseif (rSelf == NVDLC03Enc.rBox09)
							Set NVDLC03Enc.iBoxCall to 9;
						Elseif (rSelf == NVDLC03Enc.rBox10)
							Set NVDLC03Enc.iBoxCall to 10;
						Elseif (rSelf == NVDLC03Enc.rBox11)
							Set NVDLC03Enc.iBoxCall to 11;
						Elseif (rSelf == NVDLC03Enc.rBox12)
							Set NVDLC03Enc.iBoxCall to 12;
						Endif
						SetQuestDelay NVDLC03Enc 0.1 ;fDelay;	 				; Same as above - we want speed when spawning moar duadz. JS (01/24/11)
						Set NVDLC03Enc.rBoxSpawn to rSelf;						; This spawner-box's ref is set here to rBoxSpawn, so that the master script can call it to assign chosen actors through an EnterTrigger event.
						Set NVDLC03Enc.iEncounterState to 1;						; We tell the master script to get into "choose encounter" mode. No queries from any other script will go through it if this var is other than 0.
						Set NVDLC03Enc.iEncounterNumber to (NVDLC03Enc.iEncounterNumber + 1);	;Since we're going to spawn something once the master script tells the encounter script to proceed, we turn the encounter tracking var to +1.
						Set iState to 1;														; We tell this spawner-box to enter the next state in its GameMode checks, which will only run once it has been told to assign actors to its markers (EnterTrigger event, above).
					Endif
				Endif
			Endif

		Elseif (iState == 1)																	; If this spawner-box is ready to spawn stuff.

			If (bSpawn == 1)																; If an EnterTrigger event has been called on it (through the master script), that means all the actors are assigned to this box's markers.
				Set fTimer to (fTimer + GetSecondsPassed);						; We kick off a timer to avoid spawning everything on the same frame.
				If (iCount == 0)																; We spawn each creature in its own frame, with the aid of an operational counter var.
					rActor01.Enable;
					Set iCount to 1;
					Return;
				Elseif (iCount == 1 && fTimer >= fDelay)
					rActor02.Enable;
					Set iCount to 2;
					Return;
				Elseif (iCount == 2 && fTimer >= (fDelay * 2))
					rActor03.Enable;
					Set iCount to 3;
					Return;
				Elseif (iCount == 3 && fTimer >= (fDelay * 3))
					rActor04.Enable;
					Set iCount to 4;
					Return;
				Elseif (iCount == 4 && fTimer >= (fDelay * 4))
					rActor05.Enable;
					Set iCount to 5;
					Return;
				Elseif (iCount == 5 && fTimer >= (fDelay * 5))
					rActor06.Enable;
					Set iCount to 6;
					Return;
				Elseif (iCount == 6 && fTimer >= (fDelay * 6))
					rActor07.Enable;
					Set iCount to 7;
					Return;
				Elseif (iCount == 7 && fTimer >= (fDelay * 7))
					rActor08.Enable;
					Set iCount to 8;
					Return;
				Elseif (iCount == 8 && fTimer >= (fDelay * 8))
					rActor09.Enable;
					Set NVDLC03Enc.iEncounterSpawns to 0;						; We're done spawning, so we reset the system's vars, and the operational vars of this spawner-box.
					Set NVDLC03Enc.iEncounterState to 0;
					Set NVDLC03Enc.bEncounterChosen to 0;
					Set NVDLC03Enc.iCount to 0;
					Set fTimer to 0;
					Set iCount to 0;
					Set bSpawn to 0;
					Set iState to 2;															; iState is 2, to signify that this box has active encounters linked to it. The var will return to 0 once it has been told to
					;ShowWarning "Creatures Spawned"								; cull its actors from the active encounters.
					Return;
				Endif
			Endif
		Endif
	Endif

End

scn NVDLC03X8ExperimentQuestSCRIPT
;supports x8 experiments by checking variables related to creature deaths ;JDJ 01/03/2011 ;JDJ 01/24/2011 added section to check where dog dies and sets variable that will reset him when player leaves area ;JDJ 01/25/2011 added section to move dog back to x8main if they are not in x8main but their behavior is set to 1 ;JDJ 02/18/2011 scripting standards
Int nCount; ;Used to move cyberdog
BEGIN GameMode
If (NVDLC03X8SplicingTerminal.nCyberDogAlive == 1) && (NVDLC03X8SplicingTerminal.nBehaviorCyberDog == 1) && (X8CyberDogExperiment01.GetInCell NVDLC03X8Main == 0) && (X8CyberDogExperiment01.GetInSameCell Player == 0)
If (nCount == 0) X8CyberDogExperiment01.Disable; Set nCount to 1; Return; ElseIf (nCount == 1) X8CyberDogExperiment01.MoveTo X8DogSitMarker; Set nCount to 2; EndIf
If (nCount ==2) X8CyberDogExperiment01.Enable; Set nCount to 0; Return; EndIf
EndIf
If (NVDLC03X8SplicingTerminal.nCyberDogAlive == 2) If (X8CyberDogExperiment01.nDeathCell == 1) && (Player.GetInCell NVDLC03X8Main == 0) ;checks if the cyberdog is dead and active and the player is not in x8main
;disable, resurrect, and move cyberdog back to starting position X8CyberDogExperiment01.Disable; X8CyberDogExperiment01.Resurrect; X8CyberDogExperiment01.MoveTo X8TestResult01Marker;
;sets cyberdog behavior to first package and cyberdog to inactive state Set NVDLC03X8SplicingTerminal.nBehaviorCyberDog to 0; Set NVDLC03X8SplicingTerminal.nCyberDogAlive to 0;
;stop quest StopQuest NVDLC03X8ExperimentQuest; ElseIf (X8CyberDogExperiment01.nDeathCell == 2) && (Player.GetInCell NVDLC03X8School == 0) ;checks if the cyberdog is dead and active and the player is not in x8main
;disable, resurrect, and move cyberdog back to starting position X8CyberDogExperiment01.Disable; X8CyberDogExperiment01.Resurrect; X8CyberDogExperiment01.MoveTo X8TestResult01Marker;
;sets cyberdog behavior to first package and cyberdog to inactive state Set NVDLC03X8SplicingTerminal.nBehaviorCyberDog to 0; Set NVDLC03X8SplicingTerminal.nCyberDogAlive to 0;
;stop quest StopQuest NVDLC03X8ExperimentQuest; ElseIf (X8CyberDogExperiment01.nDeathCell == 3) && (Player.GetInCell NVDLC03X8Tunnel == 0) ;checks if the cyberdog is dead and active and the player is not in x8main
;disable, resurrect, and move cyberdog back to starting position X8CyberDogExperiment01.Disable; X8CyberDogExperiment01.Resurrect; X8CyberDogExperiment01.MoveTo X8TestResult01Marker;
;sets cyberdog behavior to first package and cyberdog to inactive state Set NVDLC03X8SplicingTerminal.nBehaviorCyberDog to 0; Set NVDLC03X8SplicingTerminal.nCyberDogAlive to 0;
;stop quest StopQuest NVDLC03X8ExperimentQuest; ElseIf (X8CyberDogExperiment01.nDeathCell == 4) && (Player.GetInCell NVDLC03X8Cave == 0) ;checks if the cyberdog is dead and active and the player is not in x8main
;disable, resurrect, and move cyberdog back to starting position X8CyberDogExperiment01.Disable; X8CyberDogExperiment01.Resurrect; X8CyberDogExperiment01.MoveTo X8TestResult01Marker;
;sets cyberdog behavior to first package and cyberdog to inactive state Set NVDLC03X8SplicingTerminal.nBehaviorCyberDog to 0; Set NVDLC03X8SplicingTerminal.nCyberDogAlive to 0;
;stop quest StopQuest NVDLC03X8ExperimentQuest; EndIf EndIf
END

scn NVDLC03X8CyberDogExperimentSCRIPT


;begins reset of the cyberdog experiment if the the cyberdog made in x8 dies
;JDJ 01/03/2011
;JDJ 01/24/2011 Adding variable and scripting to check which x8 cell the dog is in and resetting the cyberdog when the player leaves that cell
;JDJ 02/24/2011 added scripting to restore stats after combat
;JDJ 03/11/2011 scripting standards


Int nDeathCell;			;Checks to see what cell the dog was in when it died 0 if not dead, 1 if died in X8Main, 2 if died in X8School, 3 if died in X8Tunnel, 4 if died in X8Cave


BEGIN OnDeath

	If (X8CyberDogExperiment01.GetInCell NVDLC03X8Main == 1)
		Set nDeathCell to 1;
	ElseIf (X8CyberDogExperiment01.GetInCell NVDLC03X8School == 1)
		Set nDeathCell to 2;
	ElseIf (X8CyberDogExperiment01.GetInCell NVDLC03X8Tunnel == 1)
		Set nDeathCell to 3;
	ElseIf (X8CyberDogExperiment01.GetInCell NVDLC03X8Cave == 1)
		Set nDeathCell to 4;
	EndIf

	Set NVDLC03X8SplicingTerminal.nCyberDogAlive to 2;											;sets the cyberdog to active and dead state

END


BEGIN OnCombatEnd

	If (NVDLC03X8SplicingTerminal.nCyberDogAlive == 1)
		ResetHealth;
		RestoreActorValue PerceptionCondition 100;
		RestoreActorValue EnduranceCondition 100;
		RestoreActorValue LeftAttackCondition 100;
		RestoreActorValue LeftMobilityCondition 100;
		RestoreActorValue RightAttackCondition 100;
		RestoreActorValue RightMobilityCondition 100;
	EndIf

END

scn NVDLC03X8ExperimentSCRIPT


;controls the different experiments the player can do in the x8 splicing room
;JDJ 12/23/2010
;JDJ 01/14/2011 made updates to meet scripting standards
;JDJ 01/21/2011 added setally line to make the cyberdog a friend of the player, updates to meet scripting standards
;JDJ 01/25/2011 added line to start quest checking to see if cyberdog has been fired but is not in x8main cell
;JDJ 01/28/2011 adding variables to give player experience the first time they make the cyberdog and the dog human explosion
;JDJ 01/30/2011 Added experiment 4
;JDJ 01/31/2011 Moved Cleanup script from terminal result script to here
;JDJ 02/01/2011 Added flags to disable player controls command to make sure player is in first person
;JDJ 02/08/2011 found misplaced variable that was causing script to misfire
;JDJ 02/15/2011 added robobrain say line
;JDJ 02/16/2011 added challenges to experiments
;JDJ 02/24/2011 added block so that Roxie can be remade without leaving x8
;JDJ 03/11/2011 added reset to timer in cleanup sequence
;JDJ 04/04/2011 added a check on activate to make sure the bkill on robobrain is set properly


Int nExperiment;   					;holds the experiment the player selected to run in the terminal, 0 is nothing selected, 1 is dog and robot combination
Int nCount;							;count for splicing sequences, works with fTimer
Float fTimer;						;Timer for splicing sequences, works with nCount
Int nBehavior;						;switches between packages during splicing sequences
Int nBehaviorCyberDog;			;switches between packages for the cyberdog
Int bCleanUp;						;Area needs to be "cleaned up" before another experiment can be performed; 0 experiment can be performed, 1 clean up option appears in monitor and experiments don't
Int bCleanUpNow;					;Starts clean up sequence
Int nCyberDogAlive;				;checks if the cyberdog has been made and is still alive so experiment can't be rerun; 0 dog is inactive and experiment can be run, 1 dog is active and experiment can't be run, 2 cyberdog is dead but still active so experiment can't be run
Short nLastDay;					;tracks the last day a player ran an experiment to limit the number of experiments the player can perform, starting with a 3 experiments in 1 day ratio
Short nCurrentDay;				;tracks the current day to compare the last day the player ran an experiment
Int nExperimentCount	;			;tracks the number of experiments the player has run 1 day, if the player has performed 3 experiments in on day, the experiments can not be run until the next day
Int bFirstTime01;					;first time player does first experiment
Int bFirstTime02;					;first time player does second experiment
Int bFirstTime03;					;first time player does third experiment
Int bRobobrainFirst;				;set after the Lobotomite Robobrain says his first line
Int bChallenge01;					;gives player challenge for running the experiment
Int bChallenge02;					;gives player challenge for running the experiment
Int bChallenge03;					;gives player challenge for running the experiment


;OnActivate block checks to see if a day has passed and if so resets nExperimentCount
BEGIN OnActivate

	Set nCurrentDay to GameDaysPassed;

	If (X8RoboBrainExperiment01REF.GetDisabled == 1) && (bCleanUp == 1) && (X8RoboBrainExperiment01REF.bKill == 0)
		Set X8RoboBrainExperiment01REF.bKill to 1;
	EndIf

	If (X8RoboBrainExperiment01REF.GetDead == 1) && (bCleanUp == 1) && (X8RoboBrainExperiment01REF.bKill == 0)
		Set X8RoboBrainExperiment01REF.bKill to 1;
	EndIf

	If ((nCurrentDay - nLastDay) >= 1)
		Set nExperimentCount to 0;
		Activate;
	Else
		Activate;
	EndIf

END


;Gamemode block runs the selected experiment
BEGIN Gamemode

	;Experiment 1 splices a robot and a dog to make a cyberdog
	If (nExperiment == 1)

		Set fTimer to fTimer + GetSecondsPassed;												;sets up timer

		If (bFirstTime01 == 0)

			RewardXP 50;
			Set bFirstTime01 to 1;

		EndIf

		If (nCount == 0)																					; at 0, disables player controls and applies a fade out/fade in

			DisablePlayerControls 1 1 1 1 1 1 1;																	
			ApplyImageSpaceModifier FadeToBlackAndBackQuickISFX;
			Set nCount to 1;

			If (nCyberDogAlive == 2)
				;disable, resurrect, and move cyberdog back to starting position			
				X8CyberDogExperiment01.Disable;																														
				X8CyberDogExperiment01.Resurrect;
				X8CyberDogExperiment01.MoveTo X8TestResult01Marker;

				;sets cyberdog behavior to first package and cyberdog to inactive state
				Set nBehaviorCyberDog to 0;																							
				Set nCyberDogAlive to 0;
			EndIf

			Return;

		ElseIf (nCount == 1)																			;at 1, moves player to window and enables robot and dog out of sight

			If (fTimer >= 1)

				Player.MoveTo X8ExperimentViewMarker;
				X8MrHandyExperiment01REF.Enable;
				X8MrHandyExperiment01REF.SetGhost 1;
				X8DogExperiment01.Enable;
				X8DogExperiment01.SetGhost 1;
				Set nCount to 2;
				Return;

			EndIf

		ElseIf (nCount == 2)																				;at 2, moves robot and dog to points where the drop out of the pipes and sets the packages to stand below the pipes

			If (fTimer >= 3)

				X8MrHandyExperiment01REF.MoveTo X8TestSubject01aMarker;
				X8DogExperiment01.MoveTo X8TestSubject02aMarker;
				PlaySound ObjRobotForgeDrop

				Set nBehavior to 1;
				X8MrHandyExperiment01REF.ResetAI;
				X8DogExperiment01.ResetAI;
				Set nCount to 3;
				Return;

			EndIf

		ElseIf (nCount == 3)																				;at 3, applies image space modifier for experiment

			If (fTimer >= 5)
				ApplyImageSpaceModifier NVDLC03X8ExperimentFX;
				Set nCount to 4;
				Return;

			EndIf

		ElseIf (nCount == 4)																			;at 4, enables light beams in front of robot and dog

			X8ExperimentLightBeam01a.Enable;
			X8ExperimentLightBeam01b.Enable;

			If (fTimer >= 6)																				;at 4 with timer, enables third beam to cover cyberdog and disables dog and robot
				X8ExperimentLightBeam02a.Enable;	
				X8MrHandyExperiment01REF.Disable;
				X8DogExperiment01.Disable;
				Set nCount to 5;
				Return;

			EndIf

		ElseIf (nCount == 5)																			;at 5, enables cyberdog and sets nCyberDogAlive to 1 because he is active and alive

			X8CyberDogExperiment01.Enable;
			SetAlly NVDLC03X8PlayerFriendFaction PlayerFaction 1;						;Makes cyberdog a friend to the player
			Set nCyberDogAlive to 1;

			If (fTimer >= 8)
				X8ExperimentLightBeam01a.Disable 1;											;at 5 with timer,  disables two light beams below pipes
				X8ExperimentLightBeam01b.Disable 1;
				Set nCount to 6;
				Return;

			EndIf

		ElseIf (nCount == 6)																			;at 6, disables central light beam

			If (fTimer >= 8.5)

				X8ExperimentLightBeam02a.Disable 1;
				Set nCount to 7;

			EndIf

		ElseIf (nCount == 7)																		; at 7, sets cyberdog behavior

			If (fTimer >= 10.5)

				StartQuest NVDLC03X8ExperimentQuest;									;starts experiment quest which resets dog experiment when player leaves NVDLC01X8Main
				X8CyberDogExperiment01.SetIgnoreFriendlyHits 1;

				Set X8RoboBrainExperiment01REF.bKill to 1;

				Set bCleanUp to 1;
				EnablePlayerControls;															;Enable player controls

				X8MrHandyExperiment01REF.MoveTo X8TestSubject01cMarker;		;move robot and dog back to original starting locations
				X8DogExperiment01.MoveTo X8TestSubject02cMarker;	

				Set nLastDay to GameDaysPassed;											;reset all internal experiment variables, set day, add one to experiment count
				Set nExperimentCount to nExperimentCount + 1;
				Set nExperiment to 0;
				Set nBehavior to 0;
				Set nCount to 0;
				Set fTimer to 0;

			EndIf

		EndIf

	;experiment 2 splices a husk and a dog that explodes
	ElseIf (nExperiment == 2)

		Set fTimer to fTimer + GetSecondsPassed;											;set experiment timer

		If (bFirstTime02 == 0)

			RewardXP 50;
			Set bFirstTime02 to 1;

		EndIf

		If (nCount == 0)																				;at 0, disable player controls, apply fade out/fade in

			DisablePlayerControls 1 1 1 1 1 1 1;
			ApplyImageSpaceModifier FadeToBlackAndBackQuickISFX;
			Set nCount to 1;
			Return;

		ElseIf (nCount == 1)																		;at 1, move player to window, enable husk and dog out of sight

			If (fTimer >= 1)

				Player.MoveTo X8ExperimentViewMarker;
				X8HuskExperiment01REF.Enable;
				X8HuskExperiment01REF.SetGhost 1;
				X8DogExperiment01.Enable;
				X8DogExperiment01.SetGhost 1;
				Set nCount to 2;
				Return;

			EndIf

		ElseIf (nCount == 2)																		;at 2, move husk and dog to drop from pipe markers and apply correct packages to stand in place

			If (fTimer >= 3)

				X8HuskExperiment01REF.MoveTo X8TestSubject01aMarker;
				X8DogExperiment01.MoveTo X8TestSubject02aMarker;
				Set nBehavior to 1;
				X8HuskExperiment01REF.ResetAI;
				X8DogExperiment01.ResetAI;
				Set nCount to 3;
				PlaySound ObjRobotForgeDrop;
				Return;

			EndIf

		ElseIf (nCount == 3)																		;at 3, apply light beam image space modifier

			If (fTimer >= 5)
				ApplyImageSpaceModifier NVDLC03X8ExperimentFX;
				Set nCount to 4;
				Return;

			EndIf

		ElseIf (nCount == 4)																		;at 4, enable beams in front of husk and dog

			X8ExperimentLightBeam01a.Enable;
			X8ExperimentLightBeam01b.Enable;
			X8HuskExperiment01REF.SetGhost 0;
			X8DogExperiment01.SetGhost 0;

			If (fTimer >= 6)																			;at 4 with time, enable central experiment beam, kill, dismember, and move husk and dog to center beam,
				X8ExperimentLightBeam02a.Enable;
				X8HuskExperiment01REF.Kill X8HuskExperiment01REF 0 0;
				X8DogExperiment01.Kill X8DogExperiment01 1 0;
				X8DogExperiment01.Kill X8DogExperiment01 3 0;
				X8HuskExperiment01REF.MoveTo X8TestResult01Marker;
				X8DogExperiment01.MoveTo X8TestResult01Marker;
				Set nCount to 5;
				Return;

			EndIf

		ElseIf (nCount == 5)																	;at 5, disable small, side light beams

			If (fTimer >= 8)
				X8ExperimentLightBeam01a.Disable 1;
				X8ExperimentLightBeam01b.Disable 1;
				Set nCount to 6;
				Return;

			EndIf

		ElseIf (nCount == 6)																;at 6, disable central beam

			If (fTimer >= 8.5)

				X8ExperimentLightBeam02a.Disable 1;
				Set nCount to 7;

			Else

				Return;

			EndIf

		ElseIf (nCount == 7)

			If (fTimer >= 10)															;at 7, enable player controls, set clean up to 1 so player has to clean up dog and husk before running a new experiment

				If (bChallenge01 == 0)
					IncrementScriptedChallenge NVDLC03ChallengeMadScientist;
					Set bChallenge01 to 1;
				EndIf

				Set X8RoboBrainExperiment01REF.bKill to 1;

				Set bCleanUp to 1;
				EnablePlayerControls;													

				Set nLastDay to GameDaysPassed;								;set day passed, add 1 to the experiment count and reset internal experiment variables
				Set nExperimentCount to nExperimentCount + 1;
				Set nExperiment to 0;
				Set nBehavior to 0;
				Set nCount to 0;
				Set fTimer to 0;

			EndIf

		EndIf

	;experiment 3 splices a husk and a robot into a brainless robobrain
	ElseIf (nExperiment == 3)

		Set fTimer to fTimer + GetSecondsPassed;															;sets experiment timer

		If (nCount == 0)																								;at 0, disable player controls and apply a fade out/ fade in

			If (X8RoboBrainExperiment01REF.GetDead == 1)
				X8RoboBrainExperiment01REF.Resurrect;
			EndIf

			DisablePlayerControls 1 1 1 1 1 1 1;
			ApplyImageSpaceModifier FadeToBlackAndBackQuickISFX;
			Set nCount to 1;
			Return;

		ElseIf (nCount == 1)																						;at 1, move player to window, enable robot and husk out of sight

			If (fTimer >= 1)

				Player.MoveTo X8ExperimentViewMarker;
				X8MrHandyExperiment01REF.Enable;
				X8MrHandyExperiment01REF.SetGhost 1;
				X8HuskExperiment01REF.Enable;
				X8HuskExperiment01REF.SetGhost 1;
				Set nCount to 2;
				Return;

			EndIf

		ElseIf (nCount == 2)																					;at 2, move robot and husk to drop markers and apply packages for them to stand there

			If (fTimer >= 3)

				X8MrHandyExperiment01REF.MoveTo X8TestSubject01aMarker;
				X8HuskExperiment01REF.MoveTo X8TestSubject02aMarker;
				Set nBehavior to 1;
				X8HuskExperiment01REF.ResetAI;
				X8MrHandyExperiment01REF.ResetAI;
				Set nCount to 3;
				PlaySound ObjRobotForgeDrop;
				Return;

			EndIf

		ElseIf (nCount == 3)																					;at 3, apply light beam image space modifier

			If (fTimer >= 5)
				ApplyImageSpaceModifier NVDLC03X8ExperimentFX;
				Set nCount to 4;
				Return;

			EndIf

		ElseIf (nCount == 4)																					;at 4, enable beams in front of husk and robot

			X8ExperimentLightBeam01a.Enable;
			X8ExperimentLightBeam01b.Enable;

			If (fTimer >= 6)
				X8ExperimentLightBeam02a.Enable;														;at 4 with time, enable central light beam an disable husk and robot
				X8HuskExperiment01REF.Disable;
				X8MrHandyExperiment01REF.Disable;
				Set nCount to 5;
				Return;

			EndIf

		ElseIf (nCount == 5)																					;at 5, enable robobrain

			X8RoboBrainExperiment01REF.Enable;

			If (fTimer >= 8)																						;at 5 with time, disable side light beams
				X8ExperimentLightBeam01a.Disable 1;
				X8ExperimentLightBeam01b.Disable 1;
				Set nCount to 6;
				Return;

			EndIf

		ElseIf (nCount == 6)																					;at 6, disable central light beam

			If (fTimer >= 8.5)

				X8ExperimentLightBeam02a.Disable 1;
				Set nCount to 7;

			EndIf

		ElseIf (nCount == 7)																				;at 7 set clean up to 1 so player must clean up robobrain before performing new experiment, enable player controls

			If (fTimer >= 10)

				If (bRobobrainFirst == 0)
					X8RoboBrainExperiment01REF.Say NVDLC03X8RobobrainBorn01 1;
				ElseIf (bRobobrainFirst == 1)
					X8RoboBrainExperiment01REF.Say NVDLC03X8RobobrainBorn02 1;
				EndIf

				If (bChallenge02 == 0)
					IncrementScriptedChallenge NVDLC03ChallengeEvilGenius;
					Set bChallenge02 to 1;
				EndIf

				Set bCleanUp to 1;
				EnablePlayerControls;

				Set nBehavior to 0;																		;apply new package to robobrain
				X8RoboBrainExperiment01REF.ResetAI;

				X8MrHandyExperiment01REF.MoveTo X8TestSubject01cMarker;				;move husk and robot back to start points
				X8HuskExperiment01REF.MoveTo X8TestSubject03cMarker;

				Set nLastDay to GameDaysPassed;													;set day, add one to experiment count, reset experiment internal variables
				Set nExperimentCount to nExperimentCount + 1;
				Set nExperiment to 0;
				Set nCount to 0;
				Set fTimer to 0;

			EndIf

		EndIf

	;experiment 4 splices a nightstalker and a robot into a brainless robobrain
	ElseIf (nExperiment == 4)

		Set fTimer to fTimer + GetSecondsPassed;															;sets experiment timer

		If (bFirstTime03 == 0)

			RewardXP 50;
			Set bFirstTime03 to 1;

		EndIf

		If (nCount == 0)																								;at 0, disable player controls and apply a fade out/ fade in

			DisablePlayerControls 1 1 1 1 1 1 1;
			ApplyImageSpaceModifier FadeToBlackAndBackQuickISFX;
			Set nCount to 1;
			Return;

		ElseIf (nCount == 1)																						;at 1, move player to window, enable robot and husk out of sight

			If (fTimer >= 1)

				Player.MoveTo X8ExperimentViewMarker;
				X8MrHandyExperiment01REF.Enable;
				X8MrHandyExperiment01REF.SetGhost 1;
				X8NighstalkerExperiment01REF.Enable;
				X8NighstalkerExperiment01REF.SetGhost 1;
				Set nCount to 2;
				Return;

			EndIf

		ElseIf (nCount == 2)																					;at 2, move robot and husk to drop markers and apply packages for them to stand there

			If (fTimer >= 3)

				X8MrHandyExperiment01REF.MoveTo X8TestSubject01aMarker;
				X8NighstalkerExperiment01REF.MoveTo X8TestSubject02aMarker;
				Set nBehavior to 1;
				X8NighstalkerExperiment01REF.ResetAI;
				X8MrHandyExperiment01REF.ResetAI;
				Set nCount to 3;
				PlaySound ObjRobotForgeDrop;
				Return;

			EndIf

		ElseIf (nCount == 3)																					;at 3, apply light beam image space modifier

			If (fTimer >= 5)
				ApplyImageSpaceModifier NVDLC03X8ExperimentFX;
				Set nCount to 4;
				Return;
			EndIf

		ElseIf (nCount == 4)																					;at 4, enable beams in front of husk and robot

			X8ExperimentLightBeam01a.Enable;
			X8ExperimentLightBeam01b.Enable;

			If (fTimer >= 6)
				X8ExperimentLightBeam02a.Enable;														;at 4 with time, enable central light beam an disable husk and robot
				X8NighstalkerExperiment01REF.Disable;
				X8MrHandyExperiment01REF.Disable;
				Set nCount to 5;
				Return;

			EndIf

		ElseIf (nCount == 5)																					;at 5, enable robobrain

			X8RoboBrainExperiment02REF.Enable;

			If (fTimer >= 8)																						;at 5 with time, disable side light beams
				X8ExperimentLightBeam01a.Disable 1;
				X8ExperimentLightBeam01b.Disable 1;
				Set nCount to 6;
				Return;

			EndIf

		ElseIf (nCount == 6)																					;at 6, disable central light beam

			If (fTimer >= 8.5)

				X8ExperimentLightBeam02a.Disable 1;
				Set nCount to 7;

			EndIf

		ElseIf (nCount == 7)																				;at 7 set clean up to 1 so player must clean up robobrain before performing new experiment, enable player controls

			If (fTimer >= 10)

				Set X8RoboBrainExperiment01REF.bKill to 1;

				Set bCleanUp to 1;
				EnablePlayerControls;

				If (bChallenge03 == 0)
					IncrementScriptedChallenge NVDLC03ChallengeAutomaton;
					Set bChallenge03 to 1;
				EndIf


				Set nBehavior to 0;																		;apply new package to robobrain
				X8RoboBrainExperiment02REF.ResetAI;

				X8MrHandyExperiment01REF.MoveTo X8TestSubject01cMarker;				;move husk and robot back to start points
				X8NighstalkerExperiment01REF.MoveTo X8TestSubject04cMarker;

				Set nLastDay to GameDaysPassed;													;set day, add one to experiment count, reset experiment internal variables
				Set nExperimentCount to nExperimentCount + 1;
				Set nExperiment to 0;
				Set nCount to 0;
				Set fTimer to 0;

			EndIf

		EndIf

	ElseIf (bCleanUpNow == 1)

		Set fTimer to fTimer + GetSecondsPassed;		

		If (nCount == 0)
			ApplyImageSpaceModifier FadeToBlackAndBackQuickISFX;
			Set nCount to 1;
			Return;
		Elseif (nCount == 1) && (fTimer >= 1)

			If (X8DogExperiment01.GetDisabled == 0)
				X8DogExperiment01.Disable;
				X8DogExperiment01.Resurrect;
				X8DogExperiment01.MoveTo X8TestSubject02cMarker;
			EndIf

			If (X8RoboBrainExperiment02REF.GetDisabled == 0)
				X8RoboBrainExperiment02REF.Disable;
				X8RoboBrainExperiment02REF.Resurrect;
       			X8RoboBrainExperiment02REF.MoveTo X8TestResult01Marker;
			EndIf

			If (X8RoboBrainExperiment01REF.GetDisabled == 0)
				X8RoboBrainExperiment01REF.Disable;
				X8RoboBrainExperiment01REF.Resurrect;
				X8RoboBrainExperiment01REF.MoveTo X8TestResult01Marker;
			EndIf

			If (X8RoboBrainExperiment01REF.bSayLine01 != 0)
				Set X8RoboBrainExperiment01REF.bSayLine01 to 0;
			EndIf

			If (X8RoboBrainExperiment01REF.bSayLine02 != 0)
				Set X8RoboBrainExperiment01REF.bSayLine02 to 0;
			EndIf

			If (X8RoboBrainExperiment01REF.bKill != 0)
				Set X8RoboBrainExperiment01REF.bKill to 0;
			EndIf

			If (X8RoboBrainExperiment01REF.bDeath != 0)
				Set X8RoboBrainExperiment01REF.bDeath to 0;
			EndIf

			If (X8RoboBrainExperiment01REF.fDeathTimer != 0)
				Set X8RoboBrainExperiment01REF.fDeathTimer to 0;
			EndIf

			If (X8MrHandyExperiment01REF.GetDisabled == 0)
				X8MrHandyExperiment01REF.Disable;
				X8MrHandyExperiment01REF.Resurrect;
				X8MrHandyExperiment01REF.MoveTo X8TestSubject01cMarker;
			EndIf

			If (X8HuskExperiment01REF.GetDisabled == 0)
				X8HuskExperiment01REF.Disable;
				X8HuskExperiment01REF.Resurrect;
				X8HuskExperiment01REF.MoveTo X8TestSubject03cMarker;
			EndIf

			If (nCyberdogAlive != 0) && (nBehaviorCyberdog == 0)
				X8CyberDogExperiment01.Disable;
				If (nCyberDogAlive == 2)
					X8CyberDogExperiment01.Resurrect;
				EndIf
				X8CyberDogExperiment01.MoveTo X8TestResult01Marker;
 				Set nCyberDogAlive to 0;
			EndIf

			Set bCleanUp to 0;
			Set bCleanUpNow to 0;
			Set nCount to 0;
			Set fTimer to 0;

		EndIf

	EndIf

END

scn NVDLC03GenericPerkQuestSCRIPT

; This script handles all scripted actions/variables needed for perks in NVDLC03.
; ETB 12/22/10 

; ETB 3/25/11 	- Added Implant Y-7/Implant Y-3/Heartless functionality.
; TLS 04/12/2011 - Accounting for player's Rad Resist value in Y-3 Implant
; TLS 04/22/2011 - Fixed Y-3 implant so it will work if you only have one drink in your inventory.
; CES 05/31/11 - Reworked GRX implant to work better.

; Perk "Implant GRX" Description: "You gain a non-additive subdermal Turbo (chem) injector. Rank 2 increases the Turbo effect from 3 to 5 seconds and the uses per day from 5 to 10."
Short nImplantGRXRank									; This perk has multiple ranks - which are tracked by this variable and used in "NVDLC03GenericPerkQUEST" --> Quest Stages <result scripts>.
Short nImplantGRXUses									; Tracks the number of uses for the GRX "Turbo" Implant.
Short nEndOfDay											; Set to gamehour - when fEndofDay >= 23 it's time to reset the GRX Implant per day uses.
Short nLastDay											; Saves the last day GRX was added
Short nCurrentDay											; Saves current day.

; Perk "Implant Y-3"
Short bSetRadsTotal 
Short nRadsTotal
Short nRadsCurrent
Short nRadsToHeal
; ----------------------------------------------------	
Short bY3SetPlayerDrink3RadTotal 
Short nY3PlayerDrink3RadTotal 
Short nY3PlayerDrink3RadCurrent 
; ----------------------------------------------------	
Short bY3SetPlayerDrink6RadTotal 
Short nY3PlayerDrink6RadTotal 
Short nY3PlayerDrink6RadCurrent 
; ----------------------------------------------------	
Short bY3SetPlayerDrink10RadTotal 
Short nY3PlayerDrink10RadTotal 
Short nY3PlayerDrink10RadCurrent 
; ----------------------------------------------------	
Short bY3SetPlayerDrink16RadTotal 
Short nY3PlayerDrink16RadTotal 
Short nY3PlayerDrink16RadCurrent 

; Perk "Implant Y-7"
Short bY7SetPlayerFoodTotal 
Short nY7PlayerFoodTotal 
Short nY7PlayerFoodCurrent 

; Perk "Heartless"/"Cardiac Arrest"
Short bSetHealthTotal 
Short nHealthTotal 
Short nHealthCurrent
; ----------------------------------------------------	
Short bHeartlessSetPlayerChemsTotal 
Short nHeartlessPlayerChemsTotal 
Short nHeartlessPlayerChemsCurrent 
; ----------------------------------------------------	
Short bHeartlessSetPlayerStimsTotal 
Short nHeartlessPlayerStimsTotal 
Short nHeartlessPlayerStimsCurrent 

; Challenges
Short bKleinDestineCompleted							; Only fires once to complete the "Klein Destine" challenge.
Short bMobiusStripCompleted							; Only fires once to complete the "Mobius Strip" challenge.


BEGIN GameMode

; -------------------------------------------------------------------	PERKS -------------------------------------------------------------------------------------	|

; ---------------------------	Perk: Implant GRX -------------------	\/ 	; ETB 12/22/10 Resets the "Uses" counter for the GRX Implant.
																	; [NOTE: Any changes here must be dupicated in the menumode block.]

	If ( Player.HasPerk NVDLC03ImplantGRXPerk == 1 )
		Set nCurrentDay to GameDaysPassed;
		If ( nCurrentDay - nLastDay >= 1 )
			If ( NVDLC03GenericPerkQUEST.nImplantGRXRank == 1 )
				Player.RemoveItem NVDLC03ImplantGRX 5 1
				Player.AddItem NVDLC03ImplantGRX 5
				Set nLastDay to GameDaysPassed;
			Elseif ( NVDLC03GenericPerkQUEST.nImplantGRXRank == 2 )
				Player.RemoveItem NVDLC03ImplantGRX 10 1
				Player.AddItem NVDLC03ImplantGRX 10
				Set nLastDay to GameDaysPassed;
			Endif
		Endif
	Endif

; ---------------------------	Perk: Implant Y-3 ---------------------	\/ 	; ETB 3/25/11 Restores Radiation damage whenever irradated liquids are consumed.
																	; [NOTE: Any changes here must be dupicated in the MenuMode block.]

	If ( Player.HasPerk NVDLC03ImplantY3Perk == 1 )
		If ( bSetRadsTotal == 0 )
			Set bSetRadsTotal to 1;
			Set nRadsTotal to ( Player.GetAV RadiationRads );
		Endif
; ------------	3 Rad Drinks ------------	\/
		If ( Player.GetItemCount NVDLC03PerkRelatedDrinks3RadLIST >= 0 );
			If ( bY3SetPlayerDrink3RadTotal == 0 )
				Set bY3SetPlayerDrink3RadTotal to 1;
				Set nY3PlayerDrink3RadTotal to ( Player.GetItemCount NVDLC03PerkRelatedDrinks3RadLIST );
			Endif
			If ( bY3SetPlayerDrink3RadTotal == 1 )
				Set nY3PlayerDrink3RadCurrent to ( Player.GetItemCount NVDLC03PerkRelatedDrinks3RadLIST );
				Set nRadsCurrent to ( Player.GetAV RadiationRads );
				If ( nY3PlayerDrink3RadCurrent < nY3PlayerDrink3RadTotal )
					Set bY3SetPlayerDrink3RadTotal to 0;
					Set bSetRadsTotal to 0;
					If ( nRadsCurrent > nRadsTotal )
						Set nRadsToHeal to ((1 - (Player.GetActorValue RadResist / 100)) * 3)
						Player.RestoreActorValue RadiationRads nRadsToHeal;		
						ShowMessage NVDLC03Y3ImplantPerkRadRemovedMSG;
					Endif
				Elseif ( nY3PlayerDrink3RadCurrent > nY3PlayerDrink3RadTotal )
					Set bY3SetPlayerDrink3RadTotal to 0;
					Set bSetRadsTotal to 0;
				Endif
			Endif
		Endif
; ------------	6 Rad Drinks ------------	\/
		If ( Player.GetItemCount NVDLC03PerkRelatedDrinks6RadLIST >= 0 );
			If ( bY3SetPlayerDrink6RadTotal == 0 )
				Set bY3SetPlayerDrink6RadTotal to 1;
				Set nY3PlayerDrink6RadTotal to ( Player.GetItemCount NVDLC03PerkRelatedDrinks6RadLIST );
			Endif
			If ( bY3SetPlayerDrink6RadTotal == 1 )
				Set nY3PlayerDrink6RadCurrent to ( Player.GetItemCount NVDLC03PerkRelatedDrinks6RadLIST );
				Set nRadsCurrent to ( Player.GetAV RadiationRads );
				If ( nY3PlayerDrink6RadCurrent < nY3PlayerDrink6RadTotal )
					Set bY3SetPlayerDrink6RadTotal to 0;
					Set bSetRadsTotal to 0;
					If ( nRadsCurrent > nRadsTotal )
						Set nRadsToHeal to ((1 - (Player.GetActorValue RadResist / 100)) * 6)
						Player.RestoreActorValue RadiationRads nRadsToHeal;		
						ShowMessage NVDLC03Y3ImplantPerkRadRemovedMSG;
					Endif
				Elseif ( nY3PlayerDrink6RadCurrent > nY3PlayerDrink6RadTotal )
					Set bY3SetPlayerDrink6RadTotal to 0;
					Set bSetRadsTotal to 0;
				Endif
			Endif
		Endif
; ------------	10 Rad Drinks ----------	\/
		If ( Player.GetItemCount NVDLC03PerkRelatedDrinks10RadLIST >= 0 );
			If ( bY3SetPlayerDrink10RadTotal == 0 )
				Set bY3SetPlayerDrink10RadTotal to 1;
				Set nY3PlayerDrink10RadTotal to ( Player.GetItemCount NVDLC03PerkRelatedDrinks10RadLIST );
			Endif
			If ( bY3SetPlayerDrink10RadTotal == 1 )
				Set nY3PlayerDrink10RadCurrent to ( Player.GetItemCount NVDLC03PerkRelatedDrinks10RadLIST );
				Set nRadsCurrent to ( Player.GetAV RadiationRads );
				If ( nY3PlayerDrink10RadCurrent < nY3PlayerDrink10RadTotal )
					Set bY3SetPlayerDrink10RadTotal to 0;
					Set bSetRadsTotal to 0;
					If ( nRadsCurrent > nRadsTotal )
						Set nRadsToHeal to ((1 - (Player.GetActorValue RadResist / 100)) * 10)
						Player.RestoreActorValue RadiationRads nRadsToHeal;		
						ShowMessage NVDLC03Y3ImplantPerkRadRemovedMSG;
					Endif
				Elseif ( nY3PlayerDrink10RadCurrent > nY3PlayerDrink10RadTotal )
					Set bY3SetPlayerDrink10RadTotal to 0;
					Set bSetRadsTotal to 0;
				Endif
			Endif
		Endif
; ------------	16 Rad Drinks ----------	\/
		If ( Player.GetItemCount NVDLC03PerkRelatedDrinks16RadLIST >= 0 );
			If ( bY3SetPlayerDrink16RadTotal == 0 )
				Set bY3SetPlayerDrink16RadTotal to 1;
				Set nY3PlayerDrink16RadTotal to ( Player.GetItemCount NVDLC03PerkRelatedDrinks16RadLIST );
			Endif
			If ( bY3SetPlayerDrink16RadTotal == 1 )
				Set nY3PlayerDrink16RadCurrent to ( Player.GetItemCount NVDLC03PerkRelatedDrinks16RadLIST );
				Set nRadsCurrent to ( Player.GetAV RadiationRads );
				If ( nY3PlayerDrink16RadCurrent < nY3PlayerDrink16RadTotal )
					Set bY3SetPlayerDrink16RadTotal to 0;
					Set bSetRadsTotal to 0;
					If ( nRadsCurrent > nRadsTotal )
						Set nRadsToHeal to ((1 - (Player.GetActorValue RadResist / 100)) * 16)
						Player.RestoreActorValue RadiationRads nRadsToHeal;		
						ShowMessage NVDLC03Y3ImplantPerkRadRemovedMSG;
					Endif
				Elseif ( nY3PlayerDrink16RadCurrent > nY3PlayerDrink16RadTotal )
					Set bY3SetPlayerDrink16RadTotal to 0;
					Set bSetRadsTotal to 0;
				Endif
			Endif
		Endif

	Endif
; ---------------------------	Perk: Implant Y-7 ---------------------	\/ 	; ETB 3/25/11 Adds AP/Health when food is consumed.
																	; [NOTE: Any changes here must be dupicated in the menumode block.]	

	If ( Player.HasPerk NVDLC03ImplantY7Perk == 1 )
		If ( bY7SetPlayerFoodTotal == 0 )
			Set bY7SetPlayerFoodTotal to 1;
			Set nY7PlayerFoodTotal to ( Player.GetItemCount NVDLC03PerkRelatedAllFoodLIST );
		Endif
		If ( bY7SetPlayerFoodTotal == 1 )
			Set nY7PlayerFoodCurrent to ( Player.GetItemCount NVDLC03PerkRelatedAllFoodLIST );
			If ( nY7PlayerFoodCurrent < nY7PlayerFoodTotal )
				Set bY7SetPlayerFoodTotal to 0;
				If ( Player.HasMagicEffect RestoreHealth )
					Player.RestoreActorValue ActionPoints 2;
					Player.RestoreActorValue Health 5;
				Endif
			Elseif ( nY7PlayerFoodCurrent > nY7PlayerFoodTotal )	
				Set bY7SetPlayerFoodTotal to 0;
			Endif
		Endif
	Endif

; ---------------------------	Perk: Brainless -----------------------	\/ 	; ETB 3/7/11Keeps Player's head from getting crippled.

	If ( Player.HasPerk NVDLC03BrainlessPerk == 1 )
		If ( Player.GetActorValue PerceptionCondition < 100 )
			Player.RestoreActorValue PerceptionCondition 100;
		Endif
		If ( Player.GetActorValue BrainCondition < 100 )
			Player.RestoreActorValue BrainCondition 100;
		Endif
	Endif

; ---------------------------	Perk: Big-Brained -----------------------	\/ 	; TLS 04/27/2011Keeps Player's head from getting crippled.

	If ( Player.HasPerk NVDLC03BigBrainedPerk == 1 )
		If ( Player.GetActorValue PerceptionCondition < 100 )
			Player.RestoreActorValue PerceptionCondition 100;
		Endif
		If ( Player.GetActorValue BrainCondition < 100 )
			Player.RestoreActorValue BrainCondition 100;
		Endif
	Endif


; -----------------	Perk: Heartless/ Cardiac Arrest	---------	\/ 	; ETB 3/25/11Adds health when chems/stimpaks are used.
																	; [NOTE: Any changes here must be dupicated in the menumode block.]

	If ( Player.HasPerk NVDLC03HeartlessPerk == 1 || Player.HasPerk NVDLC03CardiacArrestPerk == 1 )
		If ( bSetHealthTotal == 0 )
			Set bSetHealthTotal to 1;
			Set nHealthTotal to ( Player.GetAV Health );
		Endif
; -----------------	Chems ------------	\/
		If ( bHeartlessSetPlayerChemsTotal == 0 )
			Set nHeartlessPlayerChemsTotal to ( Player.GetItemCount NVDLC03PerkRelatedAllChemsLIST );
			Set bHeartlessSetPlayerChemsTotal to 1;
		Endif
		If ( bHeartlessSetPlayerChemsTotal == 1 )
			Set nHeartlessPlayerChemsCurrent to ( Player.GetItemCount NVDLC03PerkRelatedAllChemsLIST );
			Set nHealthCurrent to ( Player.GetAV Health );
			If ( nHeartlessPlayerChemsCurrent < nHeartlessPlayerChemsTotal )
				If ( nHealthCurrent > nHealthTotal )
					If ( Player.HasPerk NVDLC03HeartlessPerk == 1 )
						Player.RestoreActorValue Health 5		
					Elseif ( Player.HasPerk NVDLC03CardiacArrestPerk == 1 )	
						Player.RestoreActorValue Health 7
					Endif
				Endif
				Set bHeartlessSetPlayerChemsTotal to 0;
				Set bSetHealthTotal to 0;
			Elseif ( nHeartlessPlayerChemsCurrent > nHeartlessPlayerChemsTotal )
				Set bHeartlessSetPlayerChemsTotal to 0;
				Set bSetHealthTotal to 0;
			Endif
		Endif
; -----------------	Stims --------------	\/
		If ( bHeartlessSetPlayerStimsTotal == 0 )
			Set bHeartlessSetPlayerStimsTotal to 1;
			Set nHeartlessPlayerStimsTotal to ( Player.GetItemCount NVDLC03PerkRelatedAllStimpaksLIST );
		Endif
		If ( bHeartlessSetPlayerStimsTotal == 1 )
			Set nHeartlessPlayerStimsCurrent to ( Player.GetItemCount NVDLC03PerkRelatedAllStimpaksLIST );
			Set nHealthCurrent to ( Player.GetAV Health );
			If ( nHeartlessPlayerStimsCurrent < nHeartlessPlayerStimsTotal )
				Set bHeartlessSetPlayerStimsTotal to 0;	
				Set bSetHealthTotal to 0;
				If ( nHealthCurrent > nHealthTotal )
					If ( Player.HasPerk NVDLC03HeartlessPerk == 1 )
						Player.RestoreActorValue Health 10		
					Elseif ( Player.HasPerk NVDLC03CardiacArrestPerk == 1 )	
						Player.RestoreActorValue Health 15
					Endif		
				Endif
			Elseif ( nHeartlessPlayerStimsCurrent > nHeartlessPlayerStimsTotal )
				Set bHeartlessSetPlayerStimsTotal to 0;	
				Set bSetHealthTotal to 0;
			Endif
		Endif
	Endif

; ---------------------------	Perk: Spineless -----------------------	\/ 	; ETB 3/7/11Keeps Player's spine from getting crippled.

	If ( Player.HasPerk NVDLC03SpinelessPerk )
		If ( Player.GetActorValue EnduranceCondition < 100 )
			Player.RestoreActorValue EnduranceCondition 100;
		Endif
	Endif

; -------------------------------------------------------------------	CHALLENGES --------------------------------------------------------------------------	|

; ---------------------------	Challenge: Klein Destine ----------------------------	\/

	If ( bKleinDestineCompleted == 0 )
		If ( Player.GetEquipped NVDLC03GlassesDrKlein == 1 && Player.GetEquipped NVDLC03scientistscrubgreen && Player.GetEquipped NVDLC03WeapGlovesDrKleinL )
			IncrementScriptedChallenge NVDLC03ChallengeKleinDestine;
			Set bKleinDestineCompleted to 1;
		Endif
	Endif

; ---------------------------	Challenge: Mobius Strip -----------------------------	\/

	If ( bMobiusStripCompleted == 0 )
		If ( Player.GetEquipped NVDLC03GlassesDrMobius == 1 && Player.GetEquipped NVDLC03scientistscrubred && Player.GetEquipped NVDLC03WeapGlovesDrMobiusL)
			IncrementScriptedChallenge NVDLC03ChallengeMobiusStrip;
			Set bMobiusStripCompleted to 1;
		Endif
	Endif

END; gamemode



BEGIN MenuMode

; -------------------------------------------------------------------	PERKS -------------------------------------------------------------------------------------	|

; ---------------------------	Perk: Implant GRX -------------------	\/ 	; ETB 12/22/10 Resets the "Uses" counter for the GRX Implant.
																	; [NOTE: Any changes here must be dupicated in the GameMode block.]

	If ( Player.HasPerk NVDLC03ImplantGRXPerk == 1 )
		Set nCurrentDay to GameDaysPassed;
		If ( nCurrentDay - nLastDay >= 1 )
			If ( NVDLC03GenericPerkQUEST.nImplantGRXRank == 1 )
				Player.RemoveItem NVDLC03ImplantGRX 5 1
				Player.AddItem NVDLC03ImplantGRX 5 1
				Set nLastDay to GameDaysPassed;
			Elseif ( NVDLC03GenericPerkQUEST.nImplantGRXRank == 2 )
				Player.RemoveItem NVDLC03ImplantGRX 10 1
				Player.AddItem NVDLC03ImplantGRX 10 1
				Set nLastDay to GameDaysPassed;
			Endif
		Endif
	Endif

; ---------------------------	Perk: Implant Y-3 ---------------------	\/ 	; ETB 3/25/11 Restores Radiation damage whenever irradated liquids are consumed.
																	; [NOTE: Any changes here must be dupicated in the menumode block.]

	If ( Player.HasPerk NVDLC03ImplantY3Perk == 1 )
		If ( bSetRadsTotal == 0 )
			Set bSetRadsTotal to 1;
			Set nRadsTotal to ( Player.GetAV RadiationRads );
		Endif
