					Player.RestoreActorValue ActionPoints 2;
					Player.RestoreActorValue Health 5;
				Endif
			Elseif ( nY7PlayerFoodCurrent > nY7PlayerFoodTotal )	
				Set bY7SetPlayerFoodTotal to 0;
			Endif
		Endif
	Endif

