scn NVDLC03X8CodeBoxSCRIPT


;enables and disables cyberdogs and nightstalkers in x8 test areas
;works with x8terminal01 and x8terminal02
;JDJ 12/21/2010
;JDJ 01/04/2011 added door resets to school
;JDJ 01/16/2011 added test 2 information block
;JDJ 01/21/2011 added block that checks to see if scorpions are dead and removes follow package from dog
;JDJ 01/31/2011 removed close to school door 01
;JDJ 02/11/2011 added forcefields that appear in advanced test
;JDJ 02/18/2011 scripting standards
;JDJ 02/24/2011added scripting to move terminals and forcefields in version 3 and 4
;JDJ 03/11/2011 scripting standards
;JDJ 03/21/2011 moving door sound cue from terminal to this script
;JDJ 03/23/2011 adding a variable to play the door reset sound when the residential test is activated


Int bResurrect;                      ;used to reset the area, 0 is default mode, 1 is set at terminal when player enters test area
Int bSpawn;						;0= spawn cyberdogs, 1= spawn nightstalkers
Int nTestRunning;				;Determines what test the player has selected to run, 1 if test 1, 2 if test 2
Int bResidentialTest;			;changed to 1 when player activates the residential test to play the sound


BEGIN GameMode

	If (bResidentialTest == 1)

		PlaySound NVDLC03ObjActiveRoom;
		Set bResidentialTest to 0;
	EndIf		

	If (bResurrect == 1)

		If (X8SchoolDoor07.bDoOnce != 0)
			Set X8SchoolDoor07.bDoOnce to 0;
		EndIf
		If (X8SchoolDoor10.bDoOnce != 0)
			Set X8SchoolDoor10.bDoOnce to 0;
		EndIf

		If (X8SchoolIntercomREF.nBark05 != 0)
			Set X8SchoolIntercomREF.nBark05 to 0;
		EndIf
		If (X8SchoolIntercomREF.nBark07 != 0)
			Set X8SchoolIntercomREF.nBark07 to 0;
		EndIf

		NVDLC03X8TurretTerminal01.Lock 2;

		NVDLC03X8TurretTerminal02.Lock 1;

		If (X8SchoolTurret01.GetDead == 1)
			X8SchoolTurret01.Resurrect;
			X8SchoolTurret01.SetDestroyed 0;
		EndIf

		If (X8SchoolTurret02.GetDead == 1)
			X8SchoolTurret02.Resurrect;
			X8SchoolTurret02.SetDestroyed 0;
		EndIf

		PlaySound NVDLC03ObjActiveRoom;

		If (nTestRunning == 1)																				;If the player picked the first test

			If (bSpawn == 0)

				;enables dogs in school
				If (X8SchoolDogParentMarker.GetDisabled == 1)
					X8SchoolDogParentMarker.Enable;
				EndIf

				If (X8SchoolDog04REF.GetDisabled == 1)
					X8SchoolDog04REF.Enable;
				EndIf
				If (X8SchoolDog06REF.GetDisabled == 1)
					X8SchoolDog06REF.Enable;
				EndIf
				If (X8TunnelEntryTrigger.bTestOne == 0)
					If (X8SchoolDog07REF.GetDisabled == 0)
						X8SchoolDog07REF.Disable;
					EndIf
				Else
					If (X8SchoolDog07REF.GetDisabled == 1)
						X8SchoolDog07REF.Enable;
					EndIf
				EndIf

				If (X8SchoolTurret01.GetDisabled == 1)
					X8SchoolTurret01.Enable;
					X8SchoolTurret01.Resurrect;
					X8SchoolTurret01.SetDestroyed 0;
					X8SchoolTurret01.SetUnconscious 0;
				EndIf

				If (X8SchoolTurret02.GetDisabled == 1)
					X8SchoolTurret02.Enable;
					X8SchoolTurret02.Resurrect;
					X8SchoolTurret02.SetDestroyed 0;
					X8SchoolTurret02.SetUnconscious 0;
				EndIf
				If (X8SchoolTurret03.GetDisabled == 0)
					X8SchoolTurret03.Disable;
					X8SchoolTurret03.Resurrect;
					X8SchoolTurret03.SetDestroyed 0;
					X8SchoolTurret03.SetUnconscious 0;
				EndIf

				;disables nightstalkers in school
				If (X8SchoolNightstalkerParentMarker.GetDisabled == 0)
					X8SchoolNightstalkerParentMarker.Disable;
				EndIf

				If (X8SchoolProtectron06REF.GetDisabled == 0)
					X8SchoolProtectron06REF.Disable;
				EndIf
				If (X8SchoolProtectron04REF.GetDisabled == 0)
					X8SchoolProtectron04REF.Disable;
				EndIf

				If (X8SchoolNightstalker04REF.GetDisabled == 0)
					X8SchoolNightstalker04REF.Disable;
				EndIf
				If (X8SchoolNightstalker06REF.GetDisabled == 0)
					X8SchoolNightstalker06REF.Disable;
				EndIf

				If (X8SchoolExitNSParentMarker.GetDisabled == 0)
					X8SchoolExitNSParentMarker.Disable;
				EndIf
				If (X8SchoolExitParentMarker.GetDisabled == 0)
					X8SchoolExitParentMarker.Disable;
				EndIf

				;resurrects all dogs in school
				X8SchoolDog02REF.Resurrect;
				X8SchoolDog03REF.Resurrect;
				X8SchoolDog04REF.Resurrect;
				X8SchoolDog05REF.Resurrect;
				X8SchoolDog06REF.Resurrect;
				X8SchoolDog07REF.Resurrect;
				X8SchoolDog08REF.Resurrect;
				X8SchoolDog09REF.Resurrect;
				X8SchoolDog10REF.Resurrect;
				X8SchoolDog12REF.Resurrect;
				X8SchoolDog16REF.Resurrect;

				;moves dogs to patrol start points
				X8SchoolDog02REF.MoveTo X8SchoolPatrol02Marker;
				X8SchoolDog03REF.MoveTo X8SchoolPatrol03Marker;
				X8SchoolDog04REF.MoveTo X8SchoolPatrol04Marker;
				X8SchoolDog05REF.MoveTo X8SchoolPatrol05Marker;
				X8SchoolDog06REF.MoveTo X8SchoolPatrol06Marker;
				X8SchoolDog07REF.MoveTo X8SchoolPatrol07Marker;
				X8SchoolDog08REF.MoveTo X8SchoolPatrol08Marker;
				X8SchoolDog09REF.MoveTo X8SchoolPatrol09Marker;
				X8SchoolDog10REF.MoveTo X8SchoolPatrol10Marker;
				X8SchoolDog12REF.MoveTo X8SchoolPatrol12Marker;
				X8SchoolDog16REF.MoveTo X8SchoolPatrol16Marker;
				X8SchoolTurret01.MoveTo X8SchoolTurretMarker01;
				X8SchoolTurret02.MoveTo X8SchoolTurretMarker02;

				X8SchoolDogTrigger01.Enable;													;Resets behavior triggers and scripts
				X8SchoolDogTrigger02.Enable;
				Set X8SchoolDoor08.bBehavior to 0;
				Set X8SchoolDogTrigger01.bBehavior to 0;

				;If statement checks all doors in school that begin closed to make sure they are closed, and, if not, closes them
				If (X8SchoolDoor01 != 1)
					X8SchoolDoor01.SetOpenState 1;
				EndIf
				If (X8SchoolDoor02 != 3)
					X8SchoolDoor02.SetOpenState 0;
				EndIf
				If (X8SchoolDoor03 != 1)
					X8SchoolDoor03.SetOpenState 1;
				EndIf
				If (X8SchoolDoor04 != 3)
					X8SchoolDoor04.SetOpenState 0;
				EndIf
				If (X8SchoolDoor06 != 3)
					X8SchoolDoor06.SetOpenState 0;
				EndIf
				If (X8SchoolDoor07 != 3)
					X8SchoolDoor07.SetOpenState 0;
				EndIf
				If (X8SchoolDoor08 != 3)
					X8SchoolDoor08.SetOpenState 0;
				EndIf
				If (X8SchoolDoor09 != 3)
					X8SchoolDoor09.SetOpenState 0;
				EndIf
				If (X8SchoolDoor10 != 3)
					 X8SchoolDoor10.SetOpenState 0;
				EndIf
				If (X8SchoolDoor11 != 3)
