				X8SchoolProtectron06REF.Resurrect;
				X8SchoolNightstalker07REF.Resurrect;
				X8SchoolNightstalker08REF.Resurrect;
				X8SchoolNightstalker09REF.Resurrect;
				X8SchoolNightstalker10REF.Resurrect;
				X8SchoolNightstalker12REF.Resurrect;
				X8SchoolNightstalker16REF.Resurrect;
				X8SchoolTurret03.Resurrect;

				X8SchoolTurret03.SetDestroyed 0;				

				;moves all nightstalkers and protectrons back to there patrol start points
				X8SchoolNightstalker02REF.MoveTo X8SchoolPatrol02Marker;
				X8SchoolNightstalker03REF.MoveTo X8SchoolPatrol03Marker;
				X8SchoolProtectron04REF.MoveTo X8SchoolPatrol04Marker;
				X8SchoolNightstalker05REF.MoveTo X8SchoolPatrol05Marker;
				X8SchoolProtectron06REF.MoveTo X8SchoolPatrol06Marker;
				X8SchoolNightstalker07REF.MoveTo X8SchoolPatrol07Marker;
				X8SchoolNightstalker08REF.MoveTo X8SchoolPatrol08Marker;
				X8SchoolNightstalker09REF.MoveTo X8SchoolPatrol09Marker;
				X8SchoolNightstalker10REF.MoveTo X8SchoolPatrol10Marker;
				X8SchoolNightstalker12REF.MoveTo X8SchoolPatrol12Marker;
				X8SchoolNightstalker16REF.MoveTo X8SchoolPatrol16Marker;
				X8SchoolTurret01.MoveTo X8SchoolTurretMarker01;
				X8SchoolTurret02.MoveTo X8SchoolTurretMarker02;
				X8SchoolTurret03.MoveTo X8SchoolTurretMarker03;

				X8SchoolDogTrigger01.Enable;													;Resets behavior triggers and scripts
				X8SchoolDogTrigger02.Enable;
				Set X8SchoolDoor08.bBehavior to 0;
				Set X8SchoolDogTrigger01.bBehavior to 0;

				;If statement checks all doors in school that begin closed to make sure they are closed, and, if not, closes them
				If (X8SchoolDoor01 != 1)
					X8SchoolDoor01.SetOpenState 1;
				EndIf
				If (X8SchoolDoor02 != 3)
					X8SchoolDoor02.SetOpenState 0;			
				EndIf
				If (X8SchoolDoor04 != 3)
					X8SchoolDoor04.SetOpenState 0;
				EndIf
				If (X8SchoolDoor06 != 3)
					X8SchoolDoor06.SetOpenState 0;
				EndIf
				If (X8SchoolDoor07 != 3)
					X8SchoolDoor07.SetOpenState 0;
				EndIf
				If (X8SchoolDoor08 != 3)
					X8SchoolDoor08.SetOpenState 0;
				EndIf
				If (X8SchoolDoor09 != 3)
					X8SchoolDoor09.SetOpenState 0;
				EndIf
				If (X8SchoolDoor10 != 3)
					 X8SchoolDoor10.SetOpenState 0;
				EndIf
				If (X8SchoolDoor11 != 3)
					 X8SchoolDoor11.SetOpenState 0;
				EndIf

				If (NVDLC03X8SchoolFField01.GetDisabled == 1)										;Reset Forcefields, 3 is now disabled so player can get gun
					NVDLC03X8SchoolFField01.Enable;
				EndIf
				If (NVDLC03X8SchoolFField02.GetDisabled == 1)
					NVDLC03X8SchoolFField02.Enable;
				EndIf
				If (NVDLC03X8SchoolFField04.GetDisabled == 1)
					NVDLC03X8SchoolFField04.Enable;
				EndIf
				If (NVDLC03X8SchoolFField05.GetDisabled == 1)
					NVDLC03X8SchoolFField05.Enable;
				EndIf
				If (NVDLC03X8SchoolFField06.GetDisabled == 1)
					NVDLC03X8SchoolFField06.Enable;
				EndIf
				If (NVDLC03X8SchoolFField07.GetDisabled == 0)
					NVDLC03X8SchoolFField07.Disable;
				EndIf
				If (NVDLC03X8SchoolFField08.GetDisabled == 0)
					NVDLC03X8SchoolFField08.Disable;
				EndIf
				If (NVDLC03X8SchoolFField09.GetDisabled == 1)
					NVDLC03X8SchoolFField09.Enable;
				EndIf
				If (NVDLC03X8SchoolFField10.GetDisabled == 1)
					NVDLC03X8SchoolFField10.Enable;
				EndIf
				If (NVDLC03X8SchoolFField11.GetDisabled == 1)
					NVDLC03X8SchoolFField11.Enable;
				EndIf
				If (NVDLC03X8SchoolFField12.GetDisabled == 1)
					NVDLC03X8SchoolFField12.Enable;
				EndIf
				If (NVDLC03X8SchoolFField14.GetDisabled == 1)
					NVDLC03X8SchoolFField14.Enable;
				EndIf
				If (NVDLC03X8SchoolFField15.GetDisabled == 0)
					NVDLC03X8SchoolFField15.Disable;
				EndIf
				If (NVDLC03X8SchoolFField16.GetDisabled == 0)
					NVDLC03X8SchoolFField16.Disable;
				EndIf
				If (NVDLC03X8SchoolFField17.GetDisabled == 0)
					NVDLC03X8SchoolFField17.Disable;
				EndIf
				If (NVDLC03X8SchoolFField18.GetDisabled == 1)
					NVDLC03X8SchoolFField18.Enable;
				EndIf
				If (NVDLC03X8SchoolFField19.GetDisabled == 1)
					NVDLC03X8SchoolFField19.Enable;
				EndIf
				If (NVDLC03X8SchoolFField20.GetDisabled == 1)
					NVDLC03X8SchoolFField20.Enable;
				EndIf

				If (X8TurretKillTrigger.GetDisabled == 1)														;Resets X8TurretKillTrigger
					X8TurretKillTrigger.Enable;
				EndIf

				If (X8SchoolTurret01.GetUnconscious == 1)													;Resets Turrets if they have been deactivated
						X8SchoolTurret01.SetUnconscious 0;
				EndIf
				If (X8SchoolTurret02.GetUnconscious == 1)
						X8SchoolTurret02.SetUnconscious 0;
				EndIf
				If (X8SchoolTurret03.GetUnconscious == 1)
						X8SchoolTurret03.SetUnconscious 0;
				EndIf

				If (X8SchoolTurret01.GetActorValue BrainCondition < 100)								;Resets Turrets if targeting has been reconfigured
						X8SchoolTurret01.SetActorValue BrainCondition  100;
				EndIf
				If (X8SchoolTurret02.GetActorValue BrainCondition < 100)
						X8SchoolTurret02.SetActorValue BrainCondition  100;
				EndIf
				If (X8SchoolTurret03.GetActorValue BrainCondition < 100)
						X8SchoolTurret03.SetActorValue BrainCondition  100;
				EndIf

				If (X8SchoolTerminal04REF.GetDisabled == 1)												;Enabling and Disabling Proper Terminals
					X8SchoolTerminal04REF.Enable;
				EndIf

				If (X8SchoolTerminal06REF.GetDisabled == 1)
					X8SchoolTerminal06REF.Enable;
				EndIf

				If (X8SchoolTerminal03REF.GetDisabled == 1)
					X8SchoolTerminal03REF.Enable;
				EndIf

				If (X8SchoolTerminal01REF.GetDisabled == 0)
					X8SchoolTerminal01REF.Disable;
				EndIf

				If (X8SchoolTerminal05REF.GetDisabled == 0)
					X8SchoolTerminal05REF.Disable;
				EndIf

				If (X8SchoolTerminal02REF.GetDisabled == 0)
					X8SchoolTerminal02REF.Disable;
				EndIf

				X8SchoolTerminal06REF.Unlock;																;Unlock Terminals
				X8SchoolTerminal04REF.Unlock;
				X8SchoolTerminal03REF.Unlock;
				X8SchoolTerminalTest2REF.Unlock;

				Set bResurrect to 0;															;end set up

			EndIf

		EndIf

	EndIf

END


BEGIN GameMode

	If (NVDLC03X8SplicingTerminal.nCyberDogAlive == 1) && (NVDLC03X8SplicingTerminal.nBehaviorCyberDog == 3)		;if the cyberdog from the experiment is alive and following player

		;if all scorpions are dead
		If (X8MainScorpion01.GetDead == 1) && (X8MainScorpion02.GetDead == 1) && (X8MainScorpion03.GetDead == 1) && (X8MainScorpion04.GetDead == 1) && (X8MainScorpion05.GetDead == 1)

			;set dog to hang out at his idle marker
			Set NVDLC03X8SplicingTerminal.nBehaviorCyberDog to 1;	
			X8CyberDogExperiment01.EvaluatePackage;

		EndIf
	EndIf

END

scn NVDLC03X8TunnelEntrySCRIPT


;script to set door lock and unlocks when player enters x8 tunnels
;JDJ 12/16/2010
;JDJ 01/14/2011 formatting corrections,  added lines to reset test for test one, update quest for test one and remove notes
;JDJ 01/17/2011 added lines to reset test for test two, update quest for test two and remove notes
;JDJ 01/19/2011 added quest reset lines
;JDJ 01/20/2011 Added blocks that check for the nightstalker quest, added line to reset script delay
;JDJ 01/26/2011 added scripting that updates nvdlcx8setup to better match quest in x13
;JDJ 01/27/2011 added scripting that updates nvdlcx8setup to better match quest in x13
;JDJ 02/02/2011 klaxons play animation
;JDJ 02/04/2011 added resets to school terminal variables, added variable to switch classroom cyber dog package for wild wasteland
;JDJ 02/11/2011 added disabling of lobotmites and moved start of x8 project quest to here
;JDJ 02/18/2011 scripting standards
;JDJ 02/24/2011 adding sonic emitter quest update, added a variable to track if they've replayed the tests
;JDJ 03/11/2011 scripting standards updated; changed objective after completing the advanced test to point to the kennels info terminal in the x-8 tunnels


Int bCount; 					;lock between x8 tunnel and school, 0 = unlocked, 1 = locked
Int bFirst;   					; lock between x8 tunnel and main, 0= locked, 1 = unlocked
Int bTestOne;				;if 0 the player has not completed test 1, if 1 the player has completed test 1 at least once
Int bTestTwo;				;if 0 the player has not completed test 2, if 1 the player has completed test 2 at least once
Int bDogBehavior;			;if 0 not wild wasteland, if 1 wild wasteland
Int nStart to 1;				;Starts script when player enters trigger
Int bTestOneReplay;		;0 if the player has not replayed test 1, 1 if they have
Int bTestTwoReplay;		;0 fi the player has not replayed test 2, 1 if they have		


BEGIN OnTrigger Player

	If (nStart == 0)
		Set nStart to 1;
	EndIf

END


BEGIN GameMode

	If (nStart == 1)

		X8Klaxon01.PlayGroup Forward 1;
		X8Klaxon02.PlayGroup Forward 1;
		X8TestDoorKlaxonLight01.Enable;
		X8TestDoorKlaxonLight02.Enable;
		X8TestDoorSchoolLight.Disable;
		X8TestDoorLockedLight.Enable;

		Set NVDLC03SchoolEntryTrigger.bTerminal01 to 0;							;Resets lockout variable on school terminals
		Set NVDLC03SchoolEntryTrigger.bTerminal02 to 0;
		Set NVDLC03SchoolEntryTrigger.bTerminal03 to 0;

		Set X8SchoolIntercomREF.bFirstBark to 0;										;Replay the first line of Borous's main school bark each time the player enters

		If (Player.HasPerk WildWasteland == 1) && (bDogBehavior ==0)			;switches cyberdog package to playing poker if player has wild wasteland
			Set bDogBehavior to 1;
			X8TunnelCyberDog01.ResetAI;
			X8TunnelCyberDog02.ResetAI;
			X8TunnelCyberDog03.ResetAI;
			X8TunnelCyberDog04.ResetAI;
			X8TunnelCyberDog05.ResetAI;
			X8TunnelCyberDog01.Moveto X8DogPokerMarker01;
			X8TunnelCyberDog02.Moveto X8DogPokerMarker02;
			X8TunnelCyberDog03.Moveto X8DogPokerMarker03;
			X8TunnelCyberDog04.Moveto X8DogPokerMarker04;
			X8TunnelCyberDog05.Moveto X8DogPokerMarker05;
		ElseIf (Player.HasPerk WildWasteland == 0) && (bDogBehavior == 1)
			Set bDogBehavior to 0;
			X8TunnelCyberDog01.ResetAI;
			X8TunnelCyberDog02.ResetAI;
			X8TunnelCyberDog03.ResetAI;
			X8TunnelCyberDog04.ResetAI;
			X8TunnelCyberDog05.ResetAI;
			X8TunnelCyberDog01.Moveto X8DogBlackBoardMarker01;
			X8TunnelCyberDog02.Moveto X8DogBlackBoardMarker02;
			X8TunnelCyberDog03.Moveto X8DogBlackBoardMarker03;
			X8TunnelCyberDog04.Moveto X8DogBlackBoardMarker04;
			X8TunnelCyberDog05.Moveto X8DogBlackBoardMarker05;
		EndIf

		;unlocks the back door between the main area and the tunnels the first time the player completes the school test area
		If (bFirst == 0)
			NVDLC03X8TunneltoMain01.Unlock;
			X8SchoolLobotomite01REF.Disable;
			X8SchoolLobotomite02REF.Disable;
			X8SchoolLobotomite03REF.Disable;
			Set bFirst to 1;
		EndIf

		;locks the door behind the player between the tunnels and the school and makes exit objective completed
		If (bCount == 0)
			NVDLC03X8TunneltoSchool.Lock;
			Set bCount to 1;
		EndIf

		; JSH 01/05/11 - Updates NVDLC03MQ03 the first time the player completes the test.
		If (GetStageDone NVDLC03MQ03 40 == 0)
			SetStage NVDLC03MQ03 40;
		EndIf

		If (GetStageDone NVDLC03SonicEmitterQuest 50 == 0)
			SetStage NVDLC03SonicEmitterQuest 50;
		EndIf

		If (GetQuestCompleted NVDLC03X8Setup == 0)

			If (X8MainCodeBox.nTestRunning == 1)
				If (bTestOne == 0)
					Set NVDLC03SchoolEntryTrigger.nTalk to 1;
					X8TunnelIntercom.SayTo Player NVDLC03BorousX8Tunnel01 1;
					IncrementScriptedChallenge NVDLC03ChallengeX8Testing01;
					StartQuest NVDLC03X8Setup;										;Start Quest
					SetObjectiveCompleted NVDLC03X8Setup 20 1;
					SetObjectiveDisplayed NVDLC03X8Setup 25 1;
					SetQuestDelay NVDLC03MQ03 0;								;set quest delay back to default
					Set bTestOne to 1;													;player has completed version 1 of test so version 2 is now available

				ElseIf (X8MainCodeBox.bSpawn == 0) && (GetObjectiveCompleted NVDLC03X8Setup 140 == 1)

					If (bTestOneReplay == 0)
						Set bTestOneReplay to 1;
					EndIf

					SetObjectiveCompleted NVDLC03X8Setup 150 1;					
				EndIf
				;if the player completes test 1 with nightstalkers for the first time, it updates teh nResearchCompleted variable in the nightstalker DNAgent Perk Quest
				If (X8MainCodeBox.bSpawn == 1)

					If (bTestOneReplay == 0)
						Set bTestOneReplay to 1;
					EndIf

					If (NVDLC03X8NightstalkerQuest.nX8TestOne == 0)
						SetObjectiveCompleted NVDLC03X8Setup 90 1;

						If (NVDLC03X8NightstalkerQuest.nX8TestTwo == 0)
							SetObjectiveDisplayed NVDLC03X8Setup 100 1;
						Else 
							IncrementScriptedChallenge NVDLC03ChallengeX8Testing03;
							SetObjectiveDisplayed NVDLC03X8Setup 160 1;

						EndIf

						Set NVDLC03X8NightstalkerQuest.nResearchCompleted to (NVDLC03X8NightstalkerQuest.nResearchCompleted + 1);
						Set NVDLC03X8NightstalkerQuest.nX8TestOne to 1;

					Else
						If (NVDLC03X8NightstalkerQuest.nX8TestTwo == 0)
							If (GetObjectiveCompleted NVDLC03X8Setup 100 == 1)
								SetObjectiveDisplayed NVDLC03X8Setup 105 0;
								SetObjectiveCompleted NVDLC03X8Setup 100 0;
							EndIf
							SetObjectiveDisplayed NVDLC03X8Setup 100 1;
						EndIf
						SetObjectiveCompleted NVDLC03X8Setup 150 1;
					EndIf
				EndIf

				RemoveNote NVDLC03X8SchoolGrades;					;Removes notes from player so player can run the test again
				RemoveNote NVDLC03X8SchoolSchedule;
				RemoveNote NVDLC03X8SchoolRecord;
				Set NVDLC03X8Setup.bRecords to 0;

				Set X8MainCodeBox.nTestRunning to 0;						;sets nTestRunning to nuetral, no test is running
				SetQuestDelay NVDLC03X8Setup 0;							;set quest delay back to default

			EndIf

			If (X8MainCodeBox.nTestRunning == 2)
				If (bTestTwo == 0)
					IncrementScriptedChallenge NVDLC03ChallengeX8Testing02;
					SetObjectiveCompleted NVDLC03X8Setup 50 1;
					If (GetObjectiveDisplayed NVDLC03X8Setup 55 == 0)
						SetObjectiveDisplayed NVDLC03X8Setup 52 1;
					EndIf
					Set bTestTwo to 1;												;player has completed version 1 of test so version 2 is now available

				ElseIf (X8MainCodeBox.bSpawn == 0) && (bTestTwo == 1)
					If (bTestTwoReplay == 0)
						Set bTestTwoReplay to 1;
					EndIf
					SetObjectiveCompleted NVDLC03X8Setup 150 1;				
				EndIf

				;if the player completes test 2 with nightstalkers for the first time, it updates teh nResearchCompleted variable in the nightstalker DNAgent Perk Quest
				If (X8MainCodeBox.bSpawn == 1)

					If (bTestTwoReplay == 0)
						Set bTestTwoReplay to 1;
					EndIf

					If (NVDLC03X8NightstalkerQuest.nX8TestTwo == 0)
						SetObjectiveCompleted NVDLC03X8Setup 120 1;
						If (NVDLC03X8NightstalkerQuest.nX8TestOne == 0)
							If (GetObjectiveCompleted NVDLC03X8Setup 70 == 1)
								SetObjectiveDisplayed NVDLC03X8Setup 75 0;
								SetObjectiveCompleted NVDLC03X8Setup 70 0;
							EndIf
							SetObjectiveDisplayed NVDLC03X8Setup 70 1;
						Else
							IncrementScriptedChallenge NVDLC03ChallengeX8Testing03;
							SetObjectiveDisplayed NVDLC03X8Setup 160 1; 
						EndIf

						Set NVDLC03X8NightstalkerQuest.nResearchCompleted to (NVDLC03X8NightstalkerQuest.nResearchCompleted + 1);
						Set NVDLC03X8NightstalkerQuest.nX8TestTwo to 1;

					Else
						SetObjectiveCompleted NVDLC03X8Setup 150 1;
						If (NVDLC03X8NightstalkerQuest.nX8TestOne == 0)
							If (GetObjectiveCompleted NVDLC03X8Setup 70 == 1)
								SetObjectiveDisplayed NVDLC03X8Setup 75 0;
								SetObjectiveCompleted NVDLC03X8Setup 70 0;
							EndIf
							SetObjectiveDisplayed NVDLC03X8Setup 70 1;
						EndIf
					EndIf
				EndIf

				RemoveNote NVDLC03X8SchoolGrades;					;Removes notes from player so player can run the test again
				RemoveNote NVDLC03X8SchoolSchedule;
				RemoveNote NVDLC03X8SchoolRecord;
				Set NVDLC03X8Setup.bRecords to 0;

				Set X8MainCodeBox.nTestRunning to 0;						;sets nTestRunning to nuetral, no test is running

				SetQuestDelay NVDLC03X8Setup 0;							;set quest delay back to default

			EndIf

		ElseIf (GetQuestCompleted NVDLC03X8Setup == 1)

			If (bTestOneReplay == 0)
				Set bTestOneReplay to 1;
			EndIf

			If (bTestTwoReplay == 0)
				Set bTestTwoReplay to 1;
			EndIf

			SetObjectiveCompleted NVDLC03X8Replay 20 1;

			RemoveNote NVDLC03X8SchoolGrades;					;Removes notes from player so player can run the test again
			RemoveNote NVDLC03X8SchoolSchedule;
			RemoveNote NVDLC03X8SchoolRecord;
			Set NVDLC03X8Setup.bRecords to 0;

			Set X8MainCodeBox.nTestRunning to 0;						;sets nTestRunning to nuetral, no test is running

			ResetQuest NVDLC03X8Replay;								;set quest delay back to default

		EndIf

		Set nStart to 2;

	EndIf

END

scn NVDLC03X13LaserTripwireSCRIPT

; Tripwire script for the laser beam part of laser tripwires in X13
; RLee, 1/5/11

Ref rSelf;			; The tripwire
Ref rActor;			; Actor entering the tripwire
Ref rLinked;			; Linked XMarker enable parent for disabling multiple tripwires when one is hit


BEGIN OnTriggerEnter
	Set rSelf to GetSelf;
	Set rActor to getActionRef;

	If (rActor.hasPerk LightStep == 0) && (rActor == Player) && (NVDLC03X13Setup.nTest > 0)

		ShowMessage NVDLC03X13TrippedMsg;
		PlaySound3D TRPTripwireDeactivate;

		If (NVDLC03X13Setup.bCheat == 0) && (X13Robot1REF.GetDead) && (X13Robot2REF.GetDead) && (X13Robot3REF.GetDead) && (GetQuestCompleted NVDLC03X13Setup == 0)
			NVDLC03StealthSuitVoiceCrREF.Say StealthsuitX13RobotsBlownUpBark 1 NVDLC03StealthSuitVoiceCr 1;
			Set NVDLC03X13Setup.bCheat to 1;
		ElseIf (NVDLC03X13Replay.bCheat == 0) && (X13Robot1REF.GetDead) && (X13Robot2REF.GetDead) && (X13Robot3REF.GetDead) && (GetQuestCompleted NVDLC03X13Setup == 1)
			NVDLC03StealthSuitVoiceCrREF.Say StealthsuitX13RobotsBlownUpBark 1 NVDLC03StealthSuitVoiceCr 1;
			Set NVDLC03X13Replay.bCheat to 1;
		Else
			NVDLC03StealthSuitVoiceCrREF.Say StealthsuitX13TripwireDetectionBark 1 NVDLC03StealthSuitVoiceCr 1;	;Tell player they were detected by a tripwire
		EndIf

		Set NVDLC03X13Setup.bDetected to 1;		;Set the current stealth run failed and trigger the objective swap

		Disable;
	EndIf

END

scn NVDLC03X13SetupSCRIPT

; Main X13 control quest used for changing level settings
; RLee 1/6/11
; JDJ 03/03/2011 - removed robot compliance perk after completing the robot compliance test

Short bOnce;

Short iTripwireRSkill;			; skill required to disable tripwires with Repair
Short iTripwireSSkill;			; skill required to disable tripwires with Science
Short iMineSkill;				; skill required to disable mines
Short iTechMineSkill;			; skill required to disable tech mines
Short iDoorSkill;				; skill required to freeze doors open so they cannot lock
Short iLiftSkill;					; skill required to activate the lift
Short iDefaultSkill;			; placeholder default value for skill tests

Short bDetected;				; used to direct the player back to the terminal when detected
Short nTest;						; indicator variable to check which test is running, 1 = basic infiltration, 2 = advanced, 3 = expert, 4 = compliance
Short nBasicReplay;			; incremented upon failure to control suit barks
Short nAdvancedReplay;		;
Short nExpertReplay;			;
Short nRobotCompReplay;	;
Short bCheat;					; bark controller for destroying the robots before the first test
Short bStalkers;				; gets set when nightstalkers are spawned.
Float fBarkTimer;				; timer to delay suit barks after popup message
Short bBark;					; holder variable to run suit bark


BEGIN GameMode

	If (Player.HasPerk RoboticsExpert == 1) && (Player.HasPerk NVDLC03X13RobotCompliance == 1)
		Player.RemovePerk NVDLC03X13RobotCompliance;
	EndIf

	If (GetQuestCompleted NVDLC03X13Setup == 0)
		If (bOnce == 0)						;Initial setup 
			Set iTripwireRSkill to 50;
			Set iTripwireSSkill to 50;
			Set iMineSkill to 25;
			Set iTechMineSkill to 50;
			Set iDoorSkill to 50;
			Set iLiftSkill to 25;
			Set iDefaultSkill to 25;
			Set bOnce to 1;
		EndIf

		If (nTest > 0) && (GetObjectiveDisplayed NVDLC03X13Setup 5 == 0) && (bDetected == 0)

			If (X13Robot1REF.GetDetected Player) && (X13Robot1REF.GetDead == 0)

				ShowMessage NVDLC03X13SpottedMsg;
				Set bBark to 1;
				Set bDetected to 1;

			ElseIf (X13Robot2REF.GetDetected Player) && (X13Robot2REF.GetDead == 0)

				ShowMessage NVDLC03X13SpottedMsg;
				Set bBark to 1;
				Set bDetected to 1;

			ElseIf (X13Robot3REF.GetDetected Player) && (X13Robot3REF.GetDead == 0)
				ShowMessage NVDLC03X13SpottedMsg;
				Set bBark to 1;
				Set bDetected to 1;

			ElseIf (NVDLC03X13Setup.nTest == 4)

				If (X13Robot1REF.GetDead) || (X13Robot2REF.GetDead) || (X13Robot3REF.GetDead)
					ShowMessage NVDLC03X13DestroyedMsg;																		;Tell the player a robot was destroyed
					Set bDetected to 1;

				ElseIf (X13Robot1REF.GetUnconscious) && (X13Robot2REF.GetUnconscious) && (X13Robot3REF.GetUnconscious)
					NVDLC03X13COMPREF.Activate NVDLC03X13COMPREF 1;

				EndIf
			EndIf
		EndIf

		If (bDetected == 1)
			X13FAILREF.Activate X13FAILREF 1;
		EndIf
	EndIf

	If (bBark == 1)
		Set fBarkTimer to (fBarkTimer + GetSecondsPassed);

		If (fBarkTimer > 1)
			NVDLC03StealthSuitVoiceCrREF.Say StealthSuitX13RobotDetectionBark 1 NVDLC03StealthSuitVoiceCr 1;		;Tell the player they were spotted by robots
			Set fBarkTimer to 0;
			Set bBark to 0;
		EndIf
	EndIf

END

scn NVDLC03AutoDocTimerSCRIPT


; Does the fade to black and back when the Auto-Doc is used to cure the player.
; JSH 12/15/10

; ETB 02/15/11 	- Added condition for showing the "Healthy" msg so the routine could be used to install implants also.
; ETB 03/2/11 	- 


Short bNoHealthyMessage				; 0 = Show "NVDLC03HQHealthyMsg", 1 = Do not show "NVDLC03HQHealthyMsg".
Short	nStage								; 0 = Fadeout not started, 1 = Fadeout in progress, 2 = Fadeout completed.
Float	fTimer								; Timer used for animation control.


BEGIN GameMode

	If ( nStage == 0 )
		Set fTimer to 4;
		Set nStage to 1;
	EndIf

	Set fTimer to ( fTimer - GetSecondsPassed );

	If ( fTimer <= 3 ) && ( nStage == 1 )
		ApplyImageSpaceModifier FadeToBlackISFX;
		PlaySound QSTFortSaveCaesar;
		Set nStage to 2;
	EndIf

	If ( fTimer <= 0 ) &&  ( IsImagespaceActive FadeToBlackISFX == 0 )
		If ( bNoHealthyMessage == 0 )
			ShowMessage NVDLC03HQHealthyMsg;
		Elseif ( bNoHealthyMessage == 1 )
			Set bNoHealthyMessage to 0;
		Endif
		EnablePlayerControls;
		Set nStage to 0;
; --------------------------------	Implant Installed Msg Vars ------------------------------	\/	; ETB 03/2/11- Used in "NVDLC03SinkAutoDocSCRIPT"
		If ( NVDLC03SinkAutoDocREF.nImplantC13Installed == 1 )
			Set NVDLC03SinkAutoDocREF.nImplantC13Installed to 2;
		Endif
		If ( NVDLC03SinkAutoDocREF.nImplantM5Installed == 1 )
			Set NVDLC03SinkAutoDocREF.nImplantM5Installed to 2;
		Endif
		If ( NVDLC03SinkAutoDocREF.nImplantY3Installed == 1 )
			Set NVDLC03SinkAutoDocREF.nImplantY3Installed to 2;
		Endif
		If ( NVDLC03SinkAutoDocREF.nImplantY7Installed == 1 )
			Set NVDLC03SinkAutoDocREF.nImplantY7Installed to 2;
		Endif
; -----------------------------------------------------------------------------------------------------------	/\
		StopQuest NVDLC03AutoDocTimer;
	EndIf

END

scn NVDLC03AutoDocSCRIPT


; Heals the player and allows him to change his appearance when activated.
; JSH 12/15/10


Short nButton
Short bBarberActive					; 0 = no barber option, 1 = barber option purchased
Short bCosmeticSurgeryActive		; 0 = no cosmetic surgery option, 1 = cosmetic surgery option purchased


BEGIN OnActivate

	If ( IsActionRef player )
		ShowMessage NVDLC03HQAutoDocMsg;
	EndIf

END


BEGIN MenuMode 1001

	Set nButton to GetButtonPressed;

	; Physical Exam - total restoration.
	If ( nButton == 1 )

		DisablePlayerControls;
		StartQuest NVDLC03AutoDocTimer;

		; Heal hit points.
		Player.ResetHealth;

		; Heal crippled limbs.
		Player.RestoreActorValue PerceptionCondition 100;
		Player.RestoreActorValue EnduranceCondition 100;
		Player.RestoreActorValue LeftAttackCondition 100;
		Player.RestoreActorValue RightAttackCondition 100;
		Player.RestoreActorValue LeftMobilityCondition 100;
		Player.RestoreActorValue RightMobilityCondition 100;

		; Remove addictions.
		RemoveSpell WithdrawalAlcohol;
		RemoveSpell WithdrawalAntNectar;
		RemoveSpell WithdrawalBuffout;
		RemoveSpell WithdrawalHydra;
		RemoveSpell WithdrawalJet;
		RemoveSpell WithdrawalMentats;
		RemoveSpell WithdrawalMorphine;
		RemoveSpell WithdrawalPsycho;
		RemoveSpell WithdrawalQuantumNukacola;
		RemoveSpell WithdrawalRebound;
		RemoveSpell WithdrawalSteady;
		RemoveSpell WithdrawalTobacco;
		RemoveSpell WithdrawalTurbo;
		Set Generic.Addicted to 0;

		; Remove radiation sickness.
		Player.RestoreActorValue RadiationRads 1000;

	EndIf

	; Cosmetic Surgery - change appearance.
	If (nButton == 2)

		If ( bCosmeticSurgeryActive == 1 )
			ShowPlasticSurgeonMenu;
		Else
			ShowMessage NVDLC03HQAutoDocNoSurgeryMsg;
		EndIf

	EndIf

	; Barber - change hairstyle.
	If (nButton == 3)

		If ( bBarberActive == 1 )
			ShowBarberMenu;
		Else
			ShowMessage NVDLC03HQAutoDocNoBarberMsg;
		EndIf

	EndIf

END

scn NVDLC03RadarFenceSCRIPT

; Script control for the radar fence, teleports player back to central building if they wander too far out
; RLee 1/5/11
; SWP 01/12/11 Changed pylon distance check to 23552
; RLee 3/8/11 	 Updated inner pylon distance check to match outer 

Float fTimer;		; Timer for the scripted sequence
Short iState;			; State variable for the scripted sequence
Short bRadar;		; Controller variable for detecting if the player is in radar range

BEGIN GameMode

	If (player.GetInWorldspace NVDLC03BigMT)

		; If the player moves too close to the pylons, too far from the center of the BigMT...
		If (player.GetDistance NVDLC03PylonCenter >= 23552)

			Set fTimer to (fTimer + GetSecondsPassed);

			; Scripted sequence for playing concussion FX, teleportation FX, and moving the player to the central building
			If (iState == 0)
				ShowMessage NVDLC03RadarFenceMsg;
				ApplyImagespaceModifier NVDLC03RadarConcussionISFX;
				Set bRadar to 1;
				Set iState to 1;

			ElseIf (fTimer >= 4) && (iState == 1)
				ShowMessage NVDLC03RadarFenceMsg;
				Set iState to 2;

			ElseIf (fTimer >= 8) && (iState == 2)
				PlaySound musBTTLAllCityIntro;
				ApplyImagespaceModifier NVDLC03PlayerRezInISFX;
				Set iState to 3;

			ElseIf (fTimer > 12) && (iState == 3) && (player.getdead == 0)
				Player.MoveTo NVDLC03StartMarker;
				IncrementScriptedChallenge NVDLC03ChallengeOuterSpace;
				Set iState to 4;
			EndIf

		; If the player is currently in the affected range and return to the safe range, the variables are reset and FX removed.
		ElseIf (bRadar) && (player.GetDistance NVDLC03PylonCenter < 23552)
			RemoveImagespaceModifier NVDLC03RadarConcussionISFX;
			Set iState to 0;
			Set fTimer to 0;
			Set bRadar to 0;
		EndIf
	EndIf

END

ScriptName NVDLC03TransitionToTheSinkSCRIPT


; Transitions player from end slides back to The Sink.
; JSH 12/13/10
; OMP 03/08/2011 - Added the MQ05 SetStage call from NVDLC03ThinkTankDoorSCRIPT.
; OMP 03/30/2011 - Clear the Stealth Suit's bOverride var to allow it to talk again, now that the movie's over.
; OMP 04/05/2011 - Assigned the right value to the Stealth Suit's bOverride var.
; OMP 04/28/2011 - Removed the Set bOverride call since we're now using the new function to prevent suit barks.


Short	bDoOnce	;							; Used to run initialization code once
Float	fTimer;								; Used to transition after fade out is done
Short bFadeOutDone;						; Used to trap out once the fade out is done


BEGIN GameMode

		If ( bFadeOutDone == 1 )
			Return;
		EndIf

		If ( bDoOnce== 0 )
			PlayMusic EndTransitionStinger;
			Set fTimer To 4;
			Set bDoOnce To 1;
		EndIf

		Set fTimer to ( fTimer - GetSecondsPassed );

		If ( fTimer <= 4 ) && ( bDoOnce == 1 )
			ApplyImageSpaceModifier FadeToBlackPermanent;
			Set bDoOnce to 2;
		EndIf

		If ( fTimer <= 0 ) 
			Set bFadeOutDone To 1;
			SetStage NVDLC03MQ05 100;
			Player.MoveTo NVDLC03EndMarker;
			StopQuest NVDLC03TransitionToTheSink;
		EndIf

END

ScriptName NVDLC03EndingSCRIPT


; Ending slide dialogue.
; JSH 12/10/10
; OMP 01/07/2011 - Apply NVDLC03RestUp to player.
; OMP 01/18/2011 - Added a call to set the nRunScript variable in NVDLC03SlideShowTriggerREF to run the Ending Slide sequence.
; OMP 02/24/2011 - Added code to handle switching slides.
; OMP 03/04/2011 - Added code to track player progress on discovering secondary locations and Sink module installations.
; OMP 03/30/2011 - Set the Stealth Suit bOverride var to prevent it from talking during the movie.
; OMP 04/28/2011 - Removed the Set bOverride call since we're now using the new function to prevent suit barks.


Short	bEndingStarted;																	; Make sure the script runs once only
Short	bCheckSinkBuddies;																; Used to loop through the Sink Buddy topics, if any
Int		nLastBuddyShown;																; Holds the last Sink Buddy topic shown
Short	nBuddiesUnlockedCount;														; Used to determine how many Sink Buddies the player unlocked
Short	nLocationsFoundCount;															; Used to determine how many secondary locations the player has discovered


BEGIN GameMode

	If (bEndingStarted == 0)

		Set bEndingStarted to 1;

		; Check all the Sink Buddies to see how many were installed
		If ( NVDLC03DialogueHQBuddies.bAutodocUnlocked == 1 )
			Set nBuddiesUnlockedCount To ( nBuddiesUnlockedCount + 1 );
		EndIf
		If ( NVDLC03DialogueHQBuddies.bBiologyUnlocked == 1 )
			Set nBuddiesUnlockedCount To ( nBuddiesUnlockedCount + 1 );
		EndIf
		If ( NVDLC03DialogueHQBuddies.bBookChuteUnlocked == 1 )
			Set nBuddiesUnlockedCount To ( nBuddiesUnlockedCount + 1 );
		EndIf
		If ( NVDLC03DialogueHQBuddies.bJukeboxUnlocked == 1 )
			Set nBuddiesUnlockedCount To ( nBuddiesUnlockedCount + 1 );
		EndIf
		If ( NVDLC03DialogueHQBuddies.bLightSwitch01Unlocked == 1 )
			Set nBuddiesUnlockedCount To ( nBuddiesUnlockedCount + 1 );
		EndIf
		If ( NVDLC03DialogueHQBuddies.bLightSwitch02Unlocked == 1 )
			Set nBuddiesUnlockedCount To ( nBuddiesUnlockedCount + 1 );
		EndIf
		If ( NVDLC03DialogueHQBuddies.bMuggyUnlocked == 1 )
			Set nBuddiesUnlockedCount To ( nBuddiesUnlockedCount + 1 );
		EndIf
		If ( NVDLC03DialogueHQBuddies.bSinkBuddyUnlocked == 1 )
			Set nBuddiesUnlockedCount To ( nBuddiesUnlockedCount + 1 );
		EndIf
		If ( NVDLC03DialogueHQBuddies.bToasterUnlocked == 1 )
			Set nBuddiesUnlockedCount To ( nBuddiesUnlockedCount + 1 );
		EndIf

		; Check all the secondary locations the player discovered
		If ( NVDLC03SLArtilleryLandingMarker.GetMapMarkerVisible == 2 )
			Set nLocationsFoundCount To ( nLocationsFoundCount + 1 );
		EndIf
		If ( NVDLC03SLArtilleryLaunchMarker.GetMapMarkerVisible == 2 )
			Set nLocationsFoundCount To ( nLocationsFoundCount + 1 );
		EndIf
		If ( NVDLC03SLBotanicalMarker.GetMapMarkerVisible == 2 )
			Set nLocationsFoundCount To ( nLocationsFoundCount + 1 );
		EndIf
		If ( NVDLC03SLCazadorMarker.GetMapMarkerVisible == 2 )
			Set nLocationsFoundCount To ( nLocationsFoundCount + 1 );
		EndIf
		If ( NVDLC03SLChemicalMarker.GetMapMarkerVisible == 2 )
			Set nLocationsFoundCount To ( nLocationsFoundCount + 1 );
		EndIf
		If ( NVDLC03CommMarker.GetMapMarkerVisible == 2 )
			Set nLocationsFoundCount To ( nLocationsFoundCount + 1 );
		EndIf
		If ( NVDLC03SLConstructionMarker.GetMapMarkerVisible == 2 )
			Set nLocationsFoundCount To ( nLocationsFoundCount + 1 );
		EndIf
		If ( NVDLC03SLCuckoosNestMarker.GetMapMarkerVisible == 2 )
			Set nLocationsFoundCount To ( nLocationsFoundCount + 1 );
		EndIf
		If ( NVDLC03SLDisintegrationMarker.GetMapMarkerVisible == 2 )
			Set nLocationsFoundCount To ( nLocationsFoundCount + 1 );
		EndIf
		If ( NVDLC03ElijahsWatchMarker.GetMapMarkerVisible == 2 )
			Set nLocationsFoundCount To ( nLocationsFoundCount + 1 );
		EndIf
		If ( NVDLC03SLHazmatMarker.GetMapMarkerVisible == 2 )
			Set nLocationsFoundCount To ( nLocationsFoundCount + 1 );
		EndIf
		If ( NVDLC03SLHexMarker.GetMapMarkerVisible == 2 )
			Set nLocationsFoundCount To ( nLocationsFoundCount + 1 );
		EndIf
		If ( NVDLC03SLHologramMarker.GetMapMarkerVisible == 2 )
			Set nLocationsFoundCount To ( nLocationsFoundCount + 1 );
		EndIf
		If ( NVDLC03LittleYangtzeMarker.GetMapMarkerVisible == 2 )
			Set nLocationsFoundCount To ( nLocationsFoundCount + 1 );
		EndIf
		If ( NVDLC03SLLoadingMarker.GetMapMarkerVisible == 2 )
			Set nLocationsFoundCount To ( nLocationsFoundCount + 1 );
		EndIf
		If ( NVDLC03MedCenterMarker.GetMapMarkerVisible == 2 )
			Set nLocationsFoundCount To ( nLocationsFoundCount + 1 );
		EndIf
		If ( NVDLC03MagnetohydraulicsMarker.GetMapMarkerVisible == 2 )
			Set nLocationsFoundCount To ( nLocationsFoundCount + 1 );
		EndIf
		If ( NVDLC03SLNightstalkerMarker.GetMapMarkerVisible == 2 )
			Set nLocationsFoundCount To ( nLocationsFoundCount + 1 );
		EndIf
		If ( NVDLC03SLRuinsMarker.GetMapMarkerVisible == 2 )
			Set nLocationsFoundCount To ( nLocationsFoundCount + 1 );
		EndIf
		If ( NVDLC03SLSaturniteMarker.GetMapMarkerVisible == 2 )
			Set nLocationsFoundCount To ( nLocationsFoundCount + 1 );
		EndIf
		If ( NVDLC03SLSecuritronMarker.GetMapMarkerVisible == 2 )
			Set nLocationsFoundCount To ( nLocationsFoundCount + 1 );
		EndIf
		If ( NVDLC03SLTunnelEastMarker.GetMapMarkerVisible == 2 )
			Set nLocationsFoundCount To ( nLocationsFoundCount + 1 );
		EndIf
		If ( NVDLC03NorthTunnelMarker.GetMapMarkerVisible == 2 )
			Set nLocationsFoundCount To ( nLocationsFoundCount + 1 );
		EndIf
		If ( NVDLC03SLTunnelWestMarker.GetMapMarkerVisible == 2 )
			Set nLocationsFoundCount To ( nLocationsFoundCount + 1 );
		EndIf
		If ( NVDLC03SLUlyssesPointMarker.GetMapMarkerVisible == 2 )
			Set nLocationsFoundCount To ( nLocationsFoundCount + 1 );
		EndIf
		If ( NVDLC03SLVillageMarker.GetMapMarkerVisible == 2 )
			Set nLocationsFoundCount To ( nLocationsFoundCount + 1 );
		EndIf
		If ( NVDLC03SLWeatherStationMarker.GetMapMarkerVisible == 2 )
			Set nLocationsFoundCount To ( nLocationsFoundCount + 1 );
		EndIf
		If ( NVDLC03X12MapMarker.GetMapMarkerVisible == 2 )
			Set nLocationsFoundCount To ( nLocationsFoundCount + 1 );
		EndIf

		RemoveImageSpaceModifier FriendOfTheNightInteriorISFX;
		RemoveImageSpaceModifier FriendOfTheNightISFX;
		StopQuest DisguiseFactionPulseQuest;										; Turn off disguise notifications

		Player.DispelAllSpells;															; Removes all chem effects
		Player.CastImmediateOnSelf NVDLC03RestUp;							; Restore the player's Dehydration, Sleep Deprivation, and Starvation
		;PipBoyRadioOff;																	; Turn off PipBoy radio
		Player.ResetHealth;																; Heal the player

		PlayMusic EndSlideShow;

		SetInCharGen 1;																	; Prevent level up screen from firing
		Player.AddScriptPackage NVDLC03PlayerNoSavePackage;				; Prevent player from saving the game
		DisablePlayerControls 1 1 1 1 1 1 1;											; Prevent player from doing anything

		Set NVDLC03SlideShowTriggerREF.nRunScript To 1;						; Set up the slide show trigger to run the ending
		Player.MoveTo NVDLC03SlideShowMarkerREF;							; Send the player to the "theatre" to start the slide show

	EndIf

	If ( bCheckSinkBuddies == 1 )

		Set bCheckSinkBuddies To 0;

		PlaySound OBJProjectorGOATNext;
		ApplyImageSpaceModifier FadeToBlackAndBackQuickHalfsISFX;

		; Biological Research Station
		If ( nLastBuddyShown < 1 && NVDLC03SinkBioResearchStationREF.bBiologyUnlocked == 1 )

			Set nLastBuddyShown To 1;

			NVDLC03NarratorREF.SayTo Player NVDLC03EndingSlideChipBioResearch;

		; Book Chute
		ElseIf ( nLastBuddyShown < 2 && NVDLC03SinkBookChuteREF.bBookChuteUnlocked == 1 )

			Set nLastBuddyShown To 2;

			NVDLC03NarratorREF.SayTo Player NVDLC03EndingSlideChipBookChute;

		; Light Switches
		ElseIf ( nLastBuddyShown < 3 && NVDLC03SinkLightSwitch01REF.bLightSwitch01Unlocked == 1 && NVDLC03SinkLightSwitch02REF.bLightSwitch02Unlocked == 1 )

			Set nLastBuddyShown To 3;

			NVDLC03NarratorREF.SayTo Player NVDLC03EndingSlideChipLightSwitches;

		; Sink
		ElseIf ( nLastBuddyShown < 4 && NVDLC03DialogueHQBuddies.bSinkBuddyUnlocked == 1 )

			Set nLastBuddyShown To 4;

			NVDLC03NarratorREF.SayTo Player NVDLC03EndingSlideChipSink;

		; Toaster
		ElseIf ( nLastBuddyShown < 5 && NVDLC03SinkToasterOfDoomREF.bToasterUnlocked == 1 )

			Set nLastBuddyShown To 5;

			NVDLC03NarratorREF.SayTo Player NVDLC03EndingSlideChipToaster;

		; Muggy
		ElseIf ( nLastBuddyShown < 6 && NVDLC03MuggyREF.bMuggyUnlocked == 1 )

			Set nLastBuddyShown To 6;

			NVDLC03NarratorREF.SayTo Player NVDLC03EndingSlideChipMuggy;

		; Jukebox
		ElseIf ( nLastBuddyShown < 7 && NVDLC03DialogueHQBuddies.bJukeboxUnlocked == 1)

			Set nLastBuddyShown To 7;

			NVDLC03NarratorREF.SayTo Player NVDLC03EndingSlideChipJukebox;

		; Auto-Doc
		ElseIf ( nLastBuddyShown < 8 && NVDLC03SinkAutoDocREF.bAutoDocUnlocked == 1 )

			Set nLastBuddyShown To 8;

			NVDLC03NarratorREF.SayTo Player NVDLC03EndingSlideChipAutoDoc;

		; Done with buddies or none to display, move on to X-8
		Else

			NVDLC03NarratorREF.SayTo Player NVDLC03EndingSlideX8;

		EndIf

	EndIf

END

ScriptName NVDLC03NarratorSCRIPT


; Handles intro and ending slideshow.
; JSH 12/10/10
; OMP 01/07/2011 - Uncommented the calls to ApplyImageSpaceModifier. Added SayToDone code blocks for NVDLC03IntroSlideFNV and NVDLC03IntroSlideDLC.
; JSH 01/24/11 - set Gamehour to 12:30 AM (0.5) and fast-forwarded game 3 days later when player first arrives at Big MT.
; JSH 02/07/11 - set Gamehour to 5 AM (5) when player first arrives at Big MT.
; OMP 02/18/2011 - Changed first Ending Slide sequence topic to NVDLC03EndingSlideStart.
; OMP 02/24/2011 - Set up the Ending Slide sequence nodes.
; OMP 02/25/2011 - Set up the Failure Ending Slide sequence nodes.
